INSERT INTO `chapter` VALUES (12, 12, 'Chương 1 Người Cô Độc', 'Tỉnh giấc bắt đầu bằng việc nhận thức tôi và hiện tại.', '<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tỉnh giấc bắt đầu bằng việc nhận thức t&ocirc;i v&agrave; hiện tại. Hắn mở mắt v&agrave; cứ thế nằm y&ecirc;n đ&oacute;, nh&igrave;n b&acirc;ng quơ l&ecirc;n trần nh&agrave; v&agrave; v&agrave;o trong ch&iacute;nh hắn cho đến khi &yacute; thức được t&ocirc;i của m&igrave;nh. Để từ đ&oacute; biết được rằng hắn đang ở đ&acirc;y, trong c&aacute;i hiện tại của ng&agrave;y mới. Rồi &yacute; niệm về nơi chốn chậm r&atilde;i đến với hắn l&agrave;m hắn y&ecirc;n l&ograve;ng đ&ocirc;i ch&uacute;t; v&igrave; nơi chốn ấy, trong c&aacute;i buổi s&aacute;ng n&agrave;y, l&agrave; nơi m&agrave; hắn gọi l&agrave; NH&Agrave;.<br />\r\n<br />\r\nNhưng hiện tại kh&ocirc;ng chỉ đơn thuần l&agrave; hiện tại. Hiện tại c&ograve;n l&agrave; một gợi nhớ đau thương: l&agrave; một ng&agrave;y sau ng&agrave;y trước đ&oacute;, một năm sau năm trước đ&oacute;. Mọi hiện tại đều được gắn m&aacute;c với ng&agrave;y của ch&iacute;nh n&oacute;, kết nối qu&aacute; khứ cổ xưa với hiện tại, cho đến khi - sớm hay muộn - m&agrave; c&oacute; lẽ - kh&ocirc;ng, kh&ocirc;ng phải c&oacute; lẽ, m&agrave; l&agrave; chắc chắn - gi&acirc;y ph&uacute;t đ&oacute; sẽ đến.<br />\r\n<br />\r\nNỗi sợ h&atilde;i vặn nh&eacute;o mọi tế b&agrave;o thần kinh trong hắn. Ph&aacute;t bệnh khi nghĩ đến điều đang đợi ngo&agrave;i kia, c&aacute;i chết đang chờ hắn.<br />\r\n<br />\r\nNhưng trước đ&oacute;, c&aacute;i bộ n&atilde;o kỷ luật kh&ocirc;ng g&igrave; xoay chuyển được vẫn đang chiếm giữ quyền kiểm so&aacute;t cơ thể n&agrave;y v&agrave; đang dần ra lệnh cho ch&uacute;ng hoạt động từng bộ phận một: đ&ocirc;i ch&acirc;n duỗi d&agrave;i, phần lưng dưới khẽ cựa m&igrave;nh, những ng&oacute;n tay siết lại rồi thả ra. V&agrave; giờ th&igrave;, tổng thể c&aacute;i bộ m&aacute;y đ&oacute; đang chuẩn bị thi h&agrave;nh c&aacute;i mệnh lệnh đầu ti&ecirc;n được đưa ra trong ng&agrave;y: NGỒI DẬY.<br />\r\n<br />\r\nC&aacute;i th&acirc;n thể đ&oacute; ngoan ngo&atilde;n nhấc m&igrave;nh rời khỏi giường - khẽ nhăn mặt bởi cơn đau từ chứng vi&ecirc;m khớp nơi ng&oacute;n tay v&agrave; đầu gối tr&aacute;i, hơi buồn n&ocirc;n v&igrave; cơn đau dạ d&agrave;y lại đang trỗi dậy h&agrave;nh hạ - trần truồng l&ecirc; bước v&agrave;o nh&agrave; tắm, với c&aacute;i b&agrave;ng quang trống rỗng trong c&aacute;i th&acirc;n thể nặng tr&ecirc;n bẩy mươi k&iacute; l&ocirc;, bất chấp những nỗ lực như h&agrave;nh x&aacute;c trong ph&ograve;ng tập thể dục của hắn! Hắn đi tới trước gương.<br />\r\n<br />\r\nNhững g&igrave; hiện ra trong chiếc gương kia kh&ocirc;ng chỉ đơn giản l&agrave; một khu&ocirc;n mặt m&agrave; c&ograve;n l&agrave; vết hằn s&acirc;u của những khắc khổ. Đ&oacute; l&agrave; những g&igrave; hắn đ&atilde; tự chuốc lấy, l&agrave; mớ ngổn ngang hắn đ&atilde; tự đặt m&igrave;nh v&agrave;o sau năm mươi t&aacute;m năm r&ograve;ng r&atilde;. Phản chiếu tr&ecirc;n đ&oacute; l&agrave; c&aacute;i nh&igrave;n v&ocirc; hồn buồn b&atilde;, chiếc mũi th&ocirc; kệch, c&aacute;i miệng chảy xệ tới tận g&oacute;c của khu&ocirc;n mặt, nhăn nh&oacute; như thể hắn đang ngậm trong miệng cả một nh&uacute;m thuốc độc; hai g&ograve; m&aacute; hốc h&aacute;c thiếu vắng những thớ thịt; cổ họng ẩn nấp trong những ch&ugrave;m nếp nhăn ủ rũ. Đ&ocirc;i mắt sầu muộn như thể của một người vừa chạy hay bơi đến kh&ocirc;ng c&ograve;n một ch&uacute;t sức lực n&agrave;o trong người. C&aacute;i th&acirc;n thể tạo h&oacute;a tạo ra đ&oacute; sẽ cứ vật lộn kh&ocirc;ng ngừng cho đến khi n&oacute; kh&ocirc;ng c&ograve;n c&oacute; thể tiếp tục được nữa. Kh&ocirc;ng phải v&igrave; n&oacute; ki&ecirc;n cường. M&agrave; v&igrave; n&oacute; kh&ocirc;ng thể h&igrave;nh dung ra một con đường n&agrave;o kh&aacute;c.<br />\r\n<br />\r\nCứ d&aacute;n mắt v&agrave;o trong gương bất định, hắn nh&igrave;n thấy nhiều khu&ocirc;n mặt kh&aacute;c nhau lấp l&oacute; trong khu&ocirc;n mặt của ch&iacute;nh hắn - khu&ocirc;n mặt của một đứa trẻ sơ sinh, một cậu nh&oacute;c, một ch&agrave;ng trai trẻ, rồi của một ch&agrave;ng trai kh&ocirc;ng c&ograve;n trẻ lắm - tất cả vẫn tồn tại trong hắn, được bảo quản như những tảng h&oacute;a thạch cứ xếp chồng l&ecirc;n nhau, lớp n&agrave;y đến lớp kh&aacute;c, v&agrave;, cũng giống như h&oacute;a thạch, ch&uacute;ng đ&atilde; kh&ocirc;ng c&ograve;n sự sống. Th&ocirc;ng điệp ch&uacute;ng gửi đến cho c&aacute;i x&aacute;c sống của tạo h&oacute;a n&agrave;y l&agrave;: H&atilde;y nh&igrave;n ch&uacute;ng ta đ&acirc;y - ch&uacute;ng ta đ&atilde; chết - chết th&igrave; c&oacute; g&igrave; m&agrave; phải sợ?<br />\r\n<br />\r\nRồi hắn trả lời họ: Nhưng c&aacute;i chết đến với c&aacute;c người thật từ tốn v&agrave; dễ d&agrave;ng. Ta sợ phải vội v&atilde;.<br />\r\n<br />\r\nHắn cứ nh&igrave;n rồi nh&igrave;n, cứ đăm đăm v&agrave;o ch&iacute;nh hắn trong chiếc gương kia. Đ&ocirc;i m&ocirc;i hắn khẽ rời nhau ra. Hắn bắt đầu thở qua kh&oacute;e miệng. Cho đến khi bộ n&atilde;o hắn n&oacute;ng l&ograve;ng ra lệnh cho hắn rửa mặt, cạo r&acirc;u v&agrave; chải t&oacute;c. C&aacute;i th&acirc;n thể trần truồng của hắn cần phải được che chắn. Hắn phải mặc quần &aacute;o v&agrave;o, v&igrave; hắn đang sắp phải ra ngo&agrave;i, bước v&agrave;o thế giới của những con người kh&aacute;c ngo&agrave;i kia, m&agrave; những người n&agrave;y cần được nh&igrave;n nhận về hắn như những g&igrave; hắn đ&atilde; gầy dựng. H&agrave;nh vi của hắn cần phải nằm trong sự thỏa đ&aacute;ng với họ.<br />\r\n<br />\r\nChậm r&atilde;i, hắn rửa mặt, cạo r&acirc;u v&agrave; chải t&oacute;c, để hắn c&oacute; thể tiếp nhận những tr&aacute;ch nhiệm của ch&iacute;nh hắn đối với những con người ngo&agrave;i kia. Hắn thậm ch&iacute; lại cảm thấy phấn chấn v&igrave; trong thế giới ngo&agrave;i kia, c&oacute; chỗ cho hắn. Hắn biết người ta mong đợi g&igrave;.<br />\r\n<br />\r\nHắn biết t&ecirc;n của hắn. Người ta gọi hắn l&agrave; George.<br />\r\n<br />\r\nĐến khi đ&atilde; ăn vận chỉnh tề, hắn đ&atilde; dần trở lại l&agrave; &Ocirc;ng- một người trung ni&ecirc;n đĩnh đạc, đ&atilde; dần trở lại, d&ugrave; &iacute;t d&ugrave; nhiều, l&agrave; George - mặc d&ugrave; kh&ocirc;ng ho&agrave;n to&agrave;n l&agrave; George m&agrave; người ta tr&ocirc;ng đợi v&agrave; sẵn s&agrave;ng để nhận ra. Ai gọi điện cho hắn v&agrave;o giờ n&agrave;y của buổi s&aacute;ng chắc hẳn sẽ ngạc nhi&ecirc;n v&agrave; bối rối, thậm ch&iacute; sợ h&atilde;i, nếu họ c&oacute; thể thực sự thấy được c&aacute;i thể x&aacute;c chỉ ba phần người n&agrave;y l&agrave; thứ m&agrave; họ đang n&oacute;i chuyện c&ugrave;ng. Nhưng, dĩ nhi&ecirc;n, họ sẽ kh&ocirc;ng bao giờ biết - giọng của hắn bắt chước George của họ hầu như ho&agrave;n hảo. Thậm ch&iacute; Charlotte cũng bị n&oacute; qua mặt. Chỉ đ&ocirc;i ba lần b&agrave; cảm nhận được điều g&igrave; đ&oacute; kh&ocirc;ng tự nhi&ecirc;n, b&agrave; hỏi: &ldquo;Geo - anh vẫn ổn chứ?&rdquo;<br />\r\n<br />\r\n&Ocirc;ng đi qua căn ph&ograve;ng ph&iacute;a ngo&agrave;i, nơi &ocirc;ng gọi l&agrave; ph&ograve;ng đọc, v&agrave; đi xuống cầu thang. Những bậc thang hẹp v&agrave; dốc rẽ v&agrave;o một g&oacute;c căn nh&agrave;. Bạn c&oacute; thể chạm v&agrave;o cả hai tay vịn của lan can bằng khuỷu tay của m&igrave;nh, v&agrave; phải c&uacute;i đầu xuống, ngay cả khi nếu bạn giống như George, chỉ cao 1 m&eacute;t 73. Căn nh&agrave; n&agrave;y kh&aacute; nhỏ v&agrave; được t&iacute;nh to&aacute;n để sử dụng triệt để diện t&iacute;ch. &Ocirc;ng thường cảm thấy được bảo vệ trong sự chật hẹp của căn nh&agrave;; chẳng c&oacute; chỗ để m&agrave; cảm thấy c&ocirc; độc.<br />\r\n<br />\r\nẤy thế m&agrave;...<br />\r\n<br />\r\nH&igrave;nh dung hai con người, sống c&ugrave;ng nhau ng&agrave;y qua ng&agrave;y, th&aacute;ng đoạn th&aacute;ng, năm tiếp năm, trong một khoảng kh&ocirc;ng gian nhỏ hẹp, khuỷu tay chạm khuỷu tay khi đứng nấu nướng b&ecirc;n bếp l&ograve;, &eacute;p v&agrave;o nhau trong những bậc thang chật chội, c&ugrave;ng đứng cạo r&acirc;u trước tấm gương nhỏ trong ph&ograve;ng tắm, kh&ocirc;ng ngừng đụng chạm, hu&yacute;ch đẩy v&agrave;o th&acirc;n thể của nhau v&ocirc; t&igrave;nh hay hữu &yacute;, với tr&agrave;n đầy nhục dục, mạnh bạo, ngượng ng&ugrave;ng v&agrave; n&oacute;ng vội, trong thịnh nộ lẫn trong &yacute; t&igrave;nh - h&igrave;nh dung những g&igrave; m&agrave; họ đ&atilde; khắc dấu v&ocirc; h&igrave;nh v&agrave;o trong từng khoảng kh&ocirc;ng, từng ng&otilde; ng&aacute;ch của căn nh&agrave;. C&aacute;nh cửa ph&ograve;ng bếp được x&acirc;y qu&aacute; hẹp. Hai con người trong vội v&atilde;, với thức ăn trong đĩa tr&ecirc;n tay, kh&ocirc;ng khỏi chạm v&agrave;o nhau nơi n&agrave;y. V&agrave; cũng tại đ&acirc;y, hầu như mọi buổi s&aacute;ng, khi George đặt ch&acirc;n tới nấc cuối c&ugrave;ng của bậc thang, &ocirc;ng lại c&oacute; cảm gi&aacute;c bất chợt như thấy m&igrave;nh đang ở tr&ecirc;n một tấm răng cưa lởm chởm, t&agrave;n độc v&agrave; vỡ vụn ra từng mảnh - cho d&ugrave; những dấu vết đ&atilde; phai nhạt v&agrave; ch&igrave;m v&agrave;o trong đổ n&aacute;t.<br />\r\n<br />\r\nV&agrave; giờ, tại nơi đ&acirc;y, &ocirc;ng dừng lại trong khoảnh khắc v&agrave; nhận thức với một nỗi thống khổ tột c&ugrave;ng như thể n&oacute; chỉ mới vừa h&ocirc;m qua đấy th&ocirc;i: Jim đ&atilde; chết. Đ&atilde; chết.<br />\r\n<br />\r\n&Ocirc;ng đứng tr&acirc;n tr&acirc;n ở đ&oacute; trong y&ecirc;n lặng, đợi cho cơn đau thắt qua đi. Rồi &ocirc;ng bước v&agrave;o trong bếp. Những cơn đau v&agrave;o những buổi s&aacute;ng như thế n&agrave;y qu&aacute; m&atilde;nh liệt để c&oacute; thể dập tắt bằng sự đa cảm. Khi ch&uacute;ng qua đi, &ocirc;ng thấy nhẹ nh&otilde;m. Như thể vượt qua một cơn chuột r&uacute;t khủng khiếp.<br />\r\n<br />\r\nH&ocirc;m nay c&oacute; nhiều kiến chạy th&agrave;nh từng h&agrave;ng d&agrave;i cong cong uốn lượn dọc s&agrave;n nh&agrave;, tr&egrave;o qua bồn rửa b&aacute;t v&agrave; đe dọa ngăn tủ nơi &ocirc;ng cất giữ mứt v&agrave; mật ong. &Ocirc;ng với tay lấy b&igrave;nh xịt c&ocirc;n tr&ugrave;ng v&agrave; bất chợt tưởng tượng ra h&igrave;nh ảnh m&igrave;nh như một kẻ gi&agrave; nua ngoan cố v&agrave; đầy &aacute;c t&acirc;m đang ra oai trước đ&aacute;m c&ocirc;n tr&ugrave;ng đ&aacute;ng để ta ngưỡng mộ v&agrave; học hỏi n&agrave;y. Sinh vật sống hủy hoại sinh vật sống - trước sự chứng kiến của những đồ v&ocirc; tri gi&aacute;c - nồi v&agrave; chảo, dao v&agrave; dĩa, hộp v&agrave; chai - những thứ kh&ocirc;ng g&oacute;p mặt trong vương quốc của sự tiến h&oacute;a. Tại sao? Tại sao? C&oacute; phải những kẻ th&ugrave; từ vũ trụ, những kẻ bạo ch&uacute;a kh&ocirc;ng gian đang cố l&agrave;m lu mờ ch&uacute;ng ta trước sự tồn tại của ch&iacute;nh &ocirc;ng bằng c&aacute;ch sắp đặt cho ch&uacute;ng ta chiến đấu chống lại những đồng minh của tạo h&oacute;a kh&aacute;c, những đồng nạn nh&acirc;n với sự t&agrave;n bạo của ch&iacute;nh &ocirc;ng? Nhưng, than &ocirc;i, trong l&uacute;c George đang mải ch&igrave;m đắm trong những suy nghĩ m&ocirc;ng lung n&agrave;y th&igrave; đ&agrave;n kiến đ&atilde; chết sạch, th&acirc;n x&aacute;c ch&uacute;ng bị ch&ugrave;i sạch bằng tấm giẻ ướt v&agrave; được giũ bỏ bởi những d&ograve;ng nước xối mạnh xuống bồn rửa b&aacute;t.<br />\r\n<br />\r\n&Ocirc;ng tự chuẩn bị cho m&igrave;nh một đĩa đồ ăn s&aacute;ng gồm trứng chần, thịt lợn muối x&ocirc;ng kh&oacute;i với b&aacute;nh m&igrave; nướng v&agrave; c&agrave; ph&ecirc;. &Ocirc;ng ngồi xuống một g&oacute;c b&ecirc;n chiếc b&agrave;n ăn v&agrave; bắt đầu nhai. Trong đầu &ocirc;ng kh&ocirc;ng ngừng vang l&ecirc;n những vần điệu m&agrave; v&uacute; nu&ocirc;i của &ocirc;ng đ&atilde; dạy khi &ocirc;ng c&ograve;n l&agrave; một đứa trẻ ở Anh quốc h&agrave;ng chục năm về trước:<br />\r\n<br />\r\nTrứng chần ăn với b&aacute;nh m&igrave; nướng rất ngon.<br />\r\n<br />\r\n&Ocirc;ng vẫn nhớ r&otilde; từng n&eacute;t tr&ecirc;n khu&ocirc;n mặt của b&agrave;, m&aacute;i t&oacute;c bạc với đ&ocirc;i mắt m&agrave;u chuột x&aacute;m, th&acirc;n h&igrave;nh đẫy đ&agrave; bưng bữa s&aacute;ng trong khay đặt gọn g&agrave;ng, hơi thở của b&agrave; đứt qu&atilde;ng v&igrave; phải tr&egrave;o qua những bậc thang d&agrave;i. B&agrave; thường c&agrave;u nh&agrave;u với từng bậc thang v&agrave; gọi ch&uacute;ng l&agrave; &ldquo;Những ngọn n&uacute;i gỗ&rdquo; - một trong những c&acirc;u n&oacute;i nhiệm m&agrave;u của tuổi thơ &ocirc;ng.<br />\r\n<br />\r\nTrứng chần ăn với b&aacute;nh m&igrave; nướng rất ngon.<br />\r\n<br />\r\nNếu đ&atilde; ăn lần một, bạn sẽ muốn ăn lần hai!<br />\r\n<br />\r\nA, những kỷ niệm ấm &aacute;p đến se l&ograve;ng của những kho&aacute;i lạc được n&acirc;ng niu chăm s&oacute;c đ&oacute;! &Ocirc;ng chủ George nh&acirc;m nhi bữa s&aacute;ng của m&igrave;nh, v&uacute; nu&ocirc;i đứng nh&igrave;n, mỉm cười v&agrave; đo&aacute;n chắc rằng họ được b&igrave;nh an trong thế giới bi đ&aacute;t nhỏ b&eacute; th&acirc;n y&ecirc;u của họ!<br />\r\n<br />\r\nBữa ăn s&aacute;ng với Jim đ&atilde; từng l&agrave; một trong những thời điểm đẹp nhất trong ng&agrave;y của họ. Đ&oacute; l&agrave; l&uacute;c, khi đang tận hưởng t&aacute;ch c&agrave; ph&ecirc; thứ hai hay thứ ba của m&igrave;nh, họ chia sẻ với nhau những gi&acirc;y ph&uacute;t chuyện tr&ograve; tuyệt diệu nhất. Họ n&oacute;i với nhau về mọi thứ, về bất kể điều g&igrave; trong đầu họ - dĩ nhi&ecirc;n, cả về c&aacute;i chết, về sự tồn tại sau c&aacute;i chết l&agrave; c&oacute; hay kh&ocirc;ng, nếu c&oacute;, th&igrave; điều g&igrave; sẽ c&ograve;n được giữ lại. Họ thậm ch&iacute; c&ograve;n thường xuy&ecirc;n chia sẻ với nhau quan điểm về c&aacute;i lợi v&agrave; c&aacute;i hại của việc bị mưu s&aacute;t v&agrave; về việc dự b&aacute;o trước c&aacute;i chết của m&igrave;nh. Nhưng giờ George kh&ocirc;ng t&agrave;i n&agrave;o nhớ nổi quan điểm của Jim trong c&aacute;c vấn đề n&agrave;y. Những c&acirc;u hỏi đ&oacute; thật kh&oacute; để tiếp nhận một c&aacute;ch nghi&ecirc;m t&uacute;c. Họ đ&atilde; tr&ocirc;ng thật học thuyết, h&agrave;n l&acirc;m.<br />\r\n<br />\r\nCứ giả sử rằng kẻ đ&atilde; chết c&oacute; thể quay về thăm người c&ograve;n sống. Rằng một vật thể hữu h&igrave;nh hay v&ocirc; h&igrave;nh n&agrave;o đ&oacute; được gọi l&agrave; Jim quay trở về để biết George đang sống tiếp ra sao. Liệu n&oacute; c&oacute; thỏa m&atilde;n một ch&uacute;t n&agrave;o? Liệu n&oacute; thậm ch&iacute; c&oacute; đ&aacute;ng kh&ocirc;ng? Trường hợp tốt đẹp nhất, dĩ nhi&ecirc;n, l&agrave; n&oacute; sẽ như chuyến viếng thăm ngắn ngủi của kẻ đang d&otilde;i theo từ một đất nước kh&aacute;c, người được ph&eacute;p nh&igrave;n trộm trong gi&acirc;y l&aacute;t từ sự tự do bao la của ch&iacute;nh anh ta để trong xa c&aacute;ch, qua những tấm k&iacute;nh, nh&igrave;n thấy h&igrave;nh h&agrave;i đang ngồi lặng lẽ b&ecirc;n chiếc b&agrave;n nhỏ trong căn ph&ograve;ng chật hẹp n&agrave;y, đang ăn những quả trứng chần trong im lặng v&agrave; c&ocirc; độc, như một t&ugrave; nh&acirc;n của sự sống.<br />\r\n<br />\r\nCăn ph&ograve;ng kh&aacute;ch thấp trần v&agrave; tối tăm với những chiếc gi&aacute; s&aacute;ch trải d&agrave;i dọc tr&ecirc;n c&aacute;c bức tường đối diện cửa sổ. Những cuốn s&aacute;ch n&agrave;y đ&atilde; kh&ocirc;ng biến George trở n&ecirc;n cao qu&yacute;, tốt đẹp hay kh&ocirc;n ngoan hơn. Chỉ l&agrave; &ocirc;ng th&iacute;ch được nghe tiếng n&oacute;i của ch&uacute;ng, cuốn n&agrave;y hay cuốn kh&aacute;c, t&ugrave;y v&agrave;o t&acirc;m trạng của &ocirc;ng. &Ocirc;ng bạc đ&atilde;i ch&uacute;ng một c&aacute;ch nhẫn t&acirc;m - bất chấp những g&igrave; &ocirc;ng n&oacute;i về n&oacute; trước c&ocirc;ng ch&uacute;ng - để đem &ocirc;ng ch&igrave;m v&agrave;o giấc ngủ, để cướp đi t&acirc;m tr&iacute; của &ocirc;ng từ tay kẻ nắm giữ thời gian, để gi&uacute;p &ocirc;ng thư gi&atilde;n trước những cơn đau co thắt do chứng hở m&ocirc;n vị gi&agrave;y v&ograve;, để t&aacute;n gẫu c&ugrave;ng &ocirc;ng v&agrave; k&eacute;o &ocirc;ng ra khỏi sự sầu muộn, để khai mở những phản xạ c&oacute; điều kiện của kết tr&agrave;ng của &ocirc;ng.<br />\r\n<br />\r\n&Ocirc;ng đưa tay lấy một trong số ch&uacute;ng xuống, v&agrave; Ruskin n&oacute;i với &ocirc;ng[2]: &ldquo;Bạn th&iacute;ch những khẩu s&uacute;ng hơi khi c&ograve;n l&agrave; một đứa nh&oacute;c, r&atilde;nh s&uacute;ng v&agrave; Armstrongs l&agrave; những điều giống nhau duy nhất được tạo ra ho&agrave;n hảo. Điều tệ hại nhất l&agrave;, l&agrave; một đứa trẻ bạn th&iacute;ch bắn, những con chim nhạn lại gh&eacute;t bị bắn. L&agrave; một người lớn, bạn chơi với quyền lực của ch&iacute;nh trị, những kẻ thấp cổ b&eacute; họng ở nước Mỹ n&agrave;y, lại kh&ocirc;ng th&iacute;ch bị chĩa mũi d&ugrave;i v&agrave;o họ. Khi đối mặt với những con đại b&agrave;ng lớn, bạn sợ kh&ocirc;ng d&aacute;m bắn ch&uacute;ng, v&agrave; nếu t&ocirc;i kh&ocirc;ng lầm th&igrave; bạn kh&ocirc;ng dũng cảm trước những anh ch&agrave;ng cao lớn như bạn đ&atilde; dũng cảm trước những ch&uacute; chim nhỏ.&rdquo;<br />\r\n<br />\r\n[2] John Ruskin (1819-1900): nh&agrave; văn, nh&agrave; ph&ecirc; b&igrave;nh nghệ thuật v&agrave; b&igrave;nh luận x&atilde; hội người Anh.<br />\r\n<br />\r\n&Ocirc;ng gi&agrave; Ruskin qu&aacute; quắt, lu&ocirc;n lu&ocirc;n th&acirc;m th&uacute;y trong từng c&acirc;u n&oacute;i, đi&ecirc;n kh&ugrave;ng v&agrave; kh&ocirc;ng tr&oacute;i buộc, với sự mỉa mai tinh tế trong ng&ocirc;n ngữ của m&igrave;nh - &ocirc;ng ta ch&iacute;nh l&agrave; tri kỷ ho&agrave;n hảo cho năm ph&uacute;t trong nh&agrave; cầu s&aacute;ng nay. George thấy bụng m&igrave;nh chuyển động v&agrave; vội v&atilde; tr&egrave;o qua những bậc thang để đi v&agrave;o nh&agrave; vệ sinh, s&aacute;ch cầm tr&ecirc;n tay.<br />\r\n<br />\r\nNgồi ung dung tr&ecirc;n bệ, &ocirc;ng c&oacute; thể nh&igrave;n thấy cảnh vật ngo&agrave;i kia qua cửa sổ. Những người h&agrave;ng x&oacute;m b&ecirc;n kia đường cũng c&oacute; thể nh&igrave;n thấy từ vai &ocirc;ng trở l&ecirc;n, nhưng kh&ocirc;ng thể thấy việc &ocirc;ng đang l&agrave;m. Một buổi s&aacute;ng m&ugrave;a đ&ocirc;ng California x&aacute;m xịt v&agrave; ấm &aacute;p; bầu trời thấp v&agrave; &ecirc;m &aacute;i với sương m&ugrave; Th&aacute;i B&igrave;nh Dương. Xa xa dưới bến cảng ngo&agrave;i kia, bầu trời v&agrave; đại dương sẽ chỉ mang một m&agrave;u x&aacute;m xịt u &aacute;m. Từng h&agrave;ng cọ vẫn đứng đ&oacute; hi&ecirc;n ngang v&agrave; những giọt sương rịn ra tr&ecirc;n l&aacute; c&aacute;c bụi tr&uacute;c đ&agrave;o.<br />\r\n<br />\r\nCon đường n&agrave;y mang t&ecirc;n Hẻm c&acirc;y Nh&atilde;n. C&oacute; thể tại nơi đ&acirc;y những c&acirc;y nh&atilde;n đ&atilde; từng mọc quanh; nhưng giờ th&igrave; chẳng c&ograve;n thấy b&oacute;ng d&aacute;ng ch&uacute;ng đ&acirc;u. Nhiều khả năng c&aacute;i t&ecirc;n n&agrave;y được chọn bởi những người khai hoang đang t&igrave;m c&aacute;ch chạy trốn khỏi th&agrave;nh phố Los Angeles bụi bặm v&agrave; Pasadena[3] hợm hĩnh ngột ngạt, những người đ&atilde; đặt ch&acirc;n đến đ&acirc;y v&agrave;o đầu thế kỷ 20 v&agrave; bị quyến rũ bởi vẻ đẹp như tranh vẽ của nơi n&agrave;y. Họ gọi căn nh&agrave; gỗ một tầng tr&aacute;t vữa v&agrave; những căn l&aacute;n v&aacute;ch che của họ l&agrave; biệt thự ngoại &ocirc;, đặt cho ch&uacute;ng những c&aacute;i t&ecirc;n dễ thương như &ldquo;Mũi Boong&rdquo; v&agrave; &ldquo;Nh&agrave; Tranh&rdquo;, họ gọi những con đường của họ l&agrave; hẻm, ng&otilde; v&agrave; đường m&ograve;n cho hợp với kh&ocirc;ng gian rừng l&agrave;ng m&agrave; họ muốn tạo ra. Giấc mơ l&yacute; tưởng của họ l&agrave; tạo ra một khu l&agrave;ng nhiệt đới Anh Quốc theo kiểu Montmartre[4]: một địa điểm ho&agrave;n hảo, nơi bạn c&oacute; thể vẽ một ch&uacute;t tranh, viết một ch&uacute;t văn v&agrave; uống rượu thật nhiều. Họ tự coi m&igrave;nh l&agrave; những chiến binh đang gắng sức thực hiện những nỗ lực cuối c&ugrave;ng để chống lại thế kỷ 20. Họ g&agrave;o r&uacute; l&ecirc;n lời cảm ơn từ s&aacute;ng đến tối v&igrave; đ&atilde; tho&aacute;t khỏi t&iacute;nh con bu&ocirc;n th&agrave;nh thị đang dần hủy hoại t&acirc;m hồn những con người sống trong n&oacute;. Họ trần tục, hồ hởi, bướng bỉnh v&agrave; kh&ocirc;ng theo lề th&oacute;i x&atilde; hội th&ocirc;ng thường, t&ograve; m&ograve; kh&ocirc;ng ngừng nghỉ về h&agrave;nh vi của nhau v&agrave; khoan dung v&ocirc; bờ bến. Họ đ&aacute;nh nhau bằng nắm đấm, bằng vỏ chai v&agrave; đồ vật, chứ kh&ocirc;ng bằng luật sư. Đa phần bọn họ đ&atilde; may mắn chết trước khi Đại biến chuyển diễn ra.<br />\r\n<br />\r\n[3] Pasadena: Một th&agrave;nh phố thuộc tiểu bang California, Hoa Kỳ.<br />\r\n<br />\r\n[4] Montmartre: Một khu phố cổ của Paris, từng l&agrave; nơi tập trung của nhiều họa sĩ nổi tiếng.<br />\r\n<br />\r\nĐại biến chuyển bắt đầu v&agrave;o những năm cuối thập của ni&ecirc;n 40, thế kỷ 20, khi những cựu chiến binh của Đại chiến Thế giới II ồ ạt rời khỏi Bờ Đ&ocirc;ng, c&ugrave;ng với những c&ocirc; vợ mới cưới của họ, như những bầy ong vỡ tổ, đi t&igrave;m những v&ugrave;ng đất m&agrave;u mỡ sinh s&ocirc;i mới của miền Nam đầy nắng gi&oacute;, nơi dừng ch&acirc;n để giũ bỏ nỗi nhớ nh&agrave; tho&aacute;ng qua trong họ trước khi giong buồm ra khơi Th&aacute;i B&igrave;nh Dương bao la. V&agrave; c&oacute; mảnh đất n&agrave;o m&agrave;u mỡ hơn v&ugrave;ng đồi n&uacute;i th&acirc;n thiện n&agrave;y, chỉ năm ph&uacute;t đi bộ l&agrave; ra tới biển, v&agrave; chẳng c&oacute; b&oacute;ng d&aacute;ng xa lộ đ&ocirc;ng đ&uacute;c ồn &agrave;o n&agrave;o cản trở họ tạo ra những thằng cu, con nh&oacute;c mới. Vậy n&ecirc;n, những căn nh&agrave; ngoại &ocirc;, nơi đ&atilde; từng nồng nặc m&ugrave;i rượu tự nấu, nơi đ&atilde; từng lu&ocirc;n tr&agrave;n ngập tiếng thơ Hart Crane[5], cứ từng c&aacute;i từng c&aacute;i một trở th&agrave;nh nơi chiếm đ&oacute;ng của đội qu&acirc;n xem ti vi v&agrave; uống Coca.<br />\r\n<br />\r\n[5] Hart Cane (21/07/1899 đến 27/04/1932): l&agrave; nh&agrave; thơ nổi tiếng người Mỹ, người c&oacute; ảnh hưởng lớn đến phong tr&agrave;o văn thơ trong thế hệ của &ocirc;ng.<br />\r\n<br />\r\nNhững cựu chiến binh đ&oacute;, kh&ocirc;ng ch&uacute;t nghi ngờ, đ&atilde; th&iacute;ch nghi với sự tự do ph&oacute;ng kho&aacute;ng bản năng của mảnh đất n&agrave;y kh&aacute; tốt. Nhưng c&aacute;c c&ocirc; vợ đ&atilde; cảnh b&aacute;o với họ ngay từ những ng&agrave;y đầu rất r&otilde; r&agrave;ng, rằng sự phồn vinh v&agrave; tự do l&agrave; kh&ocirc;ng c&ugrave;ng tồn tại. Để c&oacute; sự phồn vinh bạn cần phải c&oacute; c&ocirc;ng việc ổn định, cần c&oacute; sự cầm cố, c&oacute; t&agrave;i sản v&agrave; bảo hiểm. V&agrave; bạn kh&ocirc;ng d&aacute;m chết cho đến khi tương lai của gia đ&igrave;nh đ&atilde; được đảm bảo.<br />\r\n<br />\r\nNhững thằng cu, con nh&oacute;c cứ phọt ra từng đứa, từng đứa một. V&agrave; ng&ocirc;i trường l&agrave;ng nhỏ b&eacute; cũ kỹ đ&atilde; trở th&agrave;nh những t&ograve;a cao ốc mới coong. Khu chợ tồi t&agrave;n ven biển đ&atilde; mở rộng th&agrave;nh si&ecirc;u thị quy m&ocirc;. V&agrave; ở Hẻm c&acirc;y Nh&atilde;n, hai biển hiệu được dựng l&ecirc;n. Một trong số ch&uacute;ng cảnh b&aacute;o bạn kh&ocirc;ng n&ecirc;n ăn những ngọn cải xoong mọc hai b&ecirc;n bờ s&ocirc;ng, v&igrave; nước ở đ&oacute; đ&atilde; bị &ocirc; nhiễm nặng. Những người khai hoang thuở xưa đ&atilde; ăn ch&uacute;ng h&agrave;ng năm trời chẳng thấy c&oacute; vấn đề g&igrave; xảy ra. George v&agrave; Jim cũng đ&atilde; thử, ch&uacute;ng c&oacute; vị ngon đến lạ l&ugrave;ng. Biển b&aacute;o c&ograve;n lại - những n&eacute;t vẽ đen tr&ecirc;n nền v&agrave;ng - cảnh b&aacute;o KHU VỰC NHIỀU TRẺ EM.<br />\r\n<br />\r\nDĩ nhi&ecirc;n, George v&agrave; Jim đ&atilde; ch&uacute; &yacute; đến tấm biển b&aacute;o v&agrave;ng ngay trong lần đầu ti&ecirc;n họ đặt ch&acirc;n đến nơi n&agrave;y để t&igrave;m mua nh&agrave;. Nhưng đ&atilde; lờ ch&uacute;ng đi v&igrave; họ đ&atilde; qu&aacute; y&ecirc;u mến nơi n&agrave;y. Họ y&ecirc;u căn nh&agrave; n&agrave;y, v&igrave; để đến được n&oacute; bạn chỉ c&oacute; con đường duy nhất l&agrave; c&acirc;y cầu bắc qua s&ocirc;ng đằng xa kia; căn nh&agrave; nằm khuất trong những h&agrave;ng c&acirc;y cao v&agrave; bờ đ&aacute; ch&oacute;t v&oacute;t, khiến nơi đ&acirc;y trở n&ecirc;n như một căn l&aacute;n tận s&acirc;u trong rừng vắng. &ldquo;Đ&acirc;y l&agrave; h&ograve;n đảo của ri&ecirc;ng ch&uacute;ng ta&rdquo;, George đ&atilde; n&oacute;i khi họ đang bước qua cổng v&agrave;o, x&aacute;c l&aacute; ng&ocirc; đồng ngụp đến tận mắt c&aacute; ch&acirc;n, đ&oacute; cũng ch&iacute;nh l&agrave; l&uacute;c họ quyết định, kể từ giờ, sẽ y&ecirc;u mọi thứ thuộc về nơi n&agrave;y. S&aacute;nh vai bước v&agrave;o căn ph&ograve;ng kh&aacute;ch tối tăm v&agrave; ẩm ướt, họ n&oacute;i với nhau nơi đ&acirc;y sẽ ấm c&uacute;ng biết chừng n&agrave;o khi đ&ecirc;m xuống b&ecirc;n &aacute;nh lửa bập b&ugrave;ng nơi l&ograve; sưởi. Những rặng thường xu&acirc;n rậm rạp gi&agrave; nua, nửa sống, nửa chết h&eacute;o kh&ocirc;, queo quắt b&aacute;m chặt lấy bốn bề garage, khiến n&oacute; c&oacute; vẻ rộng gấp đ&ocirc;i. Thực tế, b&ecirc;n trong n&oacute; chỉ l&agrave; một khoảng kh&ocirc;ng gian chật chội, b&eacute; tẹo teo, được x&acirc;y dựng từ thời mẫu xe T. Ford c&ograve;n đang thịnh h&agrave;nh. Jim n&oacute;i họ sẽ nu&ocirc;i một v&agrave;i con vật ở đ&acirc;y, xe của họ qu&aacute; lớn đối với gara n&agrave;y, họ sẽ đỗ ch&uacute;ng ngo&agrave;i đầu cầu. C&acirc;y cầu cũng c&oacute; vẻ như bắt đầu l&uacute;n dần, Jim lại n&oacute;i, &ldquo;Ừ th&igrave;, &iacute;t nhất n&oacute; cũng sẽ kh&ocirc;ng sập trước khi ch&uacute;ng ta chết&rdquo;.<br />\r\n<br />\r\nKh&ocirc;ng ngạc nhi&ecirc;n khi những đứa trẻ trong khu nghĩ về căn nh&agrave; giống y như George v&agrave; Jim đ&atilde; nghĩ trong buổi s&aacute;ng đầu ti&ecirc;n họ nh&igrave;n thấy n&oacute;. Ẩm thấp, tối tăm v&agrave; b&iacute; ẩn với những rặng tường vi phủ k&iacute;n, nơi ho&agrave;n hảo cho những con qu&aacute;i vật trong c&acirc;u chuyện cổ t&iacute;ch xưa ẩn n&aacute;u. Ch&iacute;nh x&aacute;c l&agrave; vai diễn m&agrave; George thấy m&igrave;nh kho&aacute;c l&ecirc;n người, v&agrave; ng&agrave;y c&agrave;ng m&atilde;nh liệt hơn kể từ khi &ocirc;ng sống một m&igrave;nh. N&oacute; giải ph&oacute;ng một phần bản chất con người &ocirc;ng, phần m&agrave; &ocirc;ng gh&eacute;t phải để cho Jim nh&igrave;n thấy. Jim sẽ n&oacute;i g&igrave; khi thấy &ocirc;ng vung vẩy hai tay v&agrave; g&agrave;o r&uacute; như một kẻ kh&ugrave;ng đi&ecirc;n qua &ocirc; cửa sổ, khi thấy nh&oacute;c Benny nh&agrave; Strunk v&agrave; nh&oacute;c Joe nh&agrave; Garfein th&aacute;ch đố nhau chạy qua chạy lại c&acirc;y cầu bắc qua nh&agrave; George? Jim l&uacute;c n&agrave;o cũng bắt chuyện với ch&uacute;ng thật dễ d&agrave;ng. Anh để cho ch&uacute;ng vỗ về những con chồn h&ocirc;i v&agrave; gấu tr&uacute;c Bắc Mỹ, v&agrave; n&oacute;i chuyện với s&aacute;o sậu, ch&uacute;ng đ&atilde; chẳng bao giờ bước qua cầu khi kh&ocirc;ng được mời.<br />\r\n<br />\r\nB&agrave; Strunk, sống b&ecirc;n kia cầu, thỉnh thoảng cũng nghi&ecirc;m khắc quở mắng con b&agrave; h&atilde;y để &ocirc;ng được y&ecirc;n, rằng &ocirc;ng l&agrave; một gi&aacute;o sư đại học, &ocirc;ng phải l&agrave;m việc vất vả. Nhưng b&agrave; Strunk, Ch&uacute;a rủ l&ograve;ng l&agrave;nh, b&agrave; quả một phụ nữ hiền hậu - đầu tắt mặt tối với c&ocirc;ng việc nh&agrave;, cam chịu từ bỏ những ng&agrave;y th&aacute;ng ca h&aacute;t tr&ecirc;n radio để chăm s&oacute;c cho năm cậu nh&oacute;c v&agrave; hai c&ocirc; nh&oacute;c của &ocirc;ng Strunk - cũng kh&ocirc;ng kiềm chế nổi m&igrave;nh khỏi n&oacute;i với George, bằng một nụ cười &aacute;y n&aacute;y pha lẫn ch&uacute;t đồng t&igrave;nh, rằng Benny (đứa con nhỏ nhất của b&agrave;) gọi &ocirc;ng l&agrave; &ldquo;&Ocirc;ng gi&agrave; ấy&rdquo;, kể từ khi George rượt đuổi Benny ra khỏi khu vườn của &ocirc;ng, qua c&acirc;y cầu v&agrave; tới tận cuối phố; rằng &ocirc;ng bị n&oacute; nện l&ecirc;n ngưỡng cửa hằng ng&agrave;y bằng một c&acirc;y b&uacute;a tạ.<br />\r\n<br />\r\nGeorge cảm thấy xấu hổ khi thấy m&igrave;nh gầm r&uacute; như vậy, v&igrave; đ&oacute; kh&ocirc;ng phải chỉ l&agrave; đ&oacute;ng kịch. &Ocirc;ng thực sự đ&atilde; kh&ocirc;ng giữ được b&igrave;nh tĩnh, đ&atilde; thực sự c&aacute;u giận. &Ocirc;ng thấy bẽ mặt v&agrave; ch&aacute;n gh&eacute;t ch&iacute;nh m&igrave;nh sau đ&oacute;. &Ocirc;ng cũng nhận thức kh&aacute; r&otilde; rằng bọn trẻ muốn &ocirc;ng h&agrave;nh xử theo lối n&agrave;y. Ch&uacute;ng thực sự th&iacute;ch th&uacute; khi thấy &ocirc;ng nổi đ&oacute;a. Nếu c&oacute; một ng&agrave;y kia &ocirc;ng bỗng nhi&ecirc;n từ chối đ&oacute;ng vai qu&aacute;i vật, v&agrave; ch&uacute;ng kh&ocirc;ng c&ograve;n c&oacute; thể chọc tức &ocirc;ng được nữa, th&igrave; ch&uacute;ng sẽ phải t&igrave;m tr&ograve; chơi kh&aacute;c thay thế. C&acirc;u hỏi liệu đ&acirc;y c&oacute; phải chỉ l&agrave; diễn kịch hay &ocirc;ng thực sự gh&eacute;t ch&uacute;ng chẳng bao giờ vụt qua t&acirc;m tr&iacute; ch&uacute;ng. Đối với ch&uacute;ng, &ocirc;ng chỉ l&agrave; một nh&acirc;n vật trong c&acirc;u chuyện thần thoại của ch&uacute;ng m&agrave; th&ocirc;i. Chỉ c&oacute; George l&agrave; để t&acirc;m. Đ&atilde; c&oacute; l&uacute;c &ocirc;ng thấy qu&aacute; xấu hổ với những ph&uacute;t yếu đuối kh&ocirc;ng giữ nổi m&igrave;nh của &ocirc;ng, &ocirc;ng mua kẹo v&agrave; chia cho ch&uacute;ng khi gặp ch&uacute;ng tr&ecirc;n phố, khoảng một th&aacute;ng trước. Ch&uacute;ng nhận n&oacute; m&agrave; kh&ocirc;ng n&oacute;i một lời cảm ơn, ch&uacute;ng nh&igrave;n &ocirc;ng lạ lẫm v&agrave; d&egrave; chừng, trong gi&acirc;y ph&uacute;t đ&oacute;, lần đầu ti&ecirc;n ch&uacute;ng biết đến con người kh&aacute;c của &ocirc;ng ngo&agrave;i con qu&aacute;i vật trong nh&agrave; hoang.</span></p>', 0, 'chuong-1-nguoi-co-doc', '2021-12-07 16:20:24', '2021-12-07 16:20:27');
INSERT INTO `chapter` VALUES (11, 12, 'Giới thiệu - Người Cô Độc', '...', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Nếu bạn đang t&igrave;m một cuốn tiểu thuyết n&oacute;ng bỏng, đang muốn đọc về những va chạm x&aacute;c thịt, hay muốn nghe những c&acirc;u chuyện ly k&igrave; về thế giới thứ 3, th&igrave; t&ocirc;i khuy&ecirc;n bạn h&atilde;y bỏ cuốn s&aacute;ch n&agrave;y xuống -&nbsp;Người c&ocirc; độc l&agrave; mọi thứ trừ sự trần tục.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><img src=\"https://www.truyenngan.com.vn/images/quanthanh/tuan6/nguoi_co_doc.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khi&nbsp;Người C&ocirc; Độc&nbsp;được xuất bản lần đầu ti&ecirc;n năm 1964, n&oacute; đ&atilde; g&acirc;y ra một cơn chấn động ở Mỹ bởi bức ch&acirc;n dung sinh động của một người trung ni&ecirc;n đồng t&iacute;nh, ch&acirc;n thực v&agrave; cảm động.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute; cũng được đ&aacute;nh gi&aacute; l&agrave; cuốn tiểu thuyết ti&ecirc;u biểu d&agrave;nh cho cộng đồng người đồng t&iacute;nh; được xếp v&agrave;o hạng mục 99 tiểu thuyết viết bằng tiếng Anh hay nhất kể từ năm 1939; được chuyển thể th&agrave;nh phim, với sự tham gia của Colin Firth - người được đề cử giải Oscar ở hạng mục Nam diễn vi&ecirc;n ch&iacute;nh xuất sắc nhất cho vai diễn n&agrave;y - c&ugrave;ng nữ minh tinh Julianne Moore.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&acirc;u chuyện mi&ecirc;u tả lại 24 giờ một ng&agrave;y b&igrave;nh thường của George - gi&aacute;o sư người Anh sống ở khu ngoại &ocirc; Nam California - đang th&iacute;ch ứng với cuộc sống của m&igrave;nh sau c&aacute;i chết bất ngờ của người bạn đời &ndash; Jim &ndash; người đ&atilde; c&ugrave;ng &ocirc;ng chung sống suốt 16 năm. &Ocirc;ng vật lộn dai dẳng với những th&oacute;i quen cuộc sống thường nhật. Ch&acirc;m biếm, s&acirc;u sắc, vui buồn thất thường, h&agrave;i hước, v&agrave; nỗi buồn mi&ecirc;n man của một con người đ&atilde; mất đi lẽ sống, chỉ c&ograve;n ba phần người, cuốn tiểu thuyết n&agrave;y đ&atilde; lột tả bộ mặt của cuộc sống một c&aacute;ch sắc sảo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><em>&quot;Một trong những cuốn tiểu thuyết đầu ti&ecirc;n v&agrave; hay nhất về sự vận động giải ph&oacute;ng nh&acirc;n quyền của người đồng t&iacute;nh hiện đại.&quot;</em>&nbsp;-&nbsp;Edmund White</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Về t&aacute;c Giả:</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Christorpher Isherwood&nbsp;(1904-1986) l&agrave; một tiểu thuyết gia lớn của thế kỷ 20, người c&oacute; những đ&oacute;ng g&oacute;p t&iacute;ch cực trong phong tr&agrave;o đấu tranh quyền lợi cho người đồng t&iacute;nh, t&aacute;c giả của hơn 40 t&aacute;c phẩm nổi tiếng trong những năm giữa thế kỷ 20.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><em>&quot;Isherwood l&agrave; nh&agrave; viết văn xu&ocirc;i tiếng Anh tuyệt vời nhất.&quot;</em>&nbsp;-&nbsp;Gore vidal</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ugrave;ng đ&oacute;n đọc cuốn s&aacute;ch sắp ph&aacute;t h&agrave;nh trong th&aacute;ng 10 với văn phong tuyệt vời để cảm nhận sự kh&aacute;c biệt của những phong c&aacute;ch văn chương tiếng Anh kinh điển!</span></p>', 0, 'gioi-thieu-nguoi-co-doc', '2021-12-07 16:20:29', '2021-12-07 16:20:38');
INSERT INTO `chapter` VALUES (10, 11, 'Chapter 1 chàng trai', 'Rất ấn tượng trước tay nghề khéo léo của anh, người quản đốc quyết định nhận anh vào làm \"Tốt lắm, hãy bắt đầu đi làm từ Thứ Hai tới\".', '<p style=\"text-align:justify\"><strong>C&oacute; 1 ch&agrave;ng trai trẻ đến gặp quản đốc của một nh&oacute;m thợ đốn gỗ để xin việc. Người quản đốc chỉ v&agrave;o một c&aacute;i c&acirc;y to v&agrave; bảo anh ta h&atilde;y đốn thử xem mất bao l&acirc;u. Ch&agrave;ng thanh ni&ecirc;n đ&atilde; kh&ocirc;ng mất nhiều thời gian đốn đổ c&aacute;i c&acirc;y đ&oacute; một c&aacute;ch thiện nghệ.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"chang-trai-va-chec-riu\" src=\"https://www.truyenngan.com.vn/images/Phuongvtm/2017-10/chang-trai-va-chec-riu.jpg\" /></p>\r\n\r\n<p>Rất ấn tượng trước tay nghề kh&eacute;o l&eacute;o của anh, người quản đốc quyết định nhận anh v&agrave;o l&agrave;m &quot;Tốt lắm, h&atilde;y bắt đầu đi l&agrave;m từ Thứ Hai tới&quot;.</p>\r\n\r\n<p>Thứ Hai. Thứ Ba. Rồi Thứ Tư tr&ocirc;i qua, ng&agrave;y ng&agrave;y ch&agrave;ng trai đều chăm chỉ l&agrave;m việc. Đến chiều ng&agrave;y Thứ Năm, bổng quản đốc tới v&agrave; cho ch&agrave;ng trai trẻ xem bảng xếp hạng:</p>\r\n\r\n<p>&quot;Ch&agrave;ng trai, năng suất l&agrave;m việc của cậu đ&atilde; tụt l&ugrave;i so với những người kh&aacute;c. Tổng kết từ đầu tuần cho thấy cậu đ&atilde; rơi từ vị tr&iacute; đứng đầu h&ocirc;m Thứ Hai xuống vị tr&iacute; b&eacute;t bảng cho đến hết ng&agrave;y h&ocirc;m nay.&quot;</p>\r\n\r\n<p>Ch&agrave;ng trai trẻ bất b&igrave;nh l&ecirc;n tiếng:</p>\r\n\r\n<p>&quot;Nhưng t&ocirc;i đ&atilde; rất chăm chỉ l&agrave;m việc. T&ocirc;i lu&ocirc;n l&agrave; người đến đ&acirc;u ti&ecirc;n v&agrave; l&agrave; người ra về cuối c&ugrave;ng, thậm ch&iacute; t&ocirc;i c&ograve;n kh&ocirc;ng cả c&oacute; thời gian uống nước!&quot;</p>\r\n\r\n<p>Người quản đốc gật đầu n&oacute;i:</p>\r\n\r\n<p>&quot;Phải c&ocirc;ng nhận cậu l&agrave; c&ocirc;ng nh&acirc;n cần c&ugrave; nhất ở đ&acirc;y, nhưng đ&atilde; bao l&acirc;u rồi cậu chưa m&agrave;i chiếc r&igrave;u của m&igrave;nh?&quot;</p>\r\n\r\n<p>Ch&agrave;ng trai thật th&agrave; thừa nhận:</p>\r\n\r\n<p>&quot;Từ đầu tuần tới giờ th&igrave; chưa lần n&agrave;o. T&ocirc;i mải l&agrave;m việc qu&aacute; n&ecirc;n kh&ocirc;ng c&oacute; thời gian để m&agrave;i r&igrave;u.&quot;</p>\r\n\r\n<p style=\"text-align:center\">***</p>\r\n\r\n<p><strong>Cuộc sống của ch&uacute;ng ta cũng như vậy. Chăm chỉ kh&ocirc;ng c&oacute; g&igrave; l&agrave; xấu, nhưng đ&ocirc;i khi cần c&oacute; một khoảng ngừng để &quot;m&agrave;i sắc chiếc r&igrave;u&quot;, l&agrave;m mới bản th&acirc;n th&ocirc;ng qua việc tự học tập v&agrave; r&egrave;n luyện. Bỏ qua việc n&agrave;y, ch&uacute;ng ta sẽ nhanh ch&oacute;ng b&igrave; &quot;c&ugrave;n&quot; đi, v&agrave; mất dần đi khả năng l&agrave;m việc hiệu quả của m&igrave;nh.</strong></p>', 0, 'chapter-1-chang-trai', '2021-12-07 16:20:31', '2021-12-07 16:20:40');
INSERT INTO `chapter` VALUES (9, 10, 'Chapter 1 tuổi trẻ', 'Tuổi trẻ là chặng đường ta tìm kiếm ngã rẽ phù hợp nhất cho bản thân.', '<p>Tuổi trẻ l&agrave; chặng đường ta t&igrave;m kiếm ng&atilde; rẽ ph&ugrave; hợp nhất cho bản th&acirc;n. Nhiều bạn trẻ đ&atilde; tự vạch ra con đường cho ch&iacute;nh m&igrave;nh từ thời l&agrave; học sinh, sinh vi&ecirc;n, v&agrave; họ ấp ủ những ước mơ, kh&aacute;t vọng đẹp đẽ, từng bước x&acirc;y dựng th&agrave;nh sự nghiệp cho m&igrave;nh. Đối với họ, mỗi ng&agrave;y l&agrave; một cơ hội qu&yacute; gi&aacute; sẽ dễ tuột mất nếu họ kh&ocirc;ng biết nắm bắt. Họ đ&atilde; sống c&ugrave;ng với bao đam m&ecirc;, nhiệt huyết tr&agrave;n trề, hy vọng biến những ước mơ th&agrave;nh hiện thực. Nhiều bạn trẻ phải đ&aacute;nh đổi c&ocirc;ng sức, thời gian, t&igrave;nh cảm...qu&yacute; gi&aacute; của m&igrave;nh để trở th&agrave;nh con người họ mong ước, được người kh&aacute;c c&ocirc;ng nhận gi&aacute; trị bản th&acirc;n. Họ bước đi tr&ecirc;n con đường lổm chổm gai nhọn với b&agrave;n ch&acirc;n rướm m&aacute;u, đ&ocirc;i l&uacute;c ng&atilde; khuỵu nhưng vẫn gượng dậy. Cũng c&oacute; nhiều người trẻ vội thụt l&ugrave;i khi gặp cơn gi&ocirc;ng b&atilde;o, ch&ocirc;n v&ugrave;i ước mơ của m&igrave;nh. Ngo&agrave;i ra, cũng c&oacute; một số bạn trẻ bối rối khi đứng trước những ng&atilde; rẽ cuộc đời, kh&ocirc;ng x&aacute;c định được con đường n&agrave;o ph&ugrave; hợp cho bản th&acirc;n. Thế n&ecirc;n, bạn đ&acirc;m ra ch&aacute;n nản, cảm thấy tương lai l&agrave; qu&atilde;ng đường sương phủ mờ mịt, v&ocirc; nghĩa. Sau khi tốt nghiệp đại học 4 năm, bạn cầm hồ sơ đi xin việc khắp nơi, v&agrave; l&agrave;m qua nhiều c&ocirc;ng việc. Những việc ấy chỉ gi&uacute;p bạn trang trải cuộc sống chứ kh&ocirc;ng mang lại niềm hạnh ph&uacute;c cho bạn. Nhiều l&uacute;c, bạn tự tr&aacute;ch m&igrave;nh l&agrave; kẻ &quot;bất t&agrave;i&quot;, &quot;v&ocirc; dụng&quot;, sống kh&ocirc;ng c&oacute; định hướng vững chắc v&agrave; bạn muốn bỏ đi đ&acirc;u đ&oacute; thật xa. Thế nhưng, đ&oacute; chỉ l&agrave; sự khởi đầu của h&agrave;nh tr&igrave;nh t&igrave;m kiếm gi&aacute; trị của bản th&acirc;n. Khi bạn trải qua nhiều c&ocirc;ng việc, trải nghiệm cuộc sống nhiều hơn, bạn sẽ nhận thức được m&igrave;nh l&agrave; ai v&agrave; cần đi tới đ&acirc;u.</p>\r\n\r\n<p><img alt=\"thang-nam-tuoi-tre\" src=\"https://www.truyenngan.com.vn/images/Phuongvtm/2020-08/thang-nam-tuoi-tre.jpg\" /></p>\r\n\r\n<p>T&ocirc;i c&oacute; người bạn, l&agrave; minh chứng cho điều n&agrave;y. Sau khi tốt nghiệp đại học, anh ta kh&ocirc;ng t&igrave;m được định hướng c&ocirc;ng việc cho bản th&acirc;n. Anh rải hồ sơ xin việc nhiều chỗ, cuối c&ugrave;ng cũng được một ch&acirc;n kế to&aacute;n tại c&ocirc;ng ty sản xuất. V&agrave;o l&agrave;m được v&agrave;i th&aacute;ng, anh cảm thấy c&ocirc;ng việc ấy như l&agrave; &quot; địa ngục&quot;. Thế rồi, anh cứ nhảy việc dăm ba chỗ. Khoảng thời gian đ&oacute;, anh ch&aacute;n nản, mệt mỏi, sống như một &quot;kẻ m&ugrave;&quot; v&igrave; kh&ocirc;ng nh&igrave;n thấy tương lai của bản th&acirc;n. Anh thường đi tham dự c&aacute;c sự kiện giải tr&iacute; như l&agrave; phương thức giải toả căng thẳng trong cuộc sống. Anh cảm thấy h&agrave;o hứng, th&uacute; vị, khao kh&aacute;t được l&agrave; người tổ chức c&aacute;c sự kiện truyền th&ocirc;ng. V&agrave; cuối c&ugrave;ng, anh đ&atilde; t&igrave;m thấy con đường ph&ugrave; hợp v&agrave; tốt nhất cho ch&iacute;nh m&igrave;nh, bắt đầu từng bước x&acirc;y dựng th&agrave;nh sự nghiệp vững chắc. Hiện tại, anh đ&atilde; l&agrave;m được c&ocirc;ng việc m&igrave;nh y&ecirc;u th&iacute;ch v&agrave; th&agrave;nh c&ocirc;ng tr&ecirc;n con đường m&igrave;nh chọn. Trong thực tế, ch&uacute;ng ta sẽ bắt gặp nhiều c&acirc;u chuyện tương tự. Ở qu&atilde;ng đời tuổi trẻ, sẽ c&oacute; những l&uacute;c bạn mệt mỏi, ch&aacute;n nản, sống kh&ocirc;ng c&oacute; định hướng. Thế nhưng, rồi một l&uacute;c n&agrave;o đ&oacute; bạn sẽ kh&aacute;m ph&aacute; được sở th&iacute;ch, năng lực của bản th&acirc;n. Từ đ&oacute;, bạn tự tin chọn cho bản th&acirc;n lối rẽ ph&ugrave; hợp nhất. V&agrave; cuối c&ugrave;ng, bạn sẽ t&igrave;m được c&ocirc;ng việc m&igrave;nh thật sự y&ecirc;u th&iacute;ch, dồn hết t&acirc;m sức v&agrave;o đ&oacute;, được sống thật với ch&iacute;nh m&igrave;nh để qu&atilde;ng đời thanh xu&acirc;n l&agrave; những chuỗi ng&agrave;y &yacute; nghĩa, hạnh ph&uacute;c.</p>\r\n\r\n<p>Tuổi trẻ l&agrave; khoảng thời gian bạn va vấp, mắc phải những sai lầm đ&aacute;ng tiếc. Bạn từng hụt hẫng, tuyệt vọng khi gặp thất bại trong c&ocirc;ng việc v&agrave; cuộc sống. Khi ấy, niềm tin trong bạn sụp đổ k&eacute;o theo đam m&ecirc;, kh&aacute;t vọng. Bạn ng&atilde; khụyu, v&agrave; muốn từ bỏ h&agrave;nh tr&igrave;nh của m&igrave;nh. Hơn thế, bạn từng cư xử kh&ocirc;ng đ&uacute;ng mực, h&agrave;nh động n&oacute;ng vội, dại dột g&acirc;y ra nhiều hậu quả đ&aacute;ng tiếc hay sợ h&atilde;i, l&uacute;ng t&uacute;ng trước những biến cố. Nguy&ecirc;n do bởi v&igrave; tuổi trẻ ch&uacute;ng ta thiếu kỹ năng sống cần thiết, h&agrave;nh trang qu&yacute; b&aacute;u trong cuộc đời. Thế n&ecirc;n, h&atilde;y đọc nhiều s&aacute;ch gi&uacute;p bạn thay đổi t&iacute;ch cực v&agrave; tự r&uacute;t ra b&agrave;i học qu&yacute; gi&aacute; từ những sai phạm của m&igrave;nh. Sau n&agrave;y trưởng th&agrave;nh, bạn sẽ cười thầm bản th&acirc;n khi nhớ lại những kỷ niệm &quot;ngốc nghếch&quot; thời thanh xu&acirc;n đ&atilde; thay đổi đời bạn.</p>\r\n\r\n<p>Tuổi trẻ l&agrave; qu&aacute; tr&igrave;nh bạn t&igrave;m hiểu v&agrave; cảm nhận t&igrave;nh y&ecirc;u. Bạn biết rung động, bị &aacute;m ảnh bởi h&igrave;nh ảnh ai đ&oacute;. Bạn y&ecirc;u ai đ&oacute; s&acirc;u đậm, cuồng nhiệt đến nỗi bạn xem như điều n&agrave;y kh&ocirc;ng thể thiếu trong cuộc sống. Bạn đắm ch&igrave;m trong những gi&acirc;y ph&uacute;t ngọt ng&agrave;o, l&atilde;ng mạn của t&igrave;nh y&ecirc;u. Hay bạn ch&ocirc;n chặt cảm gi&aacute;c y&ecirc;u cồn c&agrave;o, ch&aacute;y bỏng với nỗi khổ sở. Thanh xu&acirc;n kh&ocirc;ng những cho bạn khoảnh khắc hạnh ph&uacute;c bất tận m&agrave; c&ograve;n để lại những vết cắt s&acirc;u hoắm trong tim bạn. Bạn từng nếm trải hương vị cay đắng của mối t&igrave;nh tan vỡ. C&oacute; nhiều l&uacute;c bạn rơi v&agrave;o trạng th&aacute;i khủng hoảng t&acirc;m l&yacute;, tự nhốt m&igrave;nh v&agrave;o hố s&acirc;u tăm tối, mặc cho nỗi đau dằn x&eacute;. Bạn mệt mỏi phải bu&ocirc;ng lỏng c&aacute;nh tay, đ&agrave;nh để vụt mất điều qu&yacute; gi&aacute; nhất. Sau tất cả, mọi thứ trở th&agrave;nh miền k&yacute; ức kh&oacute; phai khiến bạn mạnh mẽ, ch&iacute;n chắn hơn trong t&igrave;nh y&ecirc;u.</p>\r\n\r\n<p>Tuổi trẻ gắn liền với những chuyến du lịch bụi &yacute; nghĩa. Bạn trẻ ch&uacute;ng ta ham th&iacute;ch đi đến những v&ugrave;ng đất mới để thoả sức kh&aacute;m ph&aacute; c&ugrave;ng bạn b&egrave;, v&agrave; được ho&agrave; m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n. Sau mỗi chuyến đi, bạn trẻ lại thu nhặt được những kinh nghiệm sống, kiến thức qu&yacute; gi&aacute;, hiểu được gi&aacute; trị của t&igrave;nh bạn v&agrave; lưu giữ những khoảnh khắc kh&oacute; qu&ecirc;n c&ugrave;ng bạn b&egrave;. Tuổi trẻ ch&uacute;ng ta ham t&igrave;m t&ograve;i, học hỏi nhiều điều lạ trong cuộc sống. Bạn tự đặt cho m&igrave;nh nhiều c&acirc;u hỏi xung quanh, v&agrave; một m&igrave;nh kh&aacute;m ph&aacute; c&acirc;u trả lời. Nhiều bạn trẻ d&agrave;nh cả thanh xu&acirc;n của m&igrave;nh cho c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu &quot;vĩ đại&quot; hay đi lang thang đ&acirc;y đ&oacute; để thu lượm những kiến thức mới. Tuổi trẻ m&agrave;, bạn cứ t&ograve; m&ograve; v&agrave; kh&aacute;m ph&aacute; những điều xung quanh, x&aacute;ch ba l&ocirc; l&ecirc;n đi v&ograve;ng quanh thế giới để thanh xu&acirc;n kh&ocirc;ng tr&ocirc;i qua một c&aacute;ch v&ocirc; vị, đầy luyến tiếc.</p>', 0, 'chapter-1-tuoi-tre', '2021-12-07 16:20:33', '2021-12-07 16:20:45');
INSERT INTO `chapter` VALUES (8, 9, 'Chapter 1 tình cờ gặp lại nhau', 'Em mà bỏ anh, anh sẽ đi tu', '<h3>&nbsp;</h3>\r\n\r\n<p>- Em m&agrave; bỏ anh, anh sẽ đi tu</p>\r\n\r\n<p>- Trời trời, g&igrave; vậy cha nội, tưởng chỉ b&agrave; Lan b&atilde; mới đi tu, &ocirc;ng l&agrave; đ&agrave;n &ocirc;ng sao n&oacute;i gh&ecirc; vậy cha. Phạt c&aacute;i tội n&oacute;i bậy phải c&otilde;ng em xuống n&uacute;i.</p>\r\n\r\n<p>N&oacute;i chơi vậy m&agrave; anh c&uacute;i xuống c&otilde;ng thiệt , mặc cho em đấm v&agrave;o lưng anh th&igrave;nh thịch v&igrave; sợ t&eacute;, đường hẹp, dốc sỏi v&agrave; tối, trăng khuyết tr&ecirc;n đỉnh trời kh&ocirc;ng đủ s&aacute;ng bước ch&acirc;n anh.Đ&oacute; cũng l&agrave; lần đầu ti&ecirc;n v&agrave; lần cuối c&ugrave;ng em đi ch&ugrave;a với anh, d&ugrave; rằng trước Đức Phật anh đ&atilde; cầu mong em v&agrave; anh m&atilde;i m&atilde;i b&ecirc;n nhau.</p>\r\n\r\n<p>Nha Trang nơi kết nối tụi m&igrave;nh. Khi đ&oacute; em đang chạy trốn khỏi qu&aacute; khứ kh&ocirc;ng vui sau một lần đổ vỡ ở tuổi 23. Anh đ&atilde; đến với em như một định mệnh. Em đ&atilde; khổ sở đắn đo, giằng x&eacute; trước việc c&oacute; n&ecirc;n nhận lời thương anh, khi anh c&ograve;n qu&aacute; trẻ để đủ sức c&ugrave;ng em vượt l&ecirc;n định kiến x&atilde; hội v&agrave; r&agrave;o cản gia đ&igrave;nh. Rốt cuộc em đ&atilde; thua trước sự ki&ecirc;n tr&igrave; của anh. Em nhắm mắt y&ecirc;u v&agrave; bất chấp.</p>\r\n\r\n<p><img alt=\"tinh-co-gap-lai-nhau\" src=\"https://www.truyenngan.com.vn/images/Phuongvtm/2020-12/tinh-co-gap-lai-nhau.jpg\" /></p>\r\n\r\n<p>Ng&agrave;y đầu ti&ecirc;n anh dẫn em về gia đ&igrave;nh ra mắt, anh biểu tạm thời m&igrave;nh lấy l&ograve;ng ba m&aacute; rồi từ từ anh n&oacute;i sau. Chắc ba m&aacute; sẽ thương em th&ocirc;i. Tin anh đi. D&ugrave; rất kh&oacute; chịu khi ba m&aacute; hỏi về ho&agrave;n cảnh m&agrave; em kh&ocirc;ng d&aacute;m n&oacute;i, nhưng em kh&ocirc;ng biết l&agrave;m g&igrave; kh&aacute;c. Ba m&aacute; rất qu&yacute; mến em, sau v&agrave;i lần ba m&aacute; đ&ograve;i mang trầu cau tới rước em về. Tụi m&igrave;nh cứ hẹn lần hẹn lựa tới ba năm sau.</p>\r\n\r\n<p>Anh l&agrave;m nghề biển. L&ecirc;nh đ&ecirc;nh đ&ecirc;m ng&agrave;y tr&ecirc;n s&oacute;ng nước . Em ở đất liền đi l&agrave;m v&agrave; chờ đợi. Sợ em buồn đ&ecirc;m n&agrave;o anh cũng gọi v&agrave; h&aacute;t cho em nghe, tới khi em giả vờ ngủ anh mới tắt m&aacute;y v&agrave; kh&ocirc;ng qu&ecirc;n n&oacute;i c&acirc;u vợ anh ngủ ngon, r&aacute;ng đợi anh th&ecirc;m thời gian nữa, nếu ba m&aacute; kh&ocirc;ng chấp nhận, m&igrave;nh cũng đủ sức n&acirc;ng đỡ nhau l&uacute;c ra ri&ecirc;ng, anh thương em rất nhiều, sẽ cố gắng hết sức để b&ugrave; đắp cho em.Em đ&atilde; kh&oacute;c v&igrave; thương, v&igrave; vui sướng, c&aacute;i cảm gi&aacute;c đ&oacute; sau n&agrave;y v&agrave; m&atilde;i m&atilde;i em sẽ kh&ocirc;ng t&igrave;m thấy được bất cứ nơi đ&acirc;u.</p>\r\n\r\n<p>Mỗi chuyến đi xa về tụi m&igrave;nh c&ugrave;ng rong ruổi b&ecirc;n nhau, buổi chiều anh nấu những m&oacute;n em th&iacute;ch hai đứa ăn, rồi rủ em ngồi tr&ecirc;n th&uacute;ng cho anh ch&egrave;o đi lanh quanh tr&ecirc;n biển, anh chở em tới chỗ mấy anh em nu&ocirc;i t&ocirc;m h&ugrave;m, em tựa đầu tr&ecirc;n mạn th&uacute;ng v&agrave; cảm nhận hạnh ph&uacute;c. Đ&ecirc;m khuya gối đầu b&ecirc;n vai anh ngồi tr&ecirc;n cầu cảng nh&igrave;n trăng tr&ecirc;n biển, v&agrave; nghe tiếng s&oacute;ng vỗ dưới ch&acirc;n sau buổi nhậu c&ugrave;ng với bạn b&egrave;. Ba năm chưa khi n&agrave;o anh khiến em đau l&ograve;ng, chỉ c&oacute; em thỉnh thoảng hay kiếm chuyện hờn giận vu vơ cho anh dỗ. V&iacute; như em kh&ocirc;ng cho anh đi lặn v&igrave; c&oacute; lần anh su&yacute;t ngột v&igrave; bị trục trặc ống dẫn kh&iacute; m&agrave; anh cũng l&eacute;n l&agrave;m v&igrave; muốn kiếm thiệt nhiều tiền nu&ocirc;i dưỡng tương lai. Anh hay n&oacute;i cuộc đời c&oacute; bao nhi&ecirc;u kh&ocirc;ng đủ để m&igrave;nh thương sao c&ograve;n hoang ph&iacute; hả em. Dường như mỗi ph&uacute;t gi&acirc;y anh đều tr&acirc;n trọng.</p>\r\n\r\n<p>Người ta n&oacute;i vợ chồng với nhau l&agrave; duy&ecirc;n l&agrave; nợ, vậy m&igrave;nh gặp nhau thương nhau l&agrave; c&aacute;i g&igrave;. Đ&oacute; l&agrave; c&acirc;u hỏi m&agrave; cả đời n&agrave;y em kh&ocirc;ng c&oacute; c&acirc;u trả lời cho vừa l&ograve;ng, để l&uacute;c kh&ocirc;ng c&ograve;n nhau thế n&agrave;y em c&oacute; c&aacute;i l&iacute; do để m&agrave; th&ocirc;i dằn vặt.</p>\r\n\r\n<p>T&aacute;m năm trời em l&aacute;nh xa anh, t&aacute;m năm trời anh m&ograve;n mỏi kiếm t&igrave;m, đợi chờ, hi vọng. Em l&agrave; người đ&agrave;n b&agrave; t&agrave;n nhẫn nhất thế gian. Em muốn anh nghĩ thế cho anh bớt đau, bớt hận.</p>\r\n\r\n<p>Sau c&aacute;i đ&ecirc;m li&ecirc;n hoan ở c&ocirc;ng ty về em đ&atilde; mượn hơi men m&agrave; th&uacute; nhận với m&aacute; anh sau những ng&agrave;y giằng vặt v&agrave; lừa dối. M&agrave; cũng ngộ thiệt, đ&atilde; say m&agrave; phải uống lưỡi cả trăm lần mới thốt ra nổi c&aacute;i c&acirc;u:&quot;m&aacute; ơi, con xin lỗi, con l&agrave; người đ&atilde; từng c&oacute; một lần dang dở v&agrave; c&oacute; một đứa con ri&ecirc;ng, con kh&ocirc;ng d&aacute;m n&oacute;i với m&aacute;, con lừa dối m&aacute; v&agrave; cả gia đ&igrave;nh, d&ograve;ng họ&quot;</p>\r\n\r\n<p>Gi&acirc;y ph&uacute;t đ&oacute; em nghe m&aacute; chết lặng, c&aacute;i đoạn im lặng khủng khiếp đ&oacute; như d&agrave;i v&ocirc; tận. Em đ&atilde; cấu n&aacute;t đ&ugrave;i m&igrave;nh để lấy hết dũng kh&iacute; n&oacute;i ra.</p>\r\n\r\n<p>Rồi em bật kh&oacute;c, kh&oacute;c đến kh&ocirc;ng thở nổi. B&ecirc;n kia m&aacute; cũng kh&oacute;c. M&aacute; n&oacute;i:&quot;sao con lại thế, sao tụi m&agrave;y lừa dối ba m&aacute;. Giờ cả họ h&agrave;ng, c&ocirc; b&aacute;c ai cũng biết rồi, sao m&aacute; đối diện với họ, v&agrave; m&aacute; phải ăn n&oacute;i với b&ecirc;n nội n&oacute; ra sao đ&acirc;y hả con, c&ograve;n ba m&agrave;y nữa, nghĩ tới đ&oacute; th&ocirc;i m&aacute; đ&atilde; kh&ocirc;ng thể. C&ograve;n thằng Hải nữa, tương lai n&oacute; nữa, sao c&oacute; thể...&quot; Tới đ&oacute; th&igrave; em im lặng , c&aacute;i kiểu kh&ocirc;ng thể mở miệng ra n&oacute;i th&ecirc;m , giải th&iacute;ch th&ecirc;m nữa, n&oacute; dồn n&eacute;n lại như c&aacute;i c&acirc;y bị sợi d&acirc;y quấn chặt, teo dần, bức bối v&agrave; ức nghẹn</p>\r\n\r\n<p>M&aacute; lại n&oacute;i:&quot;M&aacute; rất thương con, nhưng m&aacute; xin lỗi con, nếu con cũng thương m&aacute; xin con h&atilde;y chia tay với n&oacute;. Th&ecirc;m bữa m&aacute; coi thầy rồi , thầy n&oacute;i hai đứa lấy nhau thằng Hải sẽ chết con ơi, nếu kh&ocirc;ng chết sẽ sống với nhau trầy trật cả đời. M&aacute; n&oacute;i th&igrave; n&oacute; gạt lời m&aacute;, n&oacute; n&oacute;i n&oacute; kh&ocirc;ng bỏ được. M&aacute; biết n&oacute; thương con giờ n&oacute;i sao n&oacute; cũng kh&ocirc;ng bỏ được, m&aacute; chỉ tr&ocirc;ng v&agrave;o con th&ocirc;i.</p>\r\n\r\n<p>Em lại im lặng, nỗi im lặng ch&igrave;m s&acirc;u trong vũng nước mắt của sự tuyệt vọng. Gi&acirc;y ph&uacute;t đ&oacute; l&ograve;ng tự trọng v&agrave; cả t&igrave;nh thương d&agrave;nh cho m&aacute; cho anh đ&atilde; khiến em đồng &yacute;.</p>\r\n\r\n<p>Em qu&ecirc;n anh bằng c&aacute;ch ngu ngốc v&agrave; trẻ con nhất, em chọn những cơn say, v&agrave; nh&iacute; nhố với bạn b&egrave;, rồi đ&ecirc;m về tự khinh bỉ, nguyền rủa m&igrave;nh tồi tệ.Em đ&atilde; n&oacute;i lời chia tay cụt ngủn v&agrave; t&agrave;n nhẫn đến mức khiến anh kh&ocirc;ng thể để m&igrave;nh tỉnh rượu trong suốt một th&aacute;ng trời. Anh t&igrave;m em trốn, anh gọi em kh&oacute;a m&aacute;y. Em chỉ n&oacute;i m&igrave;nh kh&ocirc;ng hợp em lỡ thương người kh&aacute;c rồi.L&iacute; do đ&oacute; sao l&agrave;m thỏa m&atilde;n một tr&aacute;i tim y&ecirc;u v&agrave; tin tưởng em nhiều như thế.</p>\r\n\r\n<p>Em im lặng, bỏ số, v&agrave; bỏ đi nơi kh&aacute;c lập nghiệp. Những ng&agrave;y qu&aacute; nhớ anh em đ&atilde; d&ugrave;ng số lạ gọi tới, chỉ cần nghe giọng anh alo ngắn ngủn v&agrave; bực bội tắt m&aacute;y khi em n&iacute;n thở v&agrave; im lặng v&igrave; sợ bị anh ph&aacute;t hiện. Chỉ vậy th&ocirc;i m&agrave; em vui biết chừng n&agrave;o. Đ&ecirc;m th&egrave;m anh ca cho em nghe, th&egrave;m những c&acirc;u anh nhắc nhở mỗi tối đến ch&aacute;y ruột gan</p>\r\n\r\n<p>T&acirc;y Nguy&ecirc;n m&ugrave;a mưa dai dẳng v&agrave; buồn đứt ruột. Người ta n&oacute;i thời gian l&agrave;m t&igrave;nh y&ecirc;u ph&ocirc;i phai m&agrave; em bao nhi&ecirc;u năm rồi vẫn thế, chỉ l&agrave; nổi nhớ đ&egrave; xuống sau những bộn bề cơm &aacute;o, rồi tới khi bắt gặp một h&igrave;nh ảnh th&acirc;n quen, một &aacute;nh mắt hay tướng người gi&ocirc;ng giống, hoặc một b&agrave;i h&aacute;t ng&agrave;y xưa anh hay h&aacute;t, nổi nhớ lại trỗi dậy da diết v&agrave; bần thần cả ng&agrave;y.</p>\r\n\r\n<p>C&oacute; lần em gặp đứa bạn của anh ở S&agrave;i G&ograve;n , n&oacute; n&oacute;i anh vẫn ở vậy, &iacute;t n&oacute;i &iacute;t cười, d&ugrave; m&aacute; v&agrave; mấy đứa em mai mối bao nhi&ecirc;u người anh cũng lắc đầu bỏ đi. Em x&oacute;t lắm, nhưng dặn l&ograve;ng kh&ocirc;ng được mềm yếu, chắc anh cần th&ecirc;m thời gian nữa. Em bấm bụng thương anh.</p>\r\n\r\n<p>Kh&aacute;nh H&ograve;a năm trước m&ugrave;a mưa b&atilde;o, nghe đ&agrave;i b&aacute;o c&oacute; ghe thuyền mất t&iacute;ch, mấy người đi biển bị b&atilde;o nhấn ch&igrave;m, em đứng ngồi kh&ocirc;ng y&ecirc;n suốt ng&agrave;y chờ tin b&aacute;o đăng coi thử c&oacute; ch&uacute;t manh mối g&igrave; về anh kh&ocirc;ng, em gọi số m&aacute;y anh kh&ocirc;ng c&oacute; ai trả lời.Ruột gan em như lửa đốt. Em quyết định ra đảo t&igrave;m anh. M&aacute; đứng ph&iacute;a trong cầu cảng với mấy c&ocirc; trong x&oacute;m, thấy em m&aacute; sửng sốt nh&igrave;n, em ngại ng&ugrave;ng ch&agrave;o. M&aacute; dẫn em v&agrave;o nh&agrave; nghe tiếng kh&oacute;c trẻ con từ ngo&agrave;i s&acirc;n. Em chưa kịp hỏi g&igrave; th&igrave; thấy anh ngo&agrave;i giếng loay hoay phơi t&atilde; con n&iacute;t. Ph&iacute;a trong nh&agrave; c&oacute; tiếng c&ocirc; g&aacute;i trẻ ch&agrave;o em, em cố mỉm cười trước t&igrave;nh cảnh đ&oacute;.Nước mắt một thằng đ&agrave;n &ocirc;ng lăn xuống m&aacute;, anh kh&oacute;c v&agrave; &ocirc;m em trước mặt m&aacute; v&agrave; c&ocirc; ấy. Em kh&oacute;c, m&aacute; kh&oacute;c. Em đ&atilde; đến muộn mất rồi. Anh đ&atilde; cưới c&ocirc; ấy được 1 năm, trong lần đi dạo v&agrave; t&igrave;nh cờ gặp, bởi c&ocirc; ấy c&oacute; nụ cười giống em n&ecirc;n quen nhau chưa tới 1 th&aacute;ng anh vội v&atilde; cưới liền v&igrave; sợ tỉnh ra anh đổi &yacute;.</p>\r\n\r\n<p>Tự nhi&ecirc;n em thấy giận m&igrave;nh gh&ecirc; gớm, em đ&atilde; sai khi trở lại chuyến n&agrave;y, lẽ ra em n&ecirc;n vĩnh viễn biến mất. Em quay về bến đ&ograve; đợi chuyến muộn trở lui. Gi&oacute; biển thốc v&agrave;o l&ograve;ng nghe mặn đắng. M&aacute; tiễn em bằng một c&aacute;i &ocirc;m , m&aacute; n&oacute;i con rất tốt, m&aacute; hi vọng con t&igrave;m được người y&ecirc;u con v&agrave; lo cho con. L&ograve;ng m&aacute; vẫn thương con rất nhiều. Em chưa bao giờ giận m&aacute;. Chỉ buồn v&igrave; m&igrave;nh gặp nhau lu&ocirc;n sai thời điểm. Anh hứa sẽ sống c&oacute; tr&aacute;ch nhiệm với vợ con, để sự hi sinh của em trọn vẹn sau khi nghe m&aacute; n&oacute;i hết chuyện của ng&agrave;y xưa.</p>\r\n\r\n<p>Chuyến đ&ograve; lẫn v&agrave;o ho&agrave;ng h&ocirc;n đỏ quạch như đ&ocirc;i mắt em l&uacute;c n&agrave;y. Đ&oacute; cũng l&agrave; chuyến đ&ograve; cuối c&ugrave;ng v&ugrave;i chuyện t&igrave;nh tụi m&igrave;nh theo bọt s&oacute;ng. Rồi m&igrave;nh sẽ sống với sự lựa chọn của m&igrave;nh cho xứng đ&aacute;ng phải kh&ocirc;ng anh. Em xin gởi t&igrave;nh n&agrave;y ở lại. Để bước về ph&iacute;a bờ, l&ograve;ng nhẹ t&ecirc;nh như con thuyền vừa đi qua gi&ocirc;ng b&atilde;o.</p>', 0, 'chapter-1-tinh-co-gap-lai-nhau', '2021-12-07 16:20:35', '2021-12-07 16:20:47');
INSERT INTO `chapter` VALUES (13, 19, 'Giới thiệu Ỷ Thiên Đồ Long Ký', 'Bối cảnh tiểu thuyết lấy vào cuối thời nhà Nguyên, hơn 100 năm sau sự kiện trên đỉnh Hoa Sơn trong Thần điêu hiệp lữ,', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ỷ Thi&ecirc;n Đồ Long k&yacute; (Trung văn phồn thể: 倚天屠龍記), c&ograve;n được dịch ra tiếng Việt l&agrave; C&ocirc; g&aacute;i Đồ Long ( ch&iacute;nh l&agrave; Chu Chỉ Nhược v&igrave; c&ocirc; đ&atilde; kh&aacute;m ph&aacute; ra được b&iacute; mật), l&agrave; một tiểu thuyết v&otilde; hiệp của nh&agrave; văn Kim Dung. Đ&acirc;y l&agrave; cuốn cuối c&ugrave;ng trong bộ tiểu thuyết Xạ đi&ecirc;u tam kh&uacute;c. Tiểu thuyết được Hương Cảng Thương b&aacute;o xuất bản lần đầu năm 1961 tại Hồng K&ocirc;ng v&agrave; sau đ&oacute; bản tiếng Việt được Nh&agrave; xuất bản Văn học xuất bản tại Việt Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bối cảnh tiểu thuyết lấy v&agrave;o cuối thời nh&agrave; Nguy&ecirc;n, hơn 100 năm sau sự kiện tr&ecirc;n đỉnh Hoa Sơn trong Thần đi&ecirc;u hiệp lữ, l&uacute;c n&agrave;y nh&agrave; Nguy&ecirc;n đang bị suy yếu bởi c&aacute;c cuộc khởi nghĩa v&agrave; v&igrave; sự xa hoa l&atilde;ng ph&iacute; của triều đ&igrave;nh. Truyện xoay quanh Trương V&ocirc; Kỵ v&agrave; mối t&igrave;nh phức tạp với 4 c&ocirc; g&aacute;i, b&ecirc;n cạnh đ&oacute; l&agrave; những &acirc;m mưu thủ đoạn đầy m&aacute;u tanh tr&ecirc;n giang hồ nhằm chiếm đoạt 2 m&oacute;n b&aacute;u vật l&agrave; Đồ Long đao v&agrave; Ỷ Thi&ecirc;n kiếm, với lời đồn nếu t&igrave;m được b&iacute; mật trong đao kiếm sẽ hiệu triệu được thi&ecirc;n hạ. Truyện kết th&uacute;c với sự sụp đổ của nh&agrave; Nguy&ecirc;n, người M&ocirc;ng Cổ phải r&uacute;t về thảo nguy&ecirc;n ph&iacute;a bắc c&ugrave;ng với sự th&agrave;nh lập của nh&agrave; Minh bởi Minh Th&aacute;i Tổ Chu Nguy&ecirc;n Chương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương V&ocirc; Kỵ được xem l&agrave; c&oacute; nội t&acirc;m phức tạp hơn so với Qu&aacute;ch Tĩnh v&agrave; Dương Qu&aacute; trong khi kh&ocirc;ng c&oacute; những phẩm chất anh h&ugrave;ng như hai người kia. Điều n&agrave;y l&agrave;m cho nh&acirc;n vật trở n&ecirc;n thực tế hơn. Anh l&agrave; người hay bị t&aacute;c động bởi người kh&aacute;c, hay tự đặt m&igrave;nh v&agrave;o thế đ&atilde; rồi v&agrave; anh đ&atilde; kh&ocirc;ng thể tho&aacute;t khỏi c&aacute;i hiện thực t&agrave;n khốc đ&oacute;. Trương V&ocirc; Kỵ v&agrave;o cuối truyện xem Triệu Mẫn l&agrave; t&igrave;nh y&ecirc;u của đời m&igrave;nh, tuy nhi&ecirc;n kh&oacute; c&oacute; thể biết được ch&iacute;nh x&aacute;c ai mới l&agrave; anh y&ecirc;u nhất, kể cả ch&iacute;nh anh. Mặc d&ugrave; c&oacute; v&otilde; c&ocirc;ng cao cường, gần như v&ocirc; địch đương thời, chỉ thua c&oacute; Trương Tam Phong v&agrave; l&agrave; một người ch&iacute;nh trực khảng kh&aacute;i, người c&oacute; thể truyền cảm hứng cho người kh&aacute;c, Trương V&ocirc; Kỵ kh&ocirc;ng hề c&oacute; những tố chất cần thiết của một nh&agrave; l&atilde;nh đạo l&agrave; l&ograve;ng ham muốn m&atilde;nh liệt đối với quyền lực v&agrave; t&acirc;m kế để duy tr&igrave; quyền lực. Ch&iacute;nh v&igrave; vậy v&agrave;o cuối truyện, anh đ&atilde; bị Chu Nguy&ecirc;n Chương lừa n&ecirc;n bỏ đi c&ugrave;ng Triệu Mẫn v&agrave; quyền lực Minh gi&aacute;o dần rơi v&agrave;o tay Chu Nguy&ecirc;n Chương, l&agrave;m bước đệm cho y tranh thi&ecirc;n hạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"Ỷ thiên đồ long ký\" src=\"https://www.truyenngan.com.vn/images/linhbp/2014-tieu-thuyet/y-thien-do-long-ky.jpg\" style=\"height:667px; width:500px\" /></span></p>', 0, 'gioi-thieu-y-thien-do-long-ky', NULL, NULL);
INSERT INTO `chapter` VALUES (14, 19, 'Chương 1 Thiên nhai tư quân bất khả vọng', 'Chữ tình buộc lấy chữ sầu,  Chân trời góc biển tìm đâu bây giờ', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chữ t&igrave;nh buộc lấy chữ sầu,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&acirc;n trời g&oacute;c biển t&igrave;m đ&acirc;u b&acirc;y giờ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Xu&acirc;n du hạo đ&atilde;ng, thị ni&ecirc;n ni&ecirc;n h&agrave;n thực, l&ecirc; hoa thời tiết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bạch cẩm v&ocirc; văn hương lạn mạn, ngọc thụ quỳnh bao đ&ocirc;i tuyết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tĩnh dạ trầm trầm, ph&ugrave; quang &aacute;i &aacute;i, l&atilde;nh mạn dung dung nguyệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&acirc;n gian thi&ecirc;n thượng, lạn ng&acirc;n h&agrave; chiếu th&ocirc;ng triệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hồn tự c&ocirc; xạ ch&acirc;n nh&acirc;n, thi&ecirc;n tư linh t&uacute;, &yacute; kh&iacute; th&ugrave; cao khiết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vạn nhị s&acirc;m sai th&ugrave;y t&iacute;n đạo, bất dữ quần phương đồng liệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hạo kh&iacute; thanh anh, ti&ecirc;n t&agrave;i tr&aacute;c lạc, hạ thổ nan ph&acirc;n biệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Dao đ&agrave;i qui khứ, động thi&ecirc;n phương kh&aacute;n thanh tuyệt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tiết trời lạnh hoa l&ecirc; đang rộ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kh&aacute;ch nh&agrave;n du l&atilde;ng đ&atilde;ng chơi xu&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hương thơm bay tỏa kh&ocirc;ng gian,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&agrave;nh c&acirc;y trắng x&oacute;a lộc non nở đầy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Aacute;nh trăng lạnh canh khuya thanh tĩnh,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chiếu tr&ecirc;n cao thấp tho&aacute;ng s&ocirc;ng ng&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">T&acirc;m t&igrave;nh một vẻ mi&ecirc;n man,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trời cho t&iacute;nh kh&iacute; cao sang vẻ người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chen lẫn vẻ mu&ocirc;n hoa rực rỡ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vẻ thanh cao vẫn tự vươn cao.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thanh anh tr&aacute;c lạc hơn người,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Dẫu trong ngọc đ&aacute; c&oacute; chiều nọ kia.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Dao đ&agrave;i ai kẻ đi về,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hương trời đ&ocirc;i lứa đ&atilde; kề b&ecirc;n nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&agrave;i từ &quot;V&ocirc; Tục Niệm&quot; n&agrave;y vốn l&agrave; của một vị v&otilde; học danh gia, cũng l&agrave; một đạo sĩ ở v&agrave;o cuối đời Nam Tống họ Khưu t&ecirc;n Xứ Cơ, đạo hiệu Trường Xu&acirc;n Tử. &Ocirc;ng l&agrave; một trong To&agrave;n Ch&acirc;n thất tử v&agrave; l&agrave; nh&acirc;n vật xuất sắc của ph&aacute;i To&agrave;n Ch&acirc;n. Trong &quot;Từ Phẩm&quot; đ&atilde; b&igrave;nh về b&agrave;i từ n&agrave;y như sau :</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&quot;Trường Xu&acirc;n, người đời vẫn coi l&agrave; một vị ti&ecirc;n, n&ecirc;n lời từ mới hay v&agrave; xuất sắc đến thế&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&agrave;i từ tuy n&oacute;i về hoa l&ecirc;, nhưng thật ra ch&iacute;nh l&agrave; để ca tụng một thiếu nữ xinh đẹp mặc &aacute;o trắng, coi n&agrave;ng &quot;thực kh&ocirc;ng phải người ph&agrave;m, thi&ecirc;n tư linh t&uacute;, kh&iacute; độ cao khiết&quot;, lại n&oacute;i n&agrave;ng &quot;hạo kh&iacute; thanh anh, ti&ecirc;n t&agrave;i tr&aacute;c lạc&quot;, &quot;bất dĩ quần phương đồng liệt&quot; (kh&ocirc;ng giống như những người kh&aacute;c). Người con g&aacute;i đẹp m&ocirc; tả trong b&agrave;i từ n&agrave;y, ch&iacute;nh l&agrave; truyền nh&acirc;n của ph&aacute;i Cổ Mộ, Tiểu Long Nữ. N&agrave;ng vốn ưa mặc đồ trắng, chẳng kh&aacute;c g&igrave; gi&oacute; thổi qua c&acirc;y ngọc, đ&oacute;a quỳnh nở trong tuyết, chỉ hiềm t&iacute;nh kh&iacute; lạnh l&ugrave;ng, n&ecirc;n mới tả h&igrave;nh dung l&agrave; &quot;l&atilde;nh mạn dung dung nguyệt&quot;. Khưu Xứ Cơ tặng n&agrave;ng ba chữ &quot;V&ocirc; Tục Niệm&quot; thật mười phần x&aacute;c đ&aacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trường Xu&acirc;n Tử Khưu Xứ Cơ ở ngay b&ecirc;n cạnh n&agrave;ng tại Chung Nam Sơn, một lần gặp mặt n&ecirc;n viết ra b&agrave;i từ n&agrave;y. L&uacute;c bấy giờ Khưu Xứ Cơ tạ thế đ&atilde; l&acirc;u, Tiểu Long Nữ cũng đ&atilde; l&agrave;m vợ Thần Đi&ecirc;u đại hiệp Dương Qu&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tr&ecirc;n sơn đạo n&uacute;i Thiếu Thất, tỉnh H&agrave; Nam, c&oacute; một thiếu nữ, đang c&uacute;i đầu lẩm nhẩm b&agrave;i từ n&agrave;y. C&ocirc; g&aacute;i ước chừng mười t&aacute;m, mười ch&iacute;n tuổi, mặc &aacute;o m&agrave;u v&agrave;ng nhạt, cưỡi một con lừa đen, đi chầm chậm l&ecirc;n n&uacute;i, vừa đi vừa nghĩ thầm: &quot;Chỉ c&oacute; người như Long tỉ tỉ mới xứng đ&aacute;ng lấy được ch&agrave;ng m&agrave; th&ocirc;i&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chữ &quot;ch&agrave;ng&quot; hiển nhi&ecirc;n l&agrave; n&oacute;i đến Thần Đi&ecirc;u đại hiệp Dương Qu&aacute;. C&ocirc; g&aacute;i lỏng d&acirc;y cương, cứ để cho con lừa tự &yacute;, thẳng đường l&ecirc;n n&uacute;i. Một l&uacute;c l&acirc;u sau, c&ocirc; lại lẩm bẩm: &quot;hoan lạc th&uacute;, ly biệt khổ, tựu trung c&aacute;nh hữu si nhi nữ. Qu&acirc;n ứng hữu ngữ, diểu vạn l&yacute; tằng v&acirc;n, thi&ecirc;n sơn mộ tuyết, ch&iacute;ch ảnh hướng th&ugrave;y khứ?&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gặp nhau l&ograve;ng những vui vầy,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Xa nhau dạ những luống đầy khổ đau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&otilde;i t&igrave;nh m&ecirc; đắm ai đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chim kia thiếu bạn tiếng sầu lẻ loi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Từng m&acirc;y mu&ocirc;n dặm xa x&ocirc;i,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&uacute;i cao tuyết trắng nơi nơi cũng l&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một m&igrave;nh c&ocirc; tịch gần xa,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trăm năm bến cũ biết l&agrave; về đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; g&aacute;i lưng đeo đoản kiếm, sắc mặt c&oacute; nhuốm đ&ocirc;i ch&uacute;t phong trần, hiển nhi&ecirc;n đ&atilde; viễn du l&acirc;u ng&agrave;y. Gương mặt xinh tươi, đang ở v&agrave;o tuổi v&ocirc; tư lự, nhưng sao dung nhan như c&oacute; g&igrave; muộn phiền, đầu m&agrave;y cuối mắt kh&ocirc;ng thể che dấu được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng họ Qu&aacute;ch, t&ecirc;n chỉ c&oacute; một chữ Tương, ch&iacute;nh l&agrave; con thứ của đại hiệp Qu&aacute;ch Tĩnh v&agrave; nữ hiệp Ho&agrave;ng Dung, c&ograve;n c&oacute; ngoại hiệu l&agrave; Tiểu Đ&ocirc;ng T&agrave;. N&agrave;ng một lừa, một kiếm, lang thang một m&igrave;nh, những tưởng phiền muộn trong l&ograve;ng vơi đi, ai ngờ sầu lại th&ecirc;m sầu, nơi danh sơn lẻ b&oacute;ng lại c&agrave;ng hiu quạnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&uacute;i Thiếu Thất thuộc tỉnh H&agrave; Nam cũng kh&aacute; cao, đường l&ecirc;n n&uacute;i l&agrave; những bậc thang bằng đ&aacute; rộng r&atilde;i, qui m&ocirc; lớn lao, c&ocirc;ng tr&igrave;nh kh&ocirc;ng phải nhỏ, l&agrave; do vua Đường Th&aacute;i T&ocirc;ng đến thăm ch&ugrave;a Thiếu L&acirc;m m&agrave; bỏ ra tạo dựng, d&agrave;i cả thảy t&aacute;m dặm. Qu&aacute;ch Tương cưỡi lừa uể oải tr&egrave;o l&ecirc;n, thấy trước mặt từ ngọn tr&ecirc;n n&uacute;i &agrave;o &agrave;o đổ xuống năm d&ograve;ng th&aacute;c tung t&oacute;e như ngọc, lại nh&igrave;n những ngọn n&uacute;i kh&aacute;c chỉ nhỏ như đ&agrave;n kiến. Theo đường sơn đạo chuyển v&agrave;o một kh&uacute;c quanh, th&igrave; thấy một t&ograve;a tự viện tường v&agrave;ng ng&oacute;i xanh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng đứng ngắm d&atilde;y ch&ugrave;a một hồi, nghĩ thầm: &quot;Thiếu L&acirc;m tự vốn l&agrave; nguồn gốc v&otilde; học của thi&ecirc;n hạ, nhưng sao hai kỳ luận kiếm Hoa Sơn, trong số ngũ tuyệt kh&ocirc;ng thấy cao tăng n&agrave;o của ch&ugrave;a Thiếu L&acirc;m cả? Kh&ocirc;ng lẽ h&ograve;a thượng trong ch&ugrave;a kh&ocirc;ng t&agrave;i c&aacute;n g&igrave;, sợ mất uy danh, n&ecirc;n kh&ocirc;ng d&aacute;m ph&oacute; hội? Hay l&agrave; tăng lữ tu h&agrave;nh tinh th&acirc;m, kh&ocirc;ng c&ograve;n tham luyến hư danh, v&otilde; c&ocirc;ng tuy cao, nhưng kh&ocirc;ng muốn tranh cường đổ thắng với người ngo&agrave;i?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng xuống lừa, lững thững đến trước cửa ch&ugrave;a, chỉ thấy c&acirc;y cối rậm rạp, b&oacute;ng r&acirc;m che phủ một khu rừng bia. Bia đ&aacute; qu&aacute; nửa đ&atilde; bị hủy ph&aacute;, n&eacute;t chữ mơ hồ, kh&ocirc;ng biết viết những g&igrave;. N&agrave;ng nghĩ thầm: &quot;Chữ khắc s&acirc;u tr&ecirc;n đ&aacute;, theo năm th&aacute;ng cũng m&ograve;n, thế nhưng sao những g&igrave; khắc trong tim ta, thời gian c&agrave;ng l&acirc;u c&agrave;ng s&acirc;u đậm?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chợt thấy một tấm bia lớn khắc việc Đường Th&aacute;i T&ocirc;ng ban cho ch&ugrave;a Thiếu L&acirc;m ngự tr&aacute;t, khen tăng nh&acirc;n về việc lập c&ocirc;ng trừ loạn. Trong bia n&oacute;i l&agrave; khi Đường Th&aacute;i T&ocirc;ng c&ograve;n l&agrave; Tần Vương, đem binh đ&aacute;nh Vương Thế Sung, h&ograve;a thượng ch&ugrave;a Thiếu L&acirc;m đầu qu&acirc;n, xuất sắc nhất c&oacute; cả thảy mười ba người. Trong số đ&oacute; chỉ c&oacute; Đ&agrave;m T&ocirc;ng nhận phong l&agrave;m Đại Tướng Qu&acirc;n, c&ograve;n mười hai người c&ograve;n lại kh&ocirc;ng muốn l&agrave;m quan, Đường Th&aacute;i T&ocirc;ng ban cho mỗi người một &aacute;o c&agrave; sa bằng lụa t&iacute;a. N&agrave;ng tưởng tượng ngay từ thời T&ugrave;y Đường m&agrave; v&otilde; c&ocirc;ng ch&ugrave;a Thiếu L&acirc;m đ&atilde; danh vang thi&ecirc;n hạ, mấy trăm năm nay lại c&agrave;ng th&ecirc;m tinh xảo, kh&ocirc;ng biết trong ch&ugrave;a c&oacute; bao nhi&ecirc;u ngọa hổ t&agrave;ng long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương từ khi chia tay với vợ chồng Dương Qu&aacute;, Tiểu Long Nữ ở tr&ecirc;n đỉnh Hoa Sơn tới giờ đ&atilde; ba năm kh&ocirc;ng nghe ch&uacute;t tin tức g&igrave; của hai người. Trong l&ograve;ng mong nhớ, n&agrave;ng thưa với cha mẹ, n&oacute;i l&agrave; muốn đi ngao du sơn thủy, nhưng thực ra l&agrave; để nghe ng&oacute;ng tin tức của Dương Qu&aacute;. N&agrave;ng thực t&acirc;m cũng chẳng cần phải gặp mặt cặp vợ chồng n&agrave;y, chỉ cần nghe tin họ h&agrave;nh hiệp ra sao cũng đ&atilde; thỏa m&atilde;n. Thế nhưng từ khi từ biệt, hai người kh&ocirc;ng lộ diện giang hồ, chẳng biết ẩn cư nơi n&agrave;o. Th&agrave;nh thử Qu&aacute;ch Tương đi từ bắc xuống nam, lại từ đ&ocirc;ng sang t&acirc;y, tưởng chừng đ&atilde; đặt ch&acirc;n qu&aacute; nửa đất trung nguy&ecirc;n, thủy chung vẫn kh&ocirc;ng nghe thấy ai n&oacute;i đến Thần Đi&ecirc;u đại hiệp Dương Qu&aacute; cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một h&ocirc;m n&agrave;ng đến đất H&agrave; Nam, chợt nhớ ra ch&ugrave;a Thiếu L&acirc;m c&oacute; một nh&agrave; sư t&ecirc;n V&ocirc; Sắc l&agrave; bạn th&acirc;n của Dương Qu&aacute;, hồi m&igrave;nh sinh nhật mười s&aacute;u tuổi, nể mặt họ Dương n&ecirc;n &ocirc;ng đ&atilde; sai người đem tặng một m&oacute;n qu&agrave;. Tuy chưa bao giờ gặp mặt nh&agrave; sư n&agrave;y nhưng cũng n&ecirc;n l&ecirc;n hỏi thử một c&acirc;u, biết đ&acirc;u &ocirc;ng ta lại r&otilde; tung t&iacute;ch của hai người. V&igrave; thế Qu&aacute;ch Tương mới l&ecirc;n ch&ugrave;a Thiếu L&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đang l&uacute;c xuất thần, bỗng nghe thấy từ ch&ograve;m c&acirc;y b&ecirc;n cạnh rừng bia truyền ra tiếng leng keng, loảng xoảng v&agrave; tiếng người niệm kinh Phật:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thị thời Dược Xoa cộng vương lập yếu, tức ư v&ocirc; lượng b&aacute;ch thi&ecirc;n vạn ức đại ch&uacute;ng chi trung, thuyết thắng diệu gi&agrave; tha viết: do &aacute;i cố sinh ưu, do &aacute;i cố sinh bố; nhược li ư &aacute;i giả, v&ocirc; ưu diệc v&ocirc; bố ...[1]</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Khi đ&oacute; Thế T&ocirc;n đứng giữa thi&ecirc;n vạn ức người m&agrave; giảng rằng: V&igrave; y&ecirc;u m&agrave; sinh ra buồn phiền, v&igrave; y&ecirc;u m&agrave; sinh ra sợ h&atilde;i, nếu như dứt được y&ecirc;u, chẳng buồn cũng chẳng sợ ...)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe bốn chữ kệ đ&oacute;, bỗng thấy ngơ ngẩn, trong l&ograve;ng cũng nhẩm theo: &quot;do &aacute;i cố sinh ưu, do &aacute;i cố sinh bố; nhược li ư &aacute;i giả, v&ocirc; ưu diệc v&ocirc; bố&quot;. Chỉ nghe thấy tiếng loảng xoảng c&ugrave;ng tiếng tụng kinh xa dần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương c&uacute;i đầu : &quot;Để ta hỏi &ocirc;ng ta l&agrave;m c&aacute;ch n&agrave;o c&oacute; thể tho&aacute;t khỏi t&igrave;nh y&ecirc;u, l&agrave;m c&aacute;ch n&agrave;o để kh&ocirc;ng buồn kh&ocirc;ng sợ?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thuận tay n&agrave;ng buộc lừa v&agrave;o một gốc c&acirc;y, vạch l&ugrave;m c&acirc;y đuổi theo. Chỉ thấy đằng sau t&agrave;n c&acirc;y l&agrave; một đường m&ograve;n l&ecirc;n n&uacute;i, c&oacute; một tăng nh&acirc;n g&aacute;nh một đ&ocirc;i th&ugrave;ng lớn, đang đi chầm chậm. Qu&aacute;ch Tương rảo bước chạy theo, đến c&aacute;ch nh&agrave; sư chừng bảy, t&aacute;m trượng, bỗng giật m&igrave;nh. Chỉ thấy người đ&oacute; g&aacute;nh một đ&ocirc;i th&ugrave;ng sắt lớn, so với th&ugrave;ng g&aacute;nh nước thường phải hơn gấp đ&ocirc;i, vậy m&agrave; tr&ecirc;n cổ, c&aacute;nh tay, cẳng ch&acirc;n c&ograve;n quấn qu&iacute;t đầy x&iacute;ch lớn, mỗi khi đi đứng tiếng k&ecirc;u loảng xoảng. Hai chiếc th&ugrave;ng kh&ocirc;ng cũng đ&atilde; đến hơn hai trăm c&acirc;n, trong lại đầy nước, phải nặng đến kinh người. Qu&aacute;ch Tương k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, xin dừng ch&acirc;n, tiểu nữ c&oacute; điều muốn thỉnh gi&aacute;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư đ&oacute; quay đầu lại, hai người nh&igrave;n nhau, đều tỏ vẻ ngạc nhi&ecirc;n. H&oacute;a ra vị h&ograve;a thượng đ&oacute; l&agrave; Gi&aacute;c Viễn, ba năm trước, hai người đ&atilde; gặp nhau tại đỉnh Hoa Sơn. Qu&aacute;ch Tương biết &ocirc;ng ta tuy t&iacute;nh hơi cổ hủ nhưng nội c&ocirc;ng tinh th&acirc;m, kh&ocirc;ng k&eacute;m bất cứ cao thủ n&agrave;o hiện thời, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tưởng ai h&oacute;a ra l&agrave; Gi&aacute;c Viễn đại sư. Tại sao &ocirc;ng lại ra n&ocirc;ng nỗi n&agrave;y?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn khẽ gật đầu, mỉm cười, chắp tay h&agrave;nh lễ, nhưng kh&ocirc;ng trả lời, quay m&igrave;nh bước đi. Qu&aacute;ch Tương vội gọi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gi&aacute;c Viễn đại sư, &ocirc;ng kh&ocirc;ng nhận ra t&ocirc;i sao? T&ocirc;i l&agrave; Qu&aacute;ch Tương đ&acirc;y m&agrave;!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn quay lại mỉm cười, khẽ gật đầu nhưng kh&ocirc;ng ngừng bước. Qu&aacute;ch Tương lại hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ai lấy x&iacute;ch buộc &ocirc;ng vậy? Sao lại h&agrave;nh hạ &ocirc;ng như thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn đưa tay tr&aacute;i ra sau đầu xua xua mấy c&aacute;i, như &aacute;m chỉ c&ocirc; ta đừng hỏi nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy chuyện kỳ qu&aacute;i, lẽ n&agrave;o kh&ocirc;ng t&igrave;m cho ra đầu đu&ocirc;i? Lập tức cất bước đuổi theo, t&iacute;nh sẽ chặn trước mặt nh&agrave; sư. N&agrave;o ngờ Gi&aacute;c Viễn tuy to&agrave;n th&acirc;n đầy x&iacute;ch, lại g&aacute;nh đ&ocirc;i th&ugrave;ng sắt, vậy m&agrave; dẫu Qu&aacute;ch Tương hết sức chạy, thủy chung vẫn kh&ocirc;ng vượt qua được &ocirc;ng ta. T&iacute;nh trẻ con của c&ocirc; g&aacute;i nổi l&ecirc;n, thi triển khinh c&ocirc;ng gia truyền, hai ch&acirc;n nh&uacute;n một c&aacute;i, người bay vọt l&ecirc;n, vươn tay chụp v&agrave;o th&ugrave;ng nước. Mắt tr&ocirc;ng tưởng ra tay ắt sẽ tr&uacute;ng, n&agrave;o ngờ vẫn c&ograve;n c&aacute;ch đến hai tấc. Qu&aacute;ch Tương k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng giỏi thật, nhất định t&ocirc;i phải đuổi kịp &ocirc;ng mới th&ocirc;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy Gi&aacute;c Viễn rảo bước đi, tiếng x&iacute;ch sắt leng keng như tiếng nhạc, mỗi l&uacute;c một xa về ph&iacute;a sau n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đuổi theo một l&uacute;c hơi thở đ&atilde; dồn dập, thế nhưng vẫn c&aacute;ch &ocirc;ng ta đến hơn một trượng, trong l&ograve;ng kh&ocirc;ng khỏi bội phục: &quot;Cha mẹ ta l&uacute;c ở tr&ecirc;n n&uacute;i Hoa Sơn, đ&atilde; từng n&oacute;i l&agrave; vị h&ograve;a thượng n&agrave;y v&otilde; c&ocirc;ng cực cao, l&uacute;c đ&oacute; ta kh&ocirc;ng tin, đến nay thử một chuyến, thấy lời hai &ocirc;ng b&agrave; quả thật kh&ocirc;ng sai&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy Gi&aacute;c Viễn quẹo v&agrave;o đằng sau một ng&ocirc;i nh&agrave; nhỏ, đổ hai th&ugrave;ng nước v&agrave;o trong một miệng giếng. Qu&aacute;ch Tương hết sức kỳ qu&aacute;i, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, &ocirc;ng kh&ocirc;ng đi&ecirc;n đấy chứ? Đổ nước v&agrave;o trong giếng để l&agrave;m g&igrave; thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn thần sắc b&igrave;nh h&ograve;a, lắc đầu. Qu&aacute;ch Tương chợt hiểu ra, cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Agrave;, h&oacute;a ra &ocirc;ng đang luyện một m&ocirc;n nội c&ocirc;ng rất cao th&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn lại lắc đầu lần nữa. Qu&aacute;ch Tương hơi bực m&igrave;nh, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- R&otilde; r&agrave;ng t&ocirc;i nghe &ocirc;ng tụng kinh, tức kh&ocirc;ng bị c&acirc;m, sao b&acirc;y giờ kh&ocirc;ng trả lời t&ocirc;i?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn chắp tay ch&agrave;o, vẻ mặt hơi sượng s&ugrave;ng, kh&ocirc;ng đ&aacute;p lời, quảy đ&ocirc;i th&ugrave;ng đi xuống ch&acirc;n n&uacute;i. Qu&aacute;ch Tương th&ograve; đầu nh&igrave;n v&agrave;o trong giếng, chỉ thấy nước trong vắt, kh&ocirc;ng thấy điểm g&igrave; đặc biệt, ngẩn ngơ nh&igrave;n theo lưng Gi&aacute;c Viễn, l&ograve;ng đầy nghi hoặc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; ta đuổi theo một hồi thấy đ&atilde; hơi mệt n&ecirc;n ngồi xuống th&agrave;nh giếng, quan s&aacute;t phong cảnh bốn bề. Nơi đ&oacute; cao hơn những t&ograve;a nh&agrave; trong ch&ugrave;a Thiếu L&acirc;m, nhưng thấy n&uacute;i Thiếu Thất vươn thẳng l&ecirc;n trời, trải ra như một bức b&igrave;nh phong, b&ecirc;n dưới kh&oacute;i tỏa lung linh, tiếng chu&ocirc;ng theo gi&oacute; vang lại, khiến bao nhi&ecirc;u nỗi phiền tục bay đi cả. Qu&aacute;ch Tương thầm nghĩ: &quot;Người đệ tử của &ocirc;ng h&ograve;a thượng n&agrave;y đi đ&acirc;u, nếu &ocirc;ng ta kh&ocirc;ng n&oacute;i, ta đi hỏi thiếu ni&ecirc;n n&agrave;y vậy&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghĩ vậy cất bước hạ sơn, định đi kiếm đệ tử của Gi&aacute;c Viễn l&agrave; Trương Qu&acirc;n Bảo hỏi cho ra lẽ. Đi được một qu&atilde;ng, lại nghe tiếng x&iacute;ch leng keng, Gi&aacute;c Viễn lại g&aacute;nh nước đi tới. Qu&aacute;ch Tương lẻn ra sau một gốc c&acirc;y, nghĩ thầm:&quot; Để ta r&igrave;nh xem &ocirc;ng ta định l&agrave;m tr&ograve; quỉ qu&aacute;i g&igrave;&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tiếng x&iacute;ch sắt tới gần, chỉ thấy Gi&aacute;c Viễn g&aacute;nh đ&ocirc;i th&ugrave;ng sắt, tay cầm một quyển s&aacute;ch, ch&uacute; t&acirc;m v&agrave;o đọc. Qu&aacute;ch Tương đợi &ocirc;ng ta đến gần b&ecirc;n, hết sức nhảy vọt ra, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, &ocirc;ng đọc s&aacute;ch g&igrave; thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn thất thanh k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;i ch&agrave;, l&agrave;m t&ocirc;i hết hồn, h&oacute;a ra l&agrave; c&ocirc;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tưởng &ocirc;ng giả c&acirc;m, sao b&acirc;y giờ lại n&oacute;i được?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn hơi c&oacute; vẻ sợ h&atilde;i, nh&igrave;n quanh một lượt, xua tay. Qu&aacute;ch Tương hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng sợ c&aacute;i g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn chưa kịp trả lời, đột nhi&ecirc;n từ trong rừng c&acirc;y c&oacute; hai nh&agrave; sư mặc &aacute;o m&agrave;u x&aacute;m tro bước ra, một cao một l&ugrave;n. Nh&agrave; sư cao gầy qu&aacute;t:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gi&aacute;c Viễn kh&ocirc;ng giữ giới ph&aacute;p, d&aacute;m tự tiện mở mồm n&oacute;i chuyện với người ngo&agrave;i ch&ugrave;a, nhất l&agrave; lại n&oacute;i chuyện với một thiếu nữ trẻ tuổi? Mau đi gặp thủ tọa giới luật đường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn lủi thủi, chỉ gật đầu, đi theo hai tăng nh&acirc;n nọ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương vừa tức, vừa giận, lớn tiếng hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tr&ecirc;n đời n&agrave;y sao lại c&oacute; lề luật đ&acirc;u cấm người ta n&oacute;i chuyện bao giờ? T&ocirc;i quen biết vị đại sư n&agrave;y, t&ocirc;i n&oacute;i chuyện với &ocirc;ng ta, việc g&igrave; đến c&aacute;c &ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư cao gầy trừng mắt nh&igrave;n, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Từ một ngh&igrave;n năm nay, ch&ugrave;a Thiếu L&acirc;m kh&ocirc;ng cho ph&eacute;p nữ lưu bước ch&acirc;n v&agrave;o. Mời c&ocirc; nương xuống n&uacute;i, đừng để ch&uacute;ng t&ocirc;i phải l&agrave;m phiền.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nổi giận:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nữ lưu th&igrave; đ&atilde; sao? Đ&agrave;n b&agrave; con g&aacute;i kh&ocirc;ng phải người &agrave;? C&aacute;c ngươi tại sao l&agrave;m kh&oacute; dễ vị Gi&aacute;c Viễn đại sư n&agrave;y? Đ&atilde; lấy xiềng x&iacute;ch tr&oacute;i người ta, lại c&ograve;n kh&ocirc;ng cho người ta n&oacute;i chuyện?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư đ&oacute; cười nhạt:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Việc của bản tự, đến ho&agrave;ng đế cũng chẳng can thiệp được. Kh&ocirc;ng d&aacute;m để c&ocirc; nương phải nhọc l&ograve;ng hỏi đến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương c&agrave;ng giận hơn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị đại sư n&agrave;y l&agrave; người thực th&agrave; trung hậu, thấy người ta hiền l&agrave;nh, c&aacute;c ngươi hiếp đ&aacute;p. Hừ hừ, Thi&ecirc;n Minh thiền sư đ&acirc;u? V&ocirc; Sắc, V&ocirc; Tướng h&ograve;a thượng đ&acirc;u? C&aacute;c ngươi đi gọi họ ra đ&acirc;y để ta hỏi họ xem thế l&agrave; thế n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai nh&agrave; sư nọ nghe thấy thế đều giật m&igrave;nh. Thi&ecirc;n Minh thiền sư l&agrave; phương trượng của ch&ugrave;a, V&ocirc; Sắc thiền sư l&agrave; thủ tọa La H&aacute;n đường, c&ograve;n V&ocirc; Tướng thiền sư l&agrave; thủ tọa Đạt Ma đường. Ba người địa vị t&ocirc;n qu&iacute; n&ecirc;n tăng lữ trong ch&ugrave;a trước nay chỉ gọi l&agrave; l&atilde;o phương trượng, thủ tọa La H&aacute;n đường v&agrave; thủ tọa Đạt Ma đường, chứ kh&ocirc;ng d&aacute;m gọi bằng ph&aacute;p danh. Ngờ đ&acirc;u một c&ocirc; g&aacute;i trẻ tuổi lại d&aacute;m l&ecirc;n n&uacute;i h&ocirc; ho&aacute;n thẳng t&ecirc;n như thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai tăng nh&acirc;n n&agrave;y đều l&agrave; đệ tử của thủ tọa giới luật đường, được lệnh tọa sư sai đi gi&aacute;m thị Gi&aacute;c Viễn. Nghe thấy Qu&aacute;ch Tương ăn n&oacute;i kh&ocirc;ng ki&ecirc;ng nể g&igrave; ai, nh&agrave; sư cao gầy qu&aacute;t:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nữ th&iacute; chủ nếu c&ograve;n l&agrave;m rộn chốn Phật m&ocirc;n thanh tịnh th&igrave; đừng tr&aacute;ch tiểu tăng v&ocirc; lễ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bộ tưởng ta sợ sư s&atilde;i c&aacute;c ngươi hay sao? Mau mau th&aacute;o d&acirc;y x&iacute;ch xiềng tr&oacute;i Gi&aacute;c Viễn đại sư ra th&igrave; ta để y&ecirc;n, nếu kh&ocirc;ng ta sẽ l&ocirc;i Thi&ecirc;n Minh l&atilde;o h&ograve;a thượng ra hỏi đấy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư l&ugrave;n nghe thấy Qu&aacute;ch Tương ăn n&oacute;i lếu l&aacute;o, b&ecirc;n h&ocirc;ng lại đeo đoản kiếm, n&ecirc;n gằn giọng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; th&aacute;o binh kh&iacute; để lại, ch&uacute;ng t&ocirc;i kh&ocirc;ng c&atilde;i l&yacute; với c&ocirc;, mau xuống n&uacute;i đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương th&aacute;o đoản kiếm ra, hai t&acirc;y n&acirc;ng l&ecirc;n cười nhạt:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hay lắm, xin tu&acirc;n lệnh t&ocirc;n đ&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">G&atilde; sư l&ugrave;n từ nhỏ xuất gia, trước nay nghe sư b&aacute;, sư th&uacute;c, sư huynh đều n&oacute;i l&agrave; Thiếu L&acirc;m tự l&agrave; nguồn gốc chung của v&otilde; học, lại nghe l&agrave; d&ugrave; thanh vọng lớn đến thế n&agrave;o chăng nữa, v&otilde; l&acirc;m cao thủ t&agrave;i ba đến mấy chăng nữa, cũng kh&ocirc;ng ai d&aacute;m đeo binh kh&iacute; tiến v&agrave;o trong ch&ugrave;a Thiếu L&acirc;m. C&ocirc; g&aacute;i trẻ tuổi n&agrave;y tuy chưa bước ch&acirc;n v&agrave;o cổng ch&ugrave;a, nhưng cũng đ&atilde; thuộc phạm vi của Thiếu L&acirc;m, hẳn l&agrave; c&ocirc; ta cũng sợ n&ecirc;n l&iacute;u r&iacute;u giao đoản kiếm. Nghĩ thế n&ecirc;n y đưa tay nhận kiếm. Tay g&atilde; vừa chạm v&agrave;o bao kiếm bỗng thấy c&aacute;nh tay chấn động như bị điện giật. Một luồng sức mạnh từ đoản kiếm truyền l&ecirc;n, hất về ph&iacute;a sau, ch&acirc;n đứng kh&ocirc;ng vững khiến g&atilde; ng&atilde; bổ chửng. G&atilde; đang đứng tại triền n&uacute;i dốc, bị ng&atilde; liền lộc cộc lăn xuống mấy trượng, may cố gượng mới dừng lại được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư cao gầy vừa tức vừa sợ, qu&aacute;t:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi ăn tim sư tử, uống mật b&aacute;o hay sao m&agrave; d&aacute;m tới Thiếu L&acirc;m tự g&acirc;y rối hả?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y xoay người lại, tiến l&ecirc;n một bước, tay phải tung ra một chưởng, tay tr&aacute;i cũng đi theo đ&egrave; l&ecirc;n tay phải, hai tay c&ugrave;ng đ&aacute;nh ra, ch&iacute;nh l&agrave; thế thứ hai mươi t&aacute;m trong &quot;Sấn Thiếu L&acirc;m&quot; Phi&ecirc;n Th&acirc;n Ph&aacute;ch K&iacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương tay cầm c&aacute;n kiếm, d&ugrave;ng cả kiếm lẫn bao nhằm vai g&atilde; điểm tới. Nh&agrave; sư hạ vai xuống phản k&iacute;ch, vươn tay chộp lấy bao kiếm. Gi&aacute;c Viễn đứng b&ecirc;n thấy thế hoảng hốt k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Xin đừng động thủ, đừng động thủ! H&atilde;y từ từ n&oacute;i chuyện phải quấy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c đ&oacute; tăng nh&acirc;n đ&atilde; nắm được bao, đang định vận k&igrave;nh đoạt kiếm, chỉ thấy l&ograve;ng b&agrave;n tay bị chấn động, hai cổ tay hơi t&ecirc; đi, k&ecirc;u l&ecirc;n một tiếng: &quot;Hỏng rồi&quot;, Qu&aacute;ch Tương ch&acirc;n tr&aacute;i đ&atilde; qu&eacute;t ngang, đ&aacute; y văng xuống sườn n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y bị đ&ograve;n so với g&atilde; sư l&ugrave;n nặng hơn nhiều, lăn xuống, tr&ecirc;n đầu, tr&ecirc;n mặt bị va v&agrave;o đ&aacute; m&aacute;u chảy l&ecirc;nh l&aacute;ng, m&atilde;i mới ngừng lại được. Qu&aacute;ch Tương nghĩ thầm: &quot;Ta l&ecirc;n ch&ugrave;a Thiếu L&acirc;m vốn chỉ muốn d&ograve; hỏi tin tức đại ca, v&ocirc; cớ c&ugrave;ng bọn họ động thủ, xem ra kh&ocirc;ng ổn rồi&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Liếc mắt thấy Gi&aacute;c Viễn mặt m&agrave;y buồn thiu đứng tại b&ecirc;n cạnh, n&ecirc;n r&uacute;t đoản kiếm ra, chặt những x&iacute;ch buộc ở tay &ocirc;ng ta. Tuy đoản kiếm đ&oacute; kh&ocirc;ng phải l&agrave; vật hi hữu, qu&iacute; b&aacute;u, nhưng cũng l&agrave; một loại binh kh&iacute; rất sắc b&eacute;n, chỉ nghe loảng xoảng mấy tiếng, d&acirc;y x&iacute;ch đ&atilde; đứt th&agrave;nh ba kh&uacute;c. Gi&aacute;c Viễn lu&ocirc;n mồm k&ecirc;u:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng được đ&acirc;u! Kh&ocirc;ng được đ&acirc;u!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i g&igrave; m&agrave; kh&ocirc;ng được?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Rồi chỉ tay về ph&iacute;a hai tăng nh&acirc;n cao l&ugrave;n đang chạy v&agrave;o ch&ugrave;a m&agrave; n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hai t&ecirc;n &aacute;c h&ograve;a thượng đ&oacute; hẳn l&agrave; v&agrave;o ch&ugrave;a phi b&aacute;o, m&igrave;nh phải chạy ngay. Đồ đệ họ Trương của &ocirc;ng đ&acirc;u? Dắt cậu ta theo với.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn chỉ xua tay. Bỗng ph&iacute;a sau c&oacute; tiếng người n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đa tạ c&ocirc; nương quan t&acirc;m, t&ocirc;i đang ở đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương quay lại, thấy đằng sau lưng một thiếu ni&ecirc;n chừng mười s&aacute;u, mười bảy tuổi, m&agrave;y rậm mắt to, th&acirc;n thể cao lớn, nhưng mặt vẫn c&ograve;n trẻ con, ch&iacute;nh l&agrave; Trương Qu&acirc;n Bảo, người ba năm trước đ&atilde; gặp tr&ecirc;n n&uacute;i Hoa Sơn. So với b&acirc;y giờ, cậu ta đ&atilde; cao lớn hơn nhiều, nhưng dung mạo kh&ocirc;ng thay đổi mấy. Qu&aacute;ch Tương mừng qu&aacute;, vội n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bọn &aacute;c h&ograve;a thượng ở đ&acirc;y ch&egrave;n &eacute;p sư phụ ngươi, m&igrave;nh n&ecirc;n chạy ngay đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng ai ch&egrave;n &eacute;p sư phụ t&ocirc;i đ&acirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương chỉ Gi&aacute;c Viễn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hai g&atilde; &aacute;c h&ograve;a thượng đ&oacute; lấy x&iacute;ch buộc sư phụ ngươi, lại một c&acirc;u cũng kh&ocirc;ng cho n&oacute;i, chẳng ch&egrave;n &eacute;p th&igrave; l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn nhăn nh&oacute; lắc đầu, chỉ xuống ch&acirc;n n&uacute;i, &yacute; muốn Qu&aacute;ch Tương mau mau tho&aacute;t th&acirc;n, đừng để bị rắc rối. Qu&aacute;ch Tương biết rằng trong ch&ugrave;a Thiếu L&acirc;m số người v&otilde; c&ocirc;ng cao hơn n&agrave;ng kh&ocirc;ng biết bao nhi&ecirc;u, nhưng thấy việc bất b&igrave;nh trước mắt, kh&ocirc;ng thể xu&ocirc;i tay bỏ qua; lại ngại cao thủ trong ch&ugrave;a chạy ra ngăn trở, n&ecirc;n một tay nắm Gi&aacute;c Viễn, một tay nắm Trương Qu&acirc;n Bảo, dậm ch&acirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chạy mau, chạy mau, c&oacute; chuyện g&igrave; xuống n&uacute;i đ&atilde; rồi h&atilde;y n&oacute;i cũng được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai người chỉ đứng y&ecirc;n kh&ocirc;ng trả lời. Bỗng thấy cửa ch&ugrave;a ở triền n&uacute;i b&ecirc;n dưới chạy ra bảy t&aacute;m tăng nh&acirc;n, tay cầm tề mi đại c&ocirc;n, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con nhỏ mất dạy n&agrave;o d&aacute;m đến ch&ugrave;a Thiếu L&acirc;m m&agrave; ph&aacute; ph&aacute;ch?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo đưa tay l&agrave;m loa k&ecirc;u lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c vị sư huynh kh&ocirc;ng được v&ocirc; lễ, đ&acirc;y l&agrave;...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hoảng hốt:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đừng n&oacute;i t&ecirc;n t&ocirc;i ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng nghĩ chuyện ng&agrave;y h&ocirc;m nay xem chừng kh&ocirc;ng nhỏ, thể n&agrave;o rồi cũng đồn ra đến tai cha mẹ, n&ecirc;n th&ecirc;m một c&acirc;u:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bọn m&igrave;nh n&ecirc;n theo đường n&uacute;i chạy ngay, tuyệt đối đừng đề cập đến t&ecirc;n cha mẹ, bạn b&egrave; t&ocirc;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ nghe thấy sườn n&uacute;i ph&iacute;a sau cũng c&oacute; tiếng người la &oacute;, lại th&ecirc;m bảy t&aacute;m nh&agrave; sư nữa chạy ra. Qu&aacute;ch Tương thấy trước sau đều c&oacute; sư bao v&acirc;y, nh&iacute;u m&agrave;y, tr&aacute;ch:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c ngươi cứ lừng ch&agrave; lừng chừng, kh&ocirc;ng c&oacute; ch&uacute;t kh&iacute; ph&aacute;ch g&igrave; của nam nhi, chạy hay kh&ocirc;ng n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ, c&ocirc; nương đ&acirc;y c&oacute; l&ograve;ng tốt...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngay l&uacute;c đ&oacute;, cửa ch&ugrave;a b&ecirc;n dưới lại ra th&ecirc;m bốn nh&agrave; sư &aacute;o v&agrave;ng, phấp phới chạy thẳng l&ecirc;n, tay kh&ocirc;ng cầm binh kh&iacute; nhưng th&acirc;n ph&aacute;p nhanh nhẹn, gi&oacute; thổi v&agrave;o &aacute;o phần phật, xem ra v&otilde; c&ocirc;ng kh&ocirc;ng phải tầm thường. Qu&aacute;ch Tương thấy t&igrave;nh thế như vậy, xem chừng chạy tho&aacute;t th&acirc;n một m&igrave;nh cũng kh&ocirc;ng xong, n&ecirc;n ngưng kh&iacute; đứng y&ecirc;n, chờ xem chuyện g&igrave; xảy ra. Nh&agrave; sư chạy đầu đến c&aacute;ch c&ocirc; ta chừng bốn trượng, lớn tiếng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thủ tọa La H&aacute;n đường sư t&ocirc;n truyền rằng, người đến ch&ugrave;a mau bỏ binh kh&iacute;, xuống lều tranh dưới n&uacute;i n&oacute;i r&otilde; sự t&igrave;nh, để nghe ph&aacute;p dụ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười nhạt:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gớm, c&aacute;c đại h&ograve;a thượng ch&ugrave;a Thiếu L&acirc;m quan c&aacute;ch qu&aacute;, nghe thật lọt tai. Xin hỏi c&aacute;c đại h&ograve;a thượng l&agrave; quan của ho&agrave;ng đế Đại Tống, hay l&agrave; quan của ho&agrave;ng đế M&ocirc;ng Cổ đấy?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khi đ&oacute; đất đai ph&iacute;a bắc s&ocirc;ng Ho&agrave;i kh&ocirc;ng c&ograve;n thuộc về nh&agrave; Đại Tống nữa, khu vực ch&ugrave;a Thiếu L&acirc;m cũng đ&atilde; thuộc về M&ocirc;ng Cổ từ l&acirc;u. Thế nhưng qu&acirc;n M&ocirc;ng nhiều năm qua c&ograve;n bận c&ocirc;ng th&agrave;nh Tương Dương chưa được, phải lo điều binh khiển tướng kh&ocirc;ng hơi sức đ&acirc;u m&agrave; lo chuyện ch&ugrave;a chiền, n&ecirc;n ch&ugrave;a Thiếu L&acirc;m mọi sự vẫn y như ng&agrave;y trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">G&atilde; tăng nh&acirc;n nghe Qu&aacute;ch Tương mỉa mai thật l&agrave; chua cay, n&ecirc;n kh&ocirc;ng khỏi đỏ mặt, cảm thấy đối với người ngo&agrave;i m&agrave; truyền dụ như vậy quả kh&ocirc;ng ổn, n&ecirc;n chắp tay n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng biết nữ th&iacute; chủ c&oacute; chuyện g&igrave; m&agrave; quang l&acirc;m tệ tự, xin để binh nhận, mời xuống vi đ&igrave;nh dưới ch&acirc;n n&uacute;i uống tr&agrave; n&oacute;i chuyện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy y đổi giọng h&ograve;a nh&atilde;, n&ecirc;n cũng muốn l&agrave;m h&ograve;a, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c &ocirc;ng kh&ocirc;ng cho t&ocirc;i v&agrave;o ch&ugrave;a th&igrave; th&ocirc;i. Hừ, hay l&agrave; trong ch&ugrave;a c&oacute; b&aacute;u vật, t&ocirc;i tr&ocirc;ng thấy sẽ được thơm l&acirc;y chăng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi liếc nh&igrave;n Trương Qu&acirc;n Bảo, n&oacute;i nhỏ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&oacute; chạy hay kh&ocirc;ng th&igrave; bảo?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo lắc đầu, nhếch m&eacute;p về ph&iacute;a Gi&aacute;c Viễn, &yacute; n&oacute;i muốn ở lại hầu hạ sư phụ. Qu&aacute;ch Tương lớn tiếng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế th&igrave; th&ocirc;i ta kh&ocirc;ng lo cho nữa, ta đi đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi rảo bước chạy xuống n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư &aacute;o v&agrave;ng đi đầu n&eacute; sang nhường lối. Người thứ hai v&agrave; người thứ ba giơ tay ngăn lại, c&ugrave;ng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&atilde;y khoan, để binh kh&iacute; lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nhướng m&agrave;y, tay để l&ecirc;n c&aacute;n kiếm. Nh&agrave; sư thứ nhất n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&uacute;ng t&ocirc;i kh&ocirc;ng d&aacute;m giữ binh nhận của nữ th&iacute; chủ đ&acirc;u. Khi xuống đến ch&acirc;n n&uacute;i, ch&uacute;ng t&ocirc;i lập tức đem bảo kiếm giao lại. Đ&acirc;y l&agrave; qui củ của ch&ugrave;a Thiếu L&acirc;m h&agrave;ng ngh&igrave;n năm nay, xin lượng thứ cho.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe y ăn n&oacute;i lễ độ, trong l&ograve;ng thầm t&iacute;nh: &quot;Nếu như m&igrave;nh kh&ocirc;ng để kiếm lại, thế tất phải đ&aacute;nh nhau, ta chỉ c&oacute; một m&igrave;nh, đ&acirc;u phải l&agrave; địch thủ của tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m. Thế nhưng nếu để kiếm lại, chẳng h&aacute; đem thể diện &ocirc;ng ngoại, cha mẹ, đại ca ca, Long tỉ tỉ bỏ đi sao?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng c&ograve;n đang tr&ugrave; trừ chưa quyết, chỉ thấy trước mắt một b&oacute;ng v&agrave;ng thấp tho&aacute;ng, một người th&eacute;t:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&atilde; đeo kiếm đến ch&ugrave;a Thiếu L&acirc;m, lại đả thương người, tr&ecirc;n đời n&agrave;y đ&acirc;u c&oacute; l&yacute; n&agrave;o như thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">K&igrave;nh phong x&ocirc; tới, năm ng&oacute;n tay đ&atilde; chộp lấy bao kiếm. Gi&aacute; như tăng nh&acirc;n đ&oacute; để y&ecirc;n kh&ocirc;ng ra tay, Qu&aacute;ch Tương thấy trước mắt t&igrave;nh thế bất lợi, ắt sẽ tạm nhịn c&aacute;i kh&iacute; nhất thời, sau sẽ c&ugrave;ng với &ocirc;ng ngoại, cha mẹ b&agrave;n luận rồi quay lại t&iacute;nh chuyện. Thế nhưng đối phương đột nhi&ecirc;n ỷ mạnh, chẳng lẽ để y khơi khơi đoạt kiếm sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cầm n&atilde; thủ ph&aacute;p của nh&agrave; sư đ&oacute; quả thực xảo diệu, vừa nắm được bao kiếm, nghĩ l&agrave; thể n&agrave;o Qu&aacute;ch Tương cũng giựt lại, một h&ograve;a thượng c&ugrave;ng một c&ocirc; g&aacute;i co co k&eacute;o k&eacute;o, tr&ocirc;ng rất bất nh&atilde;, n&ecirc;n y vận k&igrave;nh đẩy sang b&ecirc;n tr&aacute;i, thuận thế lại k&eacute;o về b&ecirc;n phải. Qu&aacute;ch Tương bị y vừa nắm vừa đẩy như thế quả nhi&ecirc;n kh&ocirc;ng cưỡng lại được phải lỏng tay ra, vội chụp lấy c&aacute;n kiếm, chỉ soẹt một tiếng, h&agrave;n quang lấp l&aacute;nh. Tăng nh&acirc;n nọ tay phải tuy cầm được bao kiếm nhưng hai ng&oacute;n tay tr&aacute;i đ&atilde; bị đoản kiếm thuận thế cắt đứt, đau qu&aacute; phải n&eacute;m bao kiếm, lui sang một b&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&uacute;ng tăng nh&acirc;n thấy đồng m&ocirc;n bị thương, vừa kinh vừa giận, m&uacute;a trượng giơ c&ocirc;n c&ugrave;ng x&ocirc;ng lại. Qu&aacute;ch Tương nghĩ thầm: &quot;Th&ocirc;i th&igrave; đến đ&acirc;u th&igrave; đến, h&ocirc;m nay chuyện xem kh&ocirc;ng xong rồi&quot;. Nghĩ rồi sử dụng t&agrave;i nghệ gia truyền &quot;Lạc Anh kiếm ph&aacute;p&quot;, vừa m&uacute;a vừa x&ocirc;ng xuống ch&acirc;n n&uacute;i. C&aacute;c nh&agrave; sư xếp th&agrave;nh ba v&ograve;ng, chia nhau đỡ gạt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lạc Anh kiếm ph&aacute;p vốn do Ho&agrave;ng Dược Sư theo Lạc Anh chưởng ph&aacute;p m&agrave; chế ra, tuy kh&ocirc;ng tinh diệu bằng Ngọc Ti&ecirc;u kiếm ph&aacute;p, nhưng cũng l&agrave; một tuyệt kỹ của đảo Đ&agrave;o Hoa. Chỉ thấy &aacute;nh xanh lấp lo&aacute;ng, kiếm hoa từng điểm, như phất phới đang rơi khắp bốn bề. Trong chốc l&aacute;t đ&atilde; c&oacute; hai tăng nh&acirc;n bị thương. Thế nhưng ph&iacute;a sau lại c&oacute; mấy nh&agrave; sư x&ocirc;ng l&ecirc;n, thế từ cao đ&aacute;nh xuống. Nếu theo l&yacute; th&igrave; Qu&aacute;ch Tương kh&ocirc;ng thể n&agrave;o đương cự nổi, nhưng tăng ch&uacute;ng Thiếu L&acirc;m lấy từ bi l&agrave;m gốc, kh&ocirc;ng muốn hại đến t&iacute;nh mệnh c&ocirc; ta, chi&ecirc;u số đ&aacute;nh ra kh&ocirc;ng d&ugrave;ng s&aacute;t thủ, chỉ cốt đ&aacute;nh ng&atilde;, dạy dỗ một phen, giữ binh kh&iacute; lại, đuổi xuống ch&acirc;n n&uacute;i. Thế nhưng Qu&aacute;ch Tương kiếm quang m&ugrave; mịt, kh&ocirc;ng dễ g&igrave; tới được gần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c đầu tăng ch&uacute;ng nghĩ rằng c&ocirc; g&aacute;i tuổi c&ograve;n trẻ, n&ecirc;n coi thường. Đến l&uacute;c thấy kiếm ph&aacute;p tinh kỳ, biết l&agrave; kh&ocirc;ng thuộc gi&ograve;ng gi&otilde;i danh m&ocirc;n cũng l&agrave; đồ đệ danh sư, kh&ocirc;ng n&ecirc;n đắc tội, phải ra chi&ecirc;u d&egrave; dặt, một mặt cấp b&aacute;o cho thủ tọa La H&aacute;n đường V&ocirc; Sắc thiền sư.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đang l&uacute;c đ&oacute;, một vị sư gi&agrave; th&acirc;n thể cao gầy chầm chậm đi tới, hai tay luồn trong tay &aacute;o, mỉm cười xem hai b&ecirc;n giao đấu. Hai tăng nh&acirc;n chạy đến trước mặt &ocirc;ng ta, n&oacute;i khẽ v&agrave;i c&acirc;u. Qu&aacute;ch Tương l&uacute;c n&agrave;y đ&atilde; thở hồng hộc, kiếm ph&aacute;p lăng loạn, k&ecirc;u lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vậy m&agrave; d&aacute;m gọi l&agrave; nguồn gốc v&otilde; c&ocirc;ng thi&ecirc;n hạ, h&oacute;a ra hơn một chục h&ograve;a thượng v&acirc;y đ&aacute;nh một người, lấy đ&ocirc;ng thắng &iacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vị l&atilde;o tăng đ&oacute; ch&iacute;nh l&agrave; thủ tọa La H&aacute;n đường V&ocirc; Sắc thiền sư, nghe vậy, liền n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mọi người dừng tay!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Những nh&agrave; sư nghe thế vội ngưng lại nhảy ra ngo&agrave;i. V&ocirc; Sắc thiền sư n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương t&ecirc;n họ l&agrave; g&igrave;, lệnh t&ocirc;n v&agrave; lệnh sư l&agrave; ai? Đến ch&ugrave;a Thiếu L&acirc;m chẳng hay c&oacute; chuyện g&igrave; thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghĩ thầm &quot;Ta kh&ocirc;ng n&ecirc;n n&oacute;i t&ecirc;n họ cha mẹ cho &ocirc;ng ta biết. Việc ta đến ch&ugrave;a Thiếu L&acirc;m để thăm hỏi tin tức của đại ca cũng kh&ocirc;ng thể n&oacute;i ra cho mọi người nghe được. M&igrave;nh đ&atilde; g&acirc;y rắc rối thế n&agrave;y, cha mẹ v&agrave; đại ca biết được thể n&agrave;o cũng rầy r&agrave;, chi bằng m&igrave;nh cứ lặng thinh l&agrave; hơn. &quot; V&igrave; thế n&agrave;ng đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ecirc;n họ t&ocirc;i kh&ocirc;ng thể cho &ocirc;ng biết được, chẳng qua t&ocirc;i thấy tr&ecirc;n n&uacute;i phong cảnh đẹp đẽ, n&ecirc;n l&ecirc;n ngắm cảnh đấy th&ocirc;i. N&agrave;o ngờ ch&ugrave;a Thiếu L&acirc;m so với nội viện ho&agrave;ng cung c&ograve;n kh&oacute; khăn hơn, chẳng l&agrave;m g&igrave; cũng đ&ograve;i giữ binh kh&iacute; người ta lại. Xin hỏi đại sư, t&ocirc;i đ&atilde; bước ch&acirc;n v&agrave;o cổng ch&ugrave;a chưa? Ng&agrave;y trước Đạt Ma tổ sư truyền v&otilde; nghệ, chẳng qua cũng chỉ để cho tăng ch&uacute;ng th&acirc;n thể khỏe mạnh, khiến việc tu h&agrave;nh cho tinh tiến, n&agrave;o hay danh tiếng c&agrave;ng to, v&otilde; c&ocirc;ng c&agrave;ng cao, th&igrave; c&aacute;i việc cậy đ&ocirc;ng cậy mạnh lại c&agrave;ng lớn. Hay lắm, nếu c&aacute;c &ocirc;ng muốn giữ binh kh&iacute; của t&ocirc;i, th&igrave; t&ocirc;i để lại, trừ phi giết t&ocirc;i đi, chứ việc h&ocirc;m nay đừng tưởng tr&ecirc;n giang hồ kh&ocirc;ng ai biết đến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; ta bản chất nhanh mồm kh&eacute;o miệng, việc n&agrave;y lại kh&ocirc;ng phải ho&agrave;n to&agrave;n lỗi ở n&agrave;ng, chỉ một c&acirc;u khiến V&ocirc; Sắc thiền sư cứng họng kh&ocirc;ng trả lời được. Qu&aacute;ch Tương nh&igrave;n mặt đặt t&ecirc;n, nghĩ thầm: &quot;Việc quấy ph&aacute; n&agrave;y ch&iacute;nh ta mới l&agrave; người sợ lộ ra ngo&agrave;i, nhưng xem ra Thiếu L&acirc;m tự c&ograve;n ngại hơn. Hơn một chục h&ograve;a thượng v&acirc;y đ&aacute;nh một c&ocirc; g&aacute;i trẻ tuổi, nghe c&oacute; g&igrave; l&agrave; hay ho?&quot;. Nghĩ thế b&egrave;n hứ một tiếng, n&eacute;m đoản kiếm xuống đất, quay m&igrave;nh bước đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư lạng người tiến l&ecirc;n, phất tay &aacute;o một c&aacute;i đ&atilde; cuộn được thanh kiếm, hai tay cầm lấy lưỡi kiếm, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương nếu kh&ocirc;ng nể t&igrave;nh thầy tr&ograve; ch&uacute;ng t&ocirc;i, thanh bảo kiếm n&agrave;y xin cầm lại. L&atilde;o nạp cung k&iacute;nh tiễn c&ocirc; xuống n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe vậy cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Xem ra l&atilde;o h&ograve;a thượng th&ocirc;ng đạt t&igrave;nh l&yacute;, thế mới gọi l&agrave; phong phạm của bậc danh gia.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; ta thấy m&igrave;nh được thế n&ecirc;n thuận mồm khen V&ocirc; Sắc một c&acirc;u, rồi th&ograve; tay nhắc kiếm, bỗng thấy giật m&igrave;nh. Nguy&ecirc;n lai l&ograve;ng b&agrave;n tay đối phương sinh ra một hấp lực, tuy c&ocirc; ta đ&atilde; cầm được c&aacute;n kiếm nhưng kh&ocirc;ng sao n&acirc;ng l&ecirc;n được. N&agrave;ng ra sức vận k&igrave;nh ba lần nhưng vẫn kh&ocirc;ng sao lấy được đoản kiếm, b&egrave;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hay nhỉ, &ocirc;ng định khoe c&ocirc;ng phu chăng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đột nhi&ecirc;n tay tr&aacute;i vung ra, nhẹ nh&agrave;ng hướng v&agrave;o hai huyệt Thi&ecirc;n Đỉnh, Cự Cốt ở b&ecirc;n tr&aacute;i cổ &ocirc;ng ta phất tới. V&ocirc; Sắc kinh h&atilde;i, n&eacute; người tr&aacute;nh, kh&iacute; k&igrave;nh hơi lỏng ra n&ecirc;n Qu&aacute;ch Tương thuận thế m&agrave; lấy được đoản kiếm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;ng phu Lan Hoa Phất Huyệt Thủ hay thực. Chẳng hay c&ocirc; xưng h&ocirc; Đ&agrave;o Hoa đảo chủ như thế n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&agrave;o Hoa đảo chủ ư? T&ocirc;i gọi &ocirc;ng ta l&agrave; L&atilde;o Đ&ocirc;ng T&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đ&agrave;o Hoa đảo chủ Đ&ocirc;ng T&agrave; Ho&agrave;ng Dược Sư ch&iacute;nh l&agrave; &ocirc;ng ngoại của Qu&aacute;ch Tương, t&iacute;nh t&igrave;nh kh&aacute;c người, trước nay vốn kh&ocirc;ng c&acirc;u nệ lễ ph&eacute;p, thường đ&ugrave;a c&ocirc; ch&aacute;u ngoại gọi l&agrave; Tiểu Đ&ocirc;ng T&agrave;, Qu&aacute;ch Tương liền gọi lại &ocirc;ng ngoại l&agrave; L&atilde;o Đ&ocirc;ng T&agrave;, Ho&agrave;ng Dược Sư kh&ocirc;ng những kh&ocirc;ng la rầy, c&ograve;n tỏ ra hoan hỉ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc l&uacute;c thiếu thời vốn l&agrave; lục l&acirc;m, tuy đ&atilde; nương th&acirc;n cửa thiền mấy chục năm tu tr&igrave;, Phật học uy&ecirc;n th&acirc;m, nhưng h&agrave;o kh&iacute; xưa vẫn chưa dứt, nếu kh&ocirc;ng l&agrave;m sao kết bạn được với Dương Qu&aacute;? Thấy c&ocirc; g&aacute;i n&agrave;y kh&ocirc;ng chịu khai sư thừa lai lịch, định thử một chuyến, n&ecirc;n lớn tiếng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tiểu c&ocirc; nương tiếp ta mười chi&ecirc;u, để xem nh&atilde;n lực l&atilde;o h&ograve;a thượng thế n&agrave;o, c&oacute; n&oacute;i ra được m&ocirc;n ph&aacute;i của c&ocirc; chăng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu như mười chi&ecirc;u m&agrave; kh&ocirc;ng nh&igrave;n ra, th&igrave; &ocirc;ng t&iacute;nh sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư lớn tiếng cả cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu như c&ocirc; nương tiếp được l&atilde;o nạp mười chi&ecirc;u, th&igrave; c&ocirc; muốn chuyện g&igrave; ta cũng nghe theo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương chỉ Gi&aacute;c Viễn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i c&ugrave;ng vị đại sư n&agrave;y trước đ&acirc;y c&oacute; duy&ecirc;n một lần gặp gỡ, n&ecirc;n muốn thay &ocirc;ng ta cầu ch&uacute;t t&igrave;nh. Nếu như mười chi&ecirc;u m&agrave; &ocirc;ng kh&ocirc;ng n&oacute;i được sư phụ t&ocirc;i l&agrave; ai, chỉ xin &ocirc;ng đ&aacute;p lời, đừng l&agrave;m kh&oacute; dễ vị đại sư n&agrave;y nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc hết sức ngạc nhi&ecirc;n, nghĩ thầm Gi&aacute;c Viễn t&iacute;nh t&igrave;nh ngơ ngơ ngẩn ngẩn, mấy chục năm qua tại T&agrave;ng Kinh C&aacute;c giữ s&aacute;ch, trước nay chưa từng c&ugrave;ng người ngo&agrave;i giao thiệp, tại sao lại quen được với c&ocirc; g&aacute;i n&agrave;y? B&egrave;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&uacute;ng t&ocirc;i vốn đ&acirc;u c&oacute; l&agrave;m kh&oacute; dễ g&igrave; y. Tăng ch&uacute;ng trong ch&ugrave;a phạm giới luật, d&ugrave; ai cũng vậy, cũng phải chịu phạt chứ đ&acirc;u phải l&agrave; l&agrave;m kh&oacute; dễ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương bĩu m&ocirc;i, cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hừ, n&oacute;i qua n&oacute;i lại, &ocirc;ng cũng đều n&eacute; tr&aacute;nh cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc vỗ tay một c&aacute;i, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Được rồi, theo lời c&ocirc;. Nếu như l&atilde;o nạp thua, sẽ thay sư đệ Gi&aacute;c Viễn g&aacute;nh ba ngh&igrave;n một trăm linh t&aacute;m g&aacute;nh nước. C&ocirc; nương lưu &yacute;, ta xuất chi&ecirc;u đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khi đang n&oacute;i chuyện, Qu&aacute;ch Tương đ&atilde; thầm t&iacute;nh trong l&ograve;ng: &quot;L&atilde;o h&ograve;a thượng n&agrave;y kh&iacute; ngưng thần tĩnh, v&otilde; c&ocirc;ng cao cường, nếu như để &ocirc;ng ta xuất chi&ecirc;u, ta ắt phải hết sức chống đỡ, kh&ocirc;ng thể n&agrave;o kh&ocirc;ng lộ v&otilde; c&ocirc;ng của cha mẹ m&igrave;nh ra. Chi bằng ta chiếm ti&ecirc;n cơ, đ&aacute;nh ra mười chi&ecirc;u liền&quot;. Nghe &ocirc;ng ta vừa n&oacute;i hai c&acirc;u &quot;C&ocirc; nương lưu &yacute;, ta xuất chi&ecirc;u đ&acirc;y&quot; n&ecirc;n kh&ocirc;ng đợi &ocirc;ng ta ra quyền cước, nghe v&egrave;o một tiếng đoản kiếm đ&atilde; nhắm thẳng v&agrave;o ngực đ&acirc;m tới, sử dụng một chi&ecirc;u trong Lạc Anh kiếm ph&aacute;p của đảo Đ&agrave;o Hoa, t&ecirc;n l&agrave; Vạn Tử Thi&ecirc;n Hồng, mũi kiếm đ&acirc;m ra li&ecirc;n tiếp rung động, để cho đối thủ kh&ocirc;ng biết c&ocirc;ng v&agrave;o chỗ n&agrave;o. V&ocirc; Sắc biết l&agrave; lợi hại, kh&ocirc;ng d&aacute;m trả miếng, n&ecirc;n chỉ nghi&ecirc;ng m&igrave;nh tr&aacute;nh ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lại k&ecirc;u: &quot;Chi&ecirc;u thứ hai n&agrave;y&quot;, đoản kiếm quay lại, từ dưới đ&acirc;m l&ecirc;n tr&ecirc;n, ch&iacute;nh l&agrave; một chi&ecirc;u trong kiếm ph&aacute;p ph&aacute;i To&agrave;n Ch&acirc;n t&ecirc;n l&agrave; Thi&ecirc;n Thần Đảo Huyền. V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hay lắm, đ&oacute; l&agrave; To&agrave;n Ch&acirc;n kiếm ph&aacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chưa hẳn thế đ&acirc;u!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đoản kiếm vừa đ&acirc;m v&agrave;o khoảng kh&ocirc;ng, thấy V&ocirc; Sắc phản thủ vi c&ocirc;ng, đưa tay ra toan nắm lấy cổ tay m&igrave;nh, trong l&ograve;ng hơi sợ: &quot;L&atilde;o h&ograve;a thượng quả nhi&ecirc;n giỏi thực, trong khi kiếm chi&ecirc;u hung hiểm như thế, vậy m&agrave; d&aacute;m đưa tay kh&ocirc;ng ra c&ocirc;ng k&iacute;ch&quot;. Vừa thấy b&agrave;n tay &ocirc;ng ta đến trước mặt, đoản kiếm vội v&agrave;ng vung l&ecirc;n mấy v&ograve;ng, sử dụng chi&ecirc;u &Aacute;c Cẩu Lan Lo[2]&auml; thuộc chữ Phong trong Đả Cẩu Bổng Ph&aacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; ta từ nhỏ vẫn thường giao hảo với bang chủ tiền nhiệm C&aacute;i Bang l&agrave; Lỗ Hữu Cước, c&ugrave;ng nhau uống rượu đ&aacute;nh đố, c&oacute; khi c&ograve;n c&ugrave;ng y tỉ th&iacute; v&otilde; nghệ. Tuy C&aacute;i Bang c&oacute; qui củ, Đả Cẩu Bổng Ph&aacute;p l&agrave; thần kỹ trấn bang, kh&ocirc;ng phải bang chủ kh&ocirc;ng được truyền thụ, nhưng khi Lỗ Hữu Cước sử dụng, Qu&aacute;ch Tương cũng học l&oacute;m được một chi&ecirc;u nửa thức. Huống g&igrave; mẹ n&agrave;ng Ho&agrave;ng Dung l&agrave; bang chủ tiền nhiệm, bang chủ hiện tại Gia Luật Tề l&agrave; anh rể n&agrave;ng, m&ocirc;n Đả Cẩu Bổng Ph&aacute;p n&agrave;y n&agrave;ng xem kh&ocirc;ng biết bao nhi&ecirc;u lần. Tuy kh&ocirc;ng r&otilde; quyết khiếu b&ecirc;n trong nhưng cứ nh&aacute;i theo c&aacute;ch thức đ&aacute;nh ra, cũng l&agrave;m cho người ta kinh h&atilde;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ng&oacute;n tay V&ocirc; Sắc vừa sắp chạm đến cổ tay n&agrave;ng, bỗng thấy bạch quang lấp lo&aacute;ng, thế kiếm đ&acirc;m tới hết sức thần diệu, năm ng&oacute;n tay su&yacute;t bị chặt đứt, nhưng v&igrave; &ocirc;ng ta v&otilde; c&ocirc;ng tr&aacute;c tuyệt, biến chi&ecirc;u thật nhanh, trong l&uacute;c nguy cấp nhảy l&ugrave;i về sau hai bước, nhưng nghe soẹt soẹt, ống tay &aacute;o b&ecirc;n tr&aacute;i đ&atilde; bị đoản kiếm cắt r&aacute;ch một đường d&agrave;i. V&ocirc; Sắc thiền sư nh&igrave;n chỗ &aacute;o r&aacute;ch m&agrave; mặt m&agrave;y biến sắc, lưng to&aacute;t mồ h&ocirc;i lạnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương rất lấy l&agrave;m đắc &yacute;, cười hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i đ&oacute; l&agrave; kiếm ph&aacute;p g&igrave; thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thực ra trong thi&ecirc;n hạ kh&ocirc;ng c&oacute; loại kiếm ph&aacute;p đ&oacute;, vốn n&agrave;ng chỉ học l&eacute;n được một thế Đả Cẩu Bổng Ph&aacute;p, d&ugrave;ng lẫn v&agrave;o trong, nhưng Đả Cẩu Bổng Ph&aacute;p qu&aacute; ư &aacute;o diệu, tuy n&agrave;ng d&ugrave;ng chẳng đi đến đ&acirc;u, nhưng cũng l&agrave;m cho một vị cao tăng Thiếu L&acirc;m danh tiếng lẫy lừng hết sức nghi hoặc, kh&ocirc;ng biết phải đối đ&aacute;p thế n&agrave;o cho phải.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghĩ thầm: &quot;Ta chỉ cần sử dụng v&agrave;i chi&ecirc;u Đả Cẩu Bổng Ph&aacute;p l&agrave; thể n&agrave;o cũng đ&aacute;nh bại được l&atilde;o h&ograve;a thượng n&agrave;y, tiếc rằng ngo&agrave;i thế n&agrave;y ra, ta kh&ocirc;ng biết th&ecirc;m thức n&agrave;o nữa&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kh&ocirc;ng đợi cho V&ocirc; Sắc nghỉ mệt, đoản kiếm lại vung l&ecirc;n nhẹ nh&agrave;ng đ&acirc;m tới, tư th&aacute;i phi&ecirc;u phi&ecirc;u như ti&ecirc;n, mũi kiếm nhắm hạ b&agrave;n đối phương điểm li&ecirc;n tiếp mấy nh&aacute;t, ch&iacute;nh l&agrave; một thế học trong Ngọc Nữ kiếm ph&aacute;p của Tiểu Long Nữ t&ecirc;n l&agrave; Tiểu Vi&ecirc;n Nghệ C&uacute;c (trồng c&uacute;c trong vườn nhỏ).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngọc Nữ kiếm ph&aacute;p vốn do nữ hiệp L&acirc;m Tri&ecirc;u Anh s&aacute;ng t&aacute;c, kh&ocirc;ng những kiếm chi&ecirc;u lăng lệ m&agrave; tư thế lại tho&aacute;t tục, phong nh&atilde;, ch&uacute;ng tăng chưa nh&igrave;n thấy bao giờ, khiến vừa kinh h&atilde;i lại vừa vui mừng. Đạt Ma kiếm ph&aacute;p, La H&aacute;n kiếm ph&aacute;p của ch&ugrave;a Thiếu L&acirc;m đều theo đường cương m&atilde;nh, c&ograve;n Ngọc Nữ kiếm ph&aacute;p đ&atilde; rất &iacute;t xuất hiện tr&ecirc;n giang hồ, bản chất so với kiếm ph&aacute;p của Thiếu L&acirc;m ho&agrave;n to&agrave;n tương phản. Thực ra nếu cứ kiếm ph&aacute;p m&agrave; luận, vị tất đ&atilde; thắng được c&aacute;c lộ kiếm thuật Thiếu L&acirc;m, chỉ v&igrave; thoạt nh&igrave;n, tr&ocirc;ng hết sức đẹp mắt, chẳng kh&aacute;c g&igrave; trong kinh Phật viết l&agrave;: dung nghi uyển mị, trang nghi&ecirc;m h&ograve;a nh&atilde;, đoan ch&iacute;nh khả hỉ, quan giả v&ocirc; yếm (h&igrave;nh dung ẻo lả, trang nghi&ecirc;m h&ograve;a nh&atilde;, đoan ch&iacute;nh c&agrave;ng nh&igrave;n c&agrave;ng vui, xem kh&ocirc;ng biết ch&aacute;n).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư thấy kiếm thuật vừa tinh diệu, vừa mỹ lệ cũng muốn xem th&ecirc;m thức kh&aacute;c, n&ecirc;n n&eacute; m&igrave;nh tr&aacute;nh ra, chờ n&agrave;ng đ&aacute;nh th&ecirc;m. Bỗng Qu&aacute;ch Tương đổi kiếm chi&ecirc;u, chạy qua đ&ocirc;ng lại nhảy sang t&acirc;y, li&ecirc;n tiếp đ&acirc;m ra mấy nh&aacute;t. Trương Qu&acirc;n Bảo đứng b&ecirc;n nh&igrave;n m&agrave; xuất thần, bỗng k&ecirc;u &quot;Ồ&quot; một tiếng. Nguy&ecirc;n lai chi&ecirc;u Qu&aacute;ch Tương vừa sử dụng l&agrave; chi&ecirc;u Tứ Th&ocirc;ng B&aacute;t Đạt, ba năm trước tr&ecirc;n đỉnh n&uacute;i Hoa Sơn, Dương Qu&aacute; đ&atilde; dạy cho Trương Qu&acirc;n Bảo, Qu&aacute;ch Tương đứng ở b&ecirc;n cạnh xem, nay đem ra sử dụng. Thế nhưng l&uacute;c đ&oacute; Dương Qu&aacute; dạy Trương Qu&acirc;n Bảo l&agrave; chưởng ph&aacute;p, b&acirc;y giờ Qu&aacute;ch Tương lại biến th&agrave;nh kiếm ph&aacute;p, tuy uy lực k&eacute;m đi mấy phần, nhưng cũng rất tinh kỳ, khiến V&ocirc; Sắc trong l&ograve;ng phải thầm kinh h&atilde;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">T&iacute;nh ra, Qu&aacute;ch Tương đ&atilde; sử dụng năm chi&ecirc;u nhưng V&ocirc; Sắc chưa nh&igrave;n ra ch&uacute;t đầu mối n&agrave;o. Khi c&ograve;n trẻ, &ocirc;ng ta tung ho&agrave;nh giang hồ, lịch duyệt nhiều, mười mấy năm qua giữ chức thủ tọa La H&aacute;n đường, lại c&agrave;ng gia t&acirc;m nghi&ecirc;n cứu c&aacute;c nh&agrave;, c&aacute;c ph&aacute;i, so s&aacute;nh v&otilde; c&ocirc;ng của ch&ugrave;a với b&ecirc;n ngo&agrave;i, lấy trường bổ đoản, sửa lại những chỗ c&ograve;n khiếm khuyết. Th&agrave;nh ra &ocirc;ng ta rất tự tin, nghĩ rằng d&ugrave; cao nh&acirc;n phương n&agrave;o, chỉ cần v&agrave;i chi&ecirc;u l&agrave; c&oacute; thể nh&igrave;n ra lai lịch. &Ocirc;ng ta ước định với Qu&aacute;ch Tương mười chi&ecirc;u, l&agrave; đ&atilde; t&iacute;nh gi&agrave; lắm rồi. N&agrave;o ngờ cha mẹ, thầy bạn của Qu&aacute;ch Tương đều l&agrave; đệ nhất lưu cao thủ đương thời, chỉ cần sử dụng v&otilde; c&ocirc;ng mỗi người một chi&ecirc;u, chỗ n&agrave;y một ch&uacute;t, chỗ kia một ch&uacute;t đ&atilde; khiến cho V&ocirc; Sắc hoa cả mắt, rối cả tr&iacute;, c&ograve;n n&oacute;i g&igrave; đến chuyện n&oacute;i ra m&ocirc;n ph&aacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khi t&aacute;m thức, bốn đường kiếm của chi&ecirc;u Tứ Th&ocirc;ng B&aacute;t Đạt vừa chấm dứt, V&ocirc; Sắc nghĩ thầm: &quot;Nếu như ta cứ để cho c&ocirc; ta xuất chi&ecirc;u, chỉ e rằng qu&aacute;i chi&ecirc;u sẽ li&ecirc;n mi&ecirc;n bất tuyệt, n&oacute;i chi mười chi&ecirc;u, đến một trăm chi&ecirc;u chắc g&igrave; đ&atilde; t&igrave;m ra manh mối. Chỉ c&oacute; c&aacute;ch l&agrave; ta tấn c&ocirc;ng m&atilde;nh liệt, c&ocirc; ta kh&ocirc;ng thể kh&ocirc;ng giở v&otilde; c&ocirc;ng bản m&ocirc;n ra chống đỡ&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lập tức chuyển th&acirc;n qua b&ecirc;n tr&aacute;i, tung ra một chi&ecirc;u Song Qu&aacute;n Thủ, hổ khẩu hai nắm tay chầu v&agrave;o nhau, vẽ th&agrave;nh một h&igrave;nh v&ograve;ng, đ&aacute;nh thẳng v&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy k&igrave;nh lực của &ocirc;ng ta rất mạnh, kh&ocirc;ng d&aacute;m chống đỡ, uốn m&igrave;nh một c&aacute;i đ&atilde; theo khoảng trống giữa hai c&aacute;nh tay đối phương m&agrave; lọt ra ngo&agrave;i. Trước đ&acirc;y c&ocirc; đ&atilde; từng thấy Anh C&ocirc; đấu với Dương Qu&aacute; tại Hắc Long Đ&agrave;m, thế yếu kh&ocirc;ng chống nổi mạnh n&ecirc;n đ&atilde; sử dụng N&ecirc; Thu C&ocirc;ng (c&ocirc;ng phu con trạch ở trong b&ugrave;n) để tho&aacute;t th&acirc;n, nay cũng theo như thế. Tuy th&acirc;n ph&aacute;p v&agrave; c&ocirc;ng phu của c&ocirc; kh&ocirc;ng bằng Anh C&ocirc;, nhưng V&ocirc; Sắc thiền sư vốn kh&ocirc;ng định t&acirc;m d&ugrave;ng s&aacute;t thủ, n&ecirc;n để cho Qu&aacute;ch Tương nhẹ nh&agrave;ng tho&aacute;t ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc cất tiếng khen:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Th&acirc;n ph&aacute;p hay lắm, tiếp thử một chi&ecirc;u nữa xem n&agrave;o!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tả chưởng x&ograve;e ra, c&ugrave;i chỏ thu v&agrave;o ngực, hổ khẩu hướng l&ecirc;n trời, ch&iacute;nh l&agrave; thế Ho&agrave;ng Oanh Lạc Gi&aacute; trong Thiếu L&acirc;m quyền. &Ocirc;ng l&agrave; v&otilde; học đại sư của ch&ugrave;a Thiếu L&acirc;m, th&acirc;n phận hai b&ecirc;n bất đồng, tuy sở học b&aacute;c tạp hơn Qu&aacute;ch Tương nhiều, nhưng mỗi chi&ecirc;u, mỗi thức sử dụng đều thuần ch&iacute;nh v&otilde; c&ocirc;ng bản m&ocirc;n. Thiếu L&acirc;m quyền rất rộng lớn, thoạt xem b&igrave;nh thường kh&ocirc;ng c&oacute; g&igrave; lạ nhưng luyện đến chỗ tinh th&acirc;m, uy lực thật kh&ocirc;ng biết đến chừng n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&agrave;n tay tr&aacute;i của &ocirc;ng vừa x&ograve;e ra, Qu&aacute;ch Tương đ&atilde; thấy thượng b&aacute;n th&acirc;n bị lọt v&agrave;o trong chưởng lực, vội v&agrave;ng xoay kiếm lại, d&ugrave;ng c&aacute;n kiếm thay ng&oacute;n tay, sử dụng một chi&ecirc;u Nhất Dương Chỉ học lỏm được của V&otilde; Tu Văn, d&ugrave;ng k&igrave;nh điểm ba huyệt Uyển Cốt, Dương Cốc, Dưỡng L&atilde;o tr&ecirc;n cổ tay V&ocirc; Sắc. Thực ra c&ocirc;ng phu điểm huyệt Nhất Dương Chỉ n&agrave;ng chỉ học được ch&uacute;t vỏ b&ecirc;n ngo&agrave;i, rất l&agrave; th&ocirc; thiển. Thế nhưng một chỉ điểm ba huyệt ch&iacute;nh l&agrave; tinh yếu của c&ocirc;ng phu n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trước đ&acirc;y Nhất Dương Chỉ của Nhất Đăng đại sư vang danh thi&ecirc;n hạ, V&ocirc; Sắc thiền sư dĩ nhi&ecirc;n phải biết. Khi Qu&aacute;ch Tương vừa xuất chi&ecirc;u, &ocirc;ng kinh h&atilde;i, vội v&agrave;ng rụt tay về. Kỳ thực, nếu như V&ocirc; Sắc cứ để y&ecirc;n cho Qu&aacute;ch Tương điểm ba huyệt đạo, sẽ thấy ngay l&agrave; Nhất Dương Chỉ của n&agrave;ng l&agrave; giả, nhưng trong l&uacute;c hai b&ecirc;n to&agrave;n lực tương đấu, &ocirc;ng đ&acirc;u d&aacute;m đem danh tiếng cả đời m&igrave;nh ra thử.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đắc &yacute; cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng cũng biết l&agrave; lợi hại nhỉ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc chỉ hừ một tiếng, đ&aacute;nh ra chi&ecirc;u Đơn Phụng Triều Dương. Chi&ecirc;u n&agrave;y hai tay mở rộng, giơ từ cao đ&aacute;nh xuống, k&igrave;nh lực &agrave;o &agrave;o tr&agrave;n tới. Thanh đoản kiếm trong tay Qu&aacute;ch Tương cầm kh&ocirc;ng nổi tuột khỏi tay rơi xuống đất. N&agrave;ng biết l&agrave; đối phương kh&ocirc;ng nỡ ra tay hạ s&aacute;t thủ, n&ecirc;n kh&ocirc;ng kinh hoảng, song chưởng đan ch&eacute;o v&agrave;o nhau, dường như c&oacute; dường như kh&ocirc;ng, ch&iacute;nh l&agrave; lộ thứ năm mươi bốn c&oacute; t&ecirc;n Diệu Thủ Kh&ocirc;ng Kh&ocirc;ng, trong bảy mươi hai đường Kh&ocirc;ng Minh Quyền, kiệt t&aacute;c đắc &yacute; của L&atilde;o Ngoan Đồng Chu B&aacute; Th&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đường quyền n&agrave;y do Chu B&aacute; Th&ocirc;ng tự s&aacute;ng t&aacute;c, chưa lưu truyền trong giang hồ, n&ecirc;n tuy V&ocirc; Sắc kiến thức uy&ecirc;n b&aacute;c nhưng cũng kh&ocirc;ng biết đến. &Ocirc;ng liền vẽ song chưởng th&agrave;nh h&igrave;nh v&ograve;ng cung, đ&aacute;nh ra chi&ecirc;u Thi&ecirc;n Hoa Thất Tinh, nhanh như điện đ&atilde; tr&agrave;n tới bao phủ chưởng lực của Qu&aacute;ch Tương. Nếu Qu&aacute;ch Tương kh&ocirc;ng d&ugrave;ng nội lực chống đỡ, tay sẽ bị bẻ quặt về sau, g&atilde;y th&agrave;nh hai đoạn. Thi&ecirc;n Hoa Thất Tinh l&agrave; một chi&ecirc;u cơ bản trong c&ocirc;ng phu Thiếu L&acirc;m, tr&ocirc;ng tưởng chậm nhưng thật l&agrave; nhanh, tưởng nhẹ nhưng thật l&agrave; nặng. Chi&ecirc;u n&agrave;y tuy l&agrave; tư thức trong Sấm Thiếu L&acirc;m nhưng &yacute; k&igrave;nh nội lực lại từ tinh yếu trong Thần Hoa Thiếu L&acirc;m m&agrave; ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thủ chưởng bị khống chế, nghĩ thầm: &quot;Kh&ocirc;ng lẽ &ocirc;ng d&aacute;m đ&aacute;nh g&atilde;y xương tay ta ư?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thuận thế giơ tay l&ecirc;n, d&ugrave;ng chi&ecirc;u Thiết Bồ Phiến Thủ, lấy chưởng đối chưởng phản k&iacute;ch lại. Chi&ecirc;u n&agrave;y n&agrave;ng học của vợ V&otilde; Tu Văn l&agrave; Ho&agrave;n Nhan B&igrave;nh, vốn do Thiết Chưởng Thủy Thượng Phi&ecirc;u Cừu Thi&ecirc;n Nhận truyền lại. Thiết Chưởng C&ocirc;ng l&agrave; v&otilde; c&ocirc;ng cương m&atilde;nh hạng nhất trong mọi m&ocirc;n ph&aacute;i, V&ocirc; Sắc thiền sư chuy&ecirc;n nghi&ecirc;n cứu chưởng ph&aacute;p, lẽ n&agrave;o kh&ocirc;ng biết. Vừa thấy c&ocirc; g&aacute;i sử dụng chưởng ph&aacute;p của Thiết Chưởng Bang, &ocirc;ng giật nảy m&igrave;nh, vội v&agrave;ng thu chưởng lại, một l&agrave; kh&ocirc;ng muốn c&ocirc; ta bị thương, hai l&agrave; đối với Thiết Chưởng C&ocirc;ng cũng c&oacute; ba phần e ngại. &Ocirc;ng ta l&agrave; người trung hậu, thấy Qu&aacute;ch Tương mỗi chi&ecirc;u mỗi thức đều đ&acirc;u ra đấy, kh&ocirc;ng nghĩ rằng một c&ocirc; g&aacute;i mới hai mươi tuổi đầu kh&ocirc;ng thể n&agrave;o học được nhiều như thế, n&ecirc;n nhảy l&ugrave;i lại nửa trượng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thản nhi&ecirc;n mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ra chi&ecirc;u thứ mười đ&acirc;y, &ocirc;ng xem t&ocirc;i ở m&ocirc;n ph&aacute;i n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tay tr&aacute;i giơ l&ecirc;n, th&acirc;n h&igrave;nh hơi chồm tới, tay phải luồn v&agrave;o toan chộp lầy cằm của V&ocirc; Sắc. V&ocirc; Sắc c&ugrave;ng ch&uacute;ng tăng kh&ocirc;ng nhịn được phải ngạc nhi&ecirc;n k&ecirc;u ồ một tiếng. Chi&ecirc;u đ&oacute; c&oacute; t&ecirc;n l&agrave; Khổ Hải Hồi Đầu, ch&iacute;nh l&agrave; một chi&ecirc;u trong La H&aacute;n Quyền của ph&aacute;i Thiếu L&acirc;m ch&iacute;nh t&ocirc;ng, m&ocirc;n ph&aacute;i kh&aacute;c kh&ocirc;ng c&oacute; được. Dụng &yacute; của chi&ecirc;u n&agrave;y l&agrave; tay tr&aacute;i ấn đầu đối phương, tay phải đỡ lấy cằm, vặn một c&aacute;i, nặng ắt g&atilde;y cổ, nhẹ cũng trật khớp, l&agrave; một chi&ecirc;u s&aacute;t thủ rất lợi hại. V&ocirc; Sắc thiền sư thấy c&ocirc; ta giở một chi&ecirc;u trong La H&aacute;n Quyền, thật chẳng kh&aacute;c n&agrave;o m&uacute;a b&uacute;a trước cửa Lỗ Ban, đem Hiếu Kinh đọc cho Khổng Tử nghe, nghĩ vừa bực m&igrave;nh, vừa tức cười. Pho quyền ph&aacute;p n&agrave;y &ocirc;ng đ&atilde; th&agrave;nh thục từ mấy chục năm trước, luyện đ&atilde; đến mức kh&ocirc;ng c&ograve;n phải suy nghĩ, ra chi&ecirc;u l&agrave; ra, d&ugrave; c&oacute; đang nằm ngủ cũng c&oacute; thể giải được chi&ecirc;u n&agrave;y. &Ocirc;ng nghi&ecirc;ng người bước qua một b&ecirc;n, tay tr&aacute;i đặt ngang mặt Qu&aacute;ch Tương, vươn một c&aacute;i đ&atilde; nắm được vai phải, tay tr&aacute;i nhanh như chớp luồn ra sau cổ. Chi&ecirc;u n&agrave;y t&ecirc;n l&agrave; Hiệp Sơn Si&ecirc;u Hải, l&agrave; chi&ecirc;u để giải thế Khổ Hải Hồi Đầu c&oacute; một kh&ocirc;ng hai, hai tay vừa ra l&agrave; đ&atilde; n&acirc;ng ngang địch nh&acirc;n l&ecirc;n khỏi mặt đất. Đ&uacute;ng ra th&igrave; Qu&aacute;ch Tương c&oacute; thể theo đ&oacute; m&agrave; sử dụng thế B&agrave;n Trửu, nắm ngược lại c&ugrave;i chỏ đối phương để tho&aacute;t th&acirc;n, lại c&ograve;n c&oacute; thể chế ngự địch được nữa, nhưng V&ocirc; Sắc thiền sư phản c&ocirc;ng qu&aacute; nhanh, chỉ trong chớp mắt, đ&atilde; nhấc bổng n&agrave;ng l&ecirc;n. Qu&aacute;ch Tương hai ch&acirc;n rời khỏi mặt đất, c&ograve;n thi triển c&ocirc;ng phu n&agrave;o được, dĩ nhi&ecirc;n thua rồi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư thuận tay chế ngự Qu&aacute;ch Tương nhưng bỗng giật m&igrave;nh: &quot;Chao &ocirc;i! Ta chỉ mong thủ thắng, nhưng kh&ocirc;ng nghĩ đến việc t&igrave;m ra sư thừa m&ocirc;n ph&aacute;i của c&ocirc; ta. Trong mười chi&ecirc;u, c&ocirc; ta sử dụng mười m&ocirc;n quyền ph&aacute;p kh&aacute;c nhau, biết gọi l&agrave; m&ocirc;n ph&aacute;i n&agrave;o? Kh&ocirc;ng thể n&oacute;i c&ocirc; ta thuộc ph&aacute;i Thiếu L&acirc;m&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương ra sức gi&atilde;y dụa, la lối:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thả t&ocirc;i ra!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ nghe một tiếng cạch, từ trong người c&ocirc; một vật g&igrave; rơi ra. Qu&aacute;ch Tương lại k&ecirc;u tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o h&ograve;a thượng, c&oacute; bu&ocirc;ng t&ocirc;i ra kh&ocirc;ng n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư coi ch&uacute;ng sinh b&igrave;nh đẳng, kh&ocirc;ng n&oacute;i g&igrave; nam nữ kh&ocirc;ng ph&acirc;n biệt, m&agrave; cả đến ngựa b&ograve; heo ch&oacute;, &ocirc;ng cũng coi như nhau, cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tuổi t&aacute;c l&atilde;o nạp đ&aacute;ng &ocirc;ng nội của c&ocirc; rồi c&ograve;n ngại g&igrave; nữa?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi hai tay nhẹ nh&agrave;ng hất ra, n&eacute;m Qu&aacute;ch Tương ra ngo&agrave;i hai trượng. Phen n&agrave;y động thủ, tuy Qu&aacute;ch Tương bị khống chế nhưng trong mười chi&ecirc;u V&ocirc; Sắc cũng kh&ocirc;ng nhận ra được c&ocirc; ta thuộc m&ocirc;n ph&aacute;i n&agrave;o, đang toan mở lời nhận thua, vừa c&uacute;i đầu, bỗng thấy dưới đất c&aacute;i g&igrave; đen s&igrave;, nh&igrave;n kỹ h&oacute;a ra hai pho tượng la h&aacute;n bằng sắt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương vừa đặt ch&acirc;n chấm đất, hỏi liền:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o h&ograve;a thượng, &ocirc;ng c&oacute; nhận thua chưa?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc ngửng đầu l&ecirc;n, mặt m&agrave;y tươi tỉnh, cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tại sao ta lại thua? Ta biết lệnh t&ocirc;n l&agrave; đại hiệp Qu&aacute;ch Tĩnh, lệnh đường l&agrave; nữ hiệp Ho&agrave;ng Dung. Đ&agrave;o Hoa đảo Ho&agrave;ng đảo chủ l&agrave; &ocirc;ng ngoại của c&ocirc;. Phương danh Qu&aacute;ch nhị tiểu thư, chỉ c&oacute; một chữ Tương lấy từ hai chữ Tương Dương. Lệnh t&ocirc;n học từ Giang Nam thất qu&aacute;i, Đ&agrave;o Hoa đảo chủ, Cửu Chỉ thần c&aacute;i, v&agrave; ph&aacute;i To&agrave;n Ch&acirc;n. Qu&aacute;ch nhị tiểu thư gia học uy&ecirc;n nguy&ecirc;n, th&acirc;n thủ quả nhi&ecirc;n kh&ocirc;ng giống người thường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng n&oacute;i một hồi khiến Qu&aacute;ch Tương đứ người, trợn tr&ograve;n đ&ocirc;i mắt, một hồi l&acirc;u kh&ocirc;ng n&oacute;i được c&acirc;u n&agrave;o, nghĩ thầm: &quot;L&atilde;o h&ograve;a thượng n&agrave;y quả nhi&ecirc;n t&agrave; m&ocirc;n, ta đ&aacute;nh mười chi&ecirc;u lung tung như thế m&agrave; &ocirc;ng ta vẫn nhận ra được&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư thấy c&ocirc; ta ngơ ngẩn, cười h&igrave; h&igrave; nhặt đ&ocirc;i la h&aacute;n bằng sắt l&ecirc;n, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qu&aacute;ch nhị c&ocirc; nương, l&atilde;o h&ograve;a thượng kh&ocirc;ng lừa c&ocirc; b&eacute; con đ&acirc;u. Ta nhận ra c&ocirc; l&agrave; ở đ&ocirc;i la h&aacute;n n&agrave;y. Dương đại hiệp vẫn mạnh khỏe chứ, l&acirc;u nay c&ocirc; c&oacute; gặp &ocirc;ng ta kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương sựng người, nhưng hiểu ra ngay, n&oacute;i liền:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Agrave;, th&igrave; ra &ocirc;ng ch&iacute;nh l&agrave; V&ocirc; Sắc thiền sư, đ&ocirc;i la h&aacute;n sắt n&agrave;y ch&iacute;nh &ocirc;ng gửi tặng cho t&ocirc;i l&agrave;m qu&agrave; sinh nhật, n&ecirc;n đ&atilde; nhận ra. &Ocirc;ng c&oacute; gặp đại ca của t&ocirc;i v&agrave; Long tỉ tỉ kh&ocirc;ng? T&ocirc;i đi l&ecirc;n đ&acirc;y ch&iacute;nh l&agrave; mong gặp được &ocirc;ng để hỏi thăm tin tức hai người. &Ocirc;i, &ocirc;ng kh&ocirc;ng hiểu ra, t&ocirc;i n&oacute;i đại ca ca v&agrave; Long tỉ tỉ l&agrave; n&oacute;i đến vợ chồng đại hiệp Dương Qu&aacute; đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mấy năm trước, Dương đại hiệp c&oacute; gh&eacute; tệ tự chuyện tr&ograve; &iacute;t ng&agrave;y, c&ugrave;ng l&atilde;o h&ograve;a thượng rất tương đắc. Về sau nghe tin &ocirc;ng ta kh&aacute;ng địch tại th&agrave;nh Tương Dương, l&atilde;o nạp v&acirc;ng lệnh Dương đại hiệp, lập được ch&uacute;t c&ocirc;ng lao nho nhỏ. Kh&ocirc;ng hiểu l&uacute;c n&agrave;y &ocirc;ng ta ở đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cả hai người đều hỏi tin tức Dương Qu&aacute;, &ocirc;ng hỏi một c&acirc;u, t&ocirc;i hỏi một c&acirc;u, chẳng ai trả lời c&acirc;u hỏi của người kia. Qu&aacute;ch Tương thẫn thờ một hồi, rồi n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng cũng chẳng biết đại ca t&ocirc;i ở đ&acirc;u ư? Vậy th&igrave; ai biết được b&acirc;y giờ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng định thần lại n&oacute;i tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng l&agrave; bạn th&acirc;n của đại ca t&ocirc;i, thảo n&agrave;o v&otilde; c&ocirc;ng cao minh như vậy. Ồ, t&ocirc;i chưa c&aacute;m ơn &ocirc;ng đ&atilde; gửi qu&agrave; sinh nhật, h&ocirc;m nay xin cảm tạ vậy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bọn m&igrave;nh thật đ&uacute;ng l&agrave; kh&ocirc;ng đ&aacute;nh nhau th&igrave; kh&ocirc;ng biết l&agrave; người quen. Khi n&agrave;o c&ocirc; gặp Dương đại hiệp, đừng c&oacute; m&aacute;ch l&agrave; l&atilde;o h&ograve;a thượng cậy lớn hiếp b&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lặng nh&igrave;n ngọn n&uacute;i ở xa xa, lẩm bẩm một m&igrave;nh:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng biết đến bao giờ mới gặp lại được anh ta!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hồi Qu&aacute;ch Tương sinh nhật mười s&aacute;u tuổi, Dương Qu&aacute; nảy ra một &yacute; tưởng ngộ nghĩnh, mời hết thảy đồng đạo giang hồ tụ tập tại Tương Dương để ch&uacute;c mừng. Khi đ&oacute; v&ocirc; số v&otilde; l&acirc;m cao thủ trong cả hắc đạo lẫn bạch đạo, nể mặt Dương Qu&aacute; n&ecirc;n đến ch&uacute;c thọ. Người n&agrave;o bận qu&aacute; kh&ocirc;ng đi được th&igrave; cũng ph&aacute;i người đem qu&agrave; qu&iacute; gi&aacute; đến biếu. V&ocirc; Sắc thiền sư sai người mang đến một đ&ocirc;i la h&aacute;n đ&uacute;c bằng sắt rất tinh xảo. Đ&ocirc;i la h&aacute;n n&agrave;y trong bụng c&oacute; m&aacute;y, l&ecirc;n d&acirc;y c&oacute;t l&agrave; c&oacute; thể đấu với nhau một b&agrave;i Thiếu L&acirc;m La H&aacute;n quyền. Hơn một trăm năm trước, một dị tăng trong ch&ugrave;a đ&atilde; hao ph&iacute; kh&ocirc;ng biết bao nhi&ecirc;u t&acirc;m huyết mới chế tạo được, thực kh&eacute;o l&eacute;o kh&ocirc;ng g&igrave; s&aacute;nh bằng. Qu&aacute;ch Tương th&iacute;ch lắm, l&uacute;c n&agrave;o cũng mang theo trong người, kh&ocirc;ng ngờ h&ocirc;m nay từ trong bọc rơi ra, khiến cho V&ocirc; Sắc thiền sư nhận ra th&acirc;n phận. Chi&ecirc;u quyền ph&aacute;p Thiếu L&acirc;m sau c&ugrave;ng n&agrave;ng sử dụng ch&iacute;nh l&agrave; học từ đ&ocirc;i la h&aacute;n n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc cười n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tu&acirc;n theo qui củ của tệ tự từ trước tới nay, kh&ocirc;ng d&aacute;m mời Qu&aacute;ch nhị c&ocirc; nương v&agrave;o ch&ugrave;a v&atilde;n cảnh, xin mở lượng bao dung.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương điềm nhi&ecirc;n đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng sao cả, điều t&ocirc;i muốn hỏi &ocirc;ng, th&igrave; cũng đ&atilde; hỏi rồi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc lại chỉ Gi&aacute;c Viễn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ograve;n chuyện Gi&aacute;c Viễn sư đệ đ&acirc;y, từ từ ta sẽ giải th&iacute;ch cho c&ocirc; nương nghe. Th&ocirc;i thế n&agrave;y, để l&atilde;o h&ograve;a thượng tiễn c&ocirc; xuống n&uacute;i, m&igrave;nh t&igrave;m một qu&aacute;n ăn, để l&atilde;o đứng mời c&ocirc; uống một bữa rượu, c&ocirc; nghĩ thế n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thiền sư tại ch&ugrave;a Thiếu L&acirc;m địa vị hết sức cao, vậy m&agrave; đối với một c&ocirc; g&aacute;i nhỏ lại hết sức t&ocirc;n k&iacute;nh, đ&atilde; th&acirc;n tiễn n&agrave;ng xuống n&uacute;i, lại c&ograve;n đứng mời ăn, n&ecirc;n ch&uacute;ng tăng nghe vậy đều lấy l&agrave;m lạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại sư kh&ocirc;ng việc g&igrave; phải kh&aacute;ch s&aacute;o thế. Tiểu nữ ra tay kh&ocirc;ng biết nặng nhẹ, khiến đắc tội c&ugrave;ng mấy vị h&ograve;a thượng, kh&ocirc;ng bắt tội l&agrave; may. Nay xin từ biệt, mong c&oacute; dịp sẽ gặp lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong c&uacute;i m&igrave;nh thi lễ, rồi quay xuống n&uacute;i. V&ocirc; Sắc cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; kh&ocirc;ng muốn t&ocirc;i đưa tiễn, nhưng t&ocirc;i lại muốn. Sinh nhật c&ocirc; nương năm trước, l&atilde;o h&ograve;a thượng v&acirc;ng lệnh Dương đại hiệp đi đốt lương thảo của qu&acirc;n M&ocirc;ng Cổ tại Nam Dương, sau khi lửa ch&aacute;y, về ch&ugrave;a ngay, kh&ocirc;ng đến Tương Dương ch&uacute;c mừng được, trong l&ograve;ng vẫn thấy chưa an. H&ocirc;m nay c&oacute; dịp quang l&acirc;m tệ tự, nếu kh&ocirc;ng cung k&iacute;nh tiễn đưa ba mươi dặm, th&igrave; c&ograve;n l&agrave; g&igrave; đạo tiếp đ&atilde;i qu&iacute; kh&aacute;ch?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy &ocirc;ng ta th&agrave;nh &yacute;, lại thấy ng&ocirc;n ngữ h&agrave;o sảng, n&ecirc;n cũng muốn c&ugrave;ng &ocirc;ng ta kết bạn vong ni&ecirc;n, n&ecirc;n chỉ mỉm cười, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế th&igrave; đi vậy!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai người song h&agrave;nh c&ugrave;ng xuống n&uacute;i, đi qua một t&ograve;a nh&agrave; tranh, bỗng nghe đằng sau c&oacute; tiếng ch&acirc;n người vọng đến. Quay lại, chỉ thấy Trương Qu&acirc;n Bảo đi lẳng lặng ở đằng xa, kh&ocirc;ng d&aacute;m tới gần. Qu&aacute;ch Tương cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Trương huynh đệ, cậu cũng tiễn kh&aacute;ch xuống n&uacute;i đấy ư?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo đỏ mặt, chỉ đ&aacute;p cụt lủn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&acirc;ng!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vừa l&uacute;c ấy, từ cửa trước một tăng nh&acirc;n rảo bước chạy xuống. Y xem ra thi triển to&agrave;n lực khinh c&ocirc;ng n&ecirc;n tr&ocirc;ng rất gấp r&uacute;t. V&ocirc; Sắc hơi nh&iacute;u m&agrave;y, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&agrave;m g&igrave; m&agrave; hốt hoảng qu&aacute; vậy?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; sư đ&oacute; chạy đến trước mặt V&ocirc; Sắc, c&uacute;i ch&agrave;o xong nghi&ecirc;ng người n&oacute;i nhỏ mấy c&acirc;u. V&ocirc; Sắc biến sắc, n&oacute;i lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&oacute; chuyện như vậy sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tăng nh&acirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phương trượng mời thủ tọa về ch&ugrave;a thương nghị.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy vẻ mặt V&ocirc; Sắc xem chừng kh&oacute; xử, biết l&agrave; trong ch&ugrave;a ắt c&oacute; chuyện khẩn cấp, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o thiền sư, chuyện giao thiệp bạn b&egrave;, cốt l&agrave; ở l&ograve;ng, chứ những lề th&oacute;i ph&agrave;m tục c&oacute; đ&aacute;ng g&igrave;! &Ocirc;ng c&oacute; chuyện xin cứ về. Sau n&agrave;y c&oacute; dịp gặp lại, c&oacute; duy&ecirc;n giải cấu, m&igrave;nh lại uống rượu, luận v&otilde;, c&oacute; g&igrave; m&agrave; kh&ocirc;ng được?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc mừng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thảo n&agrave;o Dương đại hiệp coi trọng c&ocirc; như thế, c&ocirc; quả l&agrave; bậc nh&acirc;n trung anh tuấn, nữ trung trượng phu, l&atilde;o h&ograve;a thượng muốn được c&ugrave;ng c&ocirc; kết l&agrave;m bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng l&agrave; bạn của đại ca t&ocirc;i, th&igrave; cũng l&agrave; bạn của t&ocirc;i rồi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế rồi hai người thi lễ từ biệt. V&ocirc; Sắc quay trở về ch&ugrave;a. Qu&aacute;ch Tương đi theo con đường nhỏ đi xuống n&uacute;i, Trương Qu&acirc;n Bảo lẽo đẽo đi sau, c&aacute;ch chừng năm s&aacute;u bước, kh&ocirc;ng d&aacute;m đi ngang h&agrave;ng. Qu&aacute;ch Tương hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Trương huynh đệ, v&igrave; cớ g&igrave; m&agrave; bọn họ lại hiếp đ&aacute;p sư phụ của cậu thế? Sư phụ cậu nội c&ocirc;ng tinh th&acirc;m, sao lại sợ họ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo tiến l&ecirc;n hai bước đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Trong ch&ugrave;a giới luật tinh nghi&ecirc;m, ai ai trong tăng ch&uacute;ng nếu như phạm tội đều bị phạt cả, chứ đ&acirc;u phải cố &yacute; hiếp đ&aacute;p sư phụ đ&acirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lấy l&agrave;m lạ, hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ cậu l&agrave; người ch&iacute;nh nh&acirc;n qu&acirc;n tử, trong thi&ecirc;n hạ đ&acirc;u c&oacute; mấy ai tốt như &ocirc;ng ấy, m&agrave; lại phạm tội g&igrave;? Chắc l&agrave; &ocirc;ng ấy chịu phạt gi&ugrave;m người kh&aacute;c, nếu kh&ocirc;ng, chắc l&agrave; bị phạt lầm rồi?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo thở d&agrave;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nguy&ecirc;n ủy chuyện n&agrave;y c&ocirc; nương cũng đ&atilde; biết rồi, ch&iacute;nh l&agrave; v&igrave; bộ Lăng Gi&agrave;[3] kinh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Agrave;, c&oacute; phải l&agrave; cuốn kinh m&agrave; hai g&atilde; Ti&ecirc;u Tương Tử v&agrave; Do&atilde;n Khắc T&acirc;y ăn trộm đ&oacute; kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&iacute;nh đ&oacute;. H&ocirc;m đ&oacute; tr&ecirc;n đỉnh Hoa Sơn, tiểu nh&acirc;n được đại hiệp Dương Qu&aacute; chỉ điểm, tự tay kh&aacute;m to&agrave;n th&acirc;n hai g&atilde; đ&oacute;, nhưng khi xuống n&uacute;i rồi, kh&ocirc;ng c&ograve;n thấy tung t&iacute;ch g&igrave; của họ cả. Thầy tr&ograve; ch&uacute;ng t&ocirc;i kh&ocirc;ng c&ograve;n biết g&igrave; hơn, chỉ c&ograve;n về ch&ugrave;a bẩm b&aacute;o phương trượng. Bộ Lăng Gi&agrave; kinh l&agrave; do ch&iacute;nh tay tổ sư Đạt Ma viết ra, thủ tọa giới luật đường tr&aacute;ch sư phụ t&ocirc;i giữ kinh kh&ocirc;ng cẩn thận, đ&atilde; l&agrave;m mất bảo vật v&ocirc; gi&aacute; đ&oacute;, n&ecirc;n phạt thật nặng, &acirc;u cũng l&agrave; chuyện phải th&ocirc;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thở d&agrave;i một tiếng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Việc xử tội s&aacute;m hối như thế, sao lại phải được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tuy n&agrave;ng chỉ lớn hơn Trương Qu&acirc;n Bảo v&agrave;i tuổi nhưng đ&atilde; tự coi như đ&agrave;n chị, n&ecirc;n hỏi th&ecirc;m:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu chỉ như thế, tại sao lại kh&ocirc;ng cho sư phụ cậu kh&ocirc;ng được n&oacute;i năng g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&oacute; l&agrave; giới luật truyền từ đời n&agrave;y sang đời kh&aacute;c của ch&ugrave;a, hễ g&aacute;nh nước, kh&ocirc;ng được n&oacute;i chuyện. T&ocirc;i nghe c&aacute;c vị l&atilde;o thiền sư trong ch&ugrave;a n&oacute;i l&agrave;, tuy l&agrave; bị phạt, nhưng thực ra đối với người c&oacute; tội rất tốt. Nếu như kh&ocirc;ng n&oacute;i chuyện, việc tu tập c&agrave;ng mau tinh tiến, c&ograve;n tr&ecirc;n vai g&aacute;nh nước, l&agrave;m cho thể ph&aacute;ch cường tr&aacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu n&oacute;i như thế, sư phụ cậu kh&ocirc;ng phải chịu phạt, m&agrave; lại l&agrave; đang luyện c&ocirc;ng, c&ograve;n t&ocirc;i th&igrave; đ&uacute;ng l&agrave; nhiều chuyện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo vội n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&ograve;ng tốt của c&ocirc; nương, thầy t&ocirc;i v&agrave; t&ocirc;i mười phần cảm k&iacute;ch, vĩnh viễn kh&ocirc;ng d&aacute;m qu&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thở nhẹ một hơi, nghĩ thầm: &quot;Vậy m&agrave; người đời đ&atilde; qu&ecirc;n hẳn ta rồi&quot;. Bỗng nghe thấy trong rừng c&oacute; tiếng lừa k&ecirc;u, ch&iacute;nh l&agrave; con lừa đen đang ăn cỏ. Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Trương huynh đệ, cậu kh&ocirc;ng cần phải tiễn t&ocirc;i xa hơn nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng chu miệng hu&yacute;t một tiếng, gọi con lừa lại gần. Trương Qu&acirc;n Bảo vẫn c&ograve;n lưu luyến chưa muốn rời, nhưng cũng kh&ocirc;ng biết chuyện g&igrave; để n&oacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lấy đ&ocirc;i thiết la h&aacute;n trong bọc ra, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ta cho cậu c&aacute;i n&agrave;y, cầm lấy đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo &uacute; ớ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i... t&ocirc;i...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đặt đ&ocirc;i la h&aacute;n v&agrave;o tay y, nh&uacute;n người một c&aacute;i, đ&atilde; nhảy l&ecirc;n lưng lừa. Đột nhi&ecirc;n tại bậc đ&aacute; tr&ecirc;n sườn n&uacute;i c&oacute; tiếng người gọi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qu&aacute;ch nhị c&ocirc; nương, xin dừng ch&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&iacute;nh l&agrave; V&ocirc; Sắc thiền sư từ cửa ch&ugrave;a chạy xuống. Qu&aacute;ch Tương nghĩ thầm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o h&ograve;a thượng n&agrave;y thật c&acirc;u nệ đa lễ, việc g&igrave; m&agrave; phải nhất định đưa tiễn ta?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc đi rất nhanh, chỉ nh&aacute;y mắt đ&atilde; đến trước mặt Qu&aacute;ch Tương. &Ocirc;ng ta n&oacute;i với Trương Qu&acirc;n Bảo:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi về ch&ugrave;a đi, đừng chạy lăng quăng trong n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc đợi y đi khỏi, lấy trong tay &aacute;o ra một tờ giấy, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qu&aacute;ch nhị c&ocirc; nương, c&ocirc; xem c&oacute; biết ai viết giấy n&agrave;y kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương xuống lừa, tiếp lấy nh&igrave;n qua, h&oacute;a ra l&agrave; một tờ hoa ti&ecirc;n vẫn d&ugrave;ng đề thơ, tr&ecirc;n c&oacute; hai h&agrave;ng chữ lằng ngoằng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&otilde; c&ocirc;ng ph&aacute;i Thiếu L&acirc;m, xưng h&ugrave;ng Trung Nguy&ecirc;n, T&acirc;y Vực đ&atilde; l&acirc;u. Mười ng&agrave;y sau, C&ocirc;n L&ocirc;n Tam Th&aacute;nh sẽ đến lĩnh gi&aacute;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&eacute;t b&uacute;t rất cứng cỏi. Qu&aacute;ch Tương hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;n L&ocirc;n Tam Th&aacute;nh l&agrave; ai thế, ba người n&agrave;y khẩu kh&iacute; xem chừng ph&aacute;ch lối lắm nhỉ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&oacute;a ra l&agrave; c&ocirc; nương kh&ocirc;ng quen biết những người n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i kh&ocirc;ng biết, m&agrave; cả c&aacute;i t&ecirc;n C&ocirc;n L&ocirc;n Tam Th&aacute;nh cũng chưa nghe cha mẹ t&ocirc;i n&oacute;i đến bao giờ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i đ&oacute; mới l&agrave; lạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Lạ l&agrave; thế n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương với ta nhất kiến như cố, c&oacute; n&oacute;i thật cho c&ocirc; biết cũng kh&ocirc;ng sao. C&ocirc; nương thử đo&aacute;n xem tờ giấy n&agrave;y từ đ&acirc;u m&agrave; c&oacute;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phải chăng C&ocirc;n L&ocirc;n Tam Th&aacute;nh sai người mang đến?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu như sai người mang đến th&igrave; kh&ocirc;ng c&oacute; g&igrave; kỳ qu&aacute;i. Người đời thường n&oacute;i l&agrave; c&acirc;y cả gi&oacute; to, ch&ugrave;a Thiếu L&acirc;m mấy trăm năm nay vẫn được gọi l&agrave; v&otilde; học chi nguy&ecirc;n, n&ecirc;n kh&ocirc;ng l&uacute;c n&agrave;o kh&ocirc;ng c&oacute; cao thủ đến thử t&agrave;i. Mỗi khi c&oacute; nh&acirc;n vật v&otilde; l&acirc;m đến, ch&uacute;ng t&ocirc;i đều hết sức tử tế, n&oacute;i đến chuyện tỉ v&otilde; đều từ chối. Đ&atilde; l&agrave; người tu h&agrave;nh phải tr&aacute;nh s&acirc;n nộ, kh&ocirc;ng thể s&iacute;nh cường tranh thắng. C&ograve;n nếu ng&agrave;y n&agrave;o cũng đ&aacute;nh nhau với người ta, th&igrave; n&oacute;i l&agrave; đệ tử cửa Phật sao được?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương gật đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phải lắm!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngặt một nỗi v&otilde; sư đ&atilde; l&ecirc;n đến tr&ecirc;n ch&ugrave;a, nếu như kh&ocirc;ng hiển được ch&uacute;t th&acirc;n thủ th&igrave; chẳng cam l&ograve;ng. La H&aacute;n đường của Thiếu L&acirc;m ch&iacute;nh l&agrave; để tiếp những v&otilde; sư b&ecirc;n ngo&agrave;i đến viếng cảnh ch&ugrave;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&oacute;a ra đại h&ograve;a thượng giữ chức vụ đi đ&aacute;nh nhau với người ngo&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc sượng s&ugrave;ng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&otilde; sư đến đ&acirc;y d&ugrave; cao cường đến đ&acirc;u, th&igrave; bản đường đệ tử cũng c&oacute; thể đảm đương được cả, kh&ocirc;ng cần phải đến l&atilde;o h&ograve;a thượng ra tay. H&ocirc;m nay v&igrave; chưng c&ocirc; nương th&acirc;n thủ bất ph&agrave;m, n&ecirc;n l&atilde;o mới thử một ch&uacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&oacute;a ra đại sư coi trọng t&ocirc;i qu&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng phải đến đ&acirc;y để n&oacute;i chuyện đ&oacute; với c&ocirc; đ&acirc;u. Kh&ocirc;ng d&aacute;m dấu, tờ giấy n&agrave;y ch&uacute;ng t&ocirc;i lấy từ b&agrave;n tay pho tượng H&agrave;ng Long La H&aacute;n ở La H&aacute;n Đường.</span></p>', 0, 'chuong-1-thien-nhai-tu-quan-bat-kha-vong', NULL, NULL);
INSERT INTO `chapter` VALUES (15, 19, 'Chương 2 Thiên nhai tư quân bất khả vọng', 'Quách Tương lấy làm lạ, hỏi:  Ai lại để trên tay tượng Phật như thế nhỉ?', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lấy l&agrave;m lạ, hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ai lại để tr&ecirc;n tay tượng Phật như thế nhỉ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Cũng kh&ocirc;ng biết nữa. Trong mấy trăm tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m ch&uacute;ng t&ocirc;i, nếu như c&oacute; ai lẻn v&agrave;o, chẳng lẽ kh&ocirc;ng thấy? La H&aacute;n Đường l&uacute;c n&agrave;o cũng c&oacute; t&aacute;m đệ tử ng&agrave;y đ&ecirc;m lu&acirc;n phi&ecirc;n canh g&aacute;c. Khi c&oacute; người tr&ocirc;ng thấy tờ giấy n&agrave;y lập tức phi b&aacute;o l&atilde;o phương trượng, ai ai cũng lấy l&agrave;m lạ, n&ecirc;n mới triệu l&atilde;o về ch&ugrave;a thương nghị.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe đến đ&acirc;y, đ&atilde; hiểu &yacute;, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phải chăng &ocirc;ng nghi t&ocirc;i th&ocirc;ng đồng với C&ocirc;n L&ocirc;n Tam Th&aacute;nh ở b&ecirc;n ngo&agrave;i g&acirc;y rối, để cho ba g&atilde; kia lẻn v&agrave;o La H&aacute;n Đường để tờ giấy n&agrave;y, phải thế kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i đ&atilde; gặp c&ocirc; rồi quyết kh&ocirc;ng thể n&agrave;o nghi cho c&ocirc; được. Thế nhưng việc cũng tr&ugrave;ng hợp lạ kỳ, c&ocirc; nương vừa rời ch&ugrave;a, th&igrave; tờ giấy n&agrave;y lại xuất hiện tại La H&aacute;n Đường, th&agrave;nh thử phương trượng v&agrave; V&ocirc; Tướng sư đệ kh&ocirc;ng khỏi kh&ocirc;ng nghi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i kh&ocirc;ng biết ba g&atilde; n&agrave;y l&agrave; ai. Đại h&ograve;a thượng, &ocirc;ng việc g&igrave; phải sợ? Mười ng&agrave;y nữa, nếu như họ c&oacute; gan đến đ&acirc;y, th&igrave; cứ xem hai b&ecirc;n cao thấp thế n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sợ th&igrave; c&oacute; g&igrave; đ&acirc;u m&agrave; sợ. Nếu như c&ocirc; nương với họ kh&ocirc;ng c&oacute; li&ecirc;n quan g&igrave;, t&ocirc;i kh&ocirc;ng c&oacute; g&igrave; phải &aacute;y n&aacute;y cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy &ocirc;ng ta quả c&oacute; l&ograve;ng tốt, chỉ ngại C&ocirc;n L&ocirc;n Tam Th&aacute;nh l&agrave; chỗ quen biết với m&igrave;nh, khi động thủ ắt c&oacute; nhiều điều cấm kỵ, ngại rằng sẽ đắc tội với bạn b&egrave;, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu như họ thực l&ograve;ng đến t&igrave;m hiểu v&otilde; nghệ, th&igrave; kh&ocirc;ng n&oacute;i l&agrave;m g&igrave;. C&ograve;n kh&ocirc;ng, cứ việc cho họ một phen khốn khổ. Cứ theo tờ giấy n&agrave;y khẩu kh&iacute; xem ra cuồng vọng lắm. C&aacute;i g&igrave; m&agrave; &quot;một phen l&atilde;nh gi&aacute;o&quot; l&agrave; sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng n&oacute;i tới đ&acirc;y, chợt nghĩ ra một việc n&ecirc;n tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hay l&agrave; c&oacute; ai trong ch&ugrave;a cấu kết, l&eacute;n bỏ v&agrave;o tờ giấy n&agrave;y chăng? Nếu thế cũng kh&ocirc;ng lấy g&igrave; l&agrave;m lạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chuyện đ&oacute; ch&uacute;ng t&ocirc;i cũng đ&atilde; nghĩ tới rồi, nhưng nhất định kh&ocirc;ng thể c&oacute;. Tay của H&agrave;ng Long La H&aacute;n cao đến hơn ba trượng, ng&agrave;y thường muốn phủi bụi tr&ecirc;n pho tượng n&agrave;y, cũng phải bắc gi&aacute; cao. Nếu c&oacute; kẻ n&agrave;o nhảy l&ecirc;n tới đ&oacute;, khinh c&ocirc;ng giỏi như thế kh&ocirc;ng phải l&agrave; nhiều. Nếu như trong ch&ugrave;a c&oacute; phản đồ th&igrave; t&agrave;i nghệ cũng kh&ocirc;ng đạt tới mức đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&ograve;ng hiếu kỳ của Qu&aacute;ch Tương nổi l&ecirc;n, rất muốn xem thử ba g&atilde; C&ocirc;n L&ocirc;n Tam Th&aacute;nh xem họ l&agrave; hạng người n&agrave;o, tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m c&ugrave;ng họ tỉ th&iacute; v&otilde; nghệ, kết quả ai thắng ai bại, ngặt l&agrave; ch&ugrave;a Thiếu L&acirc;m kh&ocirc;ng tiếp nữ kh&aacute;ch, xem ra c&aacute;i tr&ograve; vui n&agrave;y kh&ocirc;ng c&aacute;ch n&agrave;o coi tận mắt được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc thấy c&ocirc; c&uacute;i đầu suy nghĩ, lại tưởng c&ocirc; đang tr&ugrave; liệu kế s&aacute;ch hộ cho ch&ugrave;a Thiếu L&acirc;m, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&ugrave;a Thiếu L&acirc;m cả ngh&igrave;n năm qua đ&atilde; chịu kh&ocirc;ng biết bao nhi&ecirc;u s&oacute;ng to gi&oacute; cả, c&ograve;n tồn tại đến h&ocirc;m nay. Nếu bọn C&ocirc;n L&ocirc;n Tam Th&aacute;nh kia nhất định c&ugrave;ng ch&uacute;ng t&ocirc;i qua lại một phen, ch&ugrave;a Thiếu L&acirc;m thể n&agrave;o cũng c&ugrave;ng với họ chu to&agrave;n. Qu&aacute;ch c&ocirc; nương, nửa th&aacute;ng nữa, c&ocirc; ở tr&ecirc;n chốn giang hồ sẽ nghe tin tức, để xem C&ocirc;n L&ocirc;n Tam Th&aacute;nh c&oacute; lật đổ được ch&ugrave;a Thiếu L&acirc;m kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i tới đ&acirc;y, h&agrave;o kh&iacute; của &ocirc;ng lại nổi l&ecirc;n bừng bừng như thời trai trẻ. Qu&aacute;ch Tương cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng bỗng dưng nổi giận, &ocirc;ng n&oacute;i năng như thế sao c&ograve;n gọi l&agrave; đệ tử nh&agrave; Phật được nữa? Tốt lắm, t&ocirc;i mong nửa th&aacute;ng nữa sẽ nghe được tin vui.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong n&agrave;ng quay m&igrave;nh nhảy l&ecirc;n lưng lừa. Hai người nh&igrave;n nhau c&ugrave;ng cười. Qu&aacute;ch Tương giục con lừa đi lững thững xuống ch&acirc;n n&uacute;i, trong l&ograve;ng đ&atilde; định bụng kh&ocirc;ng thể n&agrave;o kh&ocirc;ng l&ecirc;n xem trận đấu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; nghĩ thầm: &quot;L&agrave;m c&aacute;ch n&agrave;o mười ng&agrave;y sau m&igrave;nh lẻn được v&agrave;o ch&ugrave;a Thiếu L&acirc;m để xem c&aacute;i cảnh đ&aacute;ng xem n&agrave;y? Chỉ e mấy g&atilde; C&ocirc;n L&ocirc;n Tam Th&aacute;nh chẳng c&oacute; ch&acirc;n t&agrave;i thực học, bị mấy nh&agrave; sư đ&aacute;nh cho v&agrave;i c&aacute;i đ&atilde; thua, th&igrave; c&oacute; g&igrave; l&agrave; th&uacute;. Chỉ mong bọn họ chỉ cần được bằng một nửa gia gia, &ocirc;ng ngoại, hoặc đại ca, th&igrave; c&aacute;i m&agrave;n C&ocirc;n L&ocirc;n Tam Th&aacute;nh đại n&aacute;o Thiếu L&acirc;m tự kia mới đ&aacute;ng đến coi&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghĩ đến Dương Qu&aacute;, trong l&ograve;ng lại thấy u uất, ba năm qua đi khắp nơi t&igrave;m kiếm, vẫn kh&ocirc;ng thấy b&oacute;ng d&aacute;ng tăm hơi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Im l&igrave;m cổ mộ n&uacute;i Chung Nam,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vạn Hoa hoa rụng tiếng mang mang.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hang n&uacute;i Tuyệt T&igrave;nh sao vắng lặng,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đ&igrave;u hiu trăng lạnh bến Phong Lăng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Chung Nam sơn, cổ mộ trường bế,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vạn Hoa huyễn, hoa lạc v&ocirc; thanh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tuyệt T&igrave;nh cốc, kh&ocirc;ng sơn tịch tịch,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phong Lăng độ, l&atilde;nh nguyệt minh minh)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng, trong đầu n&agrave;ng cũng đ&atilde; nghĩ kh&ocirc;ng biết bao nhi&ecirc;u lần: &quot;Thực ra, dẫu ta c&oacute; kiếm được y, th&igrave; cũng để l&agrave;m g&igrave;? Chẳng c&agrave;ng th&ecirc;m buồn khổ, th&ecirc;m sầu n&atilde;o? Y đi xa x&ocirc;i biệt tăm biệt t&iacute;ch chẳng tốt hơn ư? Đ&atilde; biết rằng đi t&igrave;m y c&oacute; kh&aacute;c g&igrave; đi t&igrave;m hoa trong gương, trăng dưới nước, nhưng sao ta vẫn cứ nghĩ đến, vẫn muốn đi t&igrave;m&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng cứ để cho con lừa tự thả bước, đi lang thang trong v&ugrave;ng n&uacute;i Thiếu Thất, theo hướng t&acirc;y đ&atilde; v&agrave;o địa giới của Tung Sơn. Quay đầu nh&igrave;n lại ngọn đ&ocirc;ng phong của n&uacute;i Thiếu Thất chỉ thấy cao v&uacute;t xanh xanh, n&uacute;i nọ liền n&uacute;i kia bất tận. Cứ như thế n&agrave;ng đi mấy ng&agrave;y liền, một ng&agrave;y kia đ&atilde; tới đ&agrave;i Tam Hưu, nghĩ thầm: &quot;Sao lại tam hưu, m&agrave; l&agrave; ba c&aacute;i hưu (điều tốt l&agrave;nh) n&agrave;o? Đời người c&oacute; thi&ecirc;n hưu, vạn hưu đ&acirc;u phải chỉ tam hưu?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lại rẽ qua hướng bắc, đi qua một đỉnh n&uacute;i, thấy đến mấy trăm gốc cổ b&aacute;ch, c&acirc;y n&agrave;o cũng thẳng cao v&ograve;i vọi, &aacute;nh mặt trời chiếu xuống th&agrave;nh những đốm hoa, thật l&agrave; rực rỡ. Qu&aacute;ch Tương đang ngắm cảnh, bỗng nghe thấy từ b&ecirc;n kia sườn n&uacute;i văng vẳng nghe đ&acirc;u tiếng đ&agrave;n vọng đến, lấy l&agrave;m lạ l&ugrave;ng, nghĩ thầm: &quot;Ở nơi hoang tịch n&agrave;y, sao lại c&oacute; cao nh&acirc;n nh&atilde; sĩ đến đ&acirc;y gẩy đ&agrave;n&quot;. N&agrave;ng từ b&eacute; được mẹ dạy, cầm kỳ thư họa, m&ocirc;n n&agrave;o cũng biết, tuy chưa s&acirc;u sắc, nhưng t&iacute;nh th&ocirc;ng tuệ, lại trời sinh hay nh&igrave;n xa nghĩ rộng, mỗi khi c&ugrave;ng mẫu th&acirc;n luận cầm, đ&agrave;m thư, thỉnh thoảng cũng c&oacute; những tư tưởng độc đ&aacute;o, t&igrave;m ra được những điều chưa ai nghĩ đến. L&uacute;c n&agrave;y nghe thấy tiếng đ&agrave;n, nổi t&iacute;nh hiếu kỳ, n&ecirc;n xuống lừa, theo tiếng đ&agrave;n t&igrave;m đến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đi qua hơn mươi trượng, lại nghe thấy lẫn trong tiếng đ&agrave;n c&oacute; lẫn v&ocirc; số tiếng chim, l&uacute;c đầu chưa ch&uacute; &yacute;, nhưng nghe kỹ, tiếng đ&agrave;n dường như c&ugrave;ng tiếng chim đối đ&aacute;p, l&iacute;u l&iacute;u lo lo, dặt d&igrave;u uyển chuyển. Qu&aacute;ch Tương n&aacute;u m&igrave;nh sau gốc c&acirc;y, nh&igrave;n về ph&iacute;a c&oacute; tiếng đ&agrave;n ph&aacute;t ra, chỉ thấy một người đ&agrave;n &ocirc;ng mặc &aacute;o trắng ngồi quay lưng lại giữa ba gốc t&ugrave;ng gi&agrave;, trong l&ograve;ng để một c&acirc;y ti&ecirc;u vĩ cầm, đang gảy đ&agrave;n. Tr&ecirc;n những c&agrave;nh c&acirc;y chung quanh người đ&oacute; đậu đầy chim sẻ, ho&agrave;ng oanh, đỗ quy&ecirc;n, b&aacute;t ca, v&agrave; c&ograve;n v&ocirc; số những con chim kh&aacute;c n&agrave;ng kh&ocirc;ng biết t&ecirc;n c&ugrave;ng với tiếng đ&agrave;n, hoặc kẻ hỏi người đ&aacute;p, hoặc c&ugrave;ng cất giọng xướng theo. Qu&aacute;ch Tương nghĩ thầm: &quot;Mẹ ta n&oacute;i rằng trong c&aacute;c điệu đ&agrave;n c&oacute; một kh&uacute;c t&ecirc;n l&agrave; &quot;Kh&ocirc;ng Sơn Điểu Ngữ&quot;, thất truyền đ&atilde; l&acirc;u, phải chăng ch&iacute;nh l&agrave; kh&uacute;c đ&agrave;n n&agrave;y?&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghe một l&aacute;t, tiếng đ&agrave;n mỗi l&uacute;c một vang th&ecirc;m, nhưng mỗi lần tiếng đ&agrave;n l&ecirc;n cao, th&igrave; đ&agrave;n chim lại ngưng tiếng, chỉ nghe thấy tiếng vỗ c&aacute;nh, đ&ocirc;ng t&acirc;y nam bắc bốn ph&iacute;a bay lại v&ocirc; số l&agrave; chim, con th&igrave; đậu tr&ecirc;n c&agrave;nh, con th&igrave; bay lượn l&ecirc;n xuống, l&ocirc;ng chim bay phất phới, thật l&agrave; lạ mắt. Tiếng đ&agrave;n tuy b&igrave;nh h&ograve;a trung ch&iacute;nh nhưng b&ecirc;n trong c&oacute; ẩn một phong vị vương giả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương trong l&ograve;ng c&agrave;ng th&ecirc;m kinh h&atilde;i: &quot;Người n&agrave;y c&oacute; thể d&ugrave;ng tiếng đ&agrave;n để tập hợp đ&agrave;n chim, phải chăng bản đ&agrave;n n&agrave;y l&agrave; B&aacute;ch Điểu Triều Phượng? Tiếc thay kh&ocirc;ng c&oacute; &ocirc;ng ngoại ở đ&acirc;y, nếu kh&ocirc;ng sẽ d&ugrave;ng c&acirc;y ngọc ti&ecirc;u thi&ecirc;n hạ kh&ocirc;ng ai s&aacute;nh kịp đem ra h&ograve;a nhịp, c&oacute; thể gọi l&agrave; song tuyệt đời nay&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; đ&agrave;n đến kh&uacute;c cuối, tiếng đ&agrave;n nhỏ dần, tất cả chim ch&oacute;c tr&ecirc;n c&acirc;y c&ugrave;ng bay ra m&uacute;a may một lượt. Bỗng dưng nghe một tiếng keng, tiếng đ&agrave;n dứt hẳn, đ&agrave;n chim bay th&ecirc;m một hồi rồi từ từ bay đi mỗi con một ph&iacute;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; tiện tay đưa l&ecirc;n d&acirc;y đ&agrave;n gảy th&ecirc;m v&agrave;i đoạn ngắn, ngẩng đầu l&ecirc;n nh&igrave;n trời thở d&agrave;i, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vỗ trường kiếm, dương mi l&ecirc;n, nước trong đ&aacute; trắng sao xa c&aacute;ch? Thế gian n&agrave;y nếu kh&ocirc;ng c&oacute; kẻ tri &acirc;m, d&ugrave; sống đến ngh&igrave;n tuổi, phỏng c&oacute; &iacute;ch g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i đến đ&acirc;y, đột nhi&ecirc;n từ đ&aacute;y chiếc đ&agrave;n người đ&oacute; r&uacute;t ra một thanh trường kiếm, &aacute;nh xanh lấp l&aacute;nh, chiếu ra trong rừng. Qu&aacute;ch Tương nghĩ thầm: &quot;Nguy&ecirc;n lai người n&agrave;y văn v&otilde; to&agrave;n t&agrave;i, kh&ocirc;ng biết kiếm ph&aacute;p của y ra sao&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy y chậm r&atilde;i đi tới b&atilde;i đất trống trước một c&acirc;u cổ t&ugrave;ng, mũi kiếm chỉ xuống đất, gạch tới gạch lui, hết vạch n&agrave;y tới vạch kh&aacute;c. Qu&aacute;ch Tương lấy l&agrave;m lạ: &quot;Tr&ecirc;n thế gian c&oacute; loại kiếm ph&aacute;p kỳ qu&aacute;i đến thế sao? Chẳng lẽ chỉ trỏ kiếm xuống đất vạch một hồi, c&oacute; thể khắc địch chế thắng? Người n&agrave;y quả thật qu&aacute;i dị kh&ocirc;ng thể n&agrave;o đo lường được.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y vạch một hồi, thấy gạch ngang mười ch&iacute;n n&eacute;t, rồi đổi qua gạch dọc, tất cả cũng mười ch&iacute;n n&eacute;t. Kiếm chi&ecirc;u trước sau kh&ocirc;ng thay đổi, d&ugrave; dọc hay ngang cũng chỉ thẳng băng như chữ nhất. Qu&aacute;ch Tương theo d&otilde;i kiếm thế của y, cũng đưa tay vạch l&ecirc;n đất mười ch&iacute;n n&eacute;t, bỗng dưng bật cười, y đ&acirc;u c&oacute; sử dụng qu&aacute;i dị kiếm ph&aacute;p n&agrave;o đ&acirc;u, m&agrave; chỉ d&ugrave;ng kiếm vạch th&agrave;nh một b&agrave;n cờ dọc ngang mỗi chiều mười ch&iacute;n n&eacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; vẽ b&agrave;n cờ xong, d&ugrave;ng mũi kiếm vẽ tại g&oacute;c tr&aacute;i b&ecirc;n tr&ecirc;n v&agrave; g&oacute;c phải b&ecirc;n dưới mỗi nơi một v&ograve;ng tr&ograve;n, lại tại g&oacute;c phải b&ecirc;n tr&ecirc;n v&agrave; g&oacute;c tr&aacute;i b&ecirc;n dưới mỗi nơi vẽ một vạch ch&eacute;o. Qu&aacute;ch Tương đ&atilde; thấy y vẽ đ&acirc;y l&agrave; một b&agrave;n cờ v&acirc;y, biết y đang bố tr&iacute; trận thế tại bốn g&oacute;c, h&igrave;nh tr&ograve;n l&agrave; b&ecirc;n trắng, h&igrave;nh ch&eacute;o l&agrave; b&ecirc;n đen. Lại thấy y đặt một v&ograve;ng tr&ograve;n c&aacute;ch ba &ocirc; tr&ecirc;n g&oacute;c tr&aacute;i, lại c&aacute;ch dưới hai &ocirc; vạch một h&igrave;nh ch&eacute;o. Đi đến nước thứ mười ch&iacute;n, y chống kiếm đứng, c&uacute;i đầu suy nghĩ, đang ph&acirc;n v&acirc;n kh&ocirc;ng biết n&ecirc;n bỏ qu&acirc;n giữ thế hay cố sức tranh g&oacute;c cạnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghĩ thầm: &quot;Người n&agrave;y cũng chẳng kh&aacute;c g&igrave; ta, thật l&agrave; tịch mịch, ở trong n&uacute;i hoang gảy đ&agrave;n, lấy chim ch&oacute;c l&agrave;m tri &acirc;m; đ&aacute;nh cờ lại cũng kh&ocirc;ng c&oacute; đối thủ, chỉ tự m&igrave;nh đ&aacute;nh với m&igrave;nh.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; suy nghĩ một hồi, qu&acirc;n trắng kh&ocirc;ng c&aacute;ch g&igrave; lơi ra được, tr&ecirc;n g&oacute;c tr&aacute;i đấu với qu&acirc;n đen thật kịch liệt, nhất thời chưa t&igrave;m ra c&aacute;ch n&agrave;o hay, từ bắc xuống nam kh&ocirc;ng thể n&agrave;o tranh được ph&uacute;c địa ở Trung Nguy&ecirc;n. Qu&aacute;ch Tương xem cờ xuất thần, từ từ nh&iacute;ch lại gần, nhưng v&igrave; qu&acirc;n trắng l&uacute;c bố cục bị thua một nước, trước sau vẫn bị rơi v&agrave;o hạ phong, đến nước thứ hai mươi ba th&igrave; gặp phải li&ecirc;n ho&agrave;n kiếp, b&ecirc;n qu&acirc;n trắng l&acirc;m v&agrave;o thế nguy, nhưng y vẫn cố gắng chống đỡ. Người đời thường n&oacute;i &quot;Cờ ngo&agrave;i b&agrave;i trong&quot;, &yacute; n&oacute;i người đứng ngo&agrave;i coi đ&aacute;nh cờ bao giờ cũng s&aacute;ng nước hơn. Qu&aacute;ch Tương đ&aacute;nh cờ cũng chỉ b&igrave;nh b&igrave;nh, nhưng nh&igrave;n thấy thế cục nếu b&ecirc;n trắng c&ograve;n đ&aacute;nh giằng dai kh&ocirc;ng khỏi to&agrave;n qu&acirc;n bị mất, n&ecirc;n buột miệng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sao kh&ocirc;ng bỏ Trung Nguy&ecirc;n đi để lấy T&acirc;y Vực?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người nọ giật m&igrave;nh, thấy tr&ecirc;n b&agrave;n cờ ph&iacute;a t&acirc;y c&ograve;n một chỗ trống lớn, n&ecirc;n lợi dụng b&ecirc;n kia đang chặn bắt, để lu&ocirc;n hai qu&acirc;n, chiếm lấy chỗ yếu, tuy bỏ nửa chừng nhưng biến được th&agrave;nh thế kh&ocirc;ng thắng kh&ocirc;ng bại. Người đ&oacute; được Qu&aacute;ch Tương một lời nhắc nhở, ngửng mặt l&ecirc;n trời cười một hồi d&agrave;i, lu&ocirc;n mồm: &quot;Hay lắm, hay lắm&quot;. N&oacute;i rồi hạ lu&ocirc;n mấy qu&acirc;n. Y chợt nghĩ ra c&oacute; người đang ở b&ecirc;n cạnh, n&ecirc;n n&eacute;m trường kiếm xuống đất, quay lại n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị cao nh&acirc;n n&agrave;o đ&oacute; đ&atilde; chỉ dạy, tại hạ thật l&agrave; cảm k&iacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi hướng về ph&iacute;a Qu&aacute;ch Tương đang ẩn n&aacute;u v&aacute;i ch&agrave;o một c&aacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy người n&agrave;y mặt d&agrave;i mắt s&acirc;u, th&acirc;n h&igrave;nh gầy guộc, độ chừng tr&ecirc;n dưới ba mươi tuổi. N&agrave;ng vốn dĩ kho&aacute;ng đạt, cũng kh&ocirc;ng tị hiềm nam nữ, n&ecirc;n từ trong bụi hoa bước ra, mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mới rồi được nghe ti&ecirc;n sinh nh&atilde; tấu, kh&ocirc;ng sơn điểu ngữ, b&aacute;ch cầm lai triều, thật lấy l&agrave;m bội phục. Lại thấy ti&ecirc;n sinh vạch đất th&agrave;nh b&agrave;n cờ, đen trắng giao phong, khiến cho phải say m&ecirc; theo d&otilde;i m&agrave; qu&ecirc;n cả h&igrave;nh h&agrave;i, n&ecirc;n nhịn kh&ocirc;ng được n&ecirc;n g&oacute;p một lời, xin rộng l&ograve;ng tha thứ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người nọ thấy Qu&aacute;ch Tương chỉ l&agrave; một c&ocirc; g&aacute;i tuổi trẻ, lấy l&agrave;m lạ l&ugrave;ng, nhưng nghe c&ocirc; n&oacute;i về tiếng đ&agrave;n, quả nhi&ecirc;n kh&ocirc;ng sai một mảy n&ecirc;n c&agrave;ng cao hứng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương th&ocirc;ng thạo cầm l&yacute;, nếu như kh&ocirc;ng từ chối, xin cho được nghe một điệu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mẹ t&ocirc;i tuy đ&atilde; dạy t&ocirc;i đ&aacute;nh đ&agrave;n, nhưng so với thần kỹ của ng&agrave;i, th&igrave; k&eacute;m xa lắm. Thế nhưng đ&atilde; được nghe tiếng đ&agrave;n kỳ diệu của ng&agrave;i, kh&ocirc;ng đ&aacute;p lễ một bản, th&igrave; xem ra kh&ocirc;ng phải lẽ. Được rồi, t&ocirc;i xin đ&agrave;n một bản, nhưng xin &ocirc;ng đừng cười nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người kia đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&acirc;u d&aacute;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai tay bưng c&acirc;y dao cầm[4], đưa tới trước mặt Qu&aacute;ch Tương. Qu&aacute;ch Tương thấy chiếc đ&agrave;n đ&oacute; đ&atilde; c&oacute; nhiều vết loang lổ, hiển nhi&ecirc;n đ&atilde; cũ lắm rồi, n&ecirc;n l&ecirc;n d&acirc;y xong bắt đầu gẩy một kh&uacute;c &quot;Khảo B&agrave;n&quot;. Thủ ph&aacute;p n&agrave;ng kh&ocirc;ng c&oacute; g&igrave; thần kỳ, nhưng người đ&oacute; nghe vừa mừng vừa sợ, nghe tiếng đ&agrave;n m&agrave; tưởng đến lời từ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Suối kia r&oacute;c r&aacute;ch vỗ b&agrave;n,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&ograve;ng ta thanh thản như l&agrave;n nước khe.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">D&ugrave; ai n&oacute;i tỉnh n&oacute;i m&ecirc;,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một m&igrave;nh m&igrave;nh biết chẳng hề nh&atilde;ng qu&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khảo b&agrave;n tại giản,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thạc nh&acirc;n chi khoan,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Độc mị ngụ ng&ocirc;n,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vĩnh thỉ vật huy&ecirc;n[5]&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&agrave;i từ đ&oacute; ở Thi Kinh m&agrave; ra, l&agrave; b&agrave;i ca của một ẩn sĩ n&oacute;i đại trượng phu đi du ngoạn trong khe suối, đi đ&acirc;u th&igrave; đi, tuy tịch mịch kh&ocirc;ng ai bầu bạn, mặt m&agrave;y tiều tụy, nhưng ch&iacute; hướng cao khiết, kh&ocirc;ng bao giờ thay đổi. Người đ&oacute; nghe tiếng đ&agrave;n n&oacute;i đ&uacute;ng t&acirc;m sự m&igrave;nh, kh&ocirc;ng thể kh&ocirc;ng cực kỳ cảm k&iacute;ch. Tiếng đ&agrave;n đ&atilde; dứt rồi m&agrave; y vẫn c&ograve;n đứng ngơ ngơ ngẩn ngẩn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nhẹ nh&agrave;ng để chiếc dao cầm xuống, quay m&igrave;nh đi ra khỏi rừng t&ugrave;ng, cất tiếng h&aacute;t:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vỗ b&agrave;n lồng lộng g&ograve; đ&acirc;y,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&ograve;ng ta son sắt như c&acirc;y trục n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">D&ugrave; người n&oacute;i tỉnh n&oacute;i say,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một m&igrave;nh m&igrave;nh biết ai hay l&agrave;m g&igrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khảo b&agrave;n tại lục,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thạc nh&acirc;n chi trục,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Độc mị độc t&uacute;c,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vĩnh thỉ vật c&aacute;o[6].</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng vẫy con lừa lại nhảy l&ecirc;n đi thẳng v&agrave;o trong khu rừng s&acirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng đi lại trong giang hồ đ&atilde; ba năm qua, từng gặp nhiều chuyện lạ l&ugrave;ng, người n&agrave;y d&ugrave;ng tiếng đ&agrave;n để gọi chim, lại vạch đất th&agrave;nh b&agrave;n tự m&igrave;nh đ&aacute;nh cờ, với n&agrave;ng cũng chẳng qua như m&acirc;y kh&oacute;i trước mặt, c&aacute;nh b&egrave;o l&agrave;n gi&oacute; khi tụ khi tan, qua đi kh&ocirc;ng để lại dấu vết g&igrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lại hai ng&agrave;y qua, bấm đốt ng&oacute;n tay t&iacute;nh ra từ h&ocirc;m n&agrave;ng tiến v&agrave;o l&agrave;m huy&ecirc;n n&aacute;o ch&ugrave;a Thiếu L&acirc;m đ&atilde; được mười ng&agrave;y, cũng l&agrave; ng&agrave;y C&ocirc;n L&ocirc;n Tam Th&aacute;nh ước định đến ch&ugrave;a giảo nghiệm v&otilde; c&ocirc;ng. Qu&aacute;ch Tương chưa nghĩ ra c&aacute;ch n&agrave;o để c&oacute; thể lẻn v&agrave;o ch&ugrave;a xem tr&ograve; vui, nghĩ thầm: &quot;Mẹ ta chuyện g&igrave; cũng chỉ liếc mắt một c&aacute;i, l&agrave; đ&atilde; nghĩ ra ngay mười bảy, mười t&aacute;m diệu kế. Sao ta ngu đến vậy, đến một kế cũng nghĩ kh&ocirc;ng ra. Được rồi, kh&ocirc;ng cần biết, cứ đến b&ecirc;n ngo&agrave;i ch&ugrave;a Thiếu L&acirc;m xem x&eacute;t rồi h&atilde;y hay. C&oacute; khi bọn họ mắc đối ph&oacute; ngoại địch khẩn cấp, n&ecirc;n qu&ecirc;n bẵng ngăn ta l&ecirc;n ch&ugrave;a kh&ocirc;ng chừng.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng ăn qua loa mấy m&oacute;n lương kh&ocirc;, lại cưỡi con lừa đen nhằm hướng ch&ugrave;a Thiếu L&acirc;m. Khi n&agrave;ng c&ograve;n c&aacute;ch ch&ugrave;a độ chừng mươi dặm, bỗng nghe tiếng v&oacute; ngựa dồn dập, từ ph&iacute;a sơn đạo b&ecirc;n tr&aacute;i c&oacute; ba người nối đu&ocirc;i nhau chạy tới. Ba con ngựa bước ch&acirc;n nhanh nhẹn, chỉ trong chớp mắt đ&atilde; vượt ngang b&ecirc;n Qu&aacute;ch Tương, chạy thẳng l&ecirc;n ph&iacute;a ch&ugrave;a Thiếu L&acirc;m. Tr&ecirc;n lưng ngựa l&agrave; ba người trạc độ tr&ecirc;n dưới năm mươi, mặc &aacute;o ngắn bằng vải xanh, tr&ecirc;n y&ecirc;n ngựa người n&agrave;o cũng mang theo t&uacute;i vải đựng binh kh&iacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương chợt động t&acirc;m, nghĩ thầm: &quot;Ba người n&agrave;y đều c&oacute; v&otilde; c&ocirc;ng, h&ocirc;m nay lại đeo binh kh&iacute; l&ecirc;n ch&ugrave;a Thiếu L&acirc;m, hẳn l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh rồi! Nếu ta chậm một bước, th&igrave; chắc kh&ocirc;ng được coi c&aacute;i m&agrave;n vui n&agrave;y.&quot; N&agrave;ng giơ tay đ&aacute;nh l&ecirc;n m&ocirc;ng con lừa một c&aacute;i, con vật ngửng đầu h&iacute; một hồi d&agrave;i, lao đầu chạy lẽo đẽo theo ba con ngựa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Những người cưỡi ngựa ra roi, ba con ngựa chạy thẳng l&ecirc;n n&uacute;i, cước lực thật nhanh, chỉ nh&aacute;y mắt đ&atilde; bỏ con lừa của Qu&aacute;ch Tương một qu&atilde;ng thật xa, kh&ocirc;ng c&aacute;ch g&igrave; đuổi kịp. Một &ocirc;ng gi&agrave; quay đầu lại nh&igrave;n Qu&aacute;ch Tương một c&aacute;i, tr&ecirc;n mặt lộ vẻ lạ l&ugrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lại ra sức chạy theo hai, ba dặm nữa, nhưng tung t&iacute;ch ba con ngựa kia kh&ocirc;ng c&ograve;n thấy đ&acirc;u. Con lừa chạy một hồi l&acirc;u, miệng thở ph&igrave; ph&ograve;, xem chừng kh&ocirc;ng c&ograve;n chịu nổi. Qu&aacute;ch Tương mắng n&oacute;: &quot;Thật l&agrave; đồ s&uacute;c sinh kh&ocirc;ng ra giống g&igrave;, b&igrave;nh thời th&igrave; nổi chứng chạy loạn l&ecirc;n, l&uacute;c c&ocirc; nương cần ngươi, th&igrave; ngươi lại chạy kh&ocirc;ng bằng ai&quot;. N&agrave;ng thấy c&oacute; giục n&oacute; cũng chẳng đến đ&acirc;u, n&ecirc;n dừng lại một t&ograve;a thạch đ&igrave;nh b&ecirc;n đường nghỉ ngơi một l&aacute;t, để cho con lừa xuống suối b&ecirc;n cạnh uống nước. Một l&uacute;c sau, bỗng nghe tiếng v&oacute; ngựa vang l&ecirc;n, ba con ngựa hồi n&atilde;y lại qua một kh&uacute;c quanh, chạy ngược trở lại. Qu&aacute;ch Tương lấy l&agrave;m lạ: &quot;Sao ba người n&agrave;y vừa mới l&ecirc;n đ&atilde; quay trở lại, kh&ocirc;ng lẽ họ chịu kh&ocirc;ng nổi một đ&ograve;n hay sao?&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ba con ngựa tung v&oacute;, dựng bờm chạy thẳng đến t&ograve;a thạch đ&igrave;nh, ba người tr&ecirc;n ngựa xoay m&igrave;nh nhảy xuống. Qu&aacute;ch Tương nh&igrave;n ba người n&agrave;y, thấy một người th&acirc;n h&igrave;nh thấp l&ugrave;n, mặt đỏ như chu sa, mũi đỏ như kẻ nghiện rượu, nhưng miệng cười hề hề xem chừng &ocirc;n h&ograve;a, dễ th&acirc;n cận; một người th&igrave; gầy cao như c&acirc;y tre miễu, mặt m&agrave;y xanh xao, trong m&agrave;u trắng c&oacute; ẩn m&agrave;u xanh lục, tưởng như cả đời kh&ocirc;ng ra nắng bao giờ. Hai người n&agrave;y th&acirc;n h&igrave;nh dung mạo, kh&ocirc;ng c&oacute; chỗ n&agrave;o giống nhau. Người thứ ba tướng mạo b&igrave;nh b&igrave;nh kh&ocirc;ng c&oacute; g&igrave; lạ, chỉ c&oacute; da mặt v&agrave;ng vọt, c&oacute; vẻ như người đang bệnh hoạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&ograve;ng hiếu kỳ của Qu&aacute;ch Tương nổi l&ecirc;n, n&ecirc;n hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- N&agrave;y ba vị l&atilde;o ti&ecirc;n sinh, qu&iacute; vị đ&atilde; đến ch&ugrave;a Thiếu L&acirc;m chưa? Sao vừa mới l&ecirc;n đ&atilde; trở xuống?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&atilde;o gi&agrave; mặt xanh lườm n&agrave;ng một c&aacute;i, xem chừng kh&oacute; chịu v&igrave; c&ocirc; đ&atilde; hỏi lăng nhăng. &Ocirc;ng gi&agrave; thấp l&ugrave;n mũi đỏ, mặt hồng h&agrave;o cười hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sao c&ocirc; nương biết l&agrave; ch&uacute;ng t&ocirc;i l&ecirc;n ch&ugrave;a Thiếu L&acirc;m?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Từ đ&acirc;y trở l&ecirc;n, kh&ocirc;ng l&ecirc;n ch&ugrave;a Thiếu L&acirc;m th&igrave; c&ograve;n đi đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt đỏ gật đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; n&oacute;i thế cũng đ&uacute;ng. C&ograve;n c&ocirc; nương đang định đi đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c &ocirc;ng l&ecirc;n ch&ugrave;a Thiếu L&acirc;m th&igrave; t&ocirc;i cũng l&ecirc;n ch&ugrave;a Thiếu L&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&ugrave;a Thiếu L&acirc;m xưa nay kh&ocirc;ng để đ&agrave;n b&agrave; con g&aacute;i bước ch&acirc;n v&agrave;o sơn m&ocirc;n một bước, lại kh&ocirc;ng cho người ngo&agrave;i đeo binh kh&iacute; v&agrave;o ch&ugrave;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Giọng n&oacute;i c&oacute; vẻ ngạo mạn, th&acirc;n h&igrave;nh &ocirc;ng ta lại cao n&ecirc;n nh&igrave;n qua đầu n&agrave;ng, chứ kh&ocirc;ng nh&igrave;n v&agrave;o n&agrave;ng lấy một lần. Qu&aacute;ch Tương trong l&ograve;ng bực m&igrave;nh, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế sao c&aacute;c &ocirc;ng đeo binh kh&iacute; th&igrave; được? C&aacute;i bao vải ở b&ecirc;n cạnh y&ecirc;n ngựa kia, chẳng phải để binh kh&iacute; th&igrave; l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&atilde;o gi&agrave; mặt xanh lạnh l&ugrave;ng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi sao lại s&aacute;nh được với bọn ta?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười khẩy một tiếng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ba người c&aacute;c &ocirc;ng th&igrave; l&agrave; c&aacute;i g&igrave;? Ở đ&acirc;u m&agrave; ph&aacute;ch lối vậy? C&ocirc;n L&ocirc;n Tam Th&aacute;nh đ&atilde; đấu với c&aacute;c h&ograve;a thượng ch&ugrave;a Thiếu L&acirc;m chưa? Ai thắng ai bại?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ba &ocirc;ng gi&agrave; đ&oacute; vẻ mặt lập tức hơi biến sắc. &Ocirc;ng gi&agrave; mặt đỏ hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tiểu c&ocirc; nương, l&agrave;m sao c&ocirc; biết được chuyện C&ocirc;n L&ocirc;n Tam Th&aacute;nh?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tự nhi&ecirc;n t&ocirc;i phải biết chứ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh bất thần tiến l&ecirc;n một bước, gay gắt n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi họ g&igrave;? M&ocirc;n hạ của ai? Đến Thiếu L&acirc;m tự l&agrave;m g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương v&ecirc;nh mặt l&ecirc;n, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng hỏi l&agrave;m g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh t&iacute;nh t&igrave;nh n&oacute;ng nảy, giơ tay l&ecirc;n, định t&aacute;t cho c&ocirc; g&aacute;i một c&aacute;i, nhưng nghĩ ra ỷ lớn ăn hiếp nhỏ, đ&agrave;n &ocirc;ng bắt nạt đ&agrave;n b&agrave; kh&ocirc;ng vẻ vang g&igrave;, m&igrave;nh l&agrave; hạng người như thế kh&ocirc;ng lẽ hơn thua với một c&ocirc; b&eacute; con? Th&acirc;n h&igrave;nh hơi nh&iacute;ch một t&iacute;, y giơ tay chộp lấy thanh đoản kiếm Qu&aacute;ch Tương đeo ở b&ecirc;n h&ocirc;ng. &Ocirc;ng ta ra tay nhanh đến nỗi kh&ocirc;ng ai c&oacute; thể lường được, Qu&aacute;ch Tương chỉ thấy một l&agrave;n gi&oacute; nhẹ v&egrave;o qua, một b&oacute;ng người thấp tho&aacute;ng, bội kiếm đ&atilde; bị &ocirc;ng ta lấy mất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng kh&ocirc;ng kịp đề ph&ograve;ng, lại kh&ocirc;ng ngờ &ocirc;ng gi&agrave; l&agrave;m thế, từ trước đến nay h&agrave;nh tẩu giang hồ chưa từng gặp phải bao giờ. Kỳ thực với v&otilde; c&ocirc;ng duyệt lịch như n&agrave;ng, nếu tại giang hồ đi lại quả thực hết sức bất lợi, nhưng trong v&otilde; l&acirc;m, mười người th&igrave; đến t&aacute;m ch&iacute;n biết n&agrave;ng l&agrave; con g&aacute;i của Qu&aacute;ch Tĩnh, Ho&agrave;ng Dung. Đến sau khi Dương Qu&aacute; truyền tin ch&uacute;c mừng sinh nhật, b&agrave;ng m&ocirc;n tả đạo ai ai cũng đều biết, n&ecirc;n nếu kh&ocirc;ng nể mặt Qu&aacute;ch Tĩnh, Ho&agrave;ng Dung th&igrave; cũng phải nể mặt Dương Qu&aacute;. Hơn nữa ngo&agrave;i dung nhan mỹ lệ, t&iacute;nh c&ocirc; lại h&agrave;o sảng hiếu kh&aacute;ch, nơi thị tứ d&ugrave; người k&eacute;o xe, b&aacute;n tương cho đến đồ tể, phu phen, nếu đ&atilde; gặp cũng đều mua rượu c&ugrave;ng uống với họ một ch&eacute;n. Th&agrave;nh thử tr&ecirc;n chốn giang hồ tuy phong ba hiểm &aacute;c, n&agrave;ng cũng đều tai qua nạn khỏi, gặp dữ h&oacute;a l&agrave;nh, trước nay chưa bị l&agrave;m kh&oacute; dễ bao giờ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&acirc;y giờ bỗng nhi&ecirc;n bị &ocirc;ng gi&agrave; mặt xanh đoạt mất kiếm, nhất thời c&ocirc; kh&ocirc;ng biết phải l&agrave;m sao, nếu tiến l&ecirc;n đoạt lại, th&igrave; biết v&otilde; c&ocirc;ng kh&ocirc;ng b&igrave; kịp, c&ograve;n nếu bỏ qua, th&igrave; trong l&ograve;ng chịu sao được?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh d&ugrave;ng ng&oacute;n tay giữa v&agrave; ng&oacute;n tay trỏ tay tr&aacute;i kẹp lấy bao đoản kiếm, lạnh l&ugrave;ng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thanh kiếm của ngươi, ta tạm giữ. Ngươi c&oacute; gan v&ocirc; lễ với ta như vậy, chắc l&agrave; tại cha mẹ hay sư trưởng thiếu dạy dỗ. Ngươi bảo họ đến gặp ta để lấy lại thanh kiếm, để ta tử tế bảo cho họ biết, hầu dạy cho cha mẹ sư trưởng của ngươi một trận.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&acirc;u n&oacute;i đ&oacute; l&agrave;m cho Qu&aacute;ch Tương tức giận đến nỗi mặt đỏ bừng. Nghe &ocirc;ng ta n&oacute;i đ&atilde; coi n&agrave;ng l&agrave; một đứa trẻ con nh&agrave; mất dạy, n&ecirc;n nghĩ thầm: &quot;Giỏi nhỉ! Ngươi mắng chửi ta, lại c&ograve;n d&aacute;m chửi cả &ocirc;ng ngoại v&agrave; cha mẹ ta, chắc ngươi c&oacute; t&agrave;i th&ocirc;ng thi&ecirc;n, n&ecirc;n mới ph&aacute;ch lối đến mức kh&ocirc;ng sợ trời kh&ocirc;ng sợ đất g&igrave; cả&quot;..</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng định thần, cố nhịn, hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ecirc;n &ocirc;ng l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh hừ một tiếng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i g&igrave; m&agrave; lại &quot;T&ecirc;n &ocirc;ng l&agrave; g&igrave;?&quot; Ta dạy cho ngươi biết, ngươi phải hỏi l&agrave; :&quot;Xin thỉnh gi&aacute;o t&ocirc;n t&iacute;nh đại danh của l&atilde;o tiền bối?&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương giận n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i chỉ hỏi cho biết t&ecirc;n &ocirc;ng vậy th&ocirc;i. &Ocirc;ng kh&ocirc;ng n&oacute;i th&igrave; th&ocirc;i, ai phải cầu cạnh? Thanh kiếm đ&oacute; c&oacute; đ&aacute;ng g&igrave; đ&acirc;u? &Ocirc;ng gi&agrave; đầu kh&ocirc;ng biết xấu, ăn cắp, ăn cướp của người kh&aacute;c, t&ocirc;i kh&ocirc;ng th&egrave;m lấy lại đ&acirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong n&agrave;ng quay lưng, toan đi ra khỏi thạch đ&igrave;nh. Bỗng thấy trước mặt một b&oacute;ng thấp tho&aacute;ng, &ocirc;ng gi&agrave; l&ugrave;n mặt đỏ đ&atilde; chặn ngay trước mặt, cười h&igrave; h&igrave;:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con g&aacute;i t&iacute;nh t&igrave;nh kh&ocirc;ng n&ecirc;n n&oacute;ng nảy như vậy, sau n&agrave;y về l&agrave;m d&acirc;u nh&agrave; người ta, đ&acirc;u c&oacute; thể giữ m&atilde;i c&aacute;i t&iacute;nh trẻ con như thế? Được rồi, để ta n&oacute;i cho c&ocirc; nghe, ch&uacute;ng t&ocirc;i l&agrave; ba người sư huynh đệ, mấy ng&agrave;y qua đường x&aacute; xa x&ocirc;i từ T&acirc;y Vực đến Trung Nguy&ecirc;n ...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương dẩu m&ocirc;i đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng kh&ocirc;ng cần n&oacute;i t&ocirc;i cũng biết. Đất Thần Ch&acirc;u Trung Nguy&ecirc;n ch&uacute;ng t&ocirc;i, kh&ocirc;ng ai biết ba người c&aacute;c &ocirc;ng bao giờ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ba &ocirc;ng gi&agrave; nh&igrave;n nhau, &ocirc;ng gi&agrave; mặt đỏ hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Xin hỏi c&ocirc; nương, t&ocirc;n sư l&agrave; ai thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương khi ở ch&ugrave;a Thiếu L&acirc;m kh&ocirc;ng chịu n&oacute;i t&ecirc;n cha mẹ của m&igrave;nh, l&uacute;c n&agrave;y trong bụng tức lắm, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Cha t&ocirc;i họ Qu&aacute;ch, đơn danh một chữ Tĩnh. Mẹ t&ocirc;i họ Ho&agrave;ng, đơn danh một chữ Dung. T&ocirc;i kh&ocirc;ng c&oacute; sư phụ, chỉ nhờ cha mẹ dạy qua loa th&ocirc;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ba &ocirc;ng gi&agrave; lại nh&igrave;n nhau. &Ocirc;ng gi&agrave; mặt xanh lẩm bẩm: &quot;Qu&aacute;ch Tĩnh? Ho&agrave;ng Dung? Họ thuộc m&ocirc;n ph&aacute;i n&agrave;o thế nhỉ? L&agrave; đệ tử của ai?&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương l&uacute;c n&agrave;y trong l&ograve;ng bực kh&ocirc;ng sao chịu nổi, nghĩ thầm t&ecirc;n tuổi cha mẹ m&igrave;nh ai ai cũng biết, đừng n&oacute;i chi người trong v&otilde; l&acirc;m, m&agrave; đến d&acirc;n thường, ai kh&ocirc;ng biết Qu&aacute;ch đại hiệp nghĩa thủ Tương Dương? Thế nhưng nh&igrave;n thần sắc của ba &ocirc;ng gi&agrave; n&agrave;y, kh&ocirc;ng phải l&agrave; giả vờ kh&ocirc;ng biết. N&agrave;ng nghĩ một hồi, chợt vỡ lẽ: &quot;Ba g&atilde; C&ocirc;n L&ocirc;n Tam Th&aacute;nh từ T&acirc;y vực xa x&ocirc;i, trước nay chưa hề v&agrave;o Trung Thổ. Tuy họ v&otilde; c&ocirc;ng cao như thế nhưng cha mẹ ta cũng chưa đề cập đến t&ecirc;n tuổi họ bao giờ, cho n&ecirc;n kh&ocirc;ng biết được t&ecirc;n hai &ocirc;ng b&agrave; thật cũng kh&ocirc;ng c&oacute; g&igrave; lạ. Ắt l&agrave; họ ở s&acirc;u trong n&uacute;i C&ocirc;n L&ocirc;n, chăm chỉ tập luyện v&otilde; c&ocirc;ng, xưa nay kh&ocirc;ng hề nghe đến chuyện b&ecirc;n ngo&agrave;i.&quot; Nghĩ như vậy, trong l&ograve;ng mới thấy nhẹ nh&otilde;m, nộ kh&iacute; ti&ecirc;u tan. N&agrave;ng vốn dĩ kh&ocirc;ng phải l&agrave; người t&iacute;nh t&igrave;nh nhỏ nhặt, chấp nhất, n&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i họ Qu&aacute;ch, t&ecirc;n Tương, l&agrave; chữ Tương trong t&ecirc;n th&agrave;nh Tương Dương đ&oacute;. Hay lắm, t&ocirc;i đ&atilde; cho c&aacute;c &ocirc;ng biết t&ecirc;n t&ocirc;i rồi. Xin hỏi ba vị l&atilde;o ti&ecirc;n sinh t&ocirc;n t&iacute;nh đại đanh l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt đỏ cười h&igrave; h&igrave; n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phải đ&oacute;, c&ocirc; b&eacute; con ngoan lắm, dạy một lần l&agrave; nhớ ngay, thế mới l&agrave; đạo l&yacute; t&ocirc;n trọng bậc trưởng bối.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi chỉ &ocirc;ng gi&agrave; mặt v&agrave;ng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị n&agrave;y l&agrave; đại sư ca của anh em ta, họ Phan, t&ecirc;n l&agrave; Thi&ecirc;n Canh. Ta l&agrave; nhị sư huynh, họ Phương, t&ecirc;n gọi Phương Thi&ecirc;n Lao.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tay chỉ &ocirc;ng gi&agrave; mặt xanh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ograve;n đ&acirc;y tam sư đệ, họ Vệ, t&ecirc;n l&agrave; Thi&ecirc;n Vọng. Bọn ta c&oacute; ba anh em, trong t&ecirc;n ai cũng c&oacute; chữ Thi&ecirc;n cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương &agrave; một tiếng, nhẩm lại một lượt, rồi hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c &ocirc;ng chẳng hay đ&atilde; l&ecirc;n đến ch&ugrave;a Thiếu L&acirc;m chưa? C&aacute;c &ocirc;ng đ&atilde; tỉ th&iacute; v&otilde; nghệ với c&aacute;c h&ograve;a thượng tr&ecirc;n ch&ugrave;a chưa? Hai b&ecirc;n, b&ecirc;n n&agrave;o v&otilde; c&ocirc;ng cao cường hơn?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt xanh Vệ Thi&ecirc;n Vọng hừ một tiếng, gay gắt hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sao c&aacute;i g&igrave; ngươi cũng biết thế? Bọn ta muốn tỉ th&iacute; v&otilde; nghệ với c&aacute;c h&ograve;a thượng ch&ugrave;a Thiếu L&acirc;m, thi&ecirc;n hạ &iacute;t ai biết, l&agrave;m sao ngươi lại biết? N&oacute;i mau, n&oacute;i mau!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi &ocirc;ng ta sấn tới trước mặt Qu&aacute;ch Tương, tay phải nắm chặt, hung hăng trừng mắt nh&igrave;n c&ocirc; g&aacute;i. Qu&aacute;ch Tương nghĩ thầm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đời n&agrave;o ta để cho y uy hiếp? C&oacute; n&oacute;i cho ngươi hay cũng kh&ocirc;ng c&oacute; g&igrave; quan trọng, nhưng ngươi c&agrave;ng dữ dằn, ta c&agrave;ng kh&ocirc;ng th&egrave;m n&oacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc; trợn mắt nh&igrave;n y một c&aacute;i, thản nhi&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ecirc;n của &ocirc;ng nghe kh&ocirc;ng hay, sao kh&ocirc;ng đổi th&agrave;nh &quot;Thi&ecirc;n &Aacute;c&quot; c&oacute; hơn kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng giận dữ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hung thần &aacute;c s&aacute;t như &ocirc;ng tr&ecirc;n đời &iacute;t c&oacute;, đ&atilde; giựt mất kiếm của t&ocirc;i, lại c&ograve;n hầm hầm, c&oacute; phải l&agrave; sao Thi&ecirc;n &Aacute;c tr&ecirc;n trời xuống đ&acirc;y hay kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng gầm gừ trong họng mấy tiếng như con th&uacute;, ngực bỗng phồng l&ecirc;n thật lớn, t&oacute;c v&agrave; l&ocirc;ng m&agrave;y như muốn dựng cả l&ecirc;n. &Ocirc;ng gi&agrave; mặt đỏ Phương Thi&ecirc;n Lao vội k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tam đệ, kh&ocirc;ng được nổi n&oacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lập tức nắm vai Qu&aacute;ch Tương l&ocirc;i về ph&iacute;a sau v&agrave;i thước, lấy th&acirc;n m&igrave;nh chắn giữa hai người. Qu&aacute;ch Tương thấy Vệ Thi&ecirc;n Vọng như thế, nếu y quả thực ra tay ắt n&agrave;ng kh&ocirc;ng sao chống đỡ được, trong l&ograve;ng kh&ocirc;ng khỏi hơi khiếp sợ. Vệ Thi&ecirc;n Vọng tay phải r&uacute;t kiếm ra khỏi bao, tay tr&aacute;i hai ng&oacute;n tay kẹp lấy mũi kiếm, vận sức v&agrave;o đốt ng&oacute;n tay, nghe c&aacute;ch một tiếng, đầu kiếm đ&atilde; g&atilde;y l&agrave;m hai, rồi bỏ cả hai mảnh trở lại bao, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ai th&egrave;m lấy c&aacute;i thứ kiếm v&ocirc; dụng của ngươi l&agrave;m g&igrave;!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy k&igrave;nh lực ng&oacute;n tay của y lợi hại như thế kh&ocirc;ng khỏi h&atilde;i sợ. Vệ Thi&ecirc;n Vọng thấy n&agrave;ng biến sắc, đắc &yacute; ngửng đầu cười ha hả. Tiếng cười của y &ugrave; cả tai, rung động đến nỗi ng&oacute;i tr&ecirc;n m&aacute;i đ&igrave;nh cũng k&ecirc;u l&aacute;ch c&aacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trong khi tiếng vang chưa dứt, bỗng dưng m&aacute;i đ&igrave;nh thủng một mảng lớn, một vật g&igrave; đ&oacute; rơi bịch xuống đất. Mọi người ai nấy giật m&igrave;nh, đến Vệ Thi&ecirc;n Vọng cũng kh&ocirc;ng tưởng nổi y nội lực sung t&uacute;c đến mức tiếng cười c&oacute; thể l&agrave;m chấn động m&aacute;i ng&oacute;i. Thực ra tiếng cười của y kh&ocirc;ng h&agrave;m &yacute; vui vẻ m&agrave; chỉ l&agrave; vận k&igrave;nh k&ecirc;u l&ecirc;n &quot;ha ha&quot; v&agrave;i tiếng, vậy m&agrave; l&agrave;m thủng được m&aacute;i ng&oacute;i, khiến y vừa mừng vừa sợ, kh&ocirc;ng ngờ gần đ&acirc;y nội lực của m&igrave;nh lại tiến bộ đến thế. Thế nhưng nh&igrave;n lại c&aacute;i vật vừa rơi xuống lại c&agrave;ng sợ hơn, chỉ thấy đ&oacute; l&agrave; một h&aacute;n tử trung ni&ecirc;n mặc &aacute;o trắng, hai tay &ocirc;m một c&acirc;y dao cầm, lăn quay dưới đất, mắt vẫn nhắm nghiền ngủ say sưa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương vui mừng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ồ, &ocirc;ng cũng ở đ&acirc;y sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&oacute;a ra người đ&agrave;n &ocirc;ng n&agrave;y ch&iacute;nh l&agrave; người ngồi đ&aacute;nh đ&agrave;n n&agrave;ng gặp mấy h&ocirc;m trước tr&ecirc;n n&uacute;i. Người đ&oacute; nghe tiếng Qu&aacute;ch Tương, vội nhỏm dậy, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương, t&ocirc;i đang đi t&igrave;m c&ocirc;, kh&ocirc;ng ngờ lại gặp nhau ở đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng đi kiếm t&ocirc;i c&oacute; chuyện g&igrave; thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i qu&ecirc;n kh&ocirc;ng thỉnh gi&aacute;o t&ocirc;n t&iacute;nh đại danh của c&ocirc; nương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i g&igrave; m&agrave; t&ocirc;n t&iacute;nh đại danh? Những lời m&agrave;u m&egrave; giả dối đ&oacute; t&ocirc;i kh&ocirc;ng th&iacute;ch ch&uacute;t n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người nọ khựng lại, cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&uacute;ng lắm, đ&uacute;ng lắm. C&agrave;ng để &yacute; đến hư văn, loại người đ&oacute; c&agrave;ng kh&ocirc;ng c&oacute; ch&acirc;n t&agrave;i thực học. Hạng đ&oacute; đi l&ograve;e mấy người nh&agrave; qu&ecirc;, chứ l&agrave;m được chuyện g&igrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y vừa n&oacute;i vừa liếc nh&igrave;n Vệ Thi&ecirc;n Vọng, cười khẩy mấy tiếng. Qu&aacute;ch Tương mừng lắm, kh&ocirc;ng ngờ người n&agrave;y biết hết mọi chuyện, cố &yacute; gi&uacute;p đỡ m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng thấy người nọ liếc m&igrave;nh, mặt y đ&atilde; xanh lại c&agrave;ng xanh th&ecirc;m, lạnh l&ugrave;ng hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;n gi&aacute; l&agrave; ai?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người nọ kh&ocirc;ng th&egrave;m để &yacute; tới y, n&oacute;i với Qu&aacute;ch Tương:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc; nương, t&ecirc;n c&ocirc; l&agrave; g&igrave; nhỉ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i họ Qu&aacute;ch, đơn danh Tương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người nọ vỗ tay:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- A, thật đ&uacute;ng l&agrave; c&oacute; mắt m&agrave; kh&ocirc;ng thấy n&uacute;i Th&aacute;i Sơn, h&oacute;a ra đ&acirc;y l&agrave; Qu&aacute;ch đại c&ocirc; nương, người m&agrave; bốn bể ai cũng nghe danh. Lệnh t&ocirc;n l&agrave; Qu&aacute;ch Tĩnh Qu&aacute;ch đại hiệp, lệnh đường Ho&agrave;ng Dung Ho&agrave;ng nữ hiệp, trừ bọn v&ocirc; tri v&ocirc; thức, kh&ocirc;ng biết trời trăng g&igrave; mới kh&ocirc;ng biết đến, chứ tr&ecirc;n giang hồ c&oacute; ai kh&ocirc;ng hay, c&oacute; ai kh&ocirc;ng biết? Hai vị đ&oacute; văn v&otilde; song to&agrave;n, đao thương kiếm k&iacute;ch, quyền chưởng kh&iacute; c&ocirc;ng, cầm kỳ thư họa, thi từ ca ph&uacute;, xưa nay kh&ocirc;ng m&ocirc;n g&igrave; kh&ocirc;ng hơn người, ai m&agrave; b&igrave; kịp. Ha ha, chỉ c&oacute; c&aacute;i bọn thong manh, mới kh&ocirc;ng nghe đến tiếng vang vọng của hai &ocirc;ng b&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương trong l&ograve;ng thật vui, nghĩ thầm : &quot;Th&igrave; ra ngươi ẩn n&aacute;u ở tr&ecirc;n n&oacute;c đ&igrave;nh, đ&atilde; nghe thấy những g&igrave; ta n&oacute;i với ba người n&agrave;y rồi. Xem ra ch&iacute;nh ngươi cũng chẳng biết g&igrave; về cha mẹ ta. Ta thứ hai, vậy m&agrave; gọi l&agrave; đại c&ocirc; nương, lại bảo cha ta cũng th&ocirc;ng cầm kỳ thư họa, thi từ ca ph&uacute;, thật đ&aacute;ng nực cười&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghĩ như thế n&ecirc;n n&agrave;ng cười hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế c&ograve;n t&ecirc;n &ocirc;ng l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người kia đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i họ H&agrave;, t&ecirc;n l&agrave; T&uacute;c Đạo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; T&uacute;c Đạo, H&agrave; T&uacute;c Đạo ư? C&aacute;i t&ecirc;n &ocirc;ng sao khi&ecirc;m tốn qu&aacute; vậy.[7]</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- So với c&aacute;i bọn thi&ecirc;n g&igrave;, địa g&igrave; kho&aacute;c l&aacute;c kh&ocirc;ng biết xấu hổ, c&aacute;i thứ nh&atilde;i nh&eacute;p cuồng vọng tự t&ocirc;n, th&ugrave;ng rỗng k&ecirc;u to chỉ tổ l&agrave;m người ta buồn mửa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo li&ecirc;n tiếp mỉa mai ch&acirc;m chọc bọn ba anh em Vệ Thi&ecirc;n Vọng. Ba người thấy y &eacute;p sập n&oacute;c đ&igrave;nh rơi xuống, hẳn kh&ocirc;ng tầm thường, l&uacute;c đầu họ c&ograve;n cố nhịn, để xem bạch y qu&aacute;i kh&aacute;ch n&agrave;y l&agrave; hạng người n&agrave;o. Thế nhưng c&agrave;ng nghe lời lẽ y c&agrave;ng chua cay, Vệ Thi&ecirc;n Vọng thấy kh&ocirc;ng c&ograve;n chịu nổi, vung tay nhắm ngay m&aacute; tr&aacute;i của y đ&aacute;nh ra một chưởng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo hơi c&uacute;i đầu, luồn dưới c&aacute;nh tay y m&agrave; l&aacute;ch qua. Vệ Thi&ecirc;n Vọng chỉ thấy cổ tay tr&aacute;i hơi t&ecirc;, thanh kiếm trong tay y đ&atilde; bị đoạt mất. L&uacute;c Vệ Thi&ecirc;n Vọng đoạt kiếm của Qu&aacute;ch Tương, th&acirc;n ph&aacute;p thật l&agrave; nhanh, khiến người ta kh&ocirc;ng sao nh&igrave;n r&otilde;, nhưng l&uacute;c n&agrave;y H&agrave; T&uacute;c Đạo l&aacute;ch qua nhẹ nh&agrave;ng thuận tay lấy lại thanh đoản kiếm, từ th&acirc;n ph&aacute;p đến cử động, kh&ocirc;ng c&oacute; điểm n&agrave;o kh&aacute;c thường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng kinh h&atilde;i, tiến l&ecirc;n một bước, mấy ng&oacute;n tay như c&aacute;i m&oacute;c, chộp lu&ocirc;n v&agrave;o vai y. H&agrave; T&uacute;c Đạo lại nghi&ecirc;ng người n&eacute; tr&aacute;nh, trảo đ&oacute; lướt qua th&acirc;n y. Phan Thi&ecirc;n Canh v&agrave; Phương Thi&ecirc;n Lao đột nhi&ecirc;n nhảy l&ugrave;i lại ra ngo&agrave;i đ&igrave;nh. Vệ Thi&ecirc;n Vọng tả quyền hữu chưởng đ&aacute;nh ra, tiếng gi&oacute; k&ecirc;u v&ugrave; v&ugrave;, chỉ gi&acirc;y l&aacute;t đ&atilde; đ&aacute;nh ra bảy t&aacute;m chi&ecirc;u liền. H&agrave; T&uacute;c Đạo n&eacute; tr&aacute;i l&aacute;ch phải, khiến đến vạt &aacute;o cũng kh&ocirc;ng chạm đến. Tay y cầm thanh đoản kiếm, đối với quyền chi&ecirc;u gi&oacute; t&aacute;p mưa sa của đối phương kh&ocirc;ng hề đỡ gạt, chỉ nhẹ nh&agrave;ng nghi&ecirc;ng người đ&atilde; l&agrave;m cho Vệ Thi&ecirc;n Vọng đ&aacute;nh hụt ra ngo&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với lứa tuổi của Qu&aacute;ch Tương, tuy v&otilde; thuật n&agrave;ng kh&ocirc;ng tinh th&acirc;m nhưng trong những người quen biết kh&ocirc;ng hiếm cao thủ v&agrave;o hạng nhất, kiến thức lại hơn người, thấy H&agrave; T&uacute;c Đạo cử động tr&ocirc;ng nhẹ nh&agrave;ng nhưng th&acirc;n ph&aacute;p hết sức xảo diệu, n&eacute; tr&aacute;nh những chi&ecirc;u thức cực kỳ cương m&atilde;nh của địch, v&otilde; c&ocirc;ng th&acirc;n ph&aacute;p th&agrave;nh ri&ecirc;ng một nh&agrave;, so với c&aacute;c m&ocirc;n ph&aacute;i ở Trung Thổ kh&ocirc;ng giống ai, c&agrave;ng xem c&agrave;ng thấy kỳ lạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng li&ecirc;n tiếp đ&aacute;nh ra hơn hai chục chi&ecirc;u vẫn kh&ocirc;ng &eacute;p được đối phương xuất thủ, hự một tiếng nhỏ, quyền ph&aacute;p bất ngờ đổi hẳn, xuất chi&ecirc;u chậm chạp, nhưng quyền lực nặng nề. Qu&aacute;ch Tương đứng ở b&ecirc;n trong đ&igrave;nh, cảm thấy quyền phong c&agrave;ng l&uacute;c c&agrave;ng nặng đẩy n&agrave;ng từng bước l&ugrave;i ra b&ecirc;n ngo&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c n&agrave;y H&agrave; T&uacute;c Đạo kh&ocirc;ng c&ograve;n c&oacute; thể chỉ n&eacute; tr&aacute;nh m&agrave; kh&ocirc;ng trả đ&ograve;n, y c&agrave;i đoản kiếm v&agrave;o d&acirc;y lưng, hai ch&acirc;n đứng vững lại, qu&aacute;t l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi biết ngạnh c&ocirc;ng, tưởng ta kh&ocirc;ng biết sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đợi Vệ Thi&ecirc;n Vọng hai chưởng đ&aacute;nh tới, tay tr&aacute;i phản k&iacute;ch lại một chưởng, lấy ngạnh c&ocirc;ng chống với ngạnh c&ocirc;ng, nghe b&igrave;nh một tiếng, Vệ Thi&ecirc;n Vọng th&acirc;n h&igrave;nh hơi lảo đảo, l&ugrave;i lại hai bước. H&agrave; T&uacute;c Đạo đứng nguy&ecirc;n một chỗ kh&ocirc;ng động đậy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng vẫn tự cho l&agrave; m&igrave;nh ngoại m&ocirc;n ngạnh c&ocirc;ng tr&ecirc;n đời &iacute;t ai b&igrave; kịp, n&agrave;o ngờ đối phương lấy cứng chống cứng kh&ocirc;ng hề mượn sức hay trổ t&agrave;i kh&eacute;o l&eacute;o, đẩy m&igrave;nh l&ugrave;i lại. Y trong l&ograve;ng kh&ocirc;ng phục, h&iacute;t một hơi, qu&aacute;t l&ecirc;n một tiếng, hai chưởng lại đ&aacute;nh ra. H&agrave; T&uacute;c Đạo cũng qu&aacute;t l&ecirc;n một tiếng, đ&aacute;nh lại một chưởng. Chỉ nghe tiếng ầm ầm li&ecirc;n tiếp, khiến cho lỗ hổng tr&ecirc;n m&aacute;i đ&igrave;nh đất đ&aacute; rơi xuống r&agrave;o r&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng l&ugrave;i lại bốn bước, sau c&ugrave;ng cũng đứng lại được. Y đỡ xong hai chưởng n&agrave;y, đầu t&oacute;c rồi b&ugrave;, hai mắt lồi ra, h&igrave;nh d&aacute;ng tr&ocirc;ng thật dễ sợ. Hai tay y &ocirc;m v&agrave;o đan điền, thở h&ugrave; h&ugrave; vận kh&iacute; mấy lần, ngực l&otilde;m v&agrave;o, bụng lại ph&igrave;nh ra như c&aacute;i trống, to&agrave;n th&acirc;n c&aacute;c khớp xương k&ecirc;u lốp cốp, từng bước từng bước chậm chậm bước về ph&iacute;a H&agrave; T&uacute;c Đạo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo thấy t&igrave;nh thế của y như vậy, kh&ocirc;ng d&aacute;m coi thường, điều vận ch&acirc;n kh&iacute;, đứng chờ thế của địch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vệ Thi&ecirc;n Vọng đi đến c&ograve;n c&aacute;ch đối phương chừng bốn năm thước, đ&atilde; tưởng ph&aacute;t chi&ecirc;u, n&agrave;o ngờ kh&ocirc;ng ngừng bước m&agrave; vẫn tiếp tục tiến th&ecirc;m hai bước nữa, đến đứng đối diện tưởng như hơi thở của nhau c&oacute; thể cảm thấy được, l&uacute;c ấy hai chưởng mới tung ra, một chưởng đ&aacute;nh v&agrave;o mặt, một chưởng lại đ&aacute;nh v&agrave;o bụng dưới. Lần n&agrave;y y ph&acirc;n ra hai chưởng, cốt để đối thủ phải ph&acirc;n lực ra l&agrave;m hai. Chi&ecirc;u thế cũng như chưởng lực đều thật l&agrave; h&ugrave;ng mạnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo cũng lập tức hai chưởng đưa ra, hai tay ch&eacute;o lại, tay tr&aacute;i y đỡ lấy tay tr&aacute;i địch thủ, tay phải y đỡ lấy tay phải của địch, nhưng chưởng lực lại ph&acirc;n th&agrave;nh một cương, một nhu. Vệ Thi&ecirc;n Vọng chỉ thấy chưởng của y đ&aacute;nh v&agrave;o bụng dưới địch như đ&aacute;nh v&agrave;o chỗ kh&ocirc;ng, c&ograve;n tay phải đ&aacute;nh v&agrave;o mặt th&igrave; như đụng phải tường đồng v&aacute;ch sắt, biết l&agrave; kh&ocirc;ng ổn, chỉ thấy m&igrave;nh bị một lực to lớn đ&aacute;nh v&agrave;o, khiến cả th&acirc;n h&igrave;nh y bị đẩy văng ra khỏi thạch đ&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kỳ n&agrave;y hai người lấy cứng chọi cứng, d&ugrave;ng lực chống lực, ai yếu hơn sẽ bị thương, kh&ocirc;ng thể n&agrave;o lấy g&igrave; ngoắt ngo&eacute;o. D&ugrave; cho Vệ Thi&ecirc;n Vọng c&oacute; gượng đứng được, hoặc một chi&ecirc;u đ&atilde; ng&atilde;, th&igrave; chưởng lực của ch&iacute;nh y phản k&iacute;ch trở lại, lại th&ecirc;m chưởng lực của H&agrave; T&uacute;c Đạo, ắt y thể n&agrave;o cũng phải hộc m&aacute;u tươi. Phan Thi&ecirc;n Canh v&agrave; Phương Thi&ecirc;n Lao c&ugrave;ng k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ra tay!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai người đồng thời nhảy tới, c&ugrave;ng chia nhau mỗi người nắm một c&aacute;nh tay của Vệ Thi&ecirc;n Vọng nhắc l&ecirc;n, c&oacute; thế mới ti&ecirc;u trừ được chưởng lực cương m&atilde;nh của H&agrave; T&uacute;c Đạo. Vệ Thi&ecirc;n Vọng tuy chưa bị thương, nhưng ruột gan đ&atilde; chấn động, xương cốt to&agrave;n th&acirc;n tưởng như n&aacute;t nhừ, thở kh&ocirc;ng ra hơi, kh&ocirc;ng đứng nổi. &Ocirc;ng gi&agrave; l&ugrave;n mặt đỏ Phương Thi&ecirc;n Lao thấy sư đệ bị một trận đau như thế, trong bụng vừa giận vừa sợ, nhưng mặt ngo&agrave;i vẫn cười h&igrave; h&igrave;, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c hạ chưởng lực mạnh như thế, quả thực tr&ecirc;n đời &iacute;t thấy, bội phục bội phục.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghĩ thầm: &quot;N&oacute;i đến chưởng lực cương m&atilde;nh h&ugrave;ng hậu, ai bằng được H&agrave;ng Long Thập B&aacute;t Chưởng của cha ta? Bọn C&ocirc;n L&ocirc;n Tam Th&aacute;nh c&aacute;c ngươi trốn l&aacute;nh ở chốn hoang sơn, ếch ngồi đ&aacute;y giếng, tưởng l&agrave; m&igrave;nh giỏi, rồi sẽ c&oacute; ng&agrave;y biết đến v&otilde; c&ocirc;ng Trung Thổ&quot;. N&agrave;ng nghĩ đến đ&acirc;y, trong l&ograve;ng hơi se lại, v&igrave; l&uacute;c n&agrave;ng muốn cho bọn Phương Thi&ecirc;n Lao biết đến v&otilde; c&ocirc;ng Trung Thổ, kh&ocirc;ng phải l&agrave; biết đến phụ th&acirc;n m&agrave; ch&iacute;nh l&agrave; Dương Qu&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ nghe Phương Thi&ecirc;n Lao n&oacute;i tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tiểu l&atilde;o nhi bất t&agrave;i, xin ra l&atilde;nh gi&aacute;o kiếm ph&aacute;p của c&aacute;c hạ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phương huynh đối xử với Qu&aacute;ch c&ocirc; nương thật nể nang, tại hạ kh&ocirc;ng tr&aacute;ch g&igrave; cả, ch&uacute;ng ta tỉ th&iacute; l&agrave;m g&igrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương ngạc nhi&ecirc;n: &quot;Ngươi cho g&atilde; họ Vệ kia một phen khổ sở, nguy&ecirc;n lai chỉ v&igrave; y đối với ta kh&ocirc;ng nể nang ư?&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phương Thi&ecirc;n Lao đến b&ecirc;n con ngựa y cưỡi, từ bao vải lấy ra một thanh trường kiếm, nghe một tiếng soẹt, đ&atilde; r&uacute;t ra khỏi vỏ, giơ ng&oacute;n tay b&uacute;ng v&agrave;o th&acirc;n kiếm một c&aacute;i, tiếng u u nổi l&ecirc;n một hồi l&acirc;u kh&ocirc;ng dứt. Khi kiếm đ&atilde; trong tay, nụ cười tr&ecirc;n m&ocirc;i lập tức biến mất, tay tr&aacute;i bắt kiếm quyết đưa ra trước mặt, ng&oacute;n tay đưa l&ecirc;n, tay phải cầm kiếm chỉ l&ecirc;n trời kh&ocirc;ng động đậy, ch&iacute;nh l&agrave; chi&ecirc;u &quot;Ti&ecirc;n Nh&acirc;n Chỉ Lộ&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu quả Phương huynh muốn động thủ, th&igrave; t&ocirc;i xin d&ugrave;ng đoản kiếm của Qu&aacute;ch c&ocirc; nương thử một v&agrave;i chi&ecirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y n&oacute;i rồi r&uacute;t ra nửa thanh kiếm g&atilde;y. Thanh kiếm đ&oacute; vốn đ&atilde; d&agrave;i kh&ocirc;ng qu&aacute; hai thước, sau khi Vệ Thi&ecirc;n Vọng d&ugrave;ng ng&oacute;n tay bẻ g&atilde;y rồi, lưỡi kiếm chỉ c&ograve;n bảy t&aacute;m tấc. Đầu kiếm lại thẳng kh&ocirc;ng nhọn, đến như con dao găm cũng kh&ocirc;ng bằng. Tay tr&aacute;i y cầm bao kiếm, tay phải cầm thanh kiếm g&atilde;y nhảy v&agrave;o tấn c&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lần nay y ra tay cực kỳ nhanh nhẹn, trước mắt Phương Thi&ecirc;n Lao chỉ thấy một vệt trắng thấp tho&aacute;ng, H&agrave; T&uacute;c Đạo đ&atilde; li&ecirc;n tiếp c&ocirc;ng k&iacute;ch ba chi&ecirc;u, tuy rằng thanh kiếm g&atilde;y qu&aacute; ngắn kh&ocirc;ng l&agrave;m y bị thương, nhưng Phương Thi&ecirc;n Lao trong bụng cũng h&atilde;i sợ, nghĩ thầm: &quot;Ba chi&ecirc;u n&agrave;y nhanh thật, quả kh&ocirc;ng dễ g&igrave; n&eacute; tr&aacute;nh, kiếm ph&aacute;p n&agrave;y l&agrave; kiếm ph&aacute;p g&igrave; đ&acirc;y? Nếu trong tay y m&agrave; l&agrave; trường kiếm th&igrave; c&oacute; lẽ m&aacute;u m&igrave;nh đ&atilde; đổ ra tại chỗ rồi.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo tấn c&ocirc;ng ba chi&ecirc;u xong, lui ra một b&ecirc;n, đứng y&ecirc;n bất động. Phương Thi&ecirc;n Lao khai triển kiếm ph&aacute;p, nửa thủ nửa c&ocirc;ng, hung hăng x&ocirc;ng tới. H&agrave; T&uacute;c Đạo n&eacute; qua một b&ecirc;n, nhưng kh&ocirc;ng trả đ&ograve;n, lại bất ngờ tấn c&ocirc;ng ba chi&ecirc;u thật nhanh, &eacute;p Phương Thi&ecirc;n Lao phải tay ch&acirc;n l&uacute;ng t&uacute;ng, rồi lại nhảy ra ngo&agrave;i đứng chờ. Thanh kiếm trong tay Phương Thi&ecirc;n Lao lại tung ra tấn c&ocirc;ng, chỉ thấy &aacute;nh s&aacute;ng trắng nhấp nh&aacute;y, cực kỳ nhanh nhẹn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghĩ thầm: &quot;L&atilde;o gi&agrave; n&agrave;y chi&ecirc;u số thật l&agrave; cương m&atilde;nh, độc địa, so với chưởng ph&aacute;p của l&atilde;o họ Vệ cũng c&ugrave;ng một lối, c&oacute; điều c&oacute; th&ecirc;m ba phần linh động, lại c&oacute; vẻ lợi hại hơn ...&quot; Vừa nghĩ tới đ&acirc;y, chợt nghe H&agrave; T&uacute;c Đạo qu&aacute;t l&ecirc;n: &quot;Cẩn thận nh&eacute;&quot;. Chữ nh&eacute; vừa ra khỏi miệng, bao kiếm b&ecirc;n tay tr&aacute;i giơ l&ecirc;n, nhanh như điện chớp, nghe xẹt một tiếng nhỏ, bao kiếm trong tay đ&atilde; chụp lấy mũi kiếm của Phương Thi&ecirc;n Lao, kiếm b&ecirc;n tay phải nhanh nhẹn chĩa ngay v&agrave;o yết hầu địch thủ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trường kiếm của Phương Thi&ecirc;n Lao kh&ocirc;ng c&ograve;n tự do, kh&ocirc;ng c&aacute;ch g&igrave; c&oacute; thể thu kiếm về để gạt, mắt thấy kiếm đ&acirc;m v&agrave;o cổ họng m&igrave;nh, chỉ c&ograve;n c&aacute;ch bỏ thanh kiếm, lăn ngay xuống đất, mới tr&aacute;nh được chi&ecirc;u n&agrave;y. Y chưa kịp đứng dậy, một b&oacute;ng người thấp tho&aacute;ng, Phan Thi&ecirc;n Canh đ&atilde; vọt m&igrave;nh nhảy tới, chộp lấy chu&ocirc;i thanh trường kiếm, xoay m&igrave;nh một c&aacute;i, r&uacute;t ra khỏi bao. Cả H&agrave; T&uacute;c Đạo lẫn Qu&aacute;ch Tương đều l&ecirc;n tiếng khen ngợi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hảo th&acirc;n ph&aacute;p!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng gi&agrave; mặt tr&ocirc;ng như người bệnh ấy trước sau kh&ocirc;ng thốt ra một lời, v&otilde; c&ocirc;ng h&oacute;a ra cao nhất trong ba người. H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;ng phu của c&aacute;c hạ cao cường, tại hạ thật bội phục.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i rồi quay đầu lại n&oacute;i với Qu&aacute;ch Tương:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qu&aacute;ch c&ocirc; nương, từ khi được nghe nh&atilde; tấu của c&ocirc; nương h&ocirc;m trước, t&ocirc;i đ&atilde; s&aacute;ng t&aacute;c một bản đ&agrave;n, mong được c&ocirc; nương b&igrave;nh phẩm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bản đ&agrave;n n&agrave;o thế?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo ngồi xuống xếp bằng, lấy c&acirc;y dao cầm đặt l&ecirc;n l&ograve;ng, l&ecirc;n d&acirc;y thử vận, rồi bắt đầu gẩy đ&agrave;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phan Thi&ecirc;n Canh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;c hạ li&ecirc;n tiếp đ&aacute;nh bại hai người sư đệ của t&ocirc;i, họ Phan n&agrave;y muốn được thỉnh gi&aacute;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo xua tay:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chuyện tỉ th&iacute; v&otilde; c&ocirc;ng đ&atilde; qua rồi, kh&ocirc;ng c&ograve;n hứng th&uacute; g&igrave; nữa. Đ&acirc;y l&agrave; một bản đ&agrave;n mới, ta đang muốn gẩy cho Qu&aacute;ch c&ocirc; nương nghe. Ba vị nếu như muốn nghe, xin mời ngồi xuống, nếu như kh&ocirc;ng hiểu, xin cứ tự tiện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tay tr&aacute;i ấn ph&iacute;m, tay phải bắt đầu đ&aacute;nh đ&agrave;n. Qu&aacute;ch Tương chỉ mới nghe v&agrave;i nốt, bất gi&aacute;c vừa mừng vừa sợ. H&oacute;a ra bản đ&agrave;n n&agrave;y một phần l&agrave; từ &quot;Khảo B&agrave;n&quot; l&agrave; kh&uacute;c n&agrave;ng đ&atilde; tấu qua, nhưng một phần kh&aacute;c lại từ trong thơ &quot;Ki&ecirc;m Gia&quot;, hai bản kh&ocirc;ng c&ugrave;ng một điệu, nhưng y đ&atilde; h&ograve;a lại với nhau, một ứng một đ&aacute;p, nghe ra thật l&agrave; kỳ diệu. Tuy nhi&ecirc;n khi nghe cầm vận tới đoạn &quot;khảo b&agrave;n tại giản, thạc nh&acirc;n chi khoan. Ki&ecirc;m gia thương thương, bạch lộ vi sương, sở vị y nh&acirc;n, tại thi&ecirc;n nhất phương ... thạc nh&acirc;n chi khoan, thạc nh&acirc;n chi khoan ... tố hồi t&ograve;ng chi, đạo trở thả trường, tố du t&ograve;ng chi, uyển tại thủy trung ương ... độc tẩm ngụ ng&ocirc;n, vĩnh thỉ vật huy&ecirc;n, vĩnh thỉ vật huy&ecirc;n ...&quot; Qu&aacute;ch Tương trong l&ograve;ng thấy hơi chạnh l&ograve;ng: &quot;Trong tiếng đ&agrave;n của y c&oacute; n&oacute;i đến y nh&acirc;n, chẳng lẽ nhắc đến ta ư? Sao điệu đ&agrave;n c&oacute; vẻ &yacute; tứ triền mi&ecirc;n đến thế, nghe đầy những nhớ thương cảm mến?&quot; Nghĩ đến đ&acirc;y, n&agrave;ng kh&ocirc;ng khỏi ửng hồng đ&ocirc;i m&aacute;. C&oacute; điều điệu đ&agrave;n đ&oacute; biến chuyển thực l&agrave; kh&eacute;o l&eacute;o, nguy&ecirc;n vận hai b&agrave;i &quot;Khảo B&agrave;n&quot; v&agrave; &quot;Ki&ecirc;m Gia&quot; kh&ocirc;ng lạc nhau ch&uacute;t n&agrave;o, hai b&ecirc;n nhịp nh&agrave;ng đối đ&aacute;p, song vẫn giữ được tất cả những hoa mỹ của cả hai. Trong đời n&agrave;ng chưa bao giờ được nghe một kh&uacute;c nhạc như thế cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng bọn Phan Thi&ecirc;n Canh ba người chẳng ai hiểu g&igrave;. Họ kh&ocirc;ng biết rằng H&agrave; T&uacute;c Đạo l&agrave; người hơi đi&ecirc;n kh&ugrave;ng, c&oacute; ch&uacute;t m&ecirc; mẩn của một cuồng sĩ, vừa l&agrave;m được một bản đ&agrave;n, nhất định phải kiếm Qu&aacute;ch Tương để gẩy cho nghe bằng được, huống chi kh&uacute;c đ&agrave;n n&agrave;y cũng v&igrave; n&agrave;ng m&agrave; s&aacute;ng t&aacute;c, n&ecirc;n l&uacute;c ấy mọi việc đều g&aacute;c sang một b&ecirc;n. Thế nhưng ba &ocirc;ng gi&agrave; thấy y ngưng thần đ&aacute;nh đ&agrave;n, chẳng th&egrave;m để &yacute; chi đến m&igrave;nh, quả thực hết sức khinh người, l&agrave;m sao họ c&oacute; thể nhịn nổi? Phan Thi&ecirc;n Canh vung thanh trường kiếm, điểm ngay v&agrave;o vai b&ecirc;n tr&aacute;i H&agrave; T&uacute;c Đạo, h&eacute;t lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mau đứng l&ecirc;n, ta với ngươi hai người so t&agrave;i n&agrave;o!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo to&agrave;n thể t&acirc;m tr&iacute; đang để v&agrave;o tiếng đ&agrave;n, tưởng m&igrave;nh đang l&agrave; một thư sinh ngao du nơi khe n&uacute;i, ngắm một thiếu nữ &ocirc;n nhu đứng tr&ecirc;n một h&ograve;n đảo nhỏ xa xa, l&ograve;ng kh&ocirc;ng ngại n&uacute;i non c&aacute;ch trở, đang cố t&igrave;m c&aacute;ch qua gặp n&agrave;ng ...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bỗng dưng thấy vai tr&aacute;i nh&oacute;i một c&aacute;i, y liền tỉnh mộng, quay đầu nh&igrave;n lại, thấy trường kiếm trong tay Phan Thi&ecirc;n Canh đ&atilde; chỉ v&agrave;o đầu vai m&igrave;nh, đ&acirc;m nhẹ v&agrave;o da, nếu kh&ocirc;ng đỡ gạt, e rằng đối phương sẽ tiện đ&agrave; đ&acirc;m tới khiến cho bị thương. Thế nhưng bản đ&agrave;n chưa tấu xong, tục nh&acirc;n lại ở b&ecirc;n cạnh quấy nhiễu, thực l&agrave; ph&aacute; đ&aacute;m. Y lập tức cầm thanh kiếm g&atilde;y, keng một tiếng, gạt thanh trường kiếm của Phan Thi&ecirc;n Canh ra, tay phải vẫn tiếp tục gảy đ&agrave;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c n&agrave;y H&agrave; T&uacute;c Đạo mới giở tuyệt kỹ b&igrave;nh sinh, tay phải đ&agrave;n cầm, tay tr&aacute;i sử kiếm. Y kh&ocirc;ng sao nhấn ph&iacute;m được n&ecirc;n nhắm năm d&acirc;y đ&agrave;n d&ugrave;ng sức thổi một c&aacute;i, d&acirc;y đ&agrave;n lập tức l&otilde;m xuống, tay phải tiếp tục gảy như thường, tiếng đ&agrave;n cao thấp trầm bổng, vẫn uyển chuyển như &yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phan Thi&ecirc;n Canh cấp tốc c&ocirc;ng k&iacute;ch mấy chi&ecirc;u, H&agrave; T&uacute;c Đạo thuận tay gạt ra, hai mắt vẫn chăm ch&uacute; nh&igrave;n d&acirc;y đ&agrave;n, chỉ e hơi thổi ra kh&ocirc;ng tr&uacute;ng tiết, l&agrave;m loạn tiếng đ&agrave;n. Phan Thi&ecirc;n Canh c&agrave;ng th&ecirc;m giận dữ, kiếm chi&ecirc;u c&agrave;ng c&ocirc;ng c&agrave;ng nhanh, nhưng bất luận kiếm của y từ phương n&agrave;o đ&acirc;m tới, đều bị H&agrave; T&uacute;c Đạo nhẹ nh&agrave;ng gạt qua một b&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe tiếng đ&agrave;n, trong l&ograve;ng nổi nhạc hứng, kh&ocirc;ng để &yacute; g&igrave; đến những đường kiếm đ&aacute;nh tới của Phan Thi&ecirc;n Canh, thế nhưng hai thanh kiếm chạm nhau l&agrave;m cho cầm thanh bị loạn. Hai tay n&agrave;ng đ&aacute;nh nhịp theo tiếng đ&agrave;n, nh&iacute;u m&agrave;y nh&igrave;n Phan Thi&ecirc;n Canh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng ra chi&ecirc;u l&uacute;c nhanh l&uacute;c chậm kh&ocirc;ng hợp với tiếng đ&agrave;n, bộ &ocirc;ng kh&ocirc;ng biết g&igrave; về &acirc;m nhạc ư? Hừ, &ocirc;ng phải nghe theo tiếng đ&agrave;n m&agrave; xuất kiếm, c&oacute; theo nhịp th&igrave; nghe mới được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phan Thi&ecirc;n Canh đời n&agrave;o để &yacute; đến c&ocirc;, chỉ thấy trước mặt kẻ địch ngồi xếp bằng tr&ecirc;n đất, tay chỉ cầm một thanh kiếm g&atilde;y, mắt chăm ch&uacute; nh&igrave;n d&acirc;y đ&agrave;n, vậy m&agrave; m&igrave;nh kh&ocirc;ng sao chạm được v&agrave;o y, lại c&agrave;ng n&oacute;ng ruột. Đột nhi&ecirc;n y đổi kiếm ph&aacute;p, c&ocirc;ng k&iacute;ch c&agrave;ng nhanh hơn, hai m&oacute;n binh kh&iacute; chạm nhau leng keng như tiếng mưa r&agrave;o. &Acirc;m thanh cấp b&aacute;ch đ&oacute; so với tiếng đ&agrave;n &ocirc;n nh&atilde; triền mi&ecirc;n quả thực kh&ocirc;ng h&agrave;i h&ograve;a ch&uacute;t n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo nhướng đ&ocirc;i l&ocirc;ng m&agrave;y, truyền k&igrave;nh lực v&agrave;o thanh đoản kiếm, nghe c&aacute;ch một tiếng, thanh kiếm trong tay Phan Thi&ecirc;n Canh lập tức g&atilde;y ra l&agrave;m đ&ocirc;i. Thế nhưng cũng l&uacute;c đ&oacute;, d&acirc;y thứ năm trong bảy d&acirc;y đ&agrave;n cũng đứt theo. Phan Thi&ecirc;n Canh mặt x&aacute;m như tro, kh&ocirc;ng n&oacute;i một lời, chuyển th&acirc;n chạy ra khỏi đ&igrave;nh. Ba người nhảy l&ecirc;n lưng ngựa, chạy vọt l&ecirc;n hướng triền n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thật lạ l&ugrave;ng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hừ, ba người n&agrave;y đ&aacute;nh đ&atilde; thua rồi, tại sao c&ograve;n chạy l&ecirc;n ch&ugrave;a Thiếu L&acirc;m? Bộ họ muốn chết hay sao chứ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng quay đầu lại, thấy H&agrave; T&uacute;c Đạo mặt buồn thiu, tay vuốt ve d&acirc;y đ&agrave;n, tưởng như kh&ocirc;ng n&oacute;i l&ecirc;n được nỗi đau khổ của m&igrave;nh. Qu&aacute;ch Tương nghĩ thầm: &quot;Một sợi d&acirc;y đ&agrave;n, c&oacute; đ&aacute;ng g&igrave; đ&acirc;u?&quot;.Lập tức tiếp lấy c&acirc;y dao cầm cởi đoạn d&acirc;y đ&agrave;n bị đứt mở d&acirc;y đ&agrave;n ra căng lại sợi d&acirc;y mới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lắc đầu thở d&agrave;i, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Uổng ph&iacute; bao năm tu tập, cuối c&ugrave;ng rồi l&ograve;ng vẫn chưa tĩnh được. Tuy tay tr&aacute;i t&ocirc;i đưa k&igrave;nh lực ra l&agrave;m g&atilde;y được kiếm của y, nhưng tay phải cũng lại l&agrave;m đứt d&acirc;y đ&agrave;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bấy giờ Qu&aacute;ch Tương mới r&otilde;, nguy&ecirc;n l&agrave; y thất vọng v&igrave; v&otilde; c&ocirc;ng của m&igrave;nh luyện chưa thuần thục, n&ecirc;n cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Việc &ocirc;ng c&oacute; thể tay tr&aacute;i dũng m&atilde;nh c&ocirc;ng địch , tay phải lại thư thả chậm r&atilde;i gẩy đ&agrave;n, l&agrave; ph&eacute;p ph&acirc;n t&acirc;m nhị dụng, tr&ecirc;n c&otilde;i đời n&agrave;y hiện nay chỉ c&oacute; ba người l&agrave;m được th&ocirc;i. &Ocirc;ng luyện chưa đến mức đ&oacute;, cũng kh&ocirc;ng c&oacute; g&igrave; phải bực m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ba người đ&oacute; l&agrave; ai?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị thứ nhất l&agrave; L&atilde;o Ngoan Đồng Chu B&aacute; Th&ocirc;ng, người thứ hai l&agrave; cha t&ocirc;i, c&ograve;n người thứ ba l&agrave; Dương phu nh&acirc;n Tiểu Long Nữ. Trừ ba người ấy ra, d&ugrave; cho l&agrave; &ocirc;ng ngoại t&ocirc;i Đ&agrave;o hoa đảo chủ, hay mẹ t&ocirc;i, hoặc Thần Đi&ecirc;u đại hiệp Dương Qu&aacute;, l&agrave; những người v&otilde; c&ocirc;ng rất cao, nhưng cũng kh&ocirc;ng l&agrave;m được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế gian c&oacute; những kỳ nh&acirc;n như thế, bao giờ c&oacute; dịp mong c&ocirc; đưa t&ocirc;i đến gặp họ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương buồn b&atilde; n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Muốn gặp cha t&ocirc;i th&igrave; kh&ocirc;ng kh&oacute;, c&ograve;n hai vị kia, kh&ocirc;ng biết đi đ&acirc;u để kiếm họ được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng thấy H&agrave; T&uacute;c Đạo ngơ ngẩn xuất thần, lại nghĩ đến chuyện đứt d&acirc;y đ&agrave;n, n&ecirc;n an ủi y:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng chỉ giơ tay đ&atilde; đ&aacute;nh bại được C&ocirc;n L&ocirc;n Tam Th&aacute;nh, cũng đủ h&atilde;nh diện với đương thế rồi, h&agrave; tất v&igrave; chuyện nhỏ mọn l&agrave; đứt d&acirc;y đ&agrave;n m&agrave; rầu rĩ kh&ocirc;ng vui?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo giật m&igrave;nh kinh h&atilde;i, hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;n L&ocirc;n Tam Th&aacute;nh ư? C&ocirc; n&oacute;i g&igrave;? Sao c&ocirc; lại biết?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ba l&atilde;o gi&agrave; đ&oacute; từ T&acirc;y Vực đến, ắt l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh đ&oacute;. Bọn họ v&otilde; c&ocirc;ng quả nhi&ecirc;n c&oacute; chỗ độc đ&aacute;o, chỉ c&oacute; điều khi&ecirc;u chiến với ch&ugrave;a Thiếu L&acirc;m, e rằng kh&ocirc;ng tự lượng sức m&igrave;nh ...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng thấy sự kinh ho&agrave;ng của H&agrave; T&uacute;c Đạo mỗi l&uacute;c một nhiều, n&ecirc;n kh&ocirc;ng d&aacute;m n&oacute;i tiếp nữa, hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&oacute; g&igrave; kỳ qu&aacute;i?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lẩm bẩm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;n L&ocirc;n Tam Th&aacute;nh, C&ocirc;n L&ocirc;n Tam Th&aacute;nh H&agrave; T&uacute;c Đạo ch&iacute;nh l&agrave; t&ocirc;i đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương giật m&igrave;nh kinh h&atilde;i, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh ư? Vậy c&ograve;n hai người kia đ&acirc;u?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&ocirc;n L&ocirc;n Tam Th&aacute;nh chỉ c&oacute; một người, xưa nay kh&ocirc;ng phải ba người bao giờ. T&ocirc;i ở T&acirc;y Vực cũng c&oacute; được một ch&uacute;t danh nho nhỏ, bạn b&egrave; ở đ&oacute; n&oacute;i t&ocirc;i c&oacute; ba tuyệt kỹ, cầm kỳ kiếm, c&oacute; thể n&oacute;i l&agrave; cầm th&aacute;nh, kỳ th&aacute;nh, kiếm th&aacute;nh. Nh&acirc;n v&igrave; t&ocirc;i sinh trưởng trong n&uacute;i C&ocirc;n L&ocirc;n, n&ecirc;n g&aacute;n cho t&ocirc;i một c&aacute;i ngoại hiệu, gọi l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh. Thế nhưng t&ocirc;i nghĩ c&aacute;i chữ &quot;th&aacute;nh&quot; đ&oacute;, đ&acirc;u phải dễ d&agrave;ng g&igrave; m&agrave; xưng như thế? Thế nhưng người kh&aacute;c đ&atilde; d&aacute;t v&agrave;ng l&ecirc;n tr&ecirc;n mặt m&igrave;nh rồi, kh&ocirc;ng thể từ chối được. Th&agrave;nh thử, t&ocirc;i phải đổi t&ecirc;n, gọi m&igrave;nh l&agrave; &quot;T&uacute;c Đạo&quot;, nối liền với nhau, th&agrave;nh l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh H&agrave; T&uacute;c Đạo[8] để cho người kh&aacute;c nghe được kh&ocirc;ng cho l&agrave; t&ocirc;i cuồng vọng tự đại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương vỗ tay cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&oacute;a ra l&agrave; như thế. T&ocirc;i vẫn tưởng l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh phải l&agrave; ba người. Thế ba &ocirc;ng gi&agrave; đ&oacute; l&agrave; ai?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bọn họ ư? Họ thuộc ph&aacute;i Thiếu L&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương lấy l&agrave;m lạ, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nguy&ecirc;n lai ba người n&agrave;y thuộc ph&aacute;i Thiếu L&acirc;m. Ừ, v&otilde; c&ocirc;ng của họ quả đ&uacute;ng l&agrave; cương m&atilde;nh. Kh&ocirc;ng sai, &ocirc;ng gi&agrave; mặt đỏ sử dụng ch&iacute;nh l&agrave; Đạt Ma kiếm ph&aacute;p. Đ&uacute;ng rồi, &ocirc;ng gi&agrave; bệnh hoạn mặt v&agrave;ng l&uacute;c sau c&ocirc;ng k&iacute;ch thật gấp, chẳng phải Vi Đ&agrave; Phục Ma kiếm hay sao? Chỉ v&igrave; họ th&ecirc;m thắt biến h&oacute;a nhiều, nhất thời t&ocirc;i kh&ocirc;ng nhận ra được. Nhưng sao họ lại từ T&acirc;y Vực đến đ&acirc;y?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chuyện n&agrave;y n&oacute;i ra cũng c&oacute; nguy&ecirc;n nh&acirc;n. M&ugrave;a xu&acirc;n năm ngo&aacute;i, t&ocirc;i ở tại đỉnh Kinh Thần Phong gẩy đ&agrave;n, bỗng đ&acirc;u nghe ngo&agrave;i lều tranh c&oacute; tiếng đấm đ&aacute;, vội ra xem, thấy hai người đang vật nhau, cả hai đều đ&atilde; trọng thương, nhưng vẫn tận lực chiến đấu. T&ocirc;i qu&aacute;t họ bắt ngừng tay, nhưng kh&ocirc;ng ai chịu y&ecirc;n, t&ocirc;i phải đến can hai người ra. Một trong hai người chỉ trợn ngược mắt l&ecirc;n rồi chết, người kia c&ograve;n thoi th&oacute;p. T&ocirc;i đưa y v&agrave;o trong lều, cho y uống một vi&ecirc;n Thiếu Dương đơn, cứu chữa nửa ng&agrave;y, nhưng v&igrave; bị thương qu&aacute; nặng, linh đơn kh&ocirc;ng cứu mạng nổi. Khi y sắp chết, n&oacute;i t&ecirc;n l&agrave; Do&atilde;n Khắc T&acirc;y ...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương A l&ecirc;n một tiếng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Phải chăng người đ&aacute;nh nhau với y l&agrave; Ti&ecirc;u Tương Tử? Người đ&oacute; th&acirc;n h&igrave;nh cao gầy, mặt tr&ocirc;ng như x&aacute;c chết, phải kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lạ l&ugrave;ng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&uacute;ng vậy, sao c&ocirc; c&aacute;i g&igrave; cũng biết?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i đ&atilde; gặp họ rồi, kh&ocirc;ng ngờ hai t&ecirc;n th&acirc;n thiết như thế, cuối c&ugrave;ng lại đ&aacute;nh nhau đến chết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Do&atilde;n Khắc T&acirc;y n&oacute;i rằng y một đời l&agrave;m nhiều điều t&agrave;n &aacute;c, đến khi sắp chết, hối hận cũng đ&atilde; muộn. Y n&oacute;i y v&agrave; Ti&ecirc;u Tương Tử hai người v&agrave;o ch&ugrave;a Thiếu L&acirc;m ăn cắp một bộ kinh thư, nhưng người nọ e d&egrave; người kia, kh&ocirc;ng ai d&aacute;m để người kia coi trước, sợ nếu đối phương v&otilde; c&ocirc;ng cao hơn, sẽ ra tay trừ khử m&igrave;nh, chiếm lấy bộ kinh. Hai người ăn c&ugrave;ng m&acirc;m, ngủ c&ugrave;ng giường, một bước kh&ocirc;ng rời nhau. Thế nhưng ăn th&igrave; sợ người kia hạ độc, ngủ th&igrave; sợ người kia &aacute;m to&aacute;n, l&uacute;c n&agrave;o cũng nơm nớp kh&ocirc;ng an, lại sợ h&ograve;a thượng ch&ugrave;a Thiếu L&acirc;m đuổi theo, n&ecirc;n phải chạy đến T&acirc;y Vực. Đến khi họ đến được Kinh Thần Phong, hai người đ&atilde; g&acirc;n cốt r&atilde; rời, nhưng kh&ocirc;ng ai chịu ai, cuối c&ugrave;ng ra tay đ&aacute;nh nhau. Do&atilde;n Khắc T&acirc;y n&oacute;i l&agrave; Ti&ecirc;u Tương Tử vốn v&otilde; c&ocirc;ng cao hơn y, lại ra tay đ&aacute;nh y trước một chưởng, nhưng kết quả y vẫn chiếm thượng phong. Sau y mới nghĩ ra rằng, Ti&ecirc;u Tương Tử khi ở tr&ecirc;n n&uacute;i Hoa Sơn đ&atilde; bị trọng thương, nguy&ecirc;n kh&iacute; vẫn chưa hồi phục. N&oacute;i trắng ra, nếu hai người kh&ocirc;ng e ngại lẫn nhau, chắc cũng kh&ocirc;ng thể n&agrave;o l&ecirc;n tới n&uacute;i C&ocirc;n L&ocirc;n được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe c&acirc;u chuyện, nghĩ đến hai người đường đi l&uacute;c n&agrave;o cũng lo ngay ng&aacute;y, đến chết cũng vẫn c&ograve;n chưa y&ecirc;n bụng, kh&ocirc;ng khỏi thương cảm, n&ecirc;n than:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chỉ v&igrave; một bộ kinh thư, đến nỗi sinh ra cớ sự.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ecirc;n Do&atilde;n Khắc T&acirc;y n&oacute;i xong c&acirc;u chuyện, hơi đ&atilde; đứt đoạn, sau c&ugrave;ng cầu t&ocirc;i đến ch&ugrave;a Thiếu L&acirc;m, n&oacute;i lại với một vị t&ecirc;n h&ograve;a thượng t&ecirc;n l&agrave; Gi&aacute;c Viễn, rằng kinh thư ở trong dầu chi đ&oacute;. T&ocirc;i nghe thấy kỳ qu&aacute;i, c&aacute;i g&igrave; m&agrave; lại kinh thư ở trong dầu? Định sẽ hỏi lại cho kỹ, nhưng n&agrave;o ngờ y kh&ocirc;ng c&ograve;n chịu nổi, đ&atilde; h&ocirc;n m&ecirc; bất tỉnh rồi. T&ocirc;i đợi y tỉnh lại sẽ hỏi cho ra lẽ, n&agrave;o ngờ y thiếp đi rồi kh&ocirc;ng tỉnh nữa. T&ocirc;i nghĩ hay l&agrave; bộ kinh đ&oacute; bao trong tấm vải tẩm dầu chăng? Thế nhưng tra x&eacute;t khắp người hai t&ecirc;n n&agrave;y, kh&ocirc;ng thấy g&igrave; cả. Nhận lời ủy th&aacute;c của người, phải l&agrave;m tr&ograve;n việc. T&ocirc;i b&igrave;nh sinh chưa đặt bước đến Trung Thổ, cũng muốn nh&acirc;n dịp n&agrave;y du ngoạn một phen, v&igrave; thế đến ch&ugrave;a Thiếu L&acirc;m l&agrave; vậy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế sao &ocirc;ng lại đến ch&ugrave;a Thiếu L&acirc;m hạ chiến thư, muốn c&ugrave;ng bọn họ tỉ th&iacute; v&otilde; nghệ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chuyện đ&oacute; cũng l&agrave; từ ba g&atilde; n&agrave;y m&agrave; ra. Ba người n&agrave;y l&agrave; đệ tử tục gia của ph&aacute;i Thiếu L&acirc;m T&acirc;y Vực. Cứ theo người trong v&otilde; l&acirc;m T&acirc;y Vực n&oacute;i, họ đều l&agrave; trong h&agrave;ng chữ &quot;Thi&ecirc;n&quot;, c&ugrave;ng h&agrave;ng với phương trượng ch&ugrave;a Thiếu L&acirc;m Thi&ecirc;n Minh thiền sư. H&igrave;nh như sư tổ của họ trước đ&acirc;y c&oacute; chuyện bất đồng với sư huynh đệ, giận dữ bỏ đi, truyền ra một ph&aacute;i T&acirc;y Vực Thiếu L&acirc;m. Nguy&ecirc;n v&otilde; c&ocirc;ng của ph&aacute;i Thiếu L&acirc;m, tổ sư Đạt Ma truyền từ Thi&ecirc;n Tr&uacute;c sang Trung Thổ, nay từ Trung Thổ lại truyền sang T&acirc;y Vực, cũng kh&ocirc;ng c&oacute; g&igrave; l&agrave; lạ. Ba người đ&oacute; nghe danh hiệu t&ocirc;i l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh n&ecirc;n muốn đến so t&agrave;i cao thấp. Họ tự h&agrave;o l&agrave; v&otilde; Thiếu L&acirc;m thi&ecirc;n hạ v&ocirc; địch, t&ocirc;i muốn xưng cầm th&aacute;nh, kỳ th&aacute;nh th&igrave; cũng kh&ocirc;ng sao. C&ograve;n c&aacute;i danh hiệu kiếm th&aacute;nh th&igrave; nhất định kh&ocirc;ng chịu, kh&ocirc;ng bỏ đi kh&ocirc;ng được, chỉ được xưng l&agrave; nhị th&aacute;nh chứ kh&ocirc;ng thể xưng l&agrave; tam th&aacute;nh. Ch&iacute;nh l&uacute;c đ&oacute; t&ocirc;i lại đang sắp sửa ra đi, th&agrave;nh ra một c&ocirc;ng đ&ocirc;i chuyện, n&ecirc;n sai người đến ước hẹn sẽ gặp nhau tại ch&ugrave;a Thiếu L&acirc;m, rồi tự m&igrave;nh l&ecirc;n đường đến Trung Nguy&ecirc;n. N&agrave;o hay ba vị nh&acirc;n huynh n&agrave;y cước tr&igrave;nh cũng thật mau lẹ, đ&atilde; b&ocirc;n ba đến nơi rồi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cười n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&oacute;a ra sự việc l&agrave; như thế, l&agrave;m cho t&ocirc;i chẳng hiểu đầu đu&ocirc;i g&igrave; cả. Ba l&atilde;o gi&agrave; giờ n&agrave;y chắc đ&atilde; đến ch&ugrave;a Thiếu L&acirc;m, kh&ocirc;ng biết n&oacute;i năng l&agrave;m sao đ&acirc;y?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i vốn dĩ kh&ocirc;ng quen biết g&igrave; với c&aacute;c nh&agrave; sư ch&ugrave;a Thiếu L&acirc;m, cũng kh&ocirc;ng th&ugrave; kh&ocirc;ng o&aacute;n, sở dĩ đ&iacute;nh ước với họ mười ng&agrave;y cốt đợi ba l&atilde;o n&agrave;y tới, l&uacute;c đ&oacute; mới động thủ. Hiện tại hai b&ecirc;n đ&atilde; thử t&agrave;i nhau rồi, th&ocirc;i cả hai người m&igrave;nh c&ugrave;ng l&ecirc;n, đợi t&ocirc;i truyền lại c&acirc;u n&oacute;i đ&oacute; xong, rồi m&igrave;nh xuống n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nh&iacute;u m&agrave;y:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qui củ của mấy h&ograve;a thượng n&agrave;y rất chặt chẽ, kh&ocirc;ng cho đ&agrave;n b&agrave; con g&aacute;i v&agrave;o ch&ugrave;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hừ, c&aacute;i qui củ g&igrave; thối tha thế? Bọn m&igrave;nh cứ tiến v&agrave;o, chẳng lẽ họ sẽ giết m&igrave;nh sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương tuy l&agrave; một c&ocirc; g&aacute;i hiếu sự, nhưng từ khi l&agrave;m quen với V&ocirc; Sắc thiền sư, n&agrave;ng kh&ocirc;ng c&ograve;n địch &yacute; với ch&ugrave;a Thiếu L&acirc;m, n&ecirc;n cười lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i đứng ngo&agrave;i sơn m&ocirc;n chờ, &ocirc;ng tự m&igrave;nh đi v&agrave;o ch&ugrave;a truyền ng&ocirc;n, như thế khỏi th&ecirc;m phiền.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo gật đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thế cũng được, c&aacute;i bản đ&agrave;n t&ocirc;i chưa tấu xong, khi trở xuống t&ocirc;i sẽ đ&agrave;n lại cho c&ocirc; nghe một lượt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">----------------------------------------------------------</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[1] Đoạn n&agrave;y kh&ocirc;ng biết tr&iacute;ch từ kinh n&agrave;o n&ecirc;n kh&ocirc;ng r&otilde; nghĩa. Độc giả n&agrave;o biết xin chỉ gi&aacute;o. Đa tạ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[2] Ch&oacute; dữ chặn đường</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[3] Nguy&ecirc;n tiếng Phạn Lanka l&agrave; t&ecirc;n đảo T&iacute;ch Lan (Sri Lanka), cũng l&agrave; t&ecirc;n một ngọn n&uacute;i ở đ&acirc;y. T&iacute;ch Lan l&agrave; nơi m&agrave; kinh điển nh&agrave; Phật được viết tr&ecirc;n l&aacute; c&acirc;y đầu ti&ecirc;n. Kinh Lăng Gi&agrave; tức l&agrave; Lankavatara sutra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[4] Đ&agrave;n c&oacute; khảm ngọc</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[5] Vỗ b&agrave;n nơi suối nước l&agrave; c&aacute;i khoan kho&aacute;i của người qu&acirc;n tử. Tỉnh hay m&ecirc; cũng một m&igrave;nh m&igrave;nh biết, kh&ocirc;ng bao giờ qu&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[6] Vỗ b&agrave;n nơi g&ograve; cao l&agrave; c&aacute;i cốt c&aacute;ch của người qu&acirc;n tử. Dẫu tỉnh hay m&ecirc; cũng chỉ m&igrave;nh biết kh&ocirc;ng cần ai hay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[7] H&agrave; T&uacute;c Đạo theo nghĩa đen l&agrave; &quot;n&oacute;i tới l&agrave;m g&igrave;&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[8] C&oacute; nghĩa kh&ocirc;ng đ&aacute;ng gọi l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh.</span></p>', 0, 'chuong-2-thien-nhai-tu-quan-bat-kha-vong', NULL, NULL);
INSERT INTO `chapter` VALUES (16, 19, 'Chương 3 - Võ đường sơn đinh tùng bách trường', 'Nắng mưa đã có cổ tùng,  Suối nguồn róc rách bạn cùng kỳ nhân.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nắng mưa đ&atilde; c&oacute; cổ t&ugrave;ng,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Suối nguồn r&oacute;c r&aacute;ch bạn c&ugrave;ng kỳ nh&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khai t&ocirc;ng lập ph&aacute;i một th&acirc;n,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&otilde; Đương so với Thiếu L&acirc;m k&eacute;m g&igrave;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai người chậm r&atilde;i l&ecirc;n n&uacute;i, thẳng đến tận cửa ch&ugrave;a, vẫn kh&ocirc;ng thấy một b&oacute;ng người. H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Th&ocirc;i t&ocirc;i cũng kh&ocirc;ng v&agrave;o l&agrave;m g&igrave;, chỉ mời h&ograve;a thượng đ&oacute; ra đ&acirc;y nhắc lại c&acirc;u n&oacute;i đ&oacute; cũng đủ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y cao giọng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; T&uacute;c Đạo ở n&uacute;i C&ocirc;n L&ocirc;n đến thăm ch&ugrave;a Thiếu L&acirc;m, c&oacute; một lời phụng c&aacute;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&acirc;u n&oacute;i đ&oacute; vừa dứt, chỉ thấy trong ch&ugrave;a hơn một chục chiếc đại hồng chung c&ugrave;ng rền vang một lượt, tiếng k&ecirc;u boong boong, vang động khắp cả d&atilde;y n&uacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đột nhi&ecirc;n cửa ch&ugrave;a mở rộng, hai b&ecirc;n hai h&agrave;ng tăng nh&acirc;n mặc &aacute;o m&agrave;u tro c&ugrave;ng tiến ra, b&ecirc;n tr&aacute;i năm mươi bốn người, b&ecirc;n phải năm mươi bốn người, cả thảy một trăm lẻ t&aacute;m người, đ&oacute; l&agrave; đệ tử của La H&aacute;n Đường, ph&ugrave; hợp với số một trăm linh t&aacute;m la h&aacute;n. Đằng sau l&agrave; mười t&aacute;m tăng nh&acirc;n, tr&ecirc;n &aacute;o b&agrave;o m&agrave;u tro c&oacute; phủ th&ecirc;m c&agrave; sa m&agrave;u v&agrave;ng nhạt, tuổi t&aacute;c so với đệ tử La H&aacute;n Đường c&oacute; phần hơn, đ&oacute; l&agrave; đệ tử của Đạt Ma Đường, ở tr&ecirc;n La H&aacute;n Đường một bậc. Đi sau một qu&atilde;ng l&agrave; bảy nh&agrave; sư gi&agrave; mặc c&aacute;ch tử tăng b&agrave;o[1]. Những nh&agrave; sư đ&oacute; mặt mũi nhăn nheo, ni&ecirc;n kỷ &iacute;t ra cũng phải hơn bảy mươi, gi&agrave; c&oacute; thể đến ngo&agrave;i ch&iacute;n mươi, đ&oacute; l&agrave; T&acirc;m Thiền Đường thất l&atilde;o. Kế tiếp nữa l&agrave; Thi&ecirc;n Minh phương trượng chầm chậm bước tới, b&ecirc;n tr&aacute;i l&agrave; thủ tọa Đạt Ma Đường V&ocirc; Tướng thiền sư, b&ecirc;n phải l&agrave; thủ tọa La H&aacute;n Đường V&ocirc; Sắc thiền sư. Phan Thi&ecirc;n Canh, Phương Thi&ecirc;n Lao, Vệ Thi&ecirc;n Vọng ba người đi ở đằng sau. Cuối c&ugrave;ng l&agrave; khoảng bảy, t&aacute;m mươi tục gia đệ tử của ph&aacute;i Thiếu L&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&ocirc;m H&agrave; T&uacute;c Đạo lẻn v&agrave;o La H&aacute;n Đường, để lại tờ thiếp tr&ecirc;n tay pho tượng H&agrave;ng Long La H&aacute;n, v&otilde; c&ocirc;ng như thế khiến cho phương trượng c&ugrave;ng V&ocirc; Sắc, V&ocirc; Tướng đều thật kinh sợ. Mấy h&ocirc;m sau, anh em Phan Thi&ecirc;n Canh từ T&acirc;y Vực đến, n&oacute;i chuyện ước hẹn tỉ v&otilde;, c&aacute;c cao tăng trong ch&ugrave;a c&agrave;ng th&ecirc;m lưu t&acirc;m. Chi ph&aacute;i Thiếu L&acirc;m T&acirc;y Vực v&igrave; đường s&aacute; xa x&ocirc;i, mấy chục năm qua &iacute;t tin tức qua lại với ph&aacute;i Thiếu L&acirc;m trung ch&acirc;u, nhưng tăng ch&uacute;ng trong ch&ugrave;a đều biết rằng, vị sư th&uacute;c tổ Khổ Tuệ thiền sư ?đi qua T&acirc;y Vực khai ph&aacute;i năm đ&oacute; v&otilde; c&ocirc;ng kinh người, truyền xuống đồ tử đồ t&ocirc;n danh tiếng cũng rất ư vang động. Thấy bọn Phan Thi&ecirc;n Canh n&oacute;i về C&ocirc;n L&ocirc;n Tam Th&aacute;nh kh&ocirc;ng d&aacute;m khinh thường ch&uacute;t n&agrave;o, ai nấy đều nghĩ rằng nếu như tốt l&agrave;nh chắc đ&atilde; kh&ocirc;ng đến, đ&atilde; đến chắc cũng kh&ocirc;ng phải hiền l&agrave;nh g&igrave;, n&ecirc;n trong ch&ugrave;a c&agrave;ng th&ecirc;m gia t&acirc;m ph&ograve;ng phạm. Phương trượng lại truyền ph&aacute;p chỉ xuống, tất cả đệ tử tăng tục trong v&ograve;ng năm trăm dặm chung quanh, phải trở về ch&ugrave;a nghe điều động.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c đầu ch&uacute;ng tăng cũng tưởng C&ocirc;n L&ocirc;n Tam Th&aacute;nh gồm c&oacute; ba người, sau mới biết chỉ c&oacute; một người, c&ograve;n như dung mạo, tuổi t&aacute;c, bọn Phan Thi&ecirc;n Canh cũng kh&ocirc;ng r&otilde;, chỉ? biết y tự phụ cầm, kỳ, kiếm tam tuyệt m&agrave; th&ocirc;i. Đ&aacute;nh đ&agrave;n v&agrave; đấu cờ khiến cho t&acirc;m hồn tranh đua, lại nu&ocirc;i dưỡng t&iacute;nh nh&agrave;n nh&atilde;, vốn l&agrave; điều đại kỵ của Thiền t&ocirc;ng, tăng ch&uacute;ng trong ch&ugrave;a Thiếu L&acirc;m trước nay kh&ocirc;ng ng&oacute; tới, nhưng những cao thủ trong ch&ugrave;a tinh th&ocirc;ng kiếm thuật đều gia c&ocirc;ng r&egrave;n luyện, để c&ugrave;ng kẻ đi&ecirc;n cuồng xưng m&igrave;nh ?kiếm th&aacute;nh? kia một phen cao thấp.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Anh em bọn Phan Thi&ecirc;n Canh nghĩ rằng v&igrave; m&igrave;nh mới xảy ra cớ sự, v&igrave; thế ng&agrave;y ng&agrave;y cưỡi tuấn m&atilde;, đi tuần đằng trước đằng sau n&uacute;i, định bụng sẽ ngăn chặn kẻ d&aacute;m xưng m&igrave;nh l&agrave; &quot;cầm kỳ kiếm tam th&aacute;nh? kia, đ&aacute;nh cho y kh&ocirc;ng v&agrave;o được đến cửa ch&ugrave;a, rồi sau sẽ trở lại c&ugrave;ng c&aacute;c h&ograve;a thượng trong ch&ugrave;a so t&agrave;i, để cho từ nay ai ai cũng biết T&acirc;y Vực Thiếu L&acirc;m giỏi hơn Trung Nguy&ecirc;n Thiếu L&acirc;m. N&agrave;o ngờ ở tại thạch đ&igrave;nh, H&agrave; T&uacute;c Đạo chỉ mới đưa ra một nửa sức lực, đ&atilde; cho ba anh em thua xiểng liểng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh thiền sư vừa nghe tin, biết rằng ch&ugrave;a Thiếu L&acirc;m vinh nhục thịnh suy cũng chỉ ở một l&uacute;c n&agrave;y, nhưng x&eacute;t ra v&otilde; c&ocirc;ng của ch&iacute;nh m&igrave;nh v&agrave; V&ocirc; Sắc, V&ocirc; Tướng ba người vị tất đ&atilde; hơn được anh em Phan Thi&ecirc;n Canh bao nhi&ecirc;u, cho n&ecirc;n đ&agrave;nh phải mời T&acirc;m Thiền Đường thất l&atilde;o ra &aacute;p trận. C&oacute; điều T&acirc;m Thiền thất l&atilde;o v&otilde; c&ocirc;ng cao thấp mức n&agrave;o kh&ocirc;ng ai biết được, nhưng trong l&uacute;c khẩn cấp c&oacute; thể ra tay chế ngự được C&ocirc;n L&ocirc;n Tam Th&aacute;nh chăng, điều đ&oacute; trong l&ograve;ng phương trượng v&agrave; V&ocirc; Sắc, V&ocirc; Tướng cũng chỉ cầu may th&ocirc;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&atilde;o phương trượng Thi&ecirc;n Minh thiền sư vừa thấy H&agrave; T&uacute;c Đạo v&agrave; Qu&aacute;ch Tương? liền chắp tay ch&agrave;o v&agrave; n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị n&agrave;y chắc l&agrave; người được gọi l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh H&agrave; cư sĩ phải kh&ocirc;ng? L&atilde;o tăng kh&ocirc;ng kịp ra xa ngh&ecirc;nh đ&oacute;n, xin thứ tội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo c&uacute;i m&igrave;nh h&agrave;nh lễ, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&atilde;n sinh H&agrave; T&uacute;c Đạo, ba chữ Tam Th&aacute;nh cuồng danh, n&agrave;o d&aacute;m n&oacute;i đến! Đến đ&acirc;y l&agrave;m rộn bảo s&aacute;t, thật l&ograve;ng kh&ocirc;ng an, nhọc l&ograve;ng ch&uacute;ng vị cao tăng ra khỏi ch&ugrave;a ngh&ecirc;nh đ&oacute;n, quả thực kh&ocirc;ng d&aacute;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh nghĩ thầm: ?G&atilde; cuồng sinh n&agrave;y n&oacute;i năng chẳng thấy cuồng ch&uacute;t n&agrave;o. Xem y chỉ độ tr&ecirc;n dưới ba mươi, sao c&oacute; thể vừa ra tay đ&atilde; đ&aacute;nh bại ba anh em Phan Thi&ecirc;n Canh được?? liền n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; cư sĩ kh&ocirc;ng phải kh&aacute;ch kh&iacute;, xin mời v&agrave;o ch&ugrave;a d&ugrave;ng tr&agrave;. C&ograve;n vị nữ cư sĩ th&igrave; ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lời n&oacute;i c&oacute; ch&uacute;t ngập ngừng tỏ ra kh&oacute; xử.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo nghe giọng &ocirc;ng c&oacute; &yacute; cự tuyệt kh&ocirc;ng cho Qu&aacute;ch Tương v&agrave;o ch&ugrave;a, t&iacute;nh ng&ocirc;ng cuồng bỗng ph&aacute;t t&aacute;c, ngửng mặt l&ecirc;n cười lớn, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o phương trượng, v&atilde;n sinh đến bảo s&aacute;t n&agrave;y, vốn l&agrave; do người kh&aacute;c ủy th&aacute;c, cốt để nhắn lại một c&acirc;u th&ocirc;i. C&acirc;u đ&oacute; n&oacute;i xong rồi, sẽ phủi tay đi khỏi. Thế nhưng nh&agrave; ch&ugrave;a trọng nam khinh nữ, sao thanh qui giới luật ở đ&acirc;u m&agrave; lắm thế, điểm đ&oacute; v&atilde;n sinh xem ra kh&ocirc;ng được vừa mắt. N&ecirc;n biết Phật ph&aacute;p v&ocirc; bi&ecirc;n, ch&uacute;ng sinh như nhất, nếu như vọng ph&acirc;n nam nữ chẳng phải t&acirc;m c&ograve;n c&oacute; chỗ vướng mắc hay sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh phương trượng l&agrave; bậc cao tăng hữu đạo, thiền t&acirc;m trong s&aacute;ng, c&oacute; l&ograve;ng bao dung rộng r&atilde;i, nghe thấy H&agrave; T&uacute;c Đạo n&oacute;i như vậy, mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đa ta cư sĩ chỉ điểm. Ch&ugrave;a Thiếu L&acirc;m ch&uacute;ng t&ocirc;i cưỡng ph&acirc;n nam nữ, quả thực c&oacute; điều nhỏ nhen. Như thế th&igrave; xin Qu&aacute;ch c&ocirc; nương c&ugrave;ng v&agrave;o uống tr&agrave; lu&ocirc;n thể.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nh&igrave;n H&agrave; T&uacute;c Đạo mỉm cười, thầm nghĩ: ?Miệng anh ch&agrave;ng n&agrave;y kh&eacute;o thật, chỉ một lời đ&atilde; thuyết phục được l&atilde;o h&ograve;a thượng?. Thấy Thi&ecirc;n Minh phương trượng đứng tr&aacute;nh qua một b&ecirc;n, đưa tay mời kh&aacute;ch, n&agrave;ng đang toan cất bước tiến v&agrave;o ch&ugrave;a, bỗng từ b&ecirc;n tr&aacute;i của Thi&ecirc;n Minh bước ra một l&atilde;o tăng kh&ocirc; gầy, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chỉ v&igrave; một lời của H&agrave; cư sĩ, khiến cho ch&ugrave;a Thiếu L&acirc;m ch&uacute;ng ta phải bỏ đi một lề luật đ&atilde; c&oacute; h&agrave;ng ng&agrave;n năm, tuy kh&ocirc;ng phải l&agrave; kh&ocirc;ng thể được, nhưng cũng phải xem người n&oacute;i ra thực c&oacute; bản sự, hay chỉ được c&aacute;i hư danh. Xin H&agrave; cư sĩ lưu lại một ng&oacute;n nghề để ch&uacute;ng tăng ch&ugrave;a n&agrave;y mở mắt, tất cả t&acirc;m phục, biết được do ai m&agrave; phải bỏ đi thi&ecirc;n ni&ecirc;n qui củ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&oacute; ch&iacute;nh l&agrave; thủ tọa Đạt Ma Viện V&ocirc; Tướng thiền sư. Tiếng n&oacute;i của &ocirc;ng oang oang, chứng tỏ trung kh&iacute; sung t&uacute;c, nội lực th&acirc;m hậu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bọn Phan Thi&ecirc;n Canh nghe c&acirc;u n&agrave;y, mặt hơi biến sắc. Lời n&oacute;i của V&ocirc; Tướng hiển nhi&ecirc;m h&agrave;m &yacute; coi ba người kh&ocirc;ng v&agrave;o đ&acirc;u, H&agrave; T&uacute;c Đạo tuy đ&aacute;nh bại họ, nhưng thực sự chưa chắc đ&atilde; c&oacute; bản l&atilde;nh. Qu&aacute;ch Tương thấy V&ocirc; Sắc thiền sư c&oacute; vẻ lo &acirc;u, nghĩ thầm l&atilde;o h&ograve;a thượng n&agrave;y đối với người thật tốt bụng, lại l&agrave; bạn của đại ca ca, nếu H&agrave; T&uacute;c Đạo v&igrave; m&igrave;nh m&agrave; tranh chấp với tăng ch&uacute;ng Thiếu L&acirc;m, d&ugrave; b&ecirc;n n&agrave;o thua, l&ograve;ng cũng &aacute;y n&aacute;y, n&ecirc;n cao giọng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; đại ca,? ch&ugrave;a Thiếu L&acirc;m kh&ocirc;ng phải l&agrave; kh&ocirc;ng v&agrave;o kh&ocirc;ng xong. &Ocirc;ng chuyển lại c&acirc;u n&oacute;i đ&oacute; đi, rồi ch&uacute;ng ta đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ v&agrave;o V&ocirc; Sắc, n&agrave;ng tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị V&ocirc; Sắc thiền sư n&agrave;y l&agrave; hảo bằng hữu của t&ocirc;i, c&aacute;c vị hai b&ecirc;n đừng để l&agrave;m tổn thương h&ograve;a kh&iacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo sững người, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- A, h&oacute;a ra l&agrave; thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Quay lại n&oacute;i với Thi&ecirc;n Minh:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o phương trượng, chẳng hay qu&iacute; tự c&oacute; một người t&ecirc;n l&agrave; Gi&aacute;c Viễn thiền sư chăng? C&oacute; người ủy th&aacute;c tại hạ, chuyển một c&acirc;u n&oacute;i đến cho vị đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh nhỏ giọng hỏi lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gi&aacute;c Viễn thiền sư?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn ở trong ch&ugrave;a địa vị rất thấp, mấy chục năm ẩn th&acirc;n nơi T&agrave;ng Kinh C&aacute;c, kh&ocirc;ng ai nghe đến, trước nay chưa ai k&egrave;m th&ecirc;m v&agrave;o ph&aacute;p danh của &ocirc;ng ta hai chữ ?thiền sư ? bao giờ, cho n&ecirc;n nhất thời Thi&ecirc;n Minh kh&ocirc;ng nghĩ ra. &Ocirc;ng ngẫm nghĩ gi&acirc;y l&aacute;t, mới hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- A, c&oacute; phải l&agrave; người l&agrave;m mất bộ Lăng Gi&agrave; kinh n&ecirc;n mất chức giữ T&agrave;ng Kinh C&aacute;c. H&agrave; cư sĩ t&igrave;m y, phải chăng việc c&oacute; quan hệ đến bộ kinh Lăng Gi&agrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- T&ocirc;i? cũng kh&ocirc;ng biết nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh n&oacute;i với một t&ecirc;n đệ tử:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi truyền cho Gi&aacute;c Viễn đến đ&acirc;y gặp kh&aacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đệ tử đ&oacute; l&atilde;nh mệnh hấp tấp đi ngay. V&ocirc; Tướng thiền sư lại n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; cư sĩ hiệu xưng cầm kiếm kỳ tam th&aacute;nh, chữ th&aacute;nh đ&oacute; người thường chẳng ai d&aacute;m m&agrave;ng. Ắt l&agrave; cả ba m&oacute;n H&agrave; cư sĩ đều c&oacute; t&agrave;i nghệ qu&aacute;n tuyệt thi&ecirc;n hạ. H&ocirc;m trước để thư cho tệ tự, n&oacute;i l&agrave; muốn ph&ocirc; b&agrave;y v&otilde; c&ocirc;ng, h&ocirc;m nay quang gi&aacute;ng, kh&ocirc;ng lẽ kh&ocirc;ng tứ gi&aacute;o cho to&agrave;n thể ch&uacute;ng t&ocirc;i được xem tuyệt kỹ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị c&ocirc; nương n&agrave;y đ&atilde; n&oacute;i rồi, hai b&ecirc;n kh&ocirc;ng n&ecirc;n l&agrave;m tổn thương h&ograve;a kh&iacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Tướng bừng bừng nổi giận, nghĩ thầm: ?Ngươi để thư g&acirc;y sự trước, nay việc đến th&igrave; lại từ chối, h&agrave;ng ngh&igrave;n năm nay, c&oacute; ai d&aacute;m v&ocirc; lễ với ch&ugrave;a Thiếu L&acirc;m như thế bao giờ đ&acirc;u? Huống chi bọn Phan Thi&ecirc;n Canh đ&atilde; bị bại dưới tay ngươi, tr&ecirc;n giang hồ sẽ truyền ra l&agrave; đại đệ tử của ph&aacute;i Thiếu L&acirc;m bị thua th&igrave; hai chữ kiếm th&aacute;nh kia c&agrave;ng th&ecirc;m vang dội? Xem ra trong đ&aacute;m đệ tử kh&ocirc;ng ai c&oacute; thể l&agrave; đối thủ của ngươi, đ&agrave;nh phải ch&iacute;nh m&igrave;nh xuất m&atilde; th&igrave; mới ổn.? Nghĩ thế &ocirc;ng bước l&ecirc;n hai bước, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tỉ th&iacute; v&otilde; nghệ, chưa hẳn đ&atilde; tổn thương h&ograve;a kh&iacute;, H&agrave; cư sĩ kh&ocirc;ng n&ecirc;n chối từ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Rồi quay đầu lại n&oacute;i với đ&aacute;m đệ tử Đạt Ma Đường:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Mang kiếm ra. Ch&uacute;ng ta xin l&atilde;nh gi&aacute;o kiếm thuật của ?kiếm th&aacute;nh?, để xem chữ ?th&aacute;nh? đ&oacute; tới bực n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Binh kh&iacute; trong ch&ugrave;a đ&atilde; chuẩn bị sẵn c&oacute; điều chư tăng d&agrave;n ra ngh&ecirc;nh đ&oacute;n kh&aacute;ch kh&ocirc;ng tiện mang theo để khỏi mang vẻ nhỏ nhen. C&aacute;c đệ tử nghe V&ocirc; Tướng dặn d&ograve;, quay m&igrave;nh trở về ch&ugrave;a, mang ra bảy t&aacute;m thanh trường kiếm, hay tay bưng đến trước mặt H&agrave; T&uacute;c Đạo, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; cư sĩ d&ugrave;ng bảo kiếm ch&iacute;nh m&igrave;nh mang theo? Hay mượn binh nhận tầm thường của tệ tự để sử dụng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo kh&ocirc;ng trả lời, c&uacute;i xuống nhặt một vi&ecirc;n đ&aacute; sắc cạnh, đột nhi&ecirc;n vạch ngang, vạch dọc tr&ecirc;n nền đ&aacute; tại trước s&acirc;n ch&ugrave;a. Chỉ trong khoảnh khắc, y đ&atilde; vạch ngang dọc mỗi chiều mười ch&iacute;n đường th&agrave;nh một b&agrave;n cờ lớn. Đường kinh đường vĩ đều thẳng tắp, tưởng như d&ugrave;ng thước m&agrave; đo, mỗi n&eacute;t đều s&acirc;u ngập v&agrave;o đ&aacute; khoảng nửa tấc. Phiến đ&aacute; đ&oacute; vốn đẽo bằng đ&aacute; xanh của n&uacute;i Thiếu Thất, cứng như th&eacute;p, mấy trăm năm qua biết bao người lai v&atilde;ng cũng kh&ocirc;ng m&ograve;n đi ch&uacute;t n&agrave;o. Y thuận tay d&ugrave;ng một cục đ&aacute; vẽ l&ecirc;n m&agrave; v&agrave;o s&acirc;u cả tấc, nội c&ocirc;ng như thế tr&ecirc;n đời &iacute;t thấy. Chỉ thấy y cười n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tỉ kiếm e rằng b&aacute; đạo, đ&aacute;nh đ&agrave;n th&igrave; kh&ocirc;ng c&oacute; c&aacute;ch g&igrave; m&agrave; so t&agrave;i. Nếu đại h&ograve;a thượng cảm thấy cao hứng, ch&uacute;ng ta đ&aacute;nh với nhau một v&aacute;n cờ xem sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Việc y d&ugrave;ng đ&aacute; vạch th&agrave;nh b&agrave;n cờ quả l&agrave; một tuyệt kỹ kinh người, Thi&ecirc;n Minh, V&ocirc; Sắc, V&ocirc; Tướng v&agrave; cả đến T&acirc;m Thiền Đường thất l&atilde;o ai cũng mặt m&agrave;y ngơ ngẩn, thầm sợ trong l&ograve;ng. Thi&ecirc;n Minh phương trượng biết rằng nội lực hồn hậu như thế trong ch&ugrave;a kh&ocirc;ng ai b&igrave; kịp, t&acirc;m địa &ocirc;ng quang phong tễ nguyệt, đang định mở miệng chịu thua, bỗng nghe tiếng x&iacute;ch sắt loảng xoảng k&eacute;o l&ecirc; tr&ecirc;n đất vọng tới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy Gi&aacute;c Viễn g&aacute;nh đ&ocirc;i th&ugrave;ng sắt lớn đi tới trước mặt, theo sau l&agrave; một thiếu ni&ecirc;n cao r&aacute;o. Gi&aacute;c Viễn tay tr&aacute;i vịn lấy m&eacute;p th&ugrave;ng nước, tay phải đơn chưởng chỉ l&ecirc;n trời hướng về Thi&ecirc;n Minh h&agrave;nh lễ, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Theo lệnh l&atilde;o phương trượng cho gọi đến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị H&agrave; cư sĩ n&agrave;y c&oacute; lời muốn n&oacute;i với ngươi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn quay người nh&igrave;n lại, thấy H&agrave; T&uacute;c Đạo nhưng kh&ocirc;ng nhận ra l&agrave; ai, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tiểu tăng l&agrave; Gi&aacute;c Viễn, cư sĩ c&oacute; điều g&igrave; muốn sai bảo?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo vạch xong b&agrave;n cờ, kỳ hứng bột ph&aacute;t, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chuyện đ&oacute; để n&oacute;i sau cũng kh&ocirc;ng muộn. B&acirc;y giờ đại h&ograve;a thượng n&agrave;o c&ugrave;ng ta đ&aacute;nh cờ trước?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thực ra y kh&ocirc;ng cố &yacute; hiển thị c&ocirc;ng phu, c&oacute; điều trong đời đối với ba m&oacute;n cầm kỳ kiếm ham qu&aacute; h&oacute;a m&ecirc;, mỗi khi hứng tới th&igrave; d&ugrave; c&oacute; trời sập cũng kh&ocirc;ng coi v&agrave;o đ&acirc;u, n&ecirc;n khi muốn đ&aacute;nh cờ chỉ mong c&oacute; người đối cục, c&ograve;n qu&ecirc;n hết việc tỉ th&iacute; v&otilde; c&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thi&ecirc;n Minh thiền sư n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; cư sĩ vạch đ&aacute; th&agrave;nh b&agrave;n cờ, thần c&ocirc;ng như thế, l&atilde;o nạp từ khi sinh ra chưa thấy bao giờ, tăng ch&uacute;ng trong tệ tự cam chịu hạ phong.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn nghe Thi&ecirc;n Minh n&oacute;i như vậy, nh&igrave;n xuống thấy b&agrave;n cờ lớn tr&ecirc;n nền đ&aacute; xanh, biết rằng người n&agrave;y đến đ&acirc;y cốt để ph&ocirc; b&agrave;y v&otilde; c&ocirc;ng, lập tức xốc lại đ&ocirc;i th&ugrave;ng sắt, h&iacute;t một hơi, đem hết c&ocirc;ng lực một đời tu luyện dồn xuống hai đ&ugrave;i, theo những đường vạch của b&agrave;n cờ từng bước đi tới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy d&acirc;y x&iacute;ch tr&ecirc;n ch&acirc;n &ocirc;ng ta đi đến đ&acirc;u th&igrave; tr&ecirc;n phiến đ&aacute; hiện ra một vết l&otilde;m rộng chừng năm tấc, những đường vạch của H&agrave; T&uacute;c Đạo bị x&oacute;a sạch. Ch&uacute;ng tăng thấy thế kh&ocirc;ng nhịn được đều lớn tiếng hoan h&ocirc;. Thi&ecirc;n Minh, V&ocirc; Sắc, V&ocirc; Tướng vừa lạ l&ugrave;ng vừa mừng rỡ, kh&ocirc;ng ngờ vị l&atilde;o tăng si si ngốc ngốc n&agrave;y lại c&oacute; nội c&ocirc;ng th&acirc;m hậu đến thế, tuy c&ugrave;ng sống dưới một m&aacute;i ch&ugrave;a mấy chục năm, m&agrave; m&igrave;nh kh&ocirc;ng hề hay biết g&igrave;. Bọn Thi&ecirc;n Minh biết rằng nếu chỉ tr&ocirc;ng v&agrave;o nội lực của con người th&igrave; d&ugrave; mạnh c&aacute;ch mấy cũng kh&ocirc;ng thể n&agrave;o đạp l&ecirc;n đ&aacute; xanh m&agrave; in dấu được. Cũng nhờ Gi&aacute;c Viễn g&aacute;nh th&ecirc;m đ&ocirc;i th&ugrave;ng sắt đầy nước, tổng cộng phải đến hơn bốn trăm c&acirc;n. Hơn bốn trăm c&acirc;n đ&oacute; từ vai truyền xuống dưới x&iacute;ch sắt ở đ&ocirc;i b&agrave;n ch&acirc;n, đi tới chẳng kh&aacute;c g&igrave; một c&aacute;i đục lớn đục l&ecirc;n đ&aacute;, c&agrave;o sạch những đường vạch dọc ngang của H&agrave; T&uacute;c Đạo. Nếu Gi&aacute;c Viễn chỉ đi ch&acirc;n kh&ocirc;ng, th&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o c&oacute; thể l&agrave;m được. Tuy thế, mặc d&ugrave; c&oacute; mượn lực thật, nhưng cũng l&agrave; thần c&ocirc;ng &iacute;t c&oacute; ở tr&ecirc;n đời.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo kh&ocirc;ng đợi cho &ocirc;ng ta x&oacute;a hết ba mươi t&aacute;m đường dọc ngang của b&agrave;n cờ, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, nội c&ocirc;ng th&acirc;m hậu của &ocirc;ng, tại hạ kh&ocirc;ng sao b&igrave; kịp.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đến l&uacute;c ấy tuy ch&acirc;n kh&iacute; trong đan điền Gi&aacute;c Viễn mỗi l&uacute;c một thịnh, nhưng hai ch&acirc;n dẫu sao cũng chỉ l&agrave; thịt da con người, đ&atilde; cảm thấy đau nhức, nghe H&agrave; T&uacute;c Đạo k&ecirc;u? lập tức ngừng lại, mỉm cười ng&acirc;m nga:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhất b&igrave;nh tụ thủ tương tr&iacute; chi,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; hạ vi cừ ph&acirc;n hắc bạch?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Một nước cờ đi xem cũng đủ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đ&acirc;u cần quyết liệt đến hơn thua)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&uacute;ng thế. Cuộc cờ n&agrave;y kh&ocirc;ng cần phải đi qu&acirc;n, t&ocirc;i dĩ nhi&ecirc;n thua rồi. Để xin l&atilde;nh gi&aacute;o &ocirc;ng về kiếm ph&aacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong nghe xoẹt một tiếng, từ đ&aacute;y chiếc dao cầm y đ&atilde; r&uacute;t ra một thanh trường kiếm, mũi kiếm hướng thẳng về ngực m&igrave;nh, c&aacute;n kiếm nghi&ecirc;ng nghi&ecirc;ng ra b&ecirc;n ngo&agrave;i. Chi&ecirc;u khởi thủ n&agrave;y đ&atilde; thật qu&aacute;i dị, chẳng kh&aacute;c g&igrave; d&ugrave;ng kiếm tự giết m&igrave;nh, trong những kiếm ph&aacute;p của thi&ecirc;n hạ, chưa từng thấy một chi&ecirc;u n&agrave;o kỳ lạ như thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&atilde;o tăng chỉ biết niệm kinh đả tọa, qu&eacute;t nh&agrave; phơi s&aacute;ch, v&otilde; c&ocirc;ng một chi&ecirc;u cũng kh&ocirc;ng biết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng H&agrave; T&uacute;c Đạo đời n&agrave;o lại tin. Y cười khẩy mấy tiếng, tung m&igrave;nh vọt đến, trường kiếm ph&oacute;ng bật ra, mũi kiếm đ&acirc;m thẳng v&agrave;o ngực Gi&aacute;c Viễn, xuất chi&ecirc;u nhanh như thế kh&ocirc;ng một loại kiếm ph&aacute;p n&agrave;o b&igrave; kịp. H&oacute;a ra chi&ecirc;u kiếm n&agrave;y kh&ocirc;ng đ&acirc;m thẳng v&agrave;o đối phương m&agrave; trước hết phải tụ nội lực sau đ&oacute; gom k&igrave;nh bắn ra. Thế nhưng nội c&ocirc;ng Gi&aacute;c Viễn đ&atilde; đến mức t&ograve;ng t&acirc;m sở dục, thu ph&aacute;t tự như, kiếm của H&agrave; T&uacute;c Đạo tuy c&oacute; nhanh, nhưng t&acirc;m của Gi&aacute;c Viễn lại động c&ograve;n nhanh hơn, &yacute; đến đ&acirc;u th&igrave; tay đến đ&oacute;, th&acirc;n &yacute; l&agrave; một. Tay phải &ocirc;ng thu v&agrave;o, c&aacute;i th&ugrave;ng sắt lập tức tạt qua che ngay trước mặt, chỉ nghe keng một tiếng, mũi kiếm đ&atilde; đ&acirc;m v&agrave;o c&aacute;i th&ugrave;ng sắt. Th&acirc;n kiếm uốn lại, cong th&agrave;nh như chiếc cung. H&agrave; T&uacute;c Đạo thu trường kiếm về, thuận tay lại vung ra, th&ugrave;ng sắt b&ecirc;n tr&aacute;i của Gi&aacute;c Viễn tạt qua, chặn lại một lần nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo nghĩ thầm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&otilde; c&ocirc;ng &ocirc;ng c&oacute; cao nhưng đ&ocirc;i th&ugrave;ng n&agrave;y nặng nề chậm chạp l&agrave;m sao c&oacute; thể đỡ nổi đường kho&aacute;i kiếm của ta? Gi&aacute; như &ocirc;ng tay kh&ocirc;ng đối chi&ecirc;u th&igrave; ta c&ograve;n e ngại ba phần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y co tay b&uacute;ng v&agrave;o th&acirc;n kiếm một c&aacute;i, k&ecirc;u l&ecirc;n u u như tiếng rồng ng&acirc;m, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, xin cẩn thận!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trường kiếm rung l&ecirc;n, trước sau tr&aacute;i phải, chỉ trong nh&aacute;y mắt tấn c&ocirc;ng bốn bốn mười s&aacute;u chi&ecirc;u liền. Chỉ nghe keng keng keng li&ecirc;n tiếp, mười s&aacute;u đường ?Tấn L&ocirc;i Kiếm? của H&agrave; T&uacute;c Đạo đều đ&acirc;m v&agrave;o hai chiếc th&ugrave;ng sắt. Người đứng ngo&agrave;i thấy Gi&aacute;c Viễn tay ch&acirc;n loạng quạng, lạng tr&aacute;i n&eacute; phải, quả thực hết sức l&uacute;ng t&uacute;ng, đ&uacute;ng l&agrave; kh&ocirc;ng biết t&iacute; v&otilde; c&ocirc;ng n&agrave;o. Thế nhưng mười s&aacute;u chi&ecirc;u kiếm của H&agrave; T&uacute;c Đạo d&ugrave; thần diệu đến đ&acirc;u, đều bị Gi&aacute;c Viễn lấy c&aacute;i hết sức vụng về, hết sức nực cười d&ugrave;ng th&ugrave;ng sắt đỡ được cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bọn V&ocirc; Sắc, V&ocirc; Tướng ở b&ecirc;n ngo&agrave;i kh&ocirc;ng khỏi lo lắng, đều k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; cư sĩ kiếm hạ lưu t&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương cũng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Xin đừng d&ugrave;ng s&aacute;t thủ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mọi người đều thấy Gi&aacute;c Viễn kh&ocirc;ng biết v&otilde;, thế nhưng trong trận đấu n&agrave;y, d&ugrave; H&agrave; T&uacute;c Đạo thi triển to&agrave;n lực vẫn kh&ocirc;ng l&agrave;m sao đụng đến đối phương một mảy. Kh&ocirc;ng ai biết rằng v&igrave; Gi&aacute;c Viễn chưa học v&otilde; bao giờ nhưng c&oacute; thể đỡ được kiếm chi&ecirc;u chỉ bởi v&igrave; v&ocirc; t&igrave;nh luyện th&agrave;nh thượng thừa nội c&ocirc;ng. H&agrave; T&uacute;c Đạo tấn k&iacute;ch kh&ocirc;ng th&agrave;nh c&ocirc;ng, bất th&igrave;nh l&igrave;nh h&eacute;t l&ecirc;n một tiếng, một &aacute;nh lấp l&aacute;nh, đ&acirc;m thẳng kiếm v&agrave;o bụng dưới Gi&aacute;c Viễn. Gi&aacute;c Viễn k&ecirc;u l&ecirc;n: ?Ối chao? trong cơn bối rối hai tay chập lại, nghe b&igrave;nh một tiếng, hai chiếc th&ugrave;ng sắt đ&atilde; kẹp cứng thanh trường kiếm. H&agrave; T&uacute;c Đạo cố gắng giựt về, nhưng thanh kiếm n&agrave;o c&oacute; nh&uacute;c nh&iacute;ch. Y ứng biến thật nhanh, lập tức bỏ kiếm, song chưởng c&ugrave;ng đ&aacute;nh ra, chưởng lực như b&agrave;i sơn đảo hải đ&aacute;nh thẳng v&agrave;o mặt Gi&aacute;c Viễn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&uacute;c ấy Gi&aacute;c Viễn đ&acirc;u c&ograve;n tay n&agrave;o m&agrave; đỡ, trong t&igrave;nh thế nguy cấp Trương Qu&acirc;n Bảo nghĩa thầy tr&ograve; s&acirc;u đậm, liền nhảy tới d&ugrave;ng chi&ecirc;u Tứ Th&ocirc;ng B&aacute;t Đạt trước đ&acirc;y Dương Qu&aacute; đ&atilde; dạy cho y, đ&aacute;nh ch&eacute;o v&agrave;o vai H&agrave; T&uacute;c Đạo. Cũng l&uacute;c đ&oacute;, nội lực của Gi&aacute;c Viễn truyền v&agrave;o th&ugrave;ng sắt, hai cột nước từ trong th&ugrave;ng vọt ra, phun thẳng v&agrave;o mặt H&agrave; T&uacute;c Đạo. Chưởng lực v&agrave; cột nước đụng nhau, nước văng tung t&oacute;e khiến hai người đều ướt đẫm, tuy nhi&ecirc;n sức đ&aacute;nh của H&agrave; T&uacute;c Đạo cũng ti&ecirc;u tan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo đang to&agrave;n lực c&ugrave;ng Gi&aacute;c Viễn chiến đấu n&ecirc;n kh&ocirc;ng đỡ được thế đ&aacute;nh của Trương Qu&acirc;n Bảo, chỉ nghe bịch một c&aacute;i, vai y đ&atilde; tr&uacute;ng chưởng. Kh&ocirc;ng ngờ Trương Qu&acirc;n Bảo tuổi c&ograve;n nhỏ nhưng chưởng ph&aacute;p thật kỳ diệu, nội lực cũng thật th&acirc;m hậu, H&agrave; T&uacute;c Đạo đứng kh&ocirc;ng vững, phải lao đao đổ về ph&iacute;a tr&aacute;i ba bước. Gi&aacute;c Viễn k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- A Di Đ&agrave; Phật, A Di Đ&agrave; Phật, xin H&agrave; cư sĩ tha cho. Mấy đường kiếm của cư sĩ l&agrave;m cho l&atilde;o tăng sợ hết cả hồn v&iacute;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vừa n&oacute;i &ocirc;ng vừa lấy vạt &aacute;o lau những giọt nước tr&ecirc;n mặt, lui về tr&aacute;nh qua một b&ecirc;n. H&agrave; T&uacute;c Đạo giận dữ n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thiếu L&acirc;m tự l&agrave; nơi ngọa hổ t&agrave;ng long, quả thực kh&ocirc;ng vừa. Cả đến một đứa b&eacute; con th&acirc;n thủ cũng đ&atilde; như thế. N&agrave;y cậu b&eacute; kia, ra đ&acirc;y đấu với ta, nếu ngươi tiếp được mười chi&ecirc;u, H&agrave; T&uacute;c Đạo n&agrave;y suốt đời sẽ kh&ocirc;ng đặt ch&acirc;n v&agrave;o Trung Thổ nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc, V&ocirc; Tướng đều biết rằng Trương Qu&acirc;n Bảo chỉ l&agrave; một đứa b&eacute; tạp dịch trong T&agrave;ng Kinh C&aacute;c, chưa từng luyện qua v&otilde; c&ocirc;ng, kh&ocirc;ng biết trời xui đất khiến thế n&agrave;o lại đ&aacute;nh tr&uacute;ng y một chưởng, nếu như thực sự động v&otilde;, n&oacute;i g&igrave; mười chi&ecirc;u, chỉ sợ một chi&ecirc;u cũng đ&atilde; t&aacute;ng mạng dưới tay H&agrave; T&uacute;c Đạo rồi. V&ocirc; Tướng ngang nhi&ecirc;n n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Lời n&oacute;i đ&oacute; của H&agrave; cư sĩ sai rồi. T&ecirc;n hiệu &ocirc;ng l&agrave; C&ocirc;n L&ocirc;n Tam Th&aacute;nh, v&otilde; học chấn cổ thước kim, sao lại động thủ với một thằng b&eacute; pha tr&agrave; qu&eacute;t s&acirc;n? Nếu như kh&ocirc;ng hiềm, để l&atilde;o tăng ra tiếp cư sĩ mười chi&ecirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i nhục một chưởng vừa rồi, đ&acirc;u c&oacute; như thế m&agrave; bỏ qua được. Tiểu tử, coi chi&ecirc;u đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong nghe v&ugrave; một tiếng, y nhắm ngực Trương Qu&acirc;n Bảo đ&aacute;nh tới một quyền. Quyền đ&oacute; thế đ&aacute;nh thật nhanh, y v&agrave; Trương Qu&acirc;n Bảo lại đứng thật gần, V&ocirc; Sắc, V&ocirc; Tướng muốn cứu viện cũng kh&ocirc;ng sao kịp. Mọi người đều k&ecirc;u khổ thầm, chỉ thấy Trương Qu&acirc;n Bảo hai g&oacute;t ch&acirc;n kh&ocirc;ng động, đầu b&agrave;n ch&acirc;n lướt sang b&ecirc;n tr&aacute;i, th&acirc;n m&igrave;nh lại thuận thế nghi&ecirc;ng về b&ecirc;n phải, th&agrave;nh hữu cung tả tiễn bộ, nhẹ nh&agrave;ng kh&eacute;o l&eacute;o tr&aacute;nh được quả đấm, tiếp theo b&agrave;n tay b&ecirc;n tr&aacute;i nắm lại th&agrave;nh quyền để bảo vệ b&ecirc;n h&ocirc;ng, chưởng b&ecirc;n phải đ&aacute;nh ra, ch&iacute;nh l&agrave; một chi&ecirc;u quyền ph&aacute;p cơ bản của ph&aacute;i Thiếu L&acirc;m t&ecirc;n gọi ?Hữu Xuy&ecirc;n Hoa Thủ?. Chi&ecirc;u đ&oacute; đ&aacute;nh ra kh&iacute; ngưng trọng như n&uacute;i cao, thế h&ugrave;ng hậu như bể rộng s&ocirc;ng d&agrave;i, thực l&agrave; phong phạm của danh gia kỳ t&uacute;c, ai d&aacute;m bảo l&agrave; th&acirc;n thủ của một thiếu ni&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo từ l&uacute;c bị y đ&aacute;nh tr&uacute;ng một chưởng v&agrave;o vai, biết rằng thiếu ni&ecirc;n n&agrave;y nội lực c&ograve;n hơn xa bọn Phan Thi&ecirc;n Canh, nhưng tin rằng chỉ mười chi&ecirc;u sẽ bị đ&aacute;nh bại. Y thấy chi&ecirc;u ?Hữu Xuy&ecirc;n Hoa Thủ? chỉ l&agrave; c&ocirc;ng phu nhập m&ocirc;n của ph&aacute;i Thiếu L&acirc;m, nhưng c&aacute;ch thức ph&aacute;t chưởng chuyển th&acirc;n, k&igrave;nh lực h&ugrave;ng hồn, th&acirc;n h&igrave;nh trầm ổn, kh&ocirc;ng chỗ n&agrave;o sơ hở để c&oacute; thể phản k&iacute;ch lại, nhịn kh&ocirc;ng nổi phải k&ecirc;u l&ecirc;n khen ngợi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Hảo quyền ph&aacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Tướng chợt động l&ograve;ng, nh&igrave;n V&ocirc; Sắc mỉm cười:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ch&uacute;c mừng sư huynh &aacute;m trung thu được một đệ tử đắc &yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc lắc đầu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng phải ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy Trương Qu&acirc;n Bảo li&ecirc;n tiếp sử ba chi&ecirc;u Ảo Bộ Lạp Cung, Đơn Phượng Triều Dương, v&agrave; Nhị Lang Đảm Sam, ph&aacute;p độ nghi&ecirc;m nhặt, k&igrave;nh lực vững v&agrave;ng, kh&ocirc;ng k&eacute;m bất cứ cao thủ n&agrave;o trong ph&aacute;i Thiếu L&acirc;m. Thi&ecirc;n Minh, V&ocirc; Sắc, V&ocirc; Tướng v&agrave; cả T&acirc;m Thiền thất l&atilde;o thấy Trương Qu&acirc;n Bảo đ&aacute;nh mấy chi&ecirc;u của Thiếu L&acirc;m xuất sắc như thế, ai nấy đều kinh ngạc. V&ocirc; Tướng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Quyền ph&aacute;p của y nghi&ecirc;m cẩn th&igrave; đ&atilde; đ&agrave;nh, nhưng nội c&ocirc;ng cao như thế ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vừa n&oacute;i đến đ&acirc;y, H&agrave; T&uacute;c Đạo đ&atilde; đ&aacute;nh đến chi&ecirc;u thứ s&aacute;u, bụng nghĩ thầm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đến một thằng b&eacute; chưa r&aacute;o m&aacute;u đầu m&igrave;nh chưa đối ph&oacute; nổi, vậy m&agrave; d&aacute;m đến ch&ugrave;a Thiếu L&acirc;m đưa thư khi&ecirc;u chiến, chẳng khiến anh h&ugrave;ng trong thi&ecirc;n hạ cười vẹo cả răng ư?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đột nhi&ecirc;n y chuyển m&igrave;nh v&ugrave;n vụt, đ&aacute;nh ra chi&ecirc;u Thi&ecirc;n Sơn Tuyết Ti&ecirc;u, bốn phương t&aacute;m hướng bay m&uacute;a, chỉ nh&aacute;y mắt đ&atilde; bao phủ to&agrave;n thể th&acirc;n h&igrave;nh Trương Qu&acirc;n Bảo trong chưởng ảnh. Trương Qu&acirc;n Bảo trừ bốn chi&ecirc;u do Dương Qu&aacute; dạy cho tr&ecirc;n đỉnh n&uacute;i Hoa Sơn, chưa được một v&otilde; sư n&agrave;o giảng giải v&otilde; c&ocirc;ng, bỗng dưng thấy h&agrave;ng trăm h&igrave;nh b&oacute;ng huyễn ảo, chưởng ph&aacute;p biến h&oacute;a v&ocirc; c&ugrave;ng, chẳng biết c&aacute;ch n&agrave;o s&aacute;ch giải. Trong cơn nguy cấp,? y chuyển eo về ph&iacute;a tr&aacute;i th&agrave;nh h&agrave;n k&ecirc; thế, hay tay giơ l&ecirc;n tr&ecirc;n tr&aacute;n, hổ khẩu tay tr&aacute;i đối chiếu với hổ khẩu tay phải, đ&oacute; ch&iacute;nh l&agrave; Song Khuy&ecirc;n Thủ của Thiếu L&acirc;m quyền. Chi&ecirc;u n&agrave;y nặng nề như n&uacute;i, chi&ecirc;u của địch kh&ocirc;ng cần giải m&agrave; tự giải được. Bất luận H&agrave; T&uacute;c Đạo từ hướng n&agrave;o tấn c&ocirc;ng tới, đều vướng v&agrave;o trong v&ograve;ng Song Khuy&ecirc;n Thủ của Trương Qu&acirc;n Bảo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy tất cả c&aacute;c đệ tử của Đạt Ma Đường, La H&aacute;n Đường c&ugrave;ng reo h&ograve; như sấm động, bội phục Trương Qu&acirc;n Bảo d&ugrave;ng một chi&ecirc;u hết sức b&igrave;nh thường kh&ocirc;ng c&oacute; g&igrave; kỳ lạ h&oacute;a giải được tất cả những chi&ecirc;u thức cực kỳ phức tạp, ảo diệu của đối phương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trong khi mọi người c&ograve;n đang reo h&ograve;, H&agrave; T&uacute;c Đạo bỗng h&uacute; l&ecirc;n một tiếng d&agrave;i, nghe v&ugrave; một c&aacute;i, đ&atilde; nhắm ngực Trương Qu&acirc;n Bảo đ&aacute;nh ra một quyền. Quyền ph&aacute;p của y đang từ kh&eacute;o l&eacute;o chuyển sang vụng về, nhưng k&igrave;nh lực thật l&agrave; phi ph&agrave;m. Trương Qu&acirc;n Bảo lập tức d&ugrave;ng chi&ecirc;u Thi&ecirc;n Hoa Thất Tinh, hai chưởng c&ugrave;ng đ&aacute;nh ra. Quyền chưởng gặp nhau, chỉ nghe b&igrave;nh một tiếng, H&agrave; T&uacute;c Đạo th&acirc;n h&igrave;nh hơi loạng choạng, c&ograve;n Trương Qu&acirc;n Bảo phải l&ugrave;i về sau ba bước. H&agrave; T&uacute;c Đạo hừ một tiếng, quyền ph&aacute;p kh&ocirc;ng đổi, nhưng tiến l&ecirc;n hai bước, quyền đ&aacute;nh ra đều hết sức cương m&atilde;nh. Trương Qu&acirc;n Bảo lại d&ugrave;ng chi&ecirc;u Thi&ecirc;n Hoa Thất Tinh hai chưởng đ&aacute;nh tới. Lại nghe b&igrave;nh một tiếng thật lớn, lần n&agrave;y Trương Qu&acirc;n Bảo phải l&ugrave;i năm bước. H&agrave; T&uacute;c Đạo th&acirc;n m&igrave;nh bổ về ph&iacute;a trước, mặt biến sắc, qu&aacute;t l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Chỉ c&ograve;n một chi&ecirc;u nữa, ngươi h&atilde;y đem to&agrave;n lực ra chống đỡ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y tiến l&ecirc;n ba bước, xuống m&atilde; bộ thật ổn, một quyền từ từ đ&aacute;nh ra. L&uacute;c ấy to&agrave;n thể mấy trăm người trong ch&ugrave;a Thiếu L&acirc;m đều y&ecirc;n lặng kh&ocirc;ng nghe một tiếng động, ai cũng biết rằng quyền n&agrave;y l&agrave; to&agrave;n thể anh danh một đời của H&agrave; T&uacute;c Đạo, kh&ocirc;ng thể kh&ocirc;ng kiệt tận to&agrave;n lực.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo lại ra chi&ecirc;u Thi&ecirc;n Hoa Thất Tinh lần thứ ba, nhưng lần n&agrave;y quyền chưởng đụng nhau, v&ocirc; thanh v&ocirc; tức, hai người chỉ hơi rung động một ch&uacute;t, rồi th&uacute;c đẩy nội lực ra kh&aacute;ng cự. N&oacute;i đến v&otilde; c&ocirc;ng gia số,? H&agrave; T&uacute;c Đạo phải giỏi gấp trăm lần Trương Qu&acirc;n Bảo. Thế nhưng khi tỉ đấu nội lực, Trương Qu&acirc;n Bảo đ&atilde; từng học được t&acirc;m ph&aacute;p Cửu Dương Ch&acirc;n Kinh, nội lực li&ecirc;n mi&ecirc;n nối kết, hồn hậu tr&agrave;n ra. Chỉ trong chốc l&aacute;t, H&agrave; T&uacute;c Đạo liệu thế kh&ocirc;ng thể thắng được y, n&ecirc;n tung m&igrave;nh nhảy l&ecirc;n, để chưởng lực Trương Qu&acirc;n Bảo rơi v&agrave;o khoảng kh&ocirc;ng, lấy tay để v&agrave;o lưng y đẩy nhẹ một c&aacute;i. Trương Qu&acirc;n Bảo t&eacute; lăn ra đất, nhất thời kh&ocirc;ng đứng dậy được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&agrave; T&uacute;c Đạo xua tay phải, cười khẩy:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- H&agrave; T&uacute;c Đạo ơi l&agrave; H&agrave; T&uacute;c Đạo, sao ngươi cuồng vọng đến thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y hướng về ph&iacute;a Thi&ecirc;n Minh thiền sư v&aacute;i một c&aacute;i thật s&acirc;u, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&otilde; c&ocirc;ng ch&ugrave;a Thiếu L&acirc;m dương danh đ&atilde; h&agrave;ng ngh&igrave;n năm, quả nhi&ecirc;n kh&ocirc;ng phải tầm thường, h&ocirc;m nay cho kẻ cuồng sinh n&agrave;y được mở mắt, thật đ&uacute;ng l&agrave; người c&oacute; thịnh danh kh&ocirc;ng thể l&agrave; kẻ hư sĩ. Phục thay, phục thay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong quay m&igrave;nh, đầu ng&oacute;n ch&acirc;m điểm một c&aacute;i, đ&atilde; vọt ra xa mấy trượng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y ngừng lại, quay đầu n&oacute;i với Gi&aacute;c Viễn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gi&aacute;c Viễn đại sư, người đ&oacute; nhờ t&ocirc;i chuyển c&aacute;o một c&acirc;u, n&oacute;i l&agrave;: ?Kinh thư ở trong dầu?.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i vừa dứt, đầu ng&oacute;n ch&acirc;n lại điểm mấy c&aacute;i, trong chớp mắt đ&atilde; đi thật xa, th&acirc;n ph&aacute;p nhanh như thế quả tr&ecirc;n đời &iacute;t thấy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo từ từ đứng dậy, mặt mũi, đầu t&oacute;c d&iacute;nh đầy b&ugrave;n đất. Tuy y bị H&agrave; T&uacute;c Đạo đẩy ng&atilde;, nhưng những cao thủ đều thấy rằng H&agrave; T&uacute;c Đạo ăn gian để bỏ đi. Tuy nhi&ecirc;n, trong lời y n&oacute;i vẫn x&aacute;c định l&agrave; kh&ocirc;ng địch nổi thần c&ocirc;ng của ph&aacute;i Thiếu L&acirc;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một nh&agrave; sư gi&agrave; gầy g&ograve; trong T&acirc;m Thiền Đường thất l&atilde;o bỗng hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- V&otilde; c&ocirc;ng của đệ tử n&agrave;y ai dạy cho đ&oacute;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thanh &acirc;m của &ocirc;ng ta hết sức sắc nhọn, chẳng kh&aacute;c g&igrave; tiếng c&uacute; k&ecirc;u trong đ&ecirc;m khuya, người n&agrave;o nghe thấy cũng kh&ocirc;ng khỏi r&ugrave;ng m&igrave;nh. Bọn Thi&ecirc;n Minh, V&ocirc; Sắc, V&ocirc; Tướng đều mang c&aacute;i nghi vấn đ&oacute; trong l&ograve;ng, n&ecirc;n c&ugrave;ng hướng về ph&iacute;a Trương Qu&acirc;n Bảo v&agrave; Gi&aacute;c Viễn. Thầy tr&ograve; Gi&aacute;c Viễn đứng trơ trơ, nhất thời kh&ocirc;ng sao trả lời được. Thi&ecirc;n Minh n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Gi&aacute;c Viễn nội c&ocirc;ng tuy tinh th&ocirc;ng, nhưng chưa học quyền ph&aacute;p. C&ograve;n Thiếu L&acirc;m quyền của thiếu ni&ecirc;n n&agrave;y, do ai dạy cho vậy?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&aacute;c đệ tử của Đạt Ma Đường v&agrave; La H&aacute;n Đường đều kh&ocirc;ng ai tưởng nổi nguy nan của bản tự h&ocirc;m nay, lại do thằng b&eacute; đ&oacute; đứng ra cản được cường địch, l&atilde;o phương trượng thể n&agrave;o cũng trọng thưởng, v&agrave; người dạy y quyền ph&aacute;p nội c&ocirc;ng, cũng được vinh sủng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vị l&atilde;o tăng đ&oacute; thấy Trương Qu&acirc;n Bảo đứng y&ecirc;n kh&ocirc;ng động, bất thần l&ocirc;ng m&agrave;y nhướng l&ecirc;n, mặt đầy s&aacute;t kh&iacute;, gay gắt hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ta hỏi ngươi, b&agrave;i La H&aacute;n quyền ai dạy cho ngươi đ&oacute;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo từ trong bọc lấy ra đ&ocirc;i la h&aacute;n bằng sắt Qu&aacute;ch Tương tặng cho, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đệ tử coi theo tư thức đ&ocirc;i thiết la h&aacute;n n&agrave;y, tự m&igrave;nh học lấy v&agrave;i miếng, chứ thực kh&ocirc;ng c&oacute; ai truyền thụ v&otilde; c&ocirc;ng cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">L&atilde;o tăng đ&oacute; tiến l&ecirc;n một bước, thanh &acirc;m trầm xuống, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- - Ngươi một lần nữa minh minh bạch bạch n&oacute;i lại l&agrave; &quot;B&agrave;i La H&aacute;n quyền n&agrave;y kh&ocirc;ng phải một vị sư phụ n&agrave;o trong ch&ugrave;a dạy cho, m&agrave; chỉ tự học lấy&quot;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng ta tuy hạ giọng, nhưng trong lời n&oacute;i lại c&agrave;ng uy hiếp th&ecirc;m. Trương Qu&acirc;n Bảo trong l&ograve;ng thản nhi&ecirc;n, tự cho rằng m&igrave;nh kh&ocirc;ng l&agrave;m điều g&igrave; xấu xa, tuy l&atilde;o tăng đ&oacute; thần th&aacute;i dồn &eacute;p, nhưng cũng kh&ocirc;ng sợ, lớn tiếng n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đệ tử ở trong T&agrave;ng Kinh C&aacute;c qu&eacute;t nh&agrave;, ch&acirc;m tr&agrave;, hầu hạ Gi&aacute;c Viễn sư phụ, kh&ocirc;ng một ai trong ch&ugrave;a dạy đệ tử v&otilde; c&ocirc;ng. B&agrave;i La H&aacute;n quyền l&agrave; do đệ tử tự học, nếu sử kh&ocirc;ng được đ&uacute;ng c&aacute;ch, xin l&atilde;o sư phụ chỉ điểm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vị l&atilde;o tăng đ&oacute; mắt như t&oacute;e lửa, trừng trừng nh&igrave;n Trương Qu&acirc;n Bảo một hồi thật l&acirc;u, kh&ocirc;ng n&oacute;i một lời. Gi&aacute;c Viễn biết rằng vị n&agrave;y trong T&acirc;m Thiền Đường thất l&atilde;o vai vế thật cao, l&agrave; sư th&uacute;c của phương trượng Thi&ecirc;n Minh thiền sư. Khi thấy &ocirc;ng ta đối với Trương Qu&acirc;n Bảo c&oacute; vẻ th&ugrave; hận như thế, kh&ocirc;ng hiểu tại sao, nhưng mắt &ocirc;ng ta chứa đầy o&aacute;n độc, trong đầu bỗng dưng nảy ra một điều, chẳng kh&aacute;c g&igrave; một tia lửa xẹt, nhớ lại một năm xưa v&ocirc; t&igrave;nh đọc được trong T&agrave;ng Kinh C&aacute;c một cuốn s&aacute;ch nhỏ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cuốn s&aacute;ch mỏng đ&oacute; ch&eacute;p lại một đại sự trong ch&ugrave;a:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khoảng hơn bảy mươi năm trước, phương trượng ch&ugrave;a Thiếu L&acirc;m l&agrave; Khổ Thừa, ch&iacute;nh l&agrave; sư tổ của Thi&ecirc;n Minh thiền sư. Trung thu năm đ&oacute;,? trong ch&ugrave;a c&oacute; phi&ecirc;n khảo th&iacute; h&agrave;ng năm tại Đạt Ma Đường, do phương trượng c&ugrave;ng hai vị thủ tọa Đạt Ma Đường, La H&aacute;n Đường c&ugrave;ng x&eacute;t v&otilde; c&ocirc;ng c&aacute;c đệ tử, để xem trong năm qua đ&atilde; tiến bộ đến mức n&agrave;o. Sau khi c&aacute;c đệ tử biểu diễn xong, thủ tọa Đạt Ma Đường l&agrave; Khổ Tr&iacute; thiền sư thăng tọa b&igrave;nh phẩm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bỗng nhi&ecirc;n từ trong đ&aacute;m đ&ocirc;ng, một đầu đ&agrave; để t&oacute;c[2] vượt mọi người tiến ra, lớn tiếng ph&ecirc; b&igrave;nh Khổ Tr&iacute; thiền sư n&oacute;i năng chẳng đ&acirc;u v&agrave;o đ&acirc;u, kh&ocirc;ng biết căn bản v&otilde; c&ocirc;ng l&agrave; g&igrave;, vậy m&agrave; lại giữ chức thủ tịch Đạt Ma Đường, thật đ&aacute;ng xấu hổ. Ch&uacute;ng tăng ai nấy đều kinh ngạc, nh&igrave;n kỹ lại, h&oacute;a ra y l&agrave; một người nh&oacute;m lửa nấu ăn trong nh&agrave; bếp. C&aacute;c đệ tử Đạt Ma Đường kh&ocirc;ng đợi sư phụ mở lời, đều l&ecirc;n tiếng qu&aacute;t mắng. Hỏa c&ocirc;ng đầu đ&agrave; cũng qu&aacute;t lại:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ th&igrave; n&oacute;i năng bậy bạ, đệ tử ch&uacute;ng bay cũng chẳng ra qu&aacute;i g&igrave;!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong nhảy v&agrave;o đứng giữa s&acirc;n. C&aacute;c đệ tử từng người bước ra c&ugrave;ng y động thủ, đều bị y cho mỗi người v&agrave;i c&uacute; đấm, v&agrave;i c&aacute;i đ&aacute; l&agrave; thua cả. Thực ra tại Đạt Ma Đường khi đấu với nhau, đồng m&ocirc;n so t&agrave;i, chỉ tới mức hơn thua l&agrave; ngừng lại, ai ai cũng thủ hạ lưu t&igrave;nh. Thế nhưng g&atilde; hỏa c&ocirc;ng đầu đ&agrave; ra tay cực kỳ t&agrave;n độc, y li&ecirc;n tiếp đ&aacute;nh bại ch&iacute;n đại đệ tử của Đạt Ma Đường, ch&iacute;n nh&agrave; sư đ&oacute; kh&ocirc;ng g&atilde;y tay th&igrave; cũng qu&egrave; ch&acirc;n, người n&agrave;o cũng bị trọng thương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khổ Tr&iacute; thiền sư vừa kinh h&atilde;i, vừa tức giận, thấy sở học của g&atilde; hỏa c&ocirc;ng đầu đ&agrave; to&agrave;n l&agrave; v&otilde; c&ocirc;ng bản m&ocirc;n, chứ kh&ocirc;ng phải cao thủ m&ocirc;n ph&aacute;i kh&aacute;c lẻn v&agrave;o quấy ph&aacute;, n&ecirc;n cố n&eacute;n giận, hỏi y do ai truyền dạy. G&atilde; hỏa c&ocirc;ng đầu đ&agrave; đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Kh&ocirc;ng ai dạy t&ocirc;i cả, t&ocirc;i tự học một m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&oacute;a ra nh&agrave; sư gi&aacute;m quản bếp Hương T&iacute;ch t&iacute;nh t&igrave;nh cực kỳ n&oacute;ng nảy, đối với đầu đ&agrave; nh&oacute;m lửa, hơi sai một t&iacute; l&agrave; giơ quyền thoi ngay. Y th&acirc;n mang v&otilde; nghệ n&ecirc;n mỗi lần đ&aacute;nh rất nặng tay. G&atilde; hỏa c&ocirc;ng đầu đ&agrave; trong ba năm liền bị đ&aacute;nh hộc m&aacute;u ba lần, trong l&ograve;ng t&iacute;ch o&aacute;n, n&ecirc;n l&eacute;n l&uacute;t học v&otilde;. Đệ tử ch&ugrave;a Thiếu L&acirc;m ai cũng biết v&otilde;, nếu muốn học l&eacute;n c&aacute;c chi&ecirc;u thức, thật rất nhiều cơ hội. Y quyết t&acirc;m học để b&aacute;o th&ugrave;, lại tr&iacute; tuệ hơn người, n&ecirc;n sau hơn hai mươi năm đ&atilde; luyện th&agrave;nh v&otilde; c&ocirc;ng thượng thừa. Tuy nhi&ecirc;n y dấu k&iacute;n kh&ocirc;ng ai hay biết,? vẫn lặng lẽ ẩn th&acirc;n dưới bếp chụm lửa, g&atilde; tăng nh&acirc;n gi&aacute;m tr&ugrave; c&oacute; đ&aacute;nh đập, y cũng kh&ocirc;ng đ&aacute;nh trả, c&oacute; điều nội c&ocirc;ng đ&atilde; tinh n&ecirc;n kh&ocirc;ng c&ograve;n bị thương nữa. T&ecirc;n hỏa c&ocirc;ng đầu đ&agrave; đ&oacute; t&iacute;nh t&igrave;nh &acirc;m hiểm, đợi tới l&uacute;c tin rằng v&otilde; c&ocirc;ng cao hơn hết mọi người trong ch&ugrave;a, nh&acirc;n kỳ khảo th&iacute; Trung Thu mới xuất đầu lộ hiển th&acirc;n thủ. Uất ức t&iacute;ch trong mấy chục năm nay, khiến y th&ugrave; hận tất cả mọi tăng nh&acirc;n, n&ecirc;n khi xuất thủ kh&ocirc;ng một ch&uacute;t dung t&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khổ Tr&iacute; thiền sư hỏi kỹ đầu đu&ocirc;i xong, cười nhạt ba tiếng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- C&aacute;i khổ t&acirc;m của ngươi, quả thật đ&aacute;ng k&iacute;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lập tức rời ghế đi xuống, ra tay c&ugrave;ng y tỉ th&iacute;. Khổ Tr&iacute; thiền sư l&agrave; cao thủ của ch&ugrave;a Thiếu L&acirc;m, nhưng phần v&igrave; ni&ecirc;n kỷ đ&atilde; cao, c&ograve;n g&atilde; hỏa c&ocirc;ng đầu đ&agrave; đang tr&aacute;ng ni&ecirc;n, hai nữa Khổ Tr&iacute; ra tay dung t&igrave;nh, ngược lại hỏa c&ocirc;ng đầu đ&agrave; ra chi&ecirc;u n&agrave;o cũng đều l&agrave; s&aacute;t thủ, th&agrave;nh ra đấu đến hơn năm trăm hiệp, Khổ Tr&iacute; mới thắng được đối phương. Khi hai người s&aacute;ch đến chi&ecirc;u Đại Triền Ti, bốn c&aacute;nh tay xoắn v&agrave;o nhau, nhưng hai tay của Khổ Tr&iacute; thiền sư đ&atilde; ấn v&agrave;o tử huyệt tr&ecirc;n ngực của địch thủ, chỉ cần ph&aacute;t nội lực l&agrave; hỏa c&ocirc;ng đầu đ&agrave; phải t&aacute;ng mệnh, kh&ocirc;ng c&ograve;n c&aacute;ch g&igrave; giải nữa. Khổ Tr&iacute; thương t&igrave;nh y tiềm t&acirc;m tự luyện, n&ecirc;n tới đ&oacute; l&agrave; coi như đ&atilde; xong, kh&ocirc;ng nỡ giết y, hai tay vừa rời nhau, h&eacute;t lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- L&ugrave;i ra ngay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;o ngờ hỏa c&ocirc;ng đầu đ&agrave; hiểu lầm &yacute; của &ocirc;ng, lại tưởng l&agrave; đối phương sử dụng một chi&ecirc;u trong Thần Chưởng B&aacute;t Đả. Thần Chưởng B&aacute;t Đả l&agrave; một trong những tuyệt học của ph&aacute;i Thiếu L&acirc;m. Y đ&atilde; từng xem đại đệ tử của Đạt Ma Đường sử qua chi&ecirc;u n&agrave;y, hai tay ch&eacute;m ra, đ&aacute;nh g&atilde;y một c&aacute;i cột gỗ, k&igrave;nh lực thật l&agrave; kinh hồn. Hỏa c&ocirc;ng đầu đ&agrave; v&otilde; c&ocirc;ng tuy cao, nhưng đều l&agrave; học l&eacute;n, chưa được minh sư chỉ điểm, tuy thời gian c&oacute; d&agrave;i, nhưng đ&acirc;u c&oacute; thể học được hết ph&aacute;i Thiếu L&acirc;m b&aacute;c đại tinh th&acirc;m. Thực ra chi&ecirc;u đ&oacute; của Khổ Tr&iacute; thiền sư l&agrave; Ph&acirc;n Giải Chưởng, mượn sức để chế ngự sức, cả hai b&ecirc;n c&ugrave;ng lui ra, c&oacute; &yacute; ngừng tay th&ocirc;i kh&ocirc;ng đấu nữa. N&agrave;o ngờ hỏa c&ocirc;ng đầu đ&agrave; lại tưởng l&agrave; chi&ecirc;u? thứ s&aacute;u trong Thần Chưởng B&aacute;t Đả l&agrave; Liệt T&acirc;m Chưởng, nghĩ thầm: ?Ngươi định giết ta, đ&acirc;u c&oacute; dễ d&agrave;ng như thế?. Lập tức tung m&igrave;nh nhảy l&ecirc;n, hai chưởng đ&aacute;nh ra một lượt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hai chưởng đ&oacute; như b&agrave;i sơn đảo hải &agrave;o tới, Khổ Tr&iacute; thiền sư hốt hoảng thu chưởng về đỡ, nhưng kh&ocirc;ng c&ograve;n kịp nữa. Chỉ nghe l&aacute;ch c&aacute;ch mấy tiếng, xương c&aacute;nh tay tr&aacute;i? c&ugrave;ng bốn rẻ xương sườn đ&atilde; bị đ&aacute;nh g&atilde;y n&aacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chư tăng đứng b&ecirc;n kinh ho&agrave;ng biến sắc, lập tức x&ocirc;ng l&ecirc;n cứu viện, nhưng Khổ Tr&iacute; thiền sư chỉ c&ograve;n thoi th&oacute;p, nội tạng đ&atilde; bị trọng thương, kh&ocirc;ng n&oacute;i được một lời n&agrave;o. Nh&igrave;n lại t&ecirc;n hỏa c&ocirc;ng đầu đ&agrave; th&igrave; nh&acirc;n l&uacute;c hỗn loạn đ&atilde; trốn mất kh&ocirc;ng biết đi đ&acirc;u. Tối h&ocirc;m đ&oacute;, Khổ Tr&iacute; thiền sư v&igrave; bị thương qu&aacute; nặng n&ecirc;n qua đời. Trong khi cả ch&ugrave;a đang sầu thảm, g&atilde; hỏa c&ocirc;ng đầu đ&agrave; lại lẻn trở v&agrave;o, d&ugrave;ng trọng thủ đ&aacute;nh chết nh&agrave; sư gi&aacute;m quản Hương T&iacute;ch tr&ugrave; v&agrave; năm tăng nh&acirc;n kh&aacute;c c&oacute; hiềm kh&iacute;ch với y. Cả ch&ugrave;a n&aacute;o loạn, phương trượng ph&aacute;i v&agrave;i chục cao thủ đi bốn phương t&igrave;m kiếm, nhưng khắp Giang Nam, Giang Bắc, kh&ocirc;ng thấy tung t&iacute;ch y đ&acirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&igrave; việc n&agrave;y m&agrave; c&aacute;c nh&agrave; sư vai vế cao trong ch&ugrave;a tranh chấp, người nọ đổ lỗi cho người kia. Thủ tọa La H&aacute;n Đường l&agrave; Khổ Tuệ thiền sư giận dữ bỏ đi, khai s&aacute;ng ph&aacute;i Thiếu L&acirc;m T&acirc;y Vực. Ba người Phan Thi&ecirc;n Canh, Phương Thi&ecirc;n Lao, Vệ Thi&ecirc;n Vọng ch&iacute;nh l&agrave; đệ tử t&aacute;i truyền của Khổ Tuệ thiền sư.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cũng tại thế m&agrave; v&otilde; học ch&ugrave;a Thiếu L&acirc;m bị suy đồi suốt mấy chục năm. Từ đ&oacute; ch&ugrave;a ra qui định rằng, ph&agrave;m người n&agrave;o kh&ocirc;ng được thầy truyền thụ m&agrave; tự m&igrave;nh học l&eacute;n v&otilde; c&ocirc;ng, nếu ph&aacute;t hiện ra nặng th&igrave; bị xử tử, nhẹ th&igrave; cắt hết c&acirc;n mạch to&agrave;n th&acirc;n cho th&agrave;nh phế nh&acirc;n. Từ đ&oacute; tới nay trong ch&ugrave;a ph&ograve;ng phạm nghi&ecirc;m mật, kh&ocirc;ng ai d&aacute;m th&acirc;u học v&otilde; c&ocirc;ng, n&ecirc;n điều tự qui n&agrave;y dần dần mọi người qu&ecirc;n l&atilde;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vị l&atilde;o tăng trong T&acirc;m Thiền Đường n&agrave;y ch&iacute;nh l&agrave; một đệ tử nhỏ của thủ tọa Đạt Ma Đường Khổ Tr&iacute; thiền sư xưa kia, c&aacute;i cảnh thảm tử của &acirc;n sư mấy chục năm trước vẫn in đậm trong đầu. B&acirc;y giờ thấy Trương Qu&acirc;n Bảo lại l&agrave; kẻ kh&ocirc;ng được thầy truyền m&agrave; học l&eacute;n v&otilde; c&ocirc;ng, n&ecirc;n vừa buồn rầu vừa giận dữ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn giữ s&aacute;ch tại T&agrave;ng Kinh C&aacute;c, s&aacute;ch n&agrave;o cũng đọc qua, giật m&igrave;nh nhớ lại c&acirc;u chuyện n&agrave;y, mồ h&ocirc;i lạnh chảy ướt đẫm lưng, k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- - L&atilde;o phương trượng, c&aacute;i n&agrave;y ... c&aacute;i n&agrave;y kh&ocirc;ng tr&aacute;ch Qu&acirc;n Bảo được .</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i chưa dứt c&acirc;u, chỉ thấy thủ tọa Đạt Ma Đường V&ocirc; Tướng thiền sư qu&aacute;t lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tất cả đệ tử Đạt Ma Đường c&ugrave;ng x&ocirc;ng l&ecirc;n, bắt thằng nhỏ đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lập tức mười t&aacute;m tăng nh&acirc;n đệ tử của Đạt Ma Đường đều tiến l&ecirc;n, v&acirc;y quanh Gi&aacute;c Viễn v&agrave; Trương Qu&acirc;n Bảo l&agrave;m th&agrave;nh một v&ograve;ng tr&ograve;n rất lớn, cả Qu&aacute;ch Tương cũng bị v&acirc;y ở b&ecirc;n trong. Vị l&atilde;o tăng T&acirc;m Thiền Đường lại cao giọng gay gắt tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- La H&aacute;n Đường đệ tử, sao c&ograve;n chưa hết sức x&ocirc;ng l&ecirc;n?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cả một trăm linh t&aacute;m đệ tử La H&aacute;n Đường c&ugrave;ng cất tiếng đ&aacute;p, giọng rền vang như sấm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Tu&acirc;n lệnh!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Họ lập tức v&acirc;y th&ecirc;m ba v&ograve;ng nữa b&ecirc;n ngo&agrave;i Đạt Ma Đường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo ch&acirc;n tay l&uacute;ng t&uacute;ng kh&ocirc;ng hiểu v&igrave; đ&acirc;u, cho rằng tại m&igrave;nh ra tay đ&aacute;nh H&agrave; T&uacute;c Đạo bỏ chạy, phạm phải tự qui, n&ecirc;n k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ, con ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn hơn mười năm nay đối với đứa học tr&ograve; chẳng kh&aacute;c g&igrave; ch&iacute;nh bản th&acirc;n m&igrave;nh, t&igrave;nh như cha con, biết rằng một khi Trương Qu&acirc;n Bảo bị bắt, nếu kh&ocirc;ng bị giết, th&igrave; cũng th&agrave;nh phế nh&acirc;n. Lại nghe thấy V&ocirc; Tướng thiền sư qu&aacute;t tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sao chưa động thủ, chờ đến bao giờ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mười t&aacute;m đệ tử Đạt Ma Đường c&ugrave;ng niệm Phật hiệu, tiến bước đi l&ecirc;n. Gi&aacute;c Viễn kh&ocirc;ng c&ograve;n suy nghĩ g&igrave; nữa, vội đứng tại chỗ quay lu&ocirc;n một v&ograve;ng, hai chiếc th&ugrave;ng sắt vung ra, một luồng k&igrave;nh phong đẩy những nh&agrave; sư kh&ocirc;ng ai d&aacute;m tiến l&ecirc;n nữa. Gi&aacute;c Viễn lại xoay người hắt nước trong th&ugrave;ng đổ &agrave;o ra, rồi tiện thế b&ecirc;n tr&aacute;i m&uacute;c Qu&aacute;ch Tương, b&ecirc;n phải m&uacute;c Trương Qu&acirc;n Bảo. &Ocirc;ng li&ecirc;n tiếp xoay bảy t&aacute;m v&ograve;ng liền, hai chiếc th&ugrave;ng sắt được nội lực h&ugrave;ng hậu v&ocirc; tỉ truyền v&agrave;o chẳng kh&aacute;c n&agrave;o hai chiếc lưu tinh ch&ugrave;y c&oacute; sức ngh&igrave;n c&acirc;n, thi&ecirc;n hạ n&agrave;o ai d&aacute;m đỡ. Ch&uacute;ng đệ tử Đạt Ma Đường tứ t&aacute;n tr&aacute;nh ra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn bước lẹ như bay, g&aacute;nh Qu&aacute;ch Tương v&agrave; Trương Qu&acirc;n Bảo chạy thẳng xuống n&uacute;i. C&aacute;c tăng nh&acirc;n miệng h&ograve; ch&acirc;n đuổi, chỉ nghe tiếng x&iacute;ch sắt loảng xoảng mỗi l&uacute;c một xa, đuổi độ bảy t&aacute;m dặm, tiếng x&iacute;ch kh&ocirc;ng c&ograve;n nghe thấy nữa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&ugrave;a Thiếu L&acirc;m qui luật cực nghi&ecirc;m, thủ tọa Đạt Ma Đường đ&atilde; ra lệnh bắt Trương Qu&acirc;n Bảo, tuy c&aacute;c nh&agrave; sư thấy đuổi kh&ocirc;ng kịp nữa rồi, nhưng vẫn hết sức rượt theo. Thời gian c&agrave;ng l&acirc;u, cước lực c&agrave;ng r&otilde; kẻ nhanh người chậm, những người khinh c&ocirc;ng k&eacute;m một ch&uacute;t bị bỏ lại sau. Đuổi đến l&uacute;c trời tối, dẫn đầu chỉ c&ograve;n năm người đại đệ tử, trước mặt lại thấy năm ba ngả đường, chẳng biết Gi&aacute;c Viễn chạy theo đường n&agrave;o. Đến l&uacute;c n&agrave;y, gi&aacute; như c&oacute; đuổi kịp th&igrave; năm tăng nh&acirc;n cũng kh&ocirc;ng thể địch lại Gi&aacute;c Viễn v&agrave; Trương Qu&acirc;n Bảo, n&ecirc;n chỉ c&ograve;n c&aacute;ch lủi thủi quay về ch&ugrave;a bẩm b&aacute;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn g&aacute;nh th&ecirc;m hai người chạy một mạch mấy chục dặm mới ngừng bước. Chỉ thấy nơi &ocirc;ng dừng ch&acirc;n l&agrave; một v&ugrave;ng n&uacute;i s&acirc;u, m&acirc;y m&ugrave; bốn bề, những đ&agrave;n quạ bay về nườm nượp. Gi&aacute;c Viễn nội lực tuy cao, nhưng qua một trận chạy b&aacute;n sống b&aacute;n chết như thế, g&acirc;n cốt cũng r&atilde; rời đến nỗi nhất thời kh&ocirc;ng để đ&ocirc;i th&ugrave;ng sắt xuống được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo v&agrave; Qu&aacute;ch Tương từ trong th&ugrave;ng nhảy ra, mỗi người đỡ một b&ecirc;n th&ugrave;ng, từ tr&ecirc;n vai &ocirc;ng xuống. Trương Qu&acirc;n Bảo n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ,? thầy nghỉ một l&aacute;t, con đi kiếm xem c&oacute; g&igrave; ăn kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng chỉ thấy bốn b&ecirc;n cỏ mọc đến ngang đầu gối, ở nơi n&uacute;i s&acirc;u hoang vắng thế n&agrave;y, c&ograve;n c&oacute; g&igrave; m&agrave; ăn được. Trương Qu&acirc;n Bảo đi kiếm cả nửa ng&agrave;y, chỉ h&aacute;i được? một vốc d&acirc;u dại, đem về ba người chia nhau ăn ngấu nghiến rồi dựa lưng v&agrave;o đ&aacute; m&agrave; nghỉ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, t&ocirc;i xem c&aacute;c nh&agrave; sư ch&ugrave;a Thiếu L&acirc;m, trừ &ocirc;ng v&agrave; V&ocirc; Sắc thiền sư ra, người n&agrave;o cũng thật l&agrave; hồ đồ cổ qu&aacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn chỉ hừ một tiếng nhưng kh&ocirc;ng đ&aacute;p lời. Qu&aacute;ch Tương n&oacute;i tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- G&atilde; C&ocirc;n L&ocirc;n Tam Th&aacute;nh kia đến ch&ugrave;a Thiếu L&acirc;m, trong ch&ugrave;a kh&ocirc;ng ai địch nổi,? chỉ nhờ hai thầy tr&ograve; &ocirc;ng đ&aacute;nh đuổi được y, mới bảo to&agrave;n được danh dự của m&ocirc;n ph&aacute;i. Họ đ&atilde; kh&ocirc;ng c&aacute;m ơn &ocirc;ng th&igrave; th&ocirc;i, lại hung hăng định bắt Trương huynh đệ, kh&ocirc;ng ph&acirc;n biệt trắng đen phải tr&aacute;i, thật chẳng c&oacute; l&yacute; do n&agrave;o hết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn thở d&agrave;i một tiếng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Việc đ&oacute; kh&ocirc;ng tr&aacute;ch l&atilde;o phương trượng v&agrave; V&ocirc; Tướng sư huynh được, ch&ugrave;a Thiếu L&acirc;m vốn c&oacute; một điều tự qui ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i đến đ&acirc;y, hơi bị đứt đoạn, &ocirc;ng ho l&ecirc;n s&ugrave; sụ. Qu&aacute;ch Tương nh&egrave; nhẹ đấm lưng cho &ocirc;ng, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- &Ocirc;ng mệt rồi, nằm ngủ một giấc đi, chuyện đ&oacute; để từ từ kể sau cũng kh&ocirc;ng muộn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn thở d&agrave;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đ&uacute;ng vậy, t&ocirc;i mệt lắm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo đi gom một mớ củi, nh&oacute;m một đống lửa, để hơ cho kh&ocirc; quần &aacute;o của m&igrave;nh v&agrave; Qu&aacute;ch Tương. Cả ba người ngồi dưới gốc c&acirc;y to m&agrave; ngủ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương ngủ đến nửa đ&ecirc;m, bỗng nghe c&oacute; tiếng Gi&aacute;c Viễn lẩm bẩm n&oacute;i một m&igrave;nh, h&igrave;nh như đang niệm kinh, mơ mơ m&agrave;ng m&agrave;ng tỉnh dậy, chỉ nghe &ocirc;ng ta đọc:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- ? bỉ chi lực phương ngại ng&atilde; chi b&igrave; mao, ng&atilde; chi &yacute; nhập bỉ cốt l&yacute;. Lưỡng thủ chi tr&agrave;ng, nhất kh&iacute; qu&aacute;n xuy&ecirc;n. Tả trọng tắc tả hư, nhi hữu dĩ khứ, hữu trọng tắc hữu hư, nhi tả dĩ khứ khứ ... (... sức của người vừa mới phớt qua b&ecirc;n ngo&agrave;i da thịt ta, &yacute; của ta đ&atilde; v&agrave;o đến xương của người. Hai tay vừa đụng nhau, một kh&iacute; đ&atilde; th&ocirc;ng qua rồi. B&ecirc;n tr&aacute;i nặng ắt b&ecirc;n tr&aacute;i cũng nhẹ, b&ecirc;n phải đ&atilde; đến rồi, b&ecirc;n phải nặng ắt b&ecirc;n phải cũng nhẹ, b&ecirc;n tr&aacute;i đ&atilde; đến rồi )</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương bỗng thấy r&ugrave;ng m&igrave;nh: ?&Ocirc;ng ta kh&ocirc;ng đọc kinh Phật kiểu kh&ocirc;ng tức thị sắc, sắc tức thị kh&ocirc;ng, m&agrave; lại g&igrave; l&agrave; tả trọng tả hư, hữu trọng hữu hư, giống như v&otilde; học quyền kinh th&igrave; phải.?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thấy &ocirc;ng ta ngưng lại một l&aacute;t rồi niệm tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- kh&iacute; như xa lu&acirc;n, chu th&acirc;n cụ yếu tương t&ugrave;y, hữu bất tướng t&ugrave;y khứ, th&acirc;n tiện t&aacute;n loạn, kỳ bệnh ư y&ecirc;u tho&aacute;i lai chi ? (kh&iacute; tựa như b&aacute;nh xe, t&ugrave;y chỗ n&agrave;o tr&ecirc;n cơ thể cần m&agrave; chạy đến, nếu kh&ocirc;ng tới lui theo &yacute; m&igrave;nh, th&acirc;n sẽ t&aacute;n loạn, ắt sẽ bị bệnh ở h&ocirc;ng v&agrave; đ&ugrave;i rồi..)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe đến c&acirc;u: ?kỳ bệnh ư y&ecirc;u tho&aacute;i lai chi?, trong bụng kh&ocirc;ng c&ograve;n nghi ngờ g&igrave; nữa, biết l&agrave; &ocirc;ng ta đang đọc những yếu chỉ trong v&otilde; học, nghĩ thầm:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Vị đại h&ograve;a thượng n&agrave;y chẳng biết t&iacute; v&otilde; n&agrave;o, chỉ v&igrave; say m&ecirc; đọc s&aacute;ch, trong s&aacute;ch c&oacute; g&igrave;, đều coi như thi&ecirc;n kinh địa nghĩa cả. Năm trước gặp &ocirc;ng ta lần đầu tr&ecirc;n đỉnh n&uacute;i Hoa Sơn, đ&atilde; từng nghe &ocirc;ng ta n&oacute;i rằng, tr&ecirc;n m&eacute;p lề cuốn kinh Lăng Gi&agrave; do tự tay Đạt Ma l&atilde;o tổ ch&eacute;p ra, lại c&oacute; ch&eacute;p bộ Cửu Dương Ch&acirc;n Kinh, &ocirc;ng ta cho rằng đ&acirc;y l&agrave; kinh thư dạy về thuật gi&uacute;p người ta được th&acirc;n thể khỏe mạnh, n&ecirc;n theo đ&oacute; m&agrave; tu tập. Hai thầy tr&ograve; &ocirc;ng ta kh&ocirc;ng được ai dạy dỗ g&igrave; cả, nhưng v&ocirc; h&igrave;nh chung lại đạt tới mức cao thủ trong thi&ecirc;n hạ. Hồi đ&oacute; Ti&ecirc;u Tương Tử đ&aacute;nh &ocirc;ng ta một chưởng, &ocirc;ng chỉ đứng y&ecirc;n m&agrave; chịu, vậy m&agrave; ch&iacute;nh Ti&ecirc;u Tương Tử lại bị trọng thương, thần c&ocirc;ng như thế, chưa chắc cha ta hay đại ca ca đ&atilde; đạt tới được. H&ocirc;m nay thầy tr&ograve; &ocirc;ng ta lại khiến cho H&agrave; T&uacute;c Đạo phải c&uacute;i đầu bỏ đi, chắc cũng nhờ c&ocirc;ng lực của Cửu Dương Ch&acirc;n Kinh. Hiện giờ miệng &ocirc;ng ta lẩm bẩm tụng đọc kia, phải chăng ch&iacute;nh l&agrave; bộ kinh đ&oacute;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng nghĩ như thế, lại e l&agrave;m dao động Gi&aacute;c Viễn, chỉ &acirc;m thầm ngồi dậy, lắng nghe kinh văn, cố gắng ghi nhớ, tự nhủ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Nếu quả thực &ocirc;ng ấy đang đọc bộ Cửu Dương Ch&acirc;n Kinh th&igrave; những chỗ ảo diệu tinh vi, kh&ocirc;ng phải chỉ một l&uacute;c m&agrave; hiểu ngay được. Chi bằng m&igrave;nh cứ ghi nhớ, sau n&agrave;y nhờ &ocirc;ng ta giảng lại cho cũng chưa muộn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lại nghe nh&agrave; sư gi&agrave; đọc tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- ? ti&ecirc;n dĩ t&acirc;m sử th&acirc;n, t&ograve;ng nh&acirc;n bất t&ograve;ng kỷ, hậu th&acirc;n năng t&ograve;ng t&acirc;m, do kỷ nhưng t&ograve;ng nh&acirc;n. Do kỷ tắc trệ, t&ograve;ng nh&acirc;n tắc hoạt. Năng t&ograve;ng nh&acirc;n, thủ thượng tiện hữu ph&acirc;n thốn, xứng bỉ k&igrave;nh chi đại tiểu, ph&acirc;n l&yacute; bất th&aacute;c; quyền bỉ lai chi trường đoản, h&agrave;o ph&aacute;t v&ocirc; sai. Tiền tiến hậu tho&aacute;i, xứ xứ kh&aacute;p hợp, c&ocirc;ng di cữu nhi kỹ di tinh ? (trước l&agrave; d&ugrave;ng t&acirc;m để sai khiến th&acirc;n m&igrave;nh, theo người m&agrave; kh&ocirc;ng theo m&igrave;nh, sau sẽ đến lượt th&acirc;n đi theo t&acirc;m, do m&igrave;nh nhưng t&ugrave;y theo người. Theo m&igrave;nh th&igrave; sẽ vướng mắc, do người th&igrave; sẽ linh hoạt. Biết theo người th&igrave; tay m&igrave;nh c&oacute; gang tấc, c&acirc;n đo được sức của người lớn hay nhỏ, một ly một ph&acirc;n cũng kh&ocirc;ng chạy; c&acirc;n lượng của người d&agrave;i ngắn, một sợi l&ocirc;ng cũng kh&ocirc;ng sai. L&uacute;c trước tiến nhưng sau lại l&ugrave;i,? mọi chỗ đều ăn khớp với nhau,? c&ocirc;ng phu c&agrave;ng l&acirc;u th&igrave; kỹ thuật c&agrave;ng tinh vi..)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương nghe đến đ&oacute;, kh&ocirc;ng khỏi lắc đầu, trong bụng nghĩ thầm: ?Sai rồi, sai rồi. Cha mẹ ta thường n&oacute;i l&agrave;, khi l&acirc;m địch, phải chế ngự người chứ kh&ocirc;ng để người chế ngự m&igrave;nh. Đại h&ograve;a thượng n&agrave;y n&oacute;i kh&ocirc;ng đ&uacute;ng? Lại nghe Gi&aacute;c Viễn tụng tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Bỉ bất động, kỷ bất động, bỉ vi động, kỷ dĩ động. K&igrave;nh tự khoan nhi phi t&uacute;ng, tương triển vị triển, k&igrave;nh đoạn &yacute; bất đoạn ? (người chẳng động th&igrave; ta cũng chẳng động. Người vừa hơi động, ta đ&atilde; động rồi. K&igrave;nh tưởng như chậm chạp m&agrave; kh&ocirc;ng lơi lỏng, sắp bu&ocirc;ng ra nhưng lại chưa bung, k&igrave;nh đứt rồi nhưng &yacute; chưa đứt ?)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương c&agrave;ng nghe c&agrave;ng thấy m&ugrave; mờ, từ nhỏ tới giờ n&agrave;ng học v&otilde; chỉ nghe thấy n&oacute;i tới việc ra tay trước để chế ngự người, ra tay sau th&igrave; bị người ta chế ngự m&igrave;nh (ti&ecirc;n ph&aacute;t chế nh&acirc;n, hậu ph&aacute;t chế ư nh&acirc;n) l&uacute;c n&agrave;o cũng cần nhanh nhẹn, l&uacute;c n&agrave;o cũng cần đi trước. B&acirc;y giờ nghe Gi&aacute;c Viễn đọc quyền kinh yếu quyết, c&aacute;i g&igrave; m&agrave; ?theo m&igrave;nh th&igrave; sẽ vướng mắc, do người th&igrave; sẽ linh hoạt?, kh&aacute;c hẳn với những g&igrave; n&agrave;ng đ&atilde; biết, nghĩ thầm: ?Khi gặp địch phải ra tay, hai b&ecirc;n t&iacute;nh mệnh đem ra đ&aacute;nh đổi, nếu như m&igrave;nh cứ bỏ m&igrave;nh, theo người, địch bảo m&igrave;nh b&ecirc;n đ&ocirc;ng th&igrave; m&igrave;nh đi b&ecirc;n đ&ocirc;ng, bảo m&igrave;nh qua b&ecirc;n t&acirc;y th&igrave; m&igrave;nh qua b&ecirc;n t&acirc;y, th&igrave; c&oacute; kh&aacute;c g&igrave; đứng y&ecirc;n cho người ta đ&aacute;nh??</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng c&ograve;n đang tr&ugrave; trừ suy nghĩ n&ecirc;n những g&igrave; Gi&aacute;c Viễn đọc tiếp n&agrave;ng kh&ocirc;ng nghe thấy, nghe m&agrave; kh&ocirc;ng v&agrave;o. Dưới &aacute;nh trăng su&ocirc;ng, bỗng thấy Trương Qu&acirc;n Bảo cũng đang xếp bằng lắng tai nghe. Qu&aacute;ch Tương nghĩ thầm: ?Kh&ocirc;ng cần biết những g&igrave; &ocirc;ng ta đọc đ&uacute;ng hay kh&ocirc;ng đ&uacute;ng, ta cứ ghi nhớ l&agrave; được. L&atilde;o h&ograve;a thượng n&agrave;y chấn thương Ti&ecirc;u Tương Tử, lại dọa cho H&agrave; T&uacute;c Đạo chạy d&agrave;i, đều ch&iacute;nh mắt m&igrave;nh tr&ocirc;ng thấy. Những g&igrave; &ocirc;ng ta đọc l&agrave; ph&aacute;p m&ocirc;n v&otilde; c&ocirc;ng, ắt phải c&oacute; l&yacute; do cả.? Nghĩ thế n&ecirc;n n&agrave;ng dụng t&acirc;m cố nhớ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn thuận miệng đọc, chắp chắp nối nối, c&oacute; l&uacute;c lại xen v&agrave;o v&agrave;i đoạn kinh Lăng Gi&agrave;, n&oacute;i về việc Phật tổ tr&ecirc;n đảo Lăng Gi&agrave; thuyết ph&aacute;p tr&ecirc;n n&uacute;i. Nguy&ecirc;n lai bản Cửu Dương Ch&acirc;n Kinh n&agrave;y viết lẫn b&ecirc;n cạnh hay giữa những h&agrave;ng chữ của cuốn kinh Lăng Gi&agrave;, Gi&aacute;c Viễn đọc s&aacute;ch vốn c&oacute; điểm hơi lẫn thẫn, cứ thuận miệng m&agrave; đọc, n&ecirc;n v&igrave; thế cũng đọc lu&ocirc;n cả kinh Lăng Gi&agrave;. Bản kinh Lăng Gi&agrave; n&agrave;y vốn viết bằng chữ Thi&ecirc;n Tr&uacute;c, Gi&aacute;c Viễn đọc l&agrave; đọc bản dịch, n&ecirc;n c&agrave;ng th&ecirc;m lẫn lộn. Qu&aacute;ch Tương c&agrave;ng nghe lại c&agrave;ng kh&ocirc;ng m&ograve; ra đầu mối, nhưng cũng may l&agrave; n&agrave;ng bản t&iacute;nh th&ocirc;ng minh, tuy Gi&aacute;c Viễn đọc kinh lẫn lộn qua lại, n&agrave;ng cũng vẫn nhớ được hai ba th&agrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mặt trăng từ từ chếch về hướng t&acirc;y, b&oacute;ng người cũng dần dần d&agrave;i ra, tiếng tụng kinh của Gi&aacute;c Viễn c&agrave;ng l&uacute;c c&agrave;ng nhỏ, &acirc;m thanh ph&aacute;t ra mơ hồ kh&ocirc;ng r&otilde; tiếng. Qu&aacute;ch Tương khuy&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, &ocirc;ng đ&atilde; mỏi mệt cả ng&agrave;y rồi, ngủ th&ecirc;m một giấc nữa đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng dường như Gi&aacute;c Viễn kh&ocirc;ng nghe n&agrave;ng n&oacute;i, vẫn tiếp tục đọc:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- ? lực t&ograve;ng nh&acirc;n t&aacute;, kh&iacute; do t&iacute;ch ph&aacute;t. H&agrave; năng kh&iacute; do t&iacute;ch ph&aacute;t? Kh&iacute; hướng hạ trầm, do lưỡng ki&ecirc;n thu nhập t&iacute;ch cốt, ch&uacute; ư y&ecirc;u gian, thử kh&iacute; chi do thượng nhi hạ d&atilde;, vị chi hợp. Do y&ecirc;u triển ư t&iacute;ch cốt, bố ư lưỡng b&aacute;c, thi ư thủ chỉ, thử kh&iacute; chi do thượng nhi hạ d&atilde;, vị chi khai. Hợp ti&ecirc;n thị thu, khai tiện thị ph&oacute;ng. Năng đổng đắc khai hợp, tiện tri &acirc;m dương ? (lực theo người m&agrave; mượn, kh&iacute; do xương sống m&agrave; ra. Tại sao kh&iacute; lại do xương sống m&agrave; ra? Kh&iacute; đi xuống dưới, từ hai vai thu v&agrave;o trong xương sống, chảy v&agrave;o eo, kh&iacute; đ&oacute; từ tr&ecirc;n chạy xuống dưới vậy, n&ecirc;n gọi l&agrave; hợp. Từ eo chạy v&agrave;o xương sống, rồi ra hai c&aacute;nh tay, đi ra tới tận ng&oacute;n tay, kh&iacute; đ&oacute; từ dưới m&agrave; chạy l&ecirc;n tr&ecirc;n vậy, n&ecirc;n gọi l&agrave; khai. Hợp n&ecirc;n thu v&agrave;o, khai n&ecirc;n thả ra. Biết được lẽ khai hợp, tức l&agrave; biết được &acirc;m dương ?)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ocirc;ng ta c&agrave;ng tụng đọc, thanh &acirc;m c&agrave;ng nhỏ dần, sau c&ugrave;ng kh&ocirc;ng c&ograve;n nghe thấy g&igrave; nữa, chắc đ&atilde; ngủ thiếp đi rồi. Qu&aacute;ch Tương v&agrave; Trương Qu&acirc;n Bảo kh&ocirc;ng d&aacute;m kinh động, chỉ cố gắng nhớ những kinh văn vừa mới nghe.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thời gian tr&ocirc;i đi, trăng đ&atilde; lặn về hướng t&acirc;y, từ đ&acirc;u đến c&aacute;c đ&agrave;n chim bay lại, l&agrave;m đen cả một v&ugrave;ng. Lại th&ecirc;m độ t&agrave;n một bữa ăn, phương đ&ocirc;ng trời s&aacute;ng dần, chỉ thấy Gi&aacute;c Viễn mắt nhắm nghiền, vai xu&ocirc;i xuống, tĩnh tọa kh&ocirc;ng động đậy, n&eacute;t mặt tr&ocirc;ng như đang mỉm cười.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo chợt quay đầu, thấy sau gốc c&acirc;y c&oacute; b&oacute;ng người thấp tho&aacute;ng, nhưng cũng kịp nh&igrave;n thấy một g&oacute;c &aacute;o c&agrave; sa m&agrave;u v&agrave;ng. Y giật m&igrave;nh kinh h&atilde;i, qu&aacute;t lớn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ai đ&oacute;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy một l&atilde;o tăng gầy cao từ sau gốc c&acirc;y bước ra, ch&iacute;nh l&agrave; thủ tọa La H&aacute;n Đường V&ocirc; Sắc thiền sư. Qu&aacute;ch Tương vừa mừng vừa lo, hỏi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Đại h&ograve;a thượng, sao &ocirc;ng nhất định kh&ocirc;ng bỏ cuộc, đuổi tới tận đ&acirc;y? Chẳng lẽ kh&ocirc;ng bắt thầy tr&ograve; y về ch&ugrave;a kh&ocirc;ng được hay sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; Sắc n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Thiện tai, thiện tai! L&atilde;o tăng biết lẽ phải tr&aacute;i, đ&acirc;u phải l&agrave; người chấp nhặt c&aacute;i qui luật cũ từ đời nảo đời nao? L&atilde;o tăng đến đ&acirc;y từ l&uacute;c nửa đ&ecirc;m, nếu như muốn động thủ, đ&acirc;u c&oacute; chờ đến b&acirc;y giờ. Gi&aacute;c Viễn sư đệ, V&ocirc; Tướng sư đệ chỉ huy tăng ch&uacute;ng trong Đạt Ma Đường đang t&igrave;m ở hướng đ&ocirc;ng, c&aacute;c ngươi mau nhắm thẳng hướng t&acirc;y m&agrave; chạy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chỉ thấy Gi&aacute;c Viễn vẫn c&uacute;i đầu nhắm mắt, dường như chưa tỉnh. Trương Qu&acirc;n Bảo tiến l&ecirc;n thưa:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ tỉnh dậy đi, thủ tọa La H&aacute;n Đường đang n&oacute;i chuyện với sư phụ đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&aacute;c Viễn vẫn kh&ocirc;ng động đậy. Trương Qu&acirc;n Bảo kinh hoảng chạy tới, giơ tay sờ v&agrave;o tr&aacute;n &ocirc;ng ta, thấy lạnh ngắt, h&oacute;a ra đ&atilde; vi&ecirc;n tịch từ l&acirc;u rồi. Trương Qu&acirc;n Bảo đau đớn qu&aacute;, phục xuống k&ecirc;u l&ecirc;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Sư phụ, sư phụ!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thế nhưng &ocirc;ng ta l&agrave;m sao c&oacute; thể sống lại được. V&ocirc; Sắc thiền sư chắp tay h&agrave;nh lễ, đọc một b&agrave;i kệ:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chư phương v&ocirc; v&acirc;n ế,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tứ diện giai thanh minh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vi phong suy hương kh&iacute;,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&uacute;ng sơn tĩnh v&ocirc; thanh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kim nhật đại hoan hỉ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">X&aacute; khước nguy th&uacute;y th&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&ocirc; s&acirc;n diệc v&ocirc; ưu,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ninh bất đương h&acirc;n kh&aacute;nh?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Bốn phương kh&ocirc;ng m&acirc;y phủ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mọi mặt đều s&aacute;ng trong,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gi&oacute; nhẹ đưa hương tới,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tĩnh lặng n&uacute;i thinh kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&ocirc;m nay thật hoan hỉ,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cởi được th&acirc;n mong manh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kh&ocirc;ng giận cũng kh&ocirc;ng lo,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lẽ đ&acirc;u kh&ocirc;ng ch&uacute;c kh&aacute;nh?)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tụng xong &ocirc;ng lặng lẽ bỏ đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo kh&oacute;c l&oacute;c một hồi, Qu&aacute;ch Tương cũng sụt s&ugrave;i m&atilde;i. Tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m khi vi&ecirc;n tịch đều hỏa h&oacute;a. Cho n&ecirc;n hai người đi kiếm c&agrave;nh kh&ocirc; thi&ecirc;u ph&aacute;p th&acirc;n của Gi&aacute;c Viễn. Qu&aacute;ch Tương n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Trương huynh đệ, tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m chắc chưa bỏ qua cho ngươi đ&acirc;u, ngươi n&ecirc;n hết sức cẩn thận. Ch&uacute;ng ta từ biệt nhau nơi n&agrave;y, sau n&agrave;y c&oacute; l&uacute;c gặp lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo gạt lệ đ&aacute;p:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Qu&aacute;ch c&ocirc; nương, c&ocirc; đi đ&acirc;u? Cho t&ocirc;i đi theo với được kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qu&aacute;ch Tương thấy y hỏi m&igrave;nh đi đ&acirc;u, trong l&ograve;ng chua ch&aacute;t, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ta ch&acirc;n trời g&oacute;c biển, h&agrave;nh tung kh&ocirc;ng nhất định, ch&iacute;nh m&igrave;nh cũng chưa biết đi đ&acirc;u. Trương huynh đệ, ngươi tuổi c&ograve;n nhỏ, lại kh&ocirc;ng c&oacute; ch&uacute;t lịch duyệt giang hồ n&agrave;o, tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m đang đi t&igrave;m bắt ngươi ở hướng đ&ocirc;ng, chỉ biết thế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&agrave;ng th&aacute;o chiếc v&ograve;ng đeo tay đưa cho y, n&oacute;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi đem chiếc v&ograve;ng n&agrave;y đến th&agrave;nh Tương Dương kiếm cha mẹ ta, hai &ocirc;ng b&agrave; ắt sẽ đối đ&atilde;i với ngươi tử tế. Chỉ cần ngươi ở với cha mẹ ta th&igrave; d&ugrave; tăng ch&uacute;ng ch&ugrave;a Thiếu L&acirc;m c&oacute; hung &aacute;c đến đ&acirc;u, cũng kh&ocirc;ng d&aacute;m đến t&igrave;m ngươi l&agrave;m kh&oacute; dễ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo nuốt lệ nhận chiếc v&ograve;ng. Qu&aacute;ch Tương lại n&oacute;i tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Ngươi n&oacute;i với cha mẹ ta rằng, ta vẫn khỏe mạnh, &ocirc;ng b&agrave; đừng mong đợi. Cha ta rất y&ecirc;u kẻ thiếu ni&ecirc;n anh h&ugrave;ng, thấy ngươi l&agrave; một nh&acirc;n t&agrave;i thế n&agrave;y, kh&ocirc;ng chừng sẽ thu ngươi l&agrave;m đồ đệ. Em trai ta trung hậu thật th&agrave;, chắc sẽ th&acirc;n thiết với ngươi lắm đấy. Chỉ c&oacute; chị ta t&iacute;nh t&igrave;nh hơi n&oacute;ng, mỗi khi kh&ocirc;ng vừa &yacute;, ăn n&oacute;i chẳng nể nang ai. Chỉ cần ngươi ngoan ngo&atilde;n v&acirc;ng lời, th&igrave; kh&ocirc;ng sao cả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i xong n&agrave;ng quay đầu đi thẳng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo thấy trời đất m&ecirc;nh m&ocirc;ng nhưng sao kh&ocirc;ng c&oacute; chỗ n&agrave;o cho m&igrave;nh an th&acirc;n, đứng trước đống tro hỏa t&aacute;ng sư phụ trầm ng&acirc;m cả nửa ng&agrave;y, mới ra đi. Đi được một qu&atilde;ng, chợt quay đầu, g&aacute;nh đ&ocirc;i th&ugrave;ng sắt sư phụ để lại, thất thểu bước đi. Trong n&uacute;i rừng hoang vu, một thiếu ni&ecirc;n gầy g&ograve; lặng lẽ theo hướng t&acirc;y m&agrave; đi, l&ograve;ng buồn rười rượi, n&oacute;i sao cho xiết c&aacute;i cảnh th&acirc;n c&ocirc; chiếc b&oacute;ng, l&ecirc;nh đ&ecirc;nh kh&ocirc;ng nh&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đi được nửa th&aacute;ng, đ&atilde; đến cảnh giới tỉnh Hồ Bắc, kh&ocirc;ng c&ograve;n c&aacute;ch th&agrave;nh Tương Dương bao xa. C&aacute;c nh&agrave; sư ch&ugrave;a Thiếu L&acirc;m trước sau vẫn kh&ocirc;ng đuổi kịp y ch&iacute;nh v&igrave; V&ocirc; Sắc thiền sư c&oacute; l&ograve;ng che chở, cố &yacute; dẫn tăng ch&uacute;ng theo hướng đ&ocirc;ng m&agrave; t&igrave;m, th&agrave;nh ra hai b&ecirc;n c&agrave;ng l&uacute;c c&agrave;ng xa hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Xế trưa h&ocirc;m đ&oacute; y đến trước một ngọn n&uacute;i lớn, thấy c&acirc;y cối rậm rạp um t&ugrave;m, sơn thế cực kỳ h&ugrave;ng vĩ. Hỏi thăm những người d&acirc;n quanh đ&oacute;, mới biết n&uacute;i n&agrave;y t&ecirc;n gọi l&agrave; V&otilde; Đương. Y ngồi dưới ch&acirc;n n&uacute;i, tựa lưng v&agrave;o một h&ograve;n đ&aacute; m&agrave; nghỉ, bỗng thấy một đ&ocirc;i nam nữ nh&agrave; qu&ecirc; từ con đường nhỏ b&ecirc;n n&uacute;i đi tới. Hai người s&aacute;nh vai m&agrave; đi, tr&ocirc;ng thật th&acirc;n mật, r&otilde; r&agrave;ng l&agrave; một đ&ocirc;i vợ chồng trẻ. Người đ&agrave;n b&agrave; vừa đi vừa liến thoắng lu&ocirc;n mồm phiền tr&aacute;ch anh chồng. Người đ&agrave;n &ocirc;ng th&igrave; chỉ c&uacute;i đầu, lặng thinh kh&ocirc;ng đ&aacute;p.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bỗng nghe người vợ n&oacute;i với chồng:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Anh l&agrave; một nam tử h&aacute;n, đại trượng phu, lại kh&ocirc;ng chịu tự m&igrave;nh l&agrave;m ăn ri&ecirc;ng, lại đến ăn nhờ ở đậu chị v&agrave; anh rể, nếu kh&ocirc;ng đ&acirc;u đến nỗi bị nhục nh&atilde; như thế. Vợ chồng m&igrave;nh c&ograve;n mạnh ch&acirc;n khỏe tay, tay l&agrave;m h&agrave;m nhai, d&ugrave; rau dưa c&agrave; muối, cơm ch&aacute;o qua ng&agrave;y cũng được, c&oacute; phải thảnh thơi kh&ocirc;ng? Chứ anh kh&ocirc;ng d&aacute;m tự lập ở ri&ecirc;ng, sinh ra tr&ecirc;n đời n&agrave;y cũng uổng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người đ&agrave;n &ocirc;ng ậm ừ mấy tiếng, người đ&agrave;n b&agrave; lại tiếp:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Người đời c&oacute; n&oacute;i rằng, ngo&agrave;i c&aacute;i chết ra chẳng c&oacute; c&aacute;i g&igrave; gọi l&agrave; lớn hết, chẳng lẽ kh&ocirc;ng nhờ vả người kh&aacute;c kh&ocirc;ng sống được hay sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người chồng nghe vợ dồn cho một trận kh&ocirc;ng d&aacute;m mở lời đ&aacute;p lại, c&aacute;i mặt b&agrave;nh bạnh b&acirc;y giờ t&iacute;m lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Những c&acirc;u n&oacute;i của người vợ từng chữ len v&agrave;o tim Trương Qu&acirc;n Bảo: ?Anh l&agrave; một nam tử h&aacute;n, đại trượng phu, lại kh&ocirc;ng chịu tự m&igrave;nh l&agrave;m ăn ri&ecirc;ng ? nếu kh&ocirc;ng đ&acirc;u đến nỗi bị nhục nh&atilde; như thế ? Người đời c&oacute; n&oacute;i rằng, ngo&agrave;i c&aacute;i chết ra chẳng c&oacute; c&aacute;i g&igrave; gọi l&agrave; lớn hết, chẳng lẽ kh&ocirc;ng nhờ vả người kh&aacute;c kh&ocirc;ng sống được hay sao??</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y nh&igrave;n theo đ&ocirc;i vợ chồng nh&agrave; qu&ecirc;, đứng ngơ ngẩn một hồi, trong l&ograve;ng trăn qua trở lại, nghĩ đến những c&acirc;u của người đ&agrave;n b&agrave;. Chỉ thấy người đ&agrave;n &ocirc;ng đứng thẳng người l&ecirc;n, kh&ocirc;ng biết n&oacute;i mấy c&acirc;u g&igrave; với vợ, m&agrave; nghe hai người cười rộ l&ecirc;n, tựa hồ người chồng đ&atilde; quyết ch&iacute; tự lập, n&ecirc;n cả hai đều sung sướng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trương Qu&acirc;n Bảo lại nghĩ tiếp: ?Qu&aacute;ch c&ocirc; nương c&oacute; n&oacute;i rằng, b&agrave; chị c&ocirc; ta t&iacute;nh t&igrave;nh n&oacute;ng nảy, ăn n&oacute;i kh&ocirc;ng nể mặt ai n&ecirc;n muốn ta chiều l&ograve;ng b&agrave; ấy. Ta đường đường l&agrave; một người đ&agrave;n &ocirc;ng, đ&acirc;u lẽ phải hạ m&igrave;nh quị lụy người kh&aacute;c, cong lưng uốn gối mong được y&ecirc;n th&acirc;n. Đến như hai người nh&agrave; qu&ecirc; kia c&ograve;n d&aacute;m hi&ecirc;n ngang đứng ra ở ri&ecirc;ng, ta Trương Qu&acirc;n Bảo lẽ n&agrave;o lại phải n&eacute;p m&igrave;nh trong h&agrave;ng r&agrave;o nh&agrave; người ta, chờ người ta sai bảo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y nghĩ tới đ&oacute;, trong l&ograve;ng đ&atilde; quyết n&ecirc;n g&aacute;nh đ&ocirc;i th&ugrave;ng sắt, tr&egrave;o l&ecirc;n n&uacute;i V&otilde; Đương, kiếm một c&aacute;i hang, kh&aacute;t th&igrave; t&igrave;m suối, đ&oacute;i ăn tr&aacute;i hoang, chăm chỉ tập luyện Cửu Dương Ch&acirc;n Kinh m&agrave; Gi&aacute;c Viễn đ&atilde; truyền cho.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&agrave;i năm sau, bỗng dưng hiểu ra: &quot;Đạt Ma tổ sư l&agrave; người Thi&ecirc;n Tr&uacute;c, dẫu c&oacute; biết viết chữ Trung Hoa, th&igrave; văn l&yacute; cũng th&ocirc; sơ. Bộ Cửu Dương Ch&acirc;n Kinh n&agrave;y văn chương kỳ diệu kh&uacute;c chiết, người nước kh&aacute;c nhất định kh&ocirc;ng sao viết ra được, hẳn l&agrave; nh&acirc;n sĩ Trung Thổ đời sau s&aacute;ng t&aacute;c. C&oacute; lẽ l&agrave; tăng lữ của ch&ugrave;a Thiếu L&acirc;m, giả th&aacute;c t&ecirc;n tuổi Đạt Ma tổ sư , viết trong những m&eacute;p lề cuốn kinh Lăng Gi&agrave;.?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&aacute;i đạo l&yacute; đ&oacute;, người vốn dĩ tin tưởng ho&agrave;n to&agrave;n v&agrave;o kinh điển, kh&ocirc;ng d&aacute;m thay đổi biến h&oacute;a như Gi&aacute;c Viễn kh&ocirc;ng sao hiểu được. C&oacute; điều l&yacute; lẽ kh&ocirc;ng c&oacute; g&igrave; để chứng minh, Trương Qu&acirc;n Bảo l&uacute;c đ&oacute; tuổi c&ograve;n nhỏ, cũng kh&ocirc;ng biết chắc l&agrave; suy định của m&igrave;nh c&oacute; đ&uacute;ng hay kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Y được Gi&aacute;c Viễn dạy dỗ đ&atilde; l&acirc;u, bộ Cửu Dương Ch&acirc;n Kinh n&agrave;y mười phần cũng biết được năm s&aacute;u, trong mươi năm sau nội lực lại c&agrave;ng tiến bộ. Về sau y lại đọc kỹ Đạo Tạng, t&acirc;m đắc được ph&eacute;p luyện kh&iacute; của đạo gia. Một ng&agrave;y kia, y tại trong n&uacute;i nh&agrave;n du, ngửng l&ecirc;n xem m&acirc;y bay, nh&igrave;n xuống ngắm nước chảy, cảm thấy sở ngộ n&ecirc;n trở về động suy nghĩ li&ecirc;n tiếp bảy ng&agrave;y bảy đ&ecirc;m, bỗng nhi&ecirc;n qu&aacute;n th&ocirc;ng được c&aacute;i lẽ nhu khắc cương trong v&otilde; học, vui sướng qu&aacute;, ngẩng mặt l&ecirc;n cười một hồi d&agrave;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&iacute;nh tiếng cười đ&oacute; đ&atilde; khai sinh ra một vị đại t&ocirc;ng sư trước chưa c&oacute; ai m&agrave; sau cũng kh&ocirc;ng ai theo kịp. Những g&igrave; y tự m&igrave;nh t&igrave;m ra, cộng th&ecirc;m với lẽ xung hư vi&ecirc;n th&ocirc;ng của Đạo gia, v&agrave; nội c&ocirc;ng của pho Cửu Dương Ch&acirc;n Kinh đ&atilde; khai s&aacute;ng ra nền tảng cho v&otilde; c&ocirc;ng của ph&aacute;i V&otilde; Đương m&agrave; ngh&igrave;n năm sau vẫn c&ograve;n ch&oacute;i lọi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Về sau y du ngoạn phương Bắc đến v&ugrave;ng Bảo Minh, nh&igrave;n thấy ba ngọn n&uacute;i t&uacute; lệ h&ugrave;ng vĩ, ?vươn l&ecirc;n đ&acirc;m v&agrave;o m&acirc;y, lại nh&acirc;n đ&atilde; sở ngộ về v&otilde; học n&ecirc;n tự đặt cho m&igrave;nh t&ecirc;n hiệu Tam Phong. Đ&oacute; ch&iacute;nh l&agrave; kỳ nh&acirc;n Trương Tam Phong trong lịch sử v&otilde; học của Trung Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[1] &Aacute;o may bằng từng mảnh vải vu&ocirc;ng gh&eacute;p lại với nhau của nh&agrave; sư c&oacute; địa vị cao</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">[2] người đi tu nhưng kh&ocirc;ng cạo đầu</span></p>', 0, 'chuong-3-vo-duong-son-dinh-tung-bach-truong', NULL, NULL);
INSERT INTO `chapter` VALUES (17, 13, 'Phần 1: Tôi Đã Học Như Thế Nào?', 'Nếu tôi còn hai mươi', '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>NẾU T&Ocirc;I C&Ograve;N HAI MƯƠI</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Những th&oacute;i quen tốt ta h&igrave;nh th&agrave;nh khi c&ograve;n trẻ kh&ocirc;ng tạo n&ecirc;n kh&aacute;c biệt nhỏ n&agrave;o, đ&uacute;ng hơn, ch&uacute;ng tạo ra tất cả kh&aacute;c biệt.&quot; (<strong>Aristotle</strong>)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kh&ocirc;ng biết bao nhi&ecirc;u lần t&ocirc;i nghe những người trẻ quanh m&igrave;nh than buồn, ch&aacute;n, bảo kh&ocirc;ng biết g&igrave; để l&agrave;m. V&agrave; rồi kh&ocirc;ng biết l&agrave;m g&igrave; n&ecirc;n ta giết thời giờ với những th&uacute; vui nhỏ nhặt, rong chơi cho qua ng&agrave;y đoạn th&aacute;ng, ngủ v&ugrave;i lười biếng hoặc ch&igrave;m đắm v&agrave;o y&ecirc;u đương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhưng khi đ&atilde; đi qua gần hết thời đ&ocirc;i mươi, ngấp ngh&eacute; ở ngưỡng ba mươi, nh&igrave;n lại t&ocirc;i mới thấy tiếc nuối. Thấy b&acirc;y giờ cuộc sống c&oacute; qu&aacute; nhiều cơ hội, nhiều điều phải l&agrave;m, nhiều thứ để học, m&agrave; m&igrave;nh lại kh&ocirc;ng c&oacute; đủ thời gian cho ngần ấy thứ. Nghĩ nếu m&agrave; m&igrave;nh biết những điều n&agrave;y khi c&ograve;n đi học, khi m&igrave;nh c&ograve;n trẻ tuổi, chắc hẳn cuộc sống của m&igrave;nh sẽ kh&aacute;c, chắc m&igrave;nh sẽ bớt đi nhiều vật v&atilde; gian nan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ai c&oacute; trải qua rồi mới hiểu, tuổi trẻ ngắn ngủi biết bao nhi&ecirc;u. Thời gian một đi l&agrave; kh&ocirc;ng trở lại. Điều đ&aacute;ng qu&yacute; nhất m&agrave; tuổi trẻ c&oacute; được l&agrave; thời gian, nhưng rất nhiều người trẻ kh&ocirc;ng biết l&agrave;m g&igrave; c&oacute; &iacute;ch với thời gian của họ. Tr&ecirc;n thực tế, c&oacute; rất nhiều điều để l&agrave;m, khi người ta c&ograve;n trẻ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nếu c&oacute; thể quay ngược qu&aacute; khứ, trở lại thời c&ograve;n mười t&aacute;m đ&ocirc;i mươi, t&ocirc;i sẽ d&agrave;nh thời gian để:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>1. Đầu tư cho sức khỏe</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Chạy bộ, đi bơi, đ&aacute;nh b&oacute;ng b&agrave;n, tập yoga, học v&otilde;... Luyện tập thể dục thể thao kh&ocirc;ng nhất thiết phải l&agrave; mua một đ&ocirc;i gi&agrave;y h&agrave;ng hiệu gi&aacute; hai ba triệu, mua bộ đồ gi&aacute; mấy trăm ngh&igrave;n, hay đăng k&yacute; thẻ th&agrave;nh vi&ecirc;n trung t&acirc;m thể h&igrave;nh sang trọng ph&iacute; v&agrave;i chục triệu một năm. Gi&agrave;y Việt Nam chất lượng cao l&agrave; đủ để chạy, v&agrave;i chục ngh&igrave;n đồng l&agrave; c&oacute; c&aacute;i k&iacute;nh bơi. Chạy bộ trong c&ocirc;ng vi&ecirc;n kh&ocirc;ng tốn tiền, đi bơi th&igrave; chỉ mười mấy ngh&igrave;n đồng một v&eacute;, c&aacute;c lớp học v&otilde; ở trung t&acirc;m thể thao quận huyện khoảng một hai trăm ngh&igrave;n một th&aacute;ng, chịu kh&oacute; t&igrave;m kiếm th&igrave; quanh bạn sẽ c&oacute; kh&ocirc;ng &iacute;t c&acirc;u lạc bộ r&egrave;n luyện sức khỏe miễn ph&iacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Luyện tập thể lực trước hết l&agrave; đem lại một sức khỏe tốt, gi&uacute;p tinh thần hưng phấn, đ&agrave;o thải chất độc bị hấp thụ từ m&ocirc;i trường sống &ocirc; nhiễm v&agrave; thực phẩm độc hại. Một cơ thể l&agrave;nh mạnh l&agrave; điều kiện ti&ecirc;n quyết để c&oacute; một cuộc sống tốt. Th&ecirc;m v&agrave;o đ&oacute;, tương t&aacute;c trong những cộng đồng kh&aacute;c nhau gi&uacute;p ta c&oacute; nhiều mối quan hệ hơn, mở rộng tầm nh&igrave;n v&agrave; quan điểm về cuộc đời.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>2. Đọc s&aacute;ch</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Phần lớn tri thức nh&acirc;n loại đều nằm trong s&aacute;ch. C&ograve;n b&aacute;o, tạp ch&iacute; thường chỉ chứa th&ocirc;ng tin, chưa kể b&aacute;o mạng v&agrave; mạng x&atilde; hội hiện nay đa phần l&agrave; tin r&aacute;c. Vậy m&agrave; ng&agrave;y nay người ta đọc b&aacute;o nhiều hơn đọc s&aacute;ch. Sự thật l&agrave; s&aacute;ch gi&uacute;p con người thay đổi cuộc đời. Những l&uacute;c tinh thần đi xuống, kh&ocirc;ng muốn l&agrave;m g&igrave;, tốt nhất l&agrave; đọc s&aacute;ch. V&igrave; s&aacute;ch kh&ocirc;ng những n&acirc;ng cao kiến thức m&agrave; c&ograve;n tạo động lực th&uacute;c đẩy ta h&agrave;nh động tốt hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&aacute;ch cũng kh&ocirc;ng phải rẻ, nhất l&agrave; với v&iacute; tiền mỏng manh của người trẻ. Nếu kh&ocirc;ng c&oacute; điều kiện mua s&aacute;ch giấy th&igrave; l&ecirc;n mạng t&igrave;m c&aacute;c trang web đọc ebook miễn ph&iacute; như&nbsp;<strong>docsach24.com&nbsp;</strong>. Nếu đ&atilde; đọc ebook th&igrave; tốt nhất l&agrave; t&igrave;m ebook nguy&ecirc;n bản tiếng Anh, vừa c&oacute; kiến thức vừa tăng th&ecirc;m vốn từ vựng, một c&ocirc;ng đ&ocirc;i việc. Chỉ cần một tuần đọc một quyển s&aacute;ch. Một năm nh&igrave;n lại, thấy kiến thức của m&igrave;nh đ&atilde; d&agrave;y hơn v&agrave;i phần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Điều c&ocirc;ng bằng nhất trong cuộc sống n&agrave;y l&agrave; ch&uacute;ng ta đều c&oacute; quyền lựa chọn v&agrave; quyết định con đường m&igrave;nh sẽ đi. Bạn sẽ tuyệt vọng hay ở tr&ecirc;n một con đường mới, bạn sẽ biến m&igrave;nh th&agrave;nh một kẻ thất bại thảm hại hay lột x&aacute;c th&agrave;nh c&ocirc;ng ch&uacute;a ki&ecirc;u h&atilde;nh? Bạn sẽ l&agrave; ai? Cuộc đời bạn sẽ như thế n&agrave;o? Chẳng phải v&igrave; người đ&agrave;n &ocirc;ng n&agrave;o cả? trước hết l&agrave; do bạn!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>3. Học trực tuyến tr&ecirc;n mạng.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&aacute;c trang web MOOC (viết tắt của từ Massive Open Online Course - Kh&oacute;a học trực tuyến đại tr&agrave;) cho ph&eacute;p người d&ugrave;ng theo học tr&ecirc;n mạng về nhiều đề t&agrave;i kh&aacute;c nhau, như nghệ thuật, lịch sử, thuật l&atilde;nh đạo, kinh tế kinh doanh, c&aacute;ch dạy tiếng Anh, thiết kế, lập tr&igrave;nh, thi&ecirc;n văn học, vũ trụ, v&acirc;n v&acirc;n. Một số trang MOOC ho&agrave;n to&agrave;n miễn ph&iacute; như:&nbsp;<em>www.coursera.org, www.edx.org, hay www.khanacademy.org</em>được tạo ra bởi những trường đại học danh tiếng của Mỹ, với gi&aacute;o tr&igrave;nh chuẩn v&agrave; được soạn giảng bởi c&aacute;c gi&aacute;o sư nổi tiếng. Người d&ugrave;ng chỉ cần đăng k&yacute; t&agrave;i khoản l&agrave; c&oacute; thể bắt đầu học. Họ c&oacute; thể thảo luận c&aacute;c thắc mắc của m&igrave;nh với bạn b&egrave; c&ugrave;ng kh&oacute;a, c&aacute;c gi&aacute;o sư v&agrave; trợ giảng trong diễn đ&agrave;n của lớp học. Họ cũng l&agrave;m b&agrave;i tập về nh&agrave;, được kiểm tra kiến thức sau mỗi tuần v&agrave; thi kết th&uacute;c m&ocirc;n để kiểm tra tr&igrave;nh độ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tr&ecirc;n thế giới hiện nay, h&igrave;nh thức học trực tuyến qua MOOC đang ng&agrave;y c&agrave;ng thịnh h&agrave;nh. N&oacute; bổ sung, b&ugrave; đắp v&agrave; thay thế cho h&igrave;nh thức gi&aacute;o dục truyền thống. H&igrave;nh thức n&agrave;y được c&aacute;c nh&agrave; gi&aacute;o dục cho rằng sẽ trở th&agrave;nh loại h&igrave;nh gi&aacute;o dục của tương lai, v&igrave; ta kh&ocirc;ng phải tốn thời gian di chuyển, c&oacute; thể tự lựa chọn khung giờ học ph&ugrave; hợp cho m&igrave;nh, học đồng thời nhiều chương tr&igrave;nh kh&aacute;c nhau, hoặc bổ sung những kỹ năng h&aacute;i ra tiền m&agrave; kh&ocirc;ng được dạy ở c&aacute;c trường đại học. Với MOOC, người d&ugrave;ng từ khắp nơi tr&ecirc;n thế giới c&oacute; thể trau dồi kiến thức v&agrave; thoản m&atilde;n niềm khao kh&aacute;t học hỏi, bất kể họ đang ở độ tuổi, tr&igrave;nh độ v&agrave; địa điểm n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thay v&igrave; ngồi thương kh&oacute;c một mối t&igrave;nh đ&atilde; chết, đăng k&yacute; kh&oacute;a học&nbsp;<em>Apply to US Universities (Nộp đơn v&agrave;o c&aacute;c trường đại học Mỹ)</em>, rồi kiếm tấm v&eacute; đi du học Mỹ cho người y&ecirc;u cũ l&eacute; mắt, chẳng phải tốt hơn sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>4. Cho d&acirc;n du lịch bụi ở nhờ.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Người trẻ hiện nay ng&agrave;y c&agrave;ng nhiều người th&iacute;ch đi du lịch. Trong l&uacute;c chưa c&oacute; thời gian, hay chưa t&iacute;ch g&oacute;p đủ tiền để đi, sao bạn kh&ocirc;ng thử cho d&acirc;n du lịch bụi ở nhờ nh&agrave; m&igrave;nh? C&aacute;c trang web như&nbsp;<em>www. couchsurfing.com</em>&nbsp;hay&nbsp;<em>www.hospitalityclub.org</em>&nbsp;chuy&ecirc;n kết nối người đi du lịch bụi v&agrave; người bản xứ. Khi đi du lịch đến một v&ugrave;ng đất n&agrave;o đ&oacute;, ta c&oacute; thể xin ở nhờ nh&agrave; người d&acirc;n bản xứ ho&agrave;n to&agrave;n miễn ph&iacute;. Khi ở nh&agrave;, ta cũng c&oacute; thể b&agrave;y tỏ l&ograve;ng hiếu kh&aacute;ch bằng c&aacute;ch cho du kh&aacute;ch ở nhờ. D&acirc;n đi bụi chẳng cần g&igrave; sang trọng. Họ chỉ cần một chỗ vừa đủ để đặt lưng buổi tối, một chiếc ghế s&ocirc; pha, một phần giường c&ograve;n trống, hoặc đ&ocirc;i khi chỉ l&agrave; một s&agrave;n nh&agrave; sạch sẽ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Những trải nghiệm như vậy thường đem lại cho đ&ocirc;i b&ecirc;n những kỷ niệm kh&oacute; qu&ecirc;n. Với người cho ở nhờ, chẳng cần đi đ&acirc;u cũng sẽ c&oacute; những c&acirc;u chuyện lữ h&agrave;nh từ khắp nơi tr&ecirc;n thế giới ở ngay ngưỡng của nh&agrave; bạn. Những chia sẻ, kinh nghiệm của người đi du lịch cũng c&oacute; thể truyền cảm hứng v&agrave; chứa nhiều th&ocirc;ng tin hữu &iacute;ch cho chuyến đi sắp tới. Chỉ cần đăng k&yacute; t&agrave;i khoản, viết th&ocirc;ng tin c&aacute; nh&acirc;n, thế l&agrave; bạn đ&atilde; sẵn s&agrave;ng để kết nối với d&acirc;n lữ h&agrave;nh to&agrave;n cầu. Nếu kh&ocirc;ng thể cho ở nhờ, th&igrave; cũng c&oacute; thể gặp nhau tr&ograve; chuyện, dẫn kh&aacute;ch du lịch đi tham quan quanh v&ugrave;ng bạn ở, hoặc tham gia hoạt động của c&aacute;c nh&oacute;m th&agrave;nh vi&ecirc;n tại địa phương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>5. Đi du lịch bụi.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kh&ocirc;ng cần nhiều tiền v&agrave; thời gian, nhiều tỉnh gần S&agrave;i G&ograve;n c&oacute; thể đi về trong ng&agrave;y, chỉ cần đổ đầy b&igrave;nh xăng l&agrave; mặc sức chạy. Rời khỏi th&agrave;nh phố nghẹt bụi để t&igrave;m đồng l&uacute;a bao la, m&ugrave;i rơm rạ tr&ecirc;n c&aacute;nh đồng mới gặt, m&ugrave;i kh&oacute;i đốt đồng, m&ugrave;i hoa m&agrave;u tươi tốt, chim c&ograve; vỗ c&aacute;nh bay l&ecirc;n, đ&agrave;n bỏ nhởn nhơ gặm cỏ. Nếu c&ograve;n sinh vi&ecirc;n, những th&aacute;ng nghỉ h&egrave; c&oacute; thể g&oacute;i đồ xuy&ecirc;n Việt, hoặc đi một v&ograve;ng ba nước Đ&ocirc;ng Dương. Dư dả hơn, chỉ cần trong t&uacute;i mười mấy triệu đồng l&agrave; c&oacute; thể đi một v&ograve;ng mấy nước Đ&ocirc;ng Nam &Aacute; bằng đường bộ, từ Việt Nam đến Campuchia, Th&aacute;i Lan, Malaysia, xuống Singapore rồi quay trở về.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Du lịch bụi kh&ocirc;ng hề đắt đỏ như người ta tưởng. Trong quyển s&aacute;ch hướng dẫn du lịch bụi&nbsp;<em>Ta ba l&ocirc; tr&ecirc;n đất &Aacute;&nbsp;</em>của m&igrave;nh, t&ocirc;i đ&atilde; từng kể c&acirc;u chuyện người thật việc thật về c&ocirc; b&eacute; sinh vi&ecirc;n đ&atilde; đi du lịch bụi qua Campuchia v&agrave; Th&aacute;i Lan cả th&aacute;ng trời chỉ tốn c&oacute; hai triệu đồng, chủ yếu bằng c&aacute;ch đi nhờ xe (hitchhiking) v&agrave; ngủ nhờ nh&agrave; người d&acirc;n bản xứ. Trường hợp như thế trong giới du lịch bụi kh&ocirc;ng phải l&agrave; hiếm. Những chuyến đi đ&oacute; kh&ocirc;ng tốn nhiều chi ph&iacute; m&agrave; để lại những trải nghiệm thay đổi đời người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ch&igrave;a kh&oacute;a khi đi du lịch bụi kh&ocirc;ng phải trong t&uacute;i bạn c&oacute; bao nhi&ecirc;u tiền, m&agrave; l&agrave; trong l&ograve;ng bạn c&oacute; bao nhi&ecirc;u can đảm. Can đảm để bước ra khỏi v&ugrave;ng an to&agrave;n của m&igrave;nh, can đảm để l&agrave;m điều mới. Can đảm để trải nghiệm. C&oacute; nhiều c&aacute;ch để đi du lịch với &iacute;t tiền, thậm ch&iacute; kh&ocirc;ng c&oacute; tiền. C&oacute; thể đi bộ, đi xe đạp, đi nhờ xe. Ở nhờ nh&agrave; người d&acirc;n bản xứ qua&nbsp;<em>www.couchsurfing.com</em>&nbsp;như chia sẻ ở tr&ecirc;n, l&agrave;m việc t&igrave;nh nguyện để đổi lấy nơi ăn ngủ th&ocirc;ng qua&nbsp;<em>www.idealist.org</em>, hoặc&nbsp;<em>www.workaway.info</em>. Gi&uacute;p đỡ người kh&aacute;c v&agrave; đem lại gi&aacute; trị trong l&uacute;c m&igrave;nh đi, hiểu đời hiểu người hơn sau mỗi chuyến đi.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>6. L&agrave;m hướng dẫn vi&ecirc;n du lịch.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Với những người trẻ y&ecirc;u th&iacute;ch trải nghiệm du lịch, muốn thử sức trong ng&agrave;nh du lịch, c&oacute; thể thử l&agrave;m hướng dẫn vi&ecirc;n. Bạn kh&ocirc;ng cần phải c&oacute; bằng cấp về du lịch hay phải tốt nghiệp ra trường mới l&agrave;m được. Sinh vi&ecirc;n học sinh vẫn c&oacute; thể l&agrave;m. Vừa r&egrave;n luyện kỹ năng giao tiếp v&agrave; khả năng n&oacute;i tiếng Anh, vừa c&oacute; nhiều trải nghiệm th&uacute; vị, vừa c&oacute; th&ecirc;m kinh nghiệm l&agrave;m việc v&agrave; một &iacute;t thu nhập trang trải cuộc sống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hiện tại c&oacute; kh&aacute; nhiều loại h&igrave;nh c&acirc;u lạc bộ, tổ chức du lịch để bạn đăng k&yacute; l&agrave;m thử. V&iacute; dụ như&nbsp;<em>www.triip.me</em>, một trang web với những tour văn h&oacute;a, ẩm thực độc đ&aacute;o do người bản địa tự thiết kế gi&uacute;p du kh&aacute;ch c&oacute; những trải nghiệm phong ph&uacute; hơn, chủ yếu hướng dẫn vi&ecirc;n l&agrave; sinh vi&ecirc;n. Một số tổ chức tương tự l&agrave; XO Tours, Back of the Bike Tours, Vietnam Vespa Adventures Day Tours, Saigon on Bikes, Hanoi Kids Tours, Hanoi Street Food Tour... Ngo&agrave;i ra bạn c&oacute; thể l&agrave;m hướng dẫn vi&ecirc;n t&igrave;nh nguyện cho du kh&aacute;ch nước ngo&agrave;i qua c&aacute;c c&acirc;u lạc bộ Saigon Lovers, Saigon Hotpot (<em>saigonhotpot.vn</em>), Hanoi Kids Club (<em>hanoikids.org</em>) hoặc Hanoi Free Tour Guides (<em>hanoifreetourguides.com</em>).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>7. L&agrave;m c&aacute;c c&ocirc;ng việc thiện nguyện.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trở th&agrave;nh một t&igrave;nh nguyện vi&ecirc;n c&oacute; rất nhiều c&aacute;i lợi. Nếu bạn c&oacute; những sở th&iacute;ch, ước mơ n&agrave;o đ&oacute;, v&iacute; dụ t&igrave;nh y&ecirc;u động vật, hay y&ecirc;u th&iacute;ch tương t&aacute;c với con người, hay l&agrave;m việc với trẻ em, th&igrave; c&aacute;c c&ocirc;ng việc t&igrave;nh nguyện li&ecirc;n quan đến những lĩnh vực bạn hứng th&uacute; sẽ gi&uacute;p bạn thỏa m&atilde;n sở th&iacute;ch, hoặc &iacute;t nhất gi&uacute;p bạn biết được m&igrave;nh c&oacute; thực sự y&ecirc;u th&iacute;ch n&oacute; kh&ocirc;ng. C&aacute;c hoạt động t&igrave;nh nguyện c&ograve;n l&agrave; cơ hội tuyệt vời để r&egrave;n luyện th&ecirc;m c&aacute;c kỹ năng mềm v&agrave; c&oacute; th&ecirc;m kinh nghiệm rất hữu &iacute;ch cho sự nghiệp sau n&agrave;y, cũng như thiết lập mạng lưới quan hệ hỗ trợ trong c&ocirc;ng việc v&agrave; cuộc sống. Bạn c&oacute; thể t&igrave;m thấy những người thầy, người bạn đồng ch&iacute; hướng c&ugrave;ng x&acirc;y dựng cộng đồng, x&atilde; hội tốt đẹp trong những tổ chức t&igrave;nh nguyện. Những người trẻ hay tham gia v&agrave;o c&aacute;c hoạt động t&igrave;nh nguyện, từ thiện m&agrave; t&ocirc;i thấy đều l&agrave; những người dễ tương t&aacute;c, c&oacute; kỹ năng tốt, thường xuy&ecirc;n thể hiện l&ograve;ng biết ơn, tin y&ecirc;u v&agrave;o những điều tốt đẹp trong cuộc sống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&aacute;c tổ chức phi ch&iacute;nh phủ v&agrave; c&aacute;c c&acirc;u lạc bộ t&igrave;nh nguyện như AIESEC, Operation Smile, LIN, Young at Heart Vietnam, Oxfam, Free Hugs Vietnam, Helping Hand Saigon... vẫn thường xuy&ecirc;n tuyển t&igrave;nh nguyện vi&ecirc;n. Li&ecirc;n tục theo d&otilde;i tin tức của c&aacute;c trang web, facebook fanpage của c&aacute;c tổ chức n&agrave;y v&agrave; tham gia hoạt động, kh&ocirc;ng những gi&uacute;p ta c&oacute; nhiều kỹ năng hơn m&agrave; c&oacute; khi c&ograve;n kiếm được cơ hội việc l&agrave;m ch&iacute;nh thức sau đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>8. L&agrave;m th&ecirc;m.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&ugrave;ng với việc l&agrave;m thiện nguyện, việc l&agrave;m th&ecirc;m trong những năm hai mươi c&oacute; thể gi&uacute;p &iacute;ch người trẻ rất nhiều tr&ecirc;n con đường sự nghiệp. H&atilde;y chọn l&agrave;m th&ecirc;m hoặc xin thực tập trong những ng&agrave;nh nghề m&agrave; m&igrave;nh hứng th&uacute; để gi&uacute;p &iacute;ch cho CV của m&igrave;nh. Nếu th&iacute;ch viết l&aacute;ch c&oacute; thể viết b&agrave;i cộng t&aacute;c cho c&aacute;c tờ b&aacute;o d&agrave;nh cho tuổi trẻ. Sinh vi&ecirc;n Việt Nam, Hoa Học Tr&ograve;, Tuổi Trẻ, Thanh Ni&ecirc;n... rất nhiều tờ b&aacute;o cộng t&aacute;c với học sinh sinh vi&ecirc;n v&igrave; sự tinh nhạy với những xu hướng chuyển động mới của thị trường, thời gian linh động, khả năng s&aacute;ng t&aacute;c nhanh v&agrave; nhiều của c&aacute;c bạn trẻ. Nếu th&iacute;ch kinh doanh c&oacute; thể bắt đầu bằng việc l&agrave;m nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng tại c&aacute;c cửa h&agrave;ng b&aacute;n lẻ. L&agrave;m phục vụ tại c&aacute;c qu&aacute;n c&agrave; ph&ecirc; tuy đơn giản nhưng cũng gi&uacute;p ta tiếp x&uacute;c với nhiều người hơn, l&agrave;m quen với việc tương t&aacute;c kh&aacute;ch h&agrave;ng v&agrave; tạo dựng mối quan hệ c&ocirc;ng việc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>9. Tự học c&aacute;c m&ocirc;n nghệ thuật.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Gi&aacute;o dục Việt Nam kh&ocirc;ng c&oacute; xu hướng dạy ch&uacute;ng ta về nghệ thuật, c&aacute;ch l&agrave;m nghệ thuật v&agrave; c&aacute;ch thưởng thức nghệ thuật. Do vậy, qu&atilde;ng đời hai mươi chưa nhiều vướng bận l&agrave; thời điểm tốt nhất để bạn tự trang bị cho m&igrave;nh những kiến thức n&agrave;y. H&atilde;y tập chơi một nhạc cụ n&agrave;o đ&oacute; (ghita, violin, piano, trống, s&aacute;o...) hoặc học vẽ tranh, khi&ecirc;u vũ, m&uacute;a... C&aacute;c m&ocirc;n nghệ thuật gi&uacute;p cho cuộc sống tinh thần của ta th&ecirc;m phong ph&uacute; v&agrave; vững ch&atilde;i, tăng th&ecirc;m tr&iacute; th&ocirc;ng minh v&agrave; khiến cuộc sống con người trọn vẹn đầy đủ hơn. Đến một l&uacute;c n&agrave;o đ&oacute; trong đời, bạn sẽ thấy nghệ thuật l&agrave; một phần lớn của cuộc sống, v&agrave; sẽ thấy thật may mắn l&agrave; bạn đ&atilde; kh&ocirc;ng bỏ lỡ niềm vui đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>10. D&agrave;nh thời gian cho c&aacute;c hoạt động tinh thần.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ph&aacute;t triển b&ecirc;n ngo&agrave;i kh&ocirc;ng thể n&agrave;o bền vững nếu thiếu đi sự vững chắc b&ecirc;n trong, hay sự kết nối với phần tinh thần, t&acirc;m linh của con người. Tuổi trẻ thường hướng ngoại m&agrave; qu&ecirc;n đi chăm s&oacute;c nội t&acirc;m. Nhưng nhiều khi n&oacute; l&agrave; nguy&ecirc;n nh&acirc;n cho những khủng hoảng trầm cảm, chỉ v&igrave; ta kh&ocirc;ng nhận thức được r&otilde; bản th&acirc;n m&igrave;nh l&agrave; ai, m&igrave;nh muốn g&igrave; th&iacute;ch g&igrave;. Vậy n&ecirc;n thời kỳ hai mươi cũng l&agrave; thời điểm tốt để t&igrave;m hiểu về c&aacute;c hoạt động tinh thần, t&igrave;m kiếm chỗ dựa vững chắc về t&acirc;m linh để kh&ocirc;ng bị khủng hoảng niềm tin sau n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thời sinh vi&ecirc;n kh&ocirc;ng biết c&oacute; bao nhi&ecirc;u ng&agrave;y t&ocirc;i cảm thấy nh&agrave;m ch&aacute;n. C&ograve;n b&acirc;y giờ khi đ&atilde; đi l&agrave;m, bị vướng bận v&agrave; b&oacute; buộc nhiều thứ, chỉ ao ước sắp xếp được thời gian để tham dự một kh&oacute;a thiền hay học hỏi th&ecirc;m về t&ocirc;n gi&aacute;o, tinh thần m&agrave; kh&ocirc;ng được. C&oacute; rất nhiều chương tr&igrave;nh thiền kh&aacute;c nhau. V&iacute; dụ, thiền Vipassana l&agrave; một phương ph&aacute;p được giảng dạy miễn ph&iacute; tại nhiều nơi tr&ecirc;n thế giới. Nếu hứng th&uacute; bạn c&oacute; thể xem th&ocirc;ng tin tại trang www.dhamma.org.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Người ta n&oacute;i: Trẻ th&igrave; muốn l&agrave;m m&agrave; kh&ocirc;ng biết g&igrave; để l&agrave;m, gi&agrave; th&igrave; biết m&agrave; kh&ocirc;ng muốn l&agrave;m. Tuổi trẻ c&ograve;n d&aacute;m thử d&aacute;m l&agrave;m, tận dụng thời gian n&agrave;y để trau dồi kiến thức v&agrave; m&agrave;i giũa kỹ năng sẽ gi&uacute;p &iacute;ch cho người trẻ rất nhiều trong tương lai. Nếu chịu kh&oacute; r&egrave;n luyện bản th&acirc;n, ta sẽ tự tin vững bước ra biển lớn, h&ograve;a nhập c&ugrave;ng thế giới v&agrave; bạn b&egrave; năm ch&acirc;u. Người trẻ hiểu biết th&igrave; d&acirc;n tr&iacute; sẽ n&acirc;ng cao. D&acirc;n tr&iacute; n&acirc;ng cao th&igrave; ch&iacute;nh quyền cải tiến. Ch&iacute;nh quyền cải tiến th&igrave; đất nước đi l&ecirc;n. Đất nước đi l&ecirc;n th&igrave; đời sau ph&aacute;t triển. Do vậy, học tập v&agrave; r&egrave;n luyện kh&ocirc;ng chỉ l&agrave; cho bản th&acirc;n, gia đ&igrave;nh, m&agrave; c&ograve;n cho x&atilde; hội, đất nước, v&agrave; cho cả c&aacute;c thế hệ mai sau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>S&Aacute;CH L&Agrave; CẢ THẾ GIỚI</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;<em>T&ocirc;i lu&ocirc;n mường tượng rằng Thi&ecirc;n đường cũng từa tựa như một thư viện vậy.</em>&quot; (Jorge Luis Borges)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nếu mượn được cổ m&aacute;y thần kỳ của Doraemon, nếu được quay về qu&aacute; khứ, nếu c&oacute; thể nhắn nhủ với tuổi trẻ của t&ocirc;i một điều th&ocirc;i, th&igrave; điều m&agrave; t&ocirc;i lựa chọn l&agrave;: Đọc s&aacute;ch nhiều nữa v&agrave;o, đồ ngốc ạ!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">V&igrave; cứ mỗi lần rời xa s&aacute;ch l&agrave; t&ocirc;i thấy đười m&igrave;nh đi xuống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ng&agrave;y xưa, l&uacute;c c&ograve;n trẻ con, cuộc sống của t&ocirc;i xoay quanh to&agrave;n l&agrave; s&aacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Căn nh&agrave; cũ của ba m&aacute; t&ocirc;i chất đầy s&aacute;ch. S&aacute;ch từ thời thanh ni&ecirc;n của ba m&aacute;, xếp d&agrave;i tr&ecirc;n c&aacute;i kệ bự chảng cho&aacute;ng cả một bức tường trong ph&ograve;ng ngủ, vẫn kh&ocirc;ng hết lại dồn th&agrave;nh đống chất v&agrave;o c&aacute;c tủ trong nh&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hồi đ&oacute; nh&agrave; to&agrave;n s&aacute;ch cũ, đủ mọi thể loại tr&ecirc;n trời dưới biển, t&ocirc;i cứ đụng l&agrave; đọc, chẳng cần biết s&aacute;ch loại g&igrave;, gi&aacute; trị ra sao. S&aacute;ch tuổi đời đ&atilde; l&acirc;u n&ecirc;n long b&igrave;a s&uacute;t chỉ, mỗi nơi mỗi xấp. Mỗi lần đọc chị em t&ocirc;i phải t&igrave;m tờ gh&eacute;p trang lại với nhau rất cực khổ. Đ&ocirc;i khi thiếu giấy nh&oacute;m bếp t&ocirc;i v&agrave; đứa em lại l&ecirc;n tủ s&aacute;ch lấy đại v&agrave;i tờ. Sau n&agrave;y lục lại tủ s&aacute;ch của &ocirc;ng nội, mới giật m&igrave;nh ph&aacute;t hiện ra hai đứa đ&atilde; đốt gần hết quyển&nbsp;<em>T&aacute;m mươi ng&agrave;y v&ograve;ng quanh thế giới</em>&nbsp;m&agrave; kh&ocirc;ng biết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&aacute;ch của ba m&aacute; t&ocirc;i rất nhiều quyển thuộc văn học cổ điển,&nbsp;<em><a href=\"https://docsach24.com/ebook/truyen-ngan-a-p-chekhov-anton-chekhov.1191.html\" target=\"_blank\">Truyện ngắn Chekhov</a>, B&ocirc;ng hồng v&agrave;ng, Tiếng chim h&oacute;t trong bụi mận gai, Jane Eyre..</em>.T&ocirc;i cứ lần lượt ngốn hết, c&oacute; c&aacute;i hiểu c&oacute; c&aacute;i kh&ocirc;ng, nhưng cứ thấy quyền n&agrave;o l&agrave; gặm quyển đ&oacute;, đắm ch&igrave;m trong những trang s&aacute;ch. Những kỷ niệm nho nhỏ với c&aacute;c quyển s&aacute;ch xưa ng&ocirc; ngh&ecirc; m&agrave; ch&acirc;n thực. Nhớ một buổi trưa, t&ocirc;i cố sống cố chết c&agrave;y cho hết quyển&nbsp;<em><a href=\"https://docsach24.com/ebook/truyen-ngan-a-p-chekhov-anton-chekhov.1191.html\" target=\"_blank\">Truyện ngắn Chekhov</a></em>, đến chiều hai mắt sưng v&ugrave; l&ecirc;n gật g&agrave; gật g&ugrave;, n&ecirc;n bị m&aacute; l&ocirc;i ra mắng cho một trận v&igrave; bỏ ngủ trưa. Hay lần đọc&nbsp;<em>Tiếng chim h&oacute;t trong bụi mận gai</em>, tới đoạn Meggie sinh đứa con đầu l&ograve;ng ở một trang trại xa x&ocirc;i, miệng kh&ocirc;ng ngừng k&ecirc;u t&ecirc;n cha Ralph, t&ocirc;i vừa đọc vừa kh&oacute;c th&uacute;t th&iacute;t, l&ograve;ng x&oacute;t xa tự hỏi tại sao y&ecirc;u nhau nhưng kh&ocirc;ng đến được với nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hồi đ&oacute; m&aacute; t&ocirc;i l&agrave;m gi&aacute;o vi&ecirc;n ở trường cấp hai của x&atilde;. Ngo&agrave;i những điểm bất lợi như lu&ocirc;n phải học giỏi thuộc b&agrave;i để khỏi hổ danh l&agrave;m con c&ocirc; gi&aacute;o, hay thi thoảng bị m&aacute; k&eacute;o ra khỏi lớp d&uacute;i cho quả chuối v&igrave; đi học trễ qu&ecirc;n ăn s&aacute;ng, th&igrave; lợi thế lớn nhất l&agrave; c&oacute; s&aacute;ch để đọc. L&acirc;u l&acirc;u, m&aacute; mượn được của thư viện trường một chồng s&aacute;ch cũ, đem về cho hai con mọt s&aacute;ch ngấu nghiến. Bởi n&ecirc;n những m&ugrave;a h&egrave; xưa b&eacute; của hai chị em t&ocirc;i ngo&agrave;i những tr&ograve; bắt cua chăn vịt, thả diều c&acirc;u c&aacute; th&igrave; c&ograve;n c&oacute; một th&uacute; vui lớn kh&aacute;c l&agrave; đọc s&aacute;ch. Nhưng quyển s&aacute;ch đong đầy kỷ niệm thơ trẻ, n&agrave;o l&agrave;&nbsp;<em>Calich v&agrave; Valia, Cuộc phi&ecirc;u lưu của M&iacute;t Đặc v&agrave; c&aacute;c bạn,&nbsp;<a href=\"https://docsach24.com/ebook/kho-bau-cua-vua-solomon.91.html\" target=\"_blank\">Kho b&aacute;u của vua Solomon</a>, T&ocirc;-mếch v&agrave; thủ lĩnh Tia Chớp Đen,&nbsp;<a href=\"https://docsach24.com/ebook/hoang-tu-be-antoine-de-saint-exupery.825.html\" target=\"_blank\">Ho&agrave;ng tử b&eacute;</a>, C&ocirc;ng ch&uacute;a nhỏ,&nbsp;<a href=\"https://docsach24.com/ebook/canh-buom-do-tham.24.html\" target=\"_blank\">C&aacute;nh buồm đỏ thắm</a>, Tuổi thơ dữ dội, Chiếc ch&igrave;a kh&oacute;a v&agrave;ng hay truyện ly kỳ của Buratino, T&acirc;m hồn cao thượng...</em>Những năm th&aacute;ng thơ b&eacute; tr&ocirc;i qua với những c&acirc;u chuyện trong trẻo v&agrave; b&igrave;nh y&ecirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cứ như thế, thế giới trẻ thơ của t&ocirc;i được x&acirc;y dựng n&ecirc;n từ c&aacute;i kh&ocirc;ng gian mộc mạc xanh tươi của v&ugrave;ng n&ocirc;ng th&ocirc;n y&ecirc;n tĩnh, của những nh&acirc;n vật tuổi thơ đầy kh&iacute; ph&aacute;ch. L&ograve;ng tốt v&agrave; sự nh&acirc;n hậu, sự giữ lời hứa, l&ograve;ng can đảm, sự ki&ecirc;n nhẫn, t&iacute;nh phi&ecirc;u lưu mạo hiểm, tinh thần vượt kh&oacute;...Tất cả những nền tảng của cuộc sống, t&ocirc;i v&agrave; em t&ocirc;i đ&atilde; được học từ tuổi thơ của m&igrave;nh. Đ&ocirc;i khi trong cuộc sống hiện tại, t&ocirc;i nghiệm ra rằng những rắc rối m&agrave; người ta gặp phải, hầu hết đều bắt nguồn từ sự l&atilde;ng qu&ecirc;n những nguy&ecirc;n tắc cơ bản đ&oacute; của cuộc sống. Mải m&ecirc; ch&igrave;m v&agrave;o những gi&aacute; trị hư ảo m&agrave; qu&ecirc;n đi điều g&igrave; l&agrave; cốt l&otilde;i của đời người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&aacute;ch t&ocirc;i đọc chỉ l&agrave; đống s&aacute;ch của ba m&aacute; v&agrave; những quyển m&aacute; mượn thư viện, n&ecirc;n dĩ nhi&ecirc;n l&agrave; kh&ocirc;ng đủ, l&acirc;u l&acirc;u thiếu s&aacute;ch cứ phải đọc đi đọc lại như b&ograve; nhai lại cỏ. S&aacute;ch mới th&igrave; đắt, n&ecirc;n thường nh&agrave; t&ocirc;i chỉ d&aacute;m mua quyển n&agrave;o thật cần thiết. Nh&agrave; t&ocirc;i vốn c&oacute; mấy c&acirc;y xo&agrave;i rất to, mỗi m&ugrave;a ra b&ocirc;ng gi&oacute; thổi &agrave;o qua một lượt l&agrave; cả s&acirc;n tr&agrave;n ngập b&ocirc;ng rụng, qu&eacute;t đi qu&eacute;t lại rất mệt. Nhưng xo&agrave;i ra trĩu c&agrave;nh, chấm với mắm ruốc ăn th&igrave; tuyệt vời. Năm t&ocirc;i học lớp ch&iacute;n, t&ocirc;i quyết định luyện thi v&agrave;o trường chuy&ecirc;n cấp ba của tỉnh. Một ng&agrave;y trong m&ugrave;a h&egrave; năm ấy, ba d&agrave;nh cả ng&agrave;y tr&ecirc;n c&acirc;y xo&agrave;i keo h&aacute;i. Rồi s&aacute;ng h&ocirc;m sau ba m&aacute; chở hai giỏ xo&agrave;i bự xuống chợ ở th&agrave;nh phố ngồi b&aacute;n tới tận chiều muộn, mỗi giỏ bằng cỡ c&aacute;i cần x&eacute; trong miền Nam. Tiền b&aacute;n xo&agrave;i được đ&acirc;u chừng năm chục ngh&igrave;n đồng, m&aacute; v&agrave;o nh&agrave; s&aacute;ch mua cho t&ocirc;i quyển&nbsp;<em>Những b&agrave;i văn hay lớp ch&iacute;n</em>, mua xong l&agrave; trong t&uacute;i cạn lu&ocirc;n tiền. T&ocirc;i nhận s&aacute;ch, vừa ngạc nhi&ecirc;n vừa hờ hững, kiểu cũng tự ki&ecirc;u m&igrave;nh đ&acirc;y văn hay chữ tốt, cần g&igrave; văn mẫu. Nhưng m&aacute; th&igrave; vui lắm, bảo đ&acirc;y l&agrave; qu&agrave; ch&uacute;c t&ocirc;i thi tốt. Đ&atilde; hơn mười năm tr&ocirc;i qua rồi m&agrave; t&ocirc;i vẫn nhớ như in n&eacute;t mặt của m&aacute; khi đưa s&aacute;ch cho m&igrave;nh buổi chiều h&ocirc;m đ&oacute;. Vẻ mặt bừng s&aacute;ng của niềm vui, hồ hởi v&agrave; hy vọng. Hai giỏ xo&agrave;i đầy đổi lấy một quyển s&aacute;ch. Nhớ lại m&agrave; nghẹn ng&agrave;o nước mắt, một thời con nh&agrave; ngh&egrave;o thiếu s&aacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">V&agrave;o cấp ba, tiền học bổng mỗi th&aacute;ng h&igrave;nh như khoảng t&aacute;m chục ngh&igrave;n, mỗi lần m&aacute; xuống thăm cho th&ecirc;m hai chục nữa. Hầu hết đổ v&agrave;o để mua s&aacute;ch tư liệu văn học , s&aacute;ch ph&ecirc; b&igrave;nh văn học, s&aacute;ch chuy&ecirc;n s&acirc;u, đủ thứ. Tiền th&igrave; &iacute;t m&agrave; s&aacute;ch cần mua th&igrave; nhiều, n&ecirc;n thỉnh thoảng quyển n&agrave;o ưng &yacute; lắm t&ocirc;i mới mua, c&ograve;n đ&acirc;u l&agrave; v&agrave;o thư viện tỉnh mượn về tra cứu. Thầy dạy văn bắt mỗi đứa phải c&oacute; một quyển sổ tư liệu văn học. T&ocirc;i lấy một quyển tập gi&aacute;o &aacute;n thiệt d&agrave;y của m&aacute; để l&agrave;m sổ tư liệu. Những ng&agrave;y g&ograve; lưng tr&ecirc;n thư viện ch&eacute;p những b&agrave;i thơ lạ, những lời ph&ecirc; b&igrave;nh t&acirc;m đắc v&agrave;o sổ, đ&ocirc;i khi nghỉ tay nh&igrave;n ra ngo&agrave;i, thấy mấy chiếc l&aacute; me t&acirc;y xanh ngắt rung rinh trong gi&oacute;, trong l&ograve;ng vừa nhẹ nh&agrave;ng vừa vui sướng. B&acirc;y giờ nhớ lại, những buổi ngồi say sưa đọc s&aacute;ch hay ghi ch&eacute;p ở thư viện tỉnh l&agrave; những khoảnh khắc hạnh ph&uacute;c nhất của t&ocirc;i thời cấp ba.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Rồi l&ecirc;n tới đại học. Ta n&oacute;i qu&atilde;ng đời đại học l&agrave; qu&atilde;ng đời ngu dại nhất của t&ocirc;i. Mang t&acirc;m l&yacute; con nh&agrave; ngh&egrave;o v&agrave;o S&agrave;i G&ograve;n trọ học, mấy năm sinh vi&ecirc;n t&ocirc;i chỉ chăm chăm l&agrave;m th&ecirc;m kiếm tiền, h&agrave; tiện đủ thứ kể cả tiền mua s&aacute;ch. Rồi cũng kh&ocirc;ng biết đường l&ecirc;n mạng t&igrave;m ebook để đọc. Đ&ocirc;i khi đọc s&aacute;ch th&igrave; chỉ đọc những quyển giải tr&iacute; rẻ tiền. S&aacute;ch c&oacute; gi&aacute; trị th&igrave; đọc mỗi năm chỉ v&agrave;i quyển, cứ như thế m&agrave; ngu dần đi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một lần đọc về tiểu sử cựu tổng thống Mỹ Bill Clinton, t&ocirc;i thấy &ocirc;ng đọc đến v&agrave;i trăm quyển s&aacute;ch trong suốt bốn năm đại học. Nghĩ m&agrave; tiếc nuối kh&ocirc;ng để đ&acirc;u cho hết. Gi&aacute; như thời đi học chuy&ecirc;n cần đọc như Clinton th&igrave; cho d&ugrave; b&acirc;y giờ kh&ocirc;ng th&agrave;nh tổng thống chắc cũng kh&ocirc;ng hổ thẹn mang danh &quot;l&agrave;m trai đứng ở trong trời đất.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đi l&agrave;m được v&agrave;i năm, kh&ocirc;ng hiểu được cơ duy&ecirc;n đưa đẩy trời Phật thương t&igrave;nh sao m&agrave; t&ocirc;i bắt đầu đọc s&aacute;ch lại. Đọc kh&ocirc;ng ngừng nghỉ. Sau một năm thấy kiến thức của m&igrave;nh tăng l&ecirc;n đ&aacute;ng kể. Nh&igrave;n lại một chặng đường d&agrave;i, thấy đời m&igrave;nh sang trang mới cũng l&agrave; nhờ đọc s&aacute;ch. T&ocirc;i y&ecirc;u qu&yacute; s&aacute;ch bao nhi&ecirc;u th&igrave; cũng biết ơn s&aacute;ch bấy nhi&ecirc;u. Bởi nhờ c&oacute; s&aacute;ch m&agrave; t&ocirc;i mới trưởng th&agrave;nh hơn, ph&aacute;t triển hơn, nu&ocirc;i dưỡng lại t&igrave;nh y&ecirc;u với nghiệp viết v&agrave; mới trở th&agrave;nh t&aacute;c giả, như ước mơ của t&ocirc;i từ thuở b&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kh&ocirc;ng chỉ t&ocirc;i m&agrave; rất nhiều người kh&aacute;c đ&atilde; thay đổi nhờ s&aacute;ch. L&ecirc; Phương Anh Vũ, người s&aacute;ng lập c&acirc;u lạc bộ sinh vi&ecirc;n lớn mạnh, l&agrave; một trong những người thuộc thế hệ 9X giỏi nhất m&agrave; t&ocirc;i biết. Em kể với t&ocirc;i rằng thời cấp ba em học h&agrave;nh kh&ocirc;ng qu&aacute; nổi bật, l&uacute;c v&agrave;o đại học cũng nh&uacute;t nh&aacute;t tự ti. Nhưng từ l&uacute;c em chăm chỉ đọc s&aacute;ch, rồi ra ngo&agrave;i l&agrave;m nhiều hơn, tự học, tự ph&aacute;t triển ch&iacute;nh m&igrave;nh, m&agrave; em trở th&agrave;nh một trong những người được biết đến nhiều nhất ở trường đại học của em. Em d&igrave;u dắt c&aacute;c thế hệ, sinh vi&ecirc;n đi sau, x&acirc;y dựng những kh&oacute;a đ&agrave;o tạo kỹ năng v&agrave; hướng dẫn nhiều người kh&aacute;c trong con đường tự học v&agrave; ph&aacute;t triển bản th&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Long Ứng Đ&agrave;i, Bộ trưởng Bộ Văn h&oacute;a Đ&agrave;i Loan, một nh&agrave; văn nổi tiếng, cũng từng viết cho con m&igrave;nh một l&aacute; thư d&agrave;i về s&aacute;ch. Người con trai hai mươi mốt tuổi cảm thấy &aacute;p lực v&igrave; c&oacute; cả cha lẫn mẹ đều l&agrave; những người nổi tiếng, th&agrave;nh đạt. Cậu n&oacute;i với mẹ: &quot;Mẹ phải chấp nhận rằng con chỉ l&agrave; một người b&igrave;nh thường, kh&ocirc;ng c&oacute; th&agrave;nh tựu g&igrave; nổi bật trong cuộc đời.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Long Ứng Đ&agrave;i trả lời: &quot;Điều quan trọng nhất đối với mẹ, kh&ocirc;ng phải l&agrave; con c&oacute; th&agrave;nh tựu hay kh&ocirc;ng, m&agrave; l&agrave; con c&oacute; hạnh ph&uacute;c hay kh&ocirc;ng, trong cuộc sống hiện đại b&acirc;y giờ, loại c&ocirc;ng việc n&agrave;o c&oacute; thể &iacute;t nhiều đem lại cho con niềm hạnh ph&uacute;c? Thứ nhất, n&oacute; cho con &yacute; nghĩa, c&ocirc;ng việc đ&oacute; kh&ocirc;ng điều khiển con, kh&ocirc;ng giam cầm con như t&ugrave; binh. Thứ hai, n&oacute; cho con thời gian, n&oacute; cho con trải nghiệm đầy đủ trong cuộc sống. V&iacute; dụ l&agrave;m quản l&yacute; ng&acirc;n h&agrave;ng ở Phố Wall, nhưng mỗi ng&agrave;y v&igrave; tiền m&agrave; căng thẳng, c&oacute; thể lại kh&ocirc;ng bằng l&agrave;m nghề nh&acirc;n vi&ecirc;n coi s&oacute;c vườn th&uacute;, hằng ng&agrave;y tắm rửa cho voi hay đ&aacute;nh răng cho h&agrave; m&atilde;.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nữ nh&agrave; văn kể lại c&acirc;u chuyện l&uacute;c c&ograve;n ở Đức, hai mẹ con họ gặp một họa sĩ t&ecirc;n l&agrave; Timothy. Anh ta từ nhỏ đ&atilde; y&ecirc;u th&iacute;ch tranh, ở trong hệ thống gi&aacute;o dục tự do, n&ecirc;n học h&agrave;nh kh&ocirc;ng ổn định, l&uacute;c th&igrave; học ngoại ngữ để l&agrave;m phi&ecirc;n dịch, l&uacute;c th&igrave; học l&agrave;m thợ kh&oacute;a, l&uacute;c học l&agrave;m thợ mộc. Sau khi tốt nghiệp, anh kh&ocirc;ng t&igrave;m được việc l&agrave;m. Đến năm 41 tuổi anh ấy vẫn thất nghiệp như thế v&agrave; vẫn ở c&ugrave;ng mẹ m&igrave;nh. L&uacute;c kh&ocirc;ng c&oacute; việc g&igrave; l&agrave;m, anh ta ngồi ở cửa sổ s&aacute;t đường, vẽ hươu cao cổ. Trong c&aacute;c bức vẽ của anh, cổ của hươu th&ograve; ra từ đỉnh của xe bu&yacute;t, xuy&ecirc;n qua s&acirc;n bay, đi v&agrave;o rạp phim. N&oacute; mở to đ&ocirc;i mắt với l&ocirc;ng mi d&agrave;i, nh&igrave;n chằm chằm v&agrave;o một đứa trẻ ngồi tr&ecirc;n xa ba b&aacute;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Long Ứng Đ&agrave;i bảo, b&agrave; sợ con th&agrave;nh người vẽ hươu cao cổ như Timothy, kh&ocirc;ng phải v&igrave; anh ta kh&ocirc;ng c&oacute; tiền hay kh&ocirc;ng c&oacute; danh. M&agrave; l&agrave; v&igrave; anh ta kh&ocirc;ng t&igrave;m được &yacute; nghĩa cuộc sống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cuối thư, b&agrave; viết: &quot;Mẹ y&ecirc;u cầu con chăm chỉ đọc s&aacute;ch, kh&ocirc;ng phải v&igrave; mẹ muốn con c&oacute; th&agrave;nh tựu hơn người kh&aacute;c, m&agrave; l&agrave; bởi v&igrave;, mẹ muốn con c&oacute; nhiều hơn nữa quyền lựa chọn cho tương lai, lựa chọn c&oacute; &yacute; nghĩa, c&oacute; thời gian l&agrave;m việc, chứ kh&ocirc;ng phải l&agrave; bị &eacute;p mưu sinh.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Quả thật, c&aacute;i m&agrave; con người ta cần nhất trong đời l&agrave; quyền lựa chọn một cuộc sống được l&agrave;m những điều c&oacute; &yacute; nghĩa. V&agrave; s&aacute;ch l&agrave; một c&ocirc;ng cụ gi&uacute;p ta đạt được điều đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&aacute;ch cho ta kiến thức, cho ta động lực, cho ta mơ về những nơi xa hơn. S&aacute;ch gi&uacute;p mở ra một thế giới tốt đẹp hơn, những con người tốt hơn, v&agrave; cả một phi&ecirc;n bản tốt hơn của ch&iacute;nh ta. N&oacute;i kh&ocirc;ng ngoa, s&aacute;ch l&agrave; cả thế giới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ngươi trẻ ở th&agrave;nh phố th&igrave; qu&aacute; nhiều s&aacute;ch để đọc. C&ograve;n mỗi lần về n&ocirc;ng th&ocirc;n th&igrave; thấy tụi nhỏ suốt cả m&ugrave;a h&egrave; la c&agrave; chơi bời l&ecirc;u lổng, thiếu s&aacute;ch trầm trọng. Những người trẻ được nu&ocirc;i dưỡng đầy đủ, được cho ăn học đ&agrave;ng ho&agrave;ng như ch&uacute;ng ta, h&atilde;y lu&ocirc;n nu&ocirc;i dưỡng cho m&igrave;nh kỹ năng đọc s&aacute;ch, V&agrave; đừng qu&ecirc;n gieo trồng th&oacute;i quen đọc ở những đứa trẻ quanh m&igrave;nh, em m&igrave;nh, con m&igrave;nh, ch&aacute;u m&igrave;nh...</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Để hy vọng rằng những hạt mầm tươi s&aacute;ng m&agrave; ta gieo cho lũ trẻ ng&agrave;y nay, sẽ h&igrave;nh th&agrave;nh những Antonie de Saint-Exup&eacute;ry hay Lucy Maud Montgomery của Việt Nam trong tương lai mai sau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">- Antonie de Saint-Exup&eacute;ry: phi c&ocirc;ng, nh&agrave; văn người Ph&aacute;p, t&aacute;c giả s&aacute;ch&nbsp;<strong><a href=\"https://docsach24.com/ebook/hoang-tu-be-antoine-de-saint-exupery.825.html\">Ho&agrave;ng tử b&eacute;</a></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">- Lucy Maud Montgomery: Nh&agrave; văn người Canada, t&aacute;c giả của hơn 20 tiểu thuyết, được biết đến nhiều nhất l&agrave;&nbsp;<a href=\"https://docsach24.com/ebook/anne-toc-do-duoi-chai-nha-xanh-lucy-maud-montgomery.2424.html\" target=\"_blank\">Anne t&oacute;c đỏ dưới ch&aacute;i nh&agrave; xanh</a>&nbsp;v&agrave; Emily ở trang trại trăng non.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>ĐỌC S&Aacute;CH NHƯ THẾ N&Agrave;O</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Bạn c&agrave;ng đọc nhiều, bạn c&agrave;ng biết nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bạn c&agrave;ng học nhiều, b&agrave;n c&agrave;ng đi nhiều.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">(Dr.Seuss)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tại sao t&ocirc;i lại n&oacute;i nhiều về s&aacute;ch đến thế trong một quyển s&aacute;ch d&agrave;nh cho người trẻ?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kh&ocirc;ng phải l&agrave; bởi v&igrave; t&ocirc;i l&agrave; một t&aacute;c giả, t&ocirc;i muốn h&ocirc; h&agrave;o cổ vũ độc giả đọc nhiều s&aacute;ch, để từ đ&oacute; gi&aacute;n tiếp th&uacute;c đẩy doanh số b&aacute;n s&aacute;ch của t&ocirc;i. Ho&agrave;n to&agrave;n kh&ocirc;ng. T&aacute;c giả viết về tự học m&agrave; mong s&aacute;ch b&aacute;n chạy th&igrave; chỉ l&agrave; chuyện nằm mơ giữa ban ng&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">L&yacute; do l&agrave; bởi v&igrave;, t&ocirc;i thấy kh&ocirc;ng biết bao nhi&ecirc;u người trẻ quanh m&igrave;nh chưa nhận thức đ&uacute;ng tầm quan trọng của s&aacute;ch, cũng như kh&ocirc;ng biết đọc s&aacute;ch thế n&agrave;o cho đ&uacute;ng c&aacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&oacute; hai loại phản ứng ti&ecirc;u cực ti&ecirc;u biểu từ người &iacute;t đọc khi được khuy&ecirc;n đọc s&aacute;ch nhiều hơn:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Đọc s&aacute;ch đ&acirc;u bảo đảm th&agrave;nh c&ocirc;ng.&quot; - Một c&acirc;u ngụy biện kinh điển của những người lười đọc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;S&aacute;ch chỉ khiến người ta mơ mộng h&atilde;o huyền phi thực tế.&quot; - Ph&aacute;t biểu từ một người thiếu hiểu biết về s&aacute;ch. Đối với họ chắc s&aacute;ch chỉ c&oacute; mỗi thể loại tiểu thuyết diễm t&igrave;nh huyễn hoặc. T&ocirc;i kh&ocirc;ng biết lần cuối c&ugrave;ng họ cầm một quyển s&aacute;ch tử tế trong tay l&agrave; khi n&agrave;o.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">R&otilde; r&agrave;ng l&agrave; kh&ocirc;ng phải ai đọc s&aacute;ch cũng th&agrave;nh c&ocirc;ng. Nhưng lại c&oacute; một sự thực r&otilde; r&agrave;ng kh&aacute;c l&agrave; những người th&agrave;nh c&ocirc;ng đọc rất nhiều s&aacute;ch. Một nghi&ecirc;n cứu được tiến h&agrave;nh tr&ecirc;n 1.200 người gi&agrave;u c&oacute; nhất thế giới cho thấy: Điểm chung giữa những người n&agrave;y l&agrave; họ tự gi&aacute;o dục bản th&acirc;n th&ocirc;ng qua việc đọc s&aacute;ch. Bill Gates, Steve Jobs, Warren Buffet đều l&agrave; những người đọc s&aacute;ch rất chuy&ecirc;n cần. Đọc s&aacute;ch kh&ocirc;ng chắc sẽ gi&uacute;p ta th&agrave;nh đạt tr&ecirc;n đường đời, nhưng kh&ocirc;ng c&oacute; n&oacute; hầu như ta kh&ocirc;ng thể th&agrave;nh người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Warren Buffet từng trả lời trong một b&agrave;i phỏng vấn rằng &ocirc;ng d&agrave;nh 80% thời gian trong ng&agrave;y để đọc s&aacute;ch v&agrave; tiếp thu kiến thức mới. Kh&ocirc;ng chỉ ri&ecirc;ng &ocirc;ng, nghi&ecirc;n cứu tiểu sử của những con người xuất ch&uacute;ng kh&aacute;c, ta sẽ thấy điểm chung l&agrave; họ vui vẻ v&agrave; say m&ecirc; đọc s&aacute;ch, ngay cả sau những giờ lao động mệt nhọc. V&igrave; đối với họ, đọc s&aacute;ch l&agrave; một c&aacute;ch tự học. V&agrave; sự học l&agrave; sự nghiệp cả đời.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhưng s&aacute;ch th&igrave; c&oacute; nhiều loại. Đ&acirc;u phải s&aacute;ch n&agrave;o cũng gi&uacute;p m&igrave;nh tiếp thu kiến thức.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vậy n&ecirc;n đọc s&aacute;ch như thế n&agrave;o?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Sau một thời gian m&agrave;y m&ograve; t&igrave;m hiểu tr&ecirc;n con đường tự học, t&ocirc;i đ&atilde; t&igrave;m được c&aacute;ch đọc s&aacute;ch th&iacute;ch hợp nhất cho m&igrave;nh. Giờ đ&acirc;y, t&ocirc;i xin hoan hỉ chia sẻ lại cho c&aacute;c bạn đọc phương ph&aacute;p đọc s&aacute;ch của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đầu ti&ecirc;n l&agrave; về số lượng, cần đọc &iacute;t nhất 50 quyển s&aacute;ch một năm. C&oacute; thể v&agrave;i người sẽ n&oacute;i: Như thế l&agrave; qu&aacute; nhiều, coi chừng ngộ chữ. Xin thưa l&agrave; kh&ocirc;ng nhiều ch&uacute;t n&agrave;o. Năm mươi quyển s&aacute;ch mỗi năm, chia ra l&agrave; mỗi tuần một quyển. S&aacute;ch c&oacute; nội dung tốt độ d&agrave;y trung b&igrave;nh thường khoảng 200 - 300 trang. Tức mỗi ng&agrave;y chỉ cần đọc 40 - 50 trang s&aacute;ch. Nếu tập trung th&igrave; chỉ mất khoảng một giờ đồng hồ cho việc đọc mỗi ng&agrave;y. Cứ chăm chỉ ng&agrave;y n&agrave;o cũng vậy, l&agrave; mỗi năm sẽ ho&agrave;n th&agrave;nh được từ 50 quyển s&aacute;ch trở l&ecirc;n. Chứ v&agrave;i th&aacute;ng mới đọc hết một quyển, th&igrave; cũng kh&ocirc;ng &iacute;ch lợi bao nhi&ecirc;u.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&aacute;ch đọc s&aacute;ch của t&ocirc;i l&agrave; c&acirc;n bằng giữa s&aacute;ch hư cấu v&agrave; phi hư cấu. Mỗi khi đọc một quyển thuộc thể loại n&agrave;y, t&ocirc;i lại đọc một quyển thuộc thể loại kia. Mục đ&iacute;ch l&agrave; để c&acirc;n bằng giữa thực tế v&agrave; tưởng tượng. T&ocirc;i đọc nhiều s&aacute;ch về ph&aacute;t triển bản th&acirc;n, r&egrave;n luyện kỹ năng v&agrave; truyền cảm hứng, c&aacute;c s&aacute;ch về lịch sử, văn ho&aacute;, tinh thần, kinh doanh. Đối với thể loại hư cấu, t&ocirc;i chọn c&aacute;c t&aacute;c phẩm văn học kinh điển v&agrave; c&oacute; chiều s&acirc;u, biểu hiện t&acirc;m l&yacute; con người hay ph&aacute;c hoạ bối cảnh lịch sử x&atilde; hội của một thời kỳ, v&agrave; c&oacute; gi&aacute; trị trị l&acirc;u d&agrave;i. Những quyển như&nbsp;<em><a href=\"https://docsach24.com/ebook/kieu-hanh-va-dinh-kien.353.html\" target=\"_blank\">Ki&ecirc;u h&atilde;nh v&agrave; định kiến</a>, Tiếng chim h&oacute;t trong bụi mận gai,&nbsp;<a href=\"https://docsach24.com/ebook/cuon-theo-chieu-gio-margaret-mitchell.2318.html\" target=\"_blank\">Cuốn theo chiều gi&oacute;</a>, Jane v&agrave; Eyre...&nbsp;</em>thay v&igrave; c&aacute;c loại s&aacute;ch chỉ n&oacute;i về t&igrave;nh cảm nam nữ y&ecirc;u đương thuần tu&yacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i tr&aacute;nh xa thể loại tiểu thuyết diễm t&igrave;nh như một người l&agrave;nh mạnh tr&aacute;nh xa dịch bệnh. Những s&aacute;ch ấy kh&ocirc;ng phải tất cả chỉ to&agrave;n rẻ tiền bỏ đi. Nhưng d&ugrave; cho t&aacute;c giả viết hay, d&ugrave; văn chương bay bổng, cảm x&uacute;c ngất trời th&igrave; cũng kh&ocirc;ng c&oacute; &iacute;ch lợi nhiều cho con đường tự học. D&ugrave; c&oacute; đọc đến h&agrave;ng trăm quyển ng&ocirc;n t&igrave;nh đi nữa, th&igrave; xong rồi cũng kh&ocirc;ng đọng lại bao nhi&ecirc;u kiến thức, c&agrave;ng g&acirc;y t&acirc;m l&yacute; uỷ mị hoang đường, xa rời thực tế. Thu Giang Nguyễn Duy Cần, một trong những học giả kỳ cựu bậc nhất Việt Nam giữa thế kỷ 20 c&oacute; viết: &quot;Kẻ n&agrave;o đọc những loại tiểu thuyết nhảm sẽ l&agrave;m mất thời giờ rất qu&yacute; b&aacute;u của họ. V&agrave; đ&aacute;ng &acirc;n hận hơn, l&agrave; rồi họ sẽ mất dần &oacute;c ph&aacute;n đo&aacute;n v&agrave; qu&acirc;n b&igrave;nh của t&acirc;m t&igrave;nh họ nữa.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&aacute;ch dở đọc chỉ tốn thời giờ. T&ocirc;i thường lựa chọn đọc s&aacute;ch n&agrave;o được đ&aacute;nh gi&aacute; cao, t&aacute;c giả nổi tiếng, gi&aacute; trị của t&aacute;c phẩm đ&atilde; được thử th&aacute;ch qua độ bền của thời gian.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vậy l&agrave;m sao để biết quyển n&agrave;o hay dở ra sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">B&iacute; quyết của t&ocirc;i l&agrave;: www.goodreads.com. Đ&acirc;y l&agrave; một trang mạng x&atilde; hội về s&aacute;ch v&agrave; l&agrave; c&ocirc;ng cụ hữu hiệu để chọn s&aacute;ch d&agrave;nh cho d&acirc;n ghiền đọc. Trang n&agrave;y tổng hợp những nhận x&eacute;t, b&igrave;nh luận, xếp hạng s&aacute;ch của người d&ugrave;ng, v&agrave; dựa tr&ecirc;n những đ&aacute;nh gi&aacute; s&aacute;ch trước đ&oacute; của người d&ugrave;ng m&agrave; gợi &yacute; những quyển s&aacute;ch kh&aacute;c c&ugrave;ng thể loại cho họ. Những người bạn phương T&acirc;y của t&ocirc;i trước khi quyết định mua một quyển s&aacute;ch n&agrave;o đ&oacute; thường v&agrave;o Goodreads đọc tất cả những b&igrave;nh luận khen ch&ecirc; c&aacute;c kiểu, rồi mới quyết định mua hay kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Goodreads d&ugrave; chủ yếu l&agrave; nhận x&eacute;t đ&aacute;nh gi&aacute; s&aacute;ch nhưng vẫn lưu trữ một số ebook c&oacute; sẵn để người d&ugrave;ng đọc trực tiếp tr&ecirc;n trang. Đa phần l&agrave; c&aacute;c t&aacute;c phẩm kinh điển như&nbsp;<em><a href=\"https://docsach24.com/ebook/ban-ve-tu-do-j-s-mill.715.html\" target=\"_blank\">B&agrave;n về tự do</a>&nbsp;</em>của John Stuart Mill hay&nbsp;<em><a href=\"https://docsach24.com/ebook/gio-qua-rang-lieu.549.html\" target=\"_blank\">Gi&oacute; qua rặng liễu</a>&nbsp;</em>của Kenneth Grahame. Goodreads hoạt động rất mạnh v&agrave; li&ecirc;n kết với nhiều t&aacute;c giả. Những t&aacute;c giả nổi tiếng thế giới như Paulo Coelho hay Elizabeth Gilbert thường c&oacute; những chương tr&igrave;nh giao lưu với độc giả, trả lời c&acirc;u hỏi v&agrave; đưa ra lời khuy&ecirc;n về viết văn tr&ecirc;n trang n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cứ mỗi năm mới đến t&ocirc;i sẽ đặt mục ti&ecirc;u năm nay cần đọc bao nhi&ecirc;u quyển s&aacute;ch. Đọc xong mỗi quyển th&igrave; l&ecirc;n trang Goodreads đ&aacute;nh gi&aacute; v&agrave; ghi lại cảm nhận quan điểm của m&igrave;nh. Nếu c&oacute; thời gian th&igrave; t&oacute;m tắt về quyển s&aacute;ch vừa đọc. Đến cuối năm Goodreads tổng hợp lại th&agrave;nh một trang&nbsp;<em>Your year in books&nbsp;</em>(tạm dịch: Năm của bạn qua những quyển s&aacute;ch). Nh&igrave;n lại một năm m&igrave;nh đ&atilde; đọc được kh&aacute; nhiều, thấy vui vui hay hay v&igrave; đ&atilde; đạt được một th&agrave;nh t&iacute;ch nho nhỏ cho bản th&acirc;n. Ng&agrave;y đầu ti&ecirc;n đăng k&yacute; Goodreads t&ocirc;i đ&atilde; chẳng biết n&oacute; sẽ đến đ&acirc;u, chẳng biết c&oacute; lợi &iacute;ch g&igrave;, chỉ đ&aacute;nh gi&aacute; s&aacute;ch v&igrave; th&iacute;ch. Đến khi đ&atilde; trở th&agrave;nh t&aacute;c giả, được trang b&aacute;n s&aacute;ch trực tuyến Tiki mời viết chuy&ecirc;n mục&nbsp;<em>Người nổi tiếng đọc g&igrave;,&nbsp;</em>t&igrave;m lại đ&aacute;nh gi&aacute; s&aacute;ch của m&igrave;nh tr&ecirc;n Goodreads, t&ocirc;i mới &agrave; l&ecirc;n, giờ đ&atilde; thấy ch&uacute;ng hữu dụng. Ngẫm ra sự đời c&oacute; những chuyện ta l&agrave;m m&agrave; ai&nbsp; biết được tương lai. Cứ để niềm y&ecirc;u th&iacute;ch dẫn dắt, rồi cũng c&oacute; l&uacute;c n&agrave;o đ&oacute; hữu &iacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một b&iacute; quyết kh&aacute;c nữa l&agrave; mỗi khi đọc s&aacute;ch, t&ocirc;i thường để b&ecirc;n m&igrave;nh một quyển sổ nhỏ. Sổ được chia trang theo nhiều chủ đề, v&iacute; dụ: Th&agrave;nh c&ocirc;ng, Kinh doanh, Viết l&aacute;ch, Th&aacute;i độ sống, Thay đổi bản th&acirc;n. Khi đọc, thấy c&acirc;u n&agrave;o t&acirc;m đắc th&igrave; ghi v&agrave;o sổ, k&egrave;m với những cảm nhận của m&igrave;nh li&ecirc;n quan tới c&acirc;u đ&oacute; hoặc c&aacute;ch diễn dịch diễn giải nếu c&oacute;. Sau n&agrave;y mỗi lần cần đọc lại để tạo động lực hay cần tư liệu để dẫn chứng, chứng minh khi n&oacute;i chuyện, viết l&aacute;ch th&igrave; t&ocirc;i lại lấy sổ ra t&igrave;m lại. Em trai t&ocirc;i cũng c&oacute; một quyển sổ tr&iacute;ch dẫn từ s&aacute;ch v&agrave; phim, nhưng với mục đ&iacute;ch kh&aacute;c. N&oacute; d&ugrave;ng đ&oacute; như l&agrave; nguồn tư liệu để &quot;ch&eacute;m gi&oacute;&quot;, n&oacute; bảo thấy lời n&oacute;i của m&igrave;nh c&oacute; chiều s&acirc;u v&agrave; sức mạnh hơn hẳn khi tr&iacute;ch dẫn mấy c&acirc;u n&oacute;i h&agrave;i hước hay ho.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i đọc nhiều s&aacute;ch của c&aacute;c t&aacute;c giả nước ngo&agrave;i, chủ yếu l&agrave; t&aacute;c giả Anh, Mỹ, v&agrave; ưu ti&ecirc;n đọc nguy&ecirc;n t&aacute;c bằng tiếng Anh: Nh&agrave; gi&aacute;o dục ti&ecirc;n phong trong thời kỳ Minh Trị l&agrave; Fukuzawa Yukichi, đ&atilde; &aacute;p dụng phương ph&aacute;p đọc s&aacute;ch nguy&ecirc;n bản n&agrave;y v&agrave;o ng&ocirc;i trường đại học tư thục hiện đại đầu ti&ecirc;n của Nhật Bản, do &ocirc;ng s&aacute;ng lập n&ecirc;n. &Ocirc;ng dồn tiền mua s&aacute;ch nguy&ecirc;n bản từ Mỹ về, cho học tr&ograve; đọc. Quan niệm của &ocirc;ng l&agrave; khi đọc s&aacute;ch nguy&ecirc;n bản ta được đọc nguồn trực tiếp. C&ograve;n s&aacute;ch dịch đ&ocirc;i khi kh&ocirc;ng được dịch s&aacute;t nguồn. Đọc s&aacute;ch dịch l&agrave; đọc lại qua một nguồn thứ cấp, theo c&aacute;ch hiểu chủ quan của người dịch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; văn J.K Rowling từng c&oacute; lời khuy&ecirc;n cho người viết trẻ rằng nếu muốn viết tốt, h&atilde;y đọc c&agrave;ng nhiều c&agrave;ng tốt. Đọc mọi loại s&aacute;ch c&oacute; thể. Đọc nhiều th&igrave; bạn sẽ ph&aacute;t hiện ra phong c&aacute;ch m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch, v&agrave; tr&aacute;nh được c&aacute;c thể loại s&aacute;ch m&agrave; bạn cho l&agrave; r&aacute;c rưởi. N&ecirc;n đối với những người ấp ủ ước mơ l&agrave;m nghề viết, nh&agrave; văn, nh&agrave; b&aacute;o, copywriter... th&igrave; việc đọc chuy&ecirc;n cần l&agrave; kh&ocirc;ng thể thiếu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">George R.R Martin th&igrave; c&oacute; c&acirc;u n&oacute;i &quot;Người đọc s&aacute;ch sống một ngh&igrave;n cuộc sống trước khi anh ta chết, c&ograve;n người kh&ocirc;ng đọc sống chỉ một đời.&quot; Những ai đọc s&aacute;ch th&igrave; biết th&ecirc;m được nhiều cuộc đời kh&aacute;c, n&ecirc;n coi như l&agrave; sống ngh&igrave;n cuộc sống chỉ trong một kiếp người. S&aacute;ch thật kỳ diệu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bởi vậy kh&ocirc;ng đọc s&aacute;ch mỗi ng&agrave;y l&agrave; một thiệt th&ograve;i lớn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>ĐỪNG DỰA V&Agrave;O TRƯỜNG HỌC</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Ai cũng l&agrave; thi&ecirc;n t&agrave;i. Nhưng nếu bạn đ&aacute;nh gi&aacute; một con c&aacute; qua khả năng leo c&acirc;y của n&oacute; th&igrave; n&oacute; sẽ sống cả đời tin rằng m&igrave;nh l&agrave; kẻ ngu ngốc.&quot; (Albert Einstein)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">N&oacute;i chuyện với đứa em đang học cấp ba. N&oacute; gặp kh&oacute; khăn nghi&ecirc;m trọng với m&ocirc;n văn, vấn đề mu&ocirc;n thuở của mấy thằng con trai kh&ocirc; khan yếu c&aacute;c m&ocirc;n x&atilde; hội. Ngồi một hồi để tư vấn cho n&oacute; v&agrave;i lời khuy&ecirc;n, t&ocirc;i nhớ lại c&acirc;u chuyện ngụ ng&ocirc;n về trường học lo&agrave;i vật m&agrave; t&ocirc;i từng&nbsp; đọc được. C&acirc;u chuyện được cho l&agrave; của t&aacute;c giả George Reavis.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Truyện kể rằng một h&ocirc;m, c&aacute;c lo&agrave;i th&uacute; vật quyết định rằng ch&uacute;ng phải l&agrave;m g&igrave; đ&oacute; thật vĩ đại để giải quyết c&aacute;c vấn đề m&agrave; thế giới đang đối mặt. V&agrave; thế l&agrave; ch&uacute;ng mở ra một trường học.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tất cả lo&agrave;i vật đ&atilde; th&ocirc;ng qua một chương tr&igrave;nh giảng dạy bao gồm: leo c&acirc;y, chạy, bơi v&agrave; bay. Để việc quản l&yacute; chương tr&igrave;nh được dễ d&agrave;ng hơn, mọi lo&agrave;i vật đều phải tham gia tất cả c&aacute;c m&ocirc;n học.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">V&agrave; rồi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Con vịt rất xuất sắc trong m&ocirc;n bơi lội, thậm ch&iacute; l&agrave; c&ograve;n giỏi hơn cả thầy gi&aacute;o. Nhưng n&oacute; chỉ đủ điểm đậu trong m&ocirc;n bay v&agrave; rất tệ trong m&ocirc;n chạy. V&igrave; vịt chạy rất chậm, n&oacute; phải ở lại trường sau giờ học v&agrave; phải bỏ lu&ocirc;n cả bơi để luyện tập m&ocirc;n chạy. Việc n&agrave;y tiếp diễn cho đến khi m&agrave;ng ch&acirc;n của n&oacute; bị r&aacute;ch toạc khiến vịt chỉ đạt điểm trung b&igrave;nhbtrong m&ocirc;n bơi. V&igrave; ở trường, điểm trung b&igrave;nh l&agrave; chấp nhận được, n&ecirc;n chẳng ai lo lắng về điều đ&oacute; trừ vịt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Con ngựa dẫn đầu lớp trong m&ocirc;n chạy, nhưng n&oacute; gặp kh&oacute; khăn lớn v&agrave;o những giờ học leo c&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">S&oacute;c th&igrave; rất giỏi trong m&ocirc;n leo c&acirc;y, nhưng n&oacute; lại thất bại trong m&ocirc;n bay, khi thầy gi&aacute;o y&ecirc;u cầu phải bay từ dưới đất l&ecirc;n thay v&igrave; từ ngọn c&acirc;y xuống. N&oacute; bị chuột r&uacute;t v&igrave; phải cố gắng qu&aacute; sức v&agrave; sau đ&oacute; bị bốn điểm trong m&ocirc;n leo v&agrave; hai điểm trong m&ocirc;n chạy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đại b&agrave;ng l&agrave; một đứa trẻ hư đốn v&agrave; thường xuy&ecirc;n bị kỷ luật nặng nề. Trong giờ học leo tr&egrave;o, n&oacute; vượt qua tất cả c&aacute;c học sinh kh&aacute;c v&agrave; leo đến ngọn c&acirc;y sớm nhất nhưng khăng khăng đ&ograve;i sử dụng c&aacute;ch ri&ecirc;ng của n&oacute; để đến đ&iacute;ch chứ kh&ocirc;ng phải d&ugrave;ng ch&acirc;n b&aacute;m v&agrave; leo từng bước như hướng dẫn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Kết th&uacute;c năm học, bạn đo&aacute;n xem con vật n&agrave;o c&oacute; tổng điểm cao nhất? Đ&oacute; l&agrave; lươn, một con vật hết sức kỳ dị. D&ugrave; chẳng học m&ocirc;n n&agrave;o xuất sắc, nhưng n&oacute; c&oacute; thể bơi, chạy, leo v&agrave; bay mỗi thứ một ch&uacute;t, n&ecirc;n n&oacute; đạt điểm trung b&igrave;nh m&ocirc;n cao nhất v&agrave; trở th&agrave;nh thủ khoa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Lo&agrave;i cầy thảo nguy&ecirc;n phản đối hệ thống đ&agrave;o tạo n&agrave;y v&igrave; ban quản l&yacute; nh&agrave; trường từ chối th&ecirc;m m&ocirc;n đ&agrave;o hang v&agrave;o chương tr&igrave;nh giảng dạy. Ch&uacute;ng quyết định kh&ocirc;ng tham gia v&agrave;o trường học v&agrave; cho lũ con học việc ở chỗ của một con lửng. Những con cầy sau đ&oacute; hợp t&aacute;c với lũ chim v&agrave; chuột t&uacute;i, mở một trường học tư nh&acirc;n, v&agrave; đạt được th&agrave;nh c&ocirc;ng vang dội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&acirc;u chuyện n&agrave;y thường xuy&ecirc;n được c&aacute;c nh&agrave; gi&aacute;o dục phương T&acirc;y sử dụng để minh họa cho sự phiến diện của gi&aacute;o dục ch&iacute;nh quy th&ocirc;ng qua trường lớp. C&aacute;c trường học ch&iacute;nh quy từ cấp một đến cấp ba thường xếp lớp dựa tr&ecirc;n tuổi t&aacute;c v&agrave; giảng dạy một số m&ocirc;n nhất định. Theo nh&agrave; gi&aacute;o dục người Mỹ Leo Buscaglia, phương ph&aacute;p n&agrave;y c&oacute; kh&ocirc;ng &iacute;t hạn chế. Một mặt, việc xếp lớp dựa tr&ecirc;n độ tuổi kh&ocirc;ng đem lại lợi &iacute;ch cho mọi học sinh, v&igrave; sự ph&aacute;t triển về thể chất v&agrave; tinh thần của con người l&agrave; kh&aacute;c nhau d&ugrave; c&ugrave;ng chung độ tuổi. Mặt kh&aacute;c, sự đ&aacute;nh gi&aacute; dựa tr&ecirc;n một số m&ocirc;n học trong nh&agrave; trường kh&ocirc;ng ph&aacute;t huy được thế mạnh của mỗi c&aacute; nh&acirc;n v&agrave; l&agrave;m hạn chế sự ph&aacute;t triển to&agrave;n diện của con người.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tại Việt Nam, gi&aacute;o dục nh&agrave; trường c&ograve;n nhiều bất cập v&igrave; phải chạy đua theo th&agrave;nh t&iacute;ch. Nh&igrave;n lại bản th&acirc;n m&igrave;nh, t&ocirc;i thấy điều n&agrave;y để lại những hậu quả nặng nề.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thời mới v&agrave;o cấp ba, t&ocirc;i đăng k&yacute; thi chuy&ecirc;n văn v&igrave; hồi đ&oacute; với t&ocirc;i học văn dễ như uống nước. Mỗi đề b&agrave;i đều t&igrave;m &yacute; hay &yacute; mới để viết, n&ecirc;n điểm văn cấp hai l&uacute;c n&agrave;o cũng cao nhất lớp. Đậu v&ocirc; trường chuy&ecirc;n, cứ tưởng học m&agrave; như chơi, ai ngờ tan t&agrave;nh giấc mộng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Chương tr&igrave;nh học nhồi nh&eacute;t khủng khiếp, lớp 10 th&igrave; học chương tr&igrave;nh lớp 11, lớp 11 th&igrave; học tiếp chương tr&igrave;nh lớp 12, rồi 12 th&igrave; &ocirc;n lại những b&agrave;i cũ. Ng&agrave;y n&agrave;o cũng c&oacute; v&agrave;i đề b&agrave;i văn phải l&agrave;m. Mỗi b&agrave;i thơ truyện ngắn l&agrave; ba, bốn b&agrave;i tập l&agrave;m văn kh&aacute;c nhau, hết đề n&agrave;y tới đề kh&aacute;c. &Yacute; tưởng đ&acirc;u m&agrave; đẻ ra li&ecirc;n tục như vậy, n&ecirc;n kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c t&ocirc;i đ&agrave;nh x&agrave;o nấu những b&agrave;i cũ, cắt x&eacute;n &yacute; n&agrave;y &yacute; nọ lắp gh&eacute;p v&agrave;o với nhau. Thầy dạy m&ocirc;n chuy&ecirc;n (th&agrave;nh thật xin lỗi c&aacute;c thầy) l&ecirc;n lớp giảng b&agrave;i theo từng &yacute;, cứ như vậy m&agrave; ch&eacute;p v&ocirc; như r&ocirc; bốt. Bởi v&igrave; học l&agrave; để thi, m&agrave; thi th&igrave; chấm b&agrave;i theo &yacute;. N&ecirc;n ngay cả m&ocirc;n văn thầy cũng phải dạy theo c&ocirc;ng sức sao cho học sinh thi đậu, đủ &yacute; đ&uacute;ng &yacute; &nbsp;của người ra đề, chỉ biết chạy theo th&ocirc;i chứ biết l&agrave;m sao.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cả năm học cứ chạy theo c&aacute;c kỳ thi, hết thi chuy&ecirc;n đề, lại thi đội tuyển trường, rồi thi học sinh giỏi tỉnh, đến thi Olympic 30/4, đến thi quốc gia. Mỗi lần gần tới kỳ thi, t&ocirc;i to&agrave;n thức dậy l&uacute;c 2 giờ s&aacute;ng để học b&agrave;i. Nhiều khi mệt qu&aacute; học kh&ocirc;ng nổi, n&ecirc;n l&ecirc;n lớp kiểm tra đ&agrave;nh nộp giấy trắng. B&acirc;y giờ đ&atilde; qua mười mấy năm m&agrave; đ&ocirc;i khi nằm mơ vẫn thấy m&igrave;nh trễ thi, tới lộn ph&ograve;ng thi, kh&ocirc;ng mặc quần &aacute;o đi thi, thật l&agrave; &aacute;c mộng. Sau ba năm học chuy&ecirc;n văn, t&ocirc;i thấy m&igrave;nh t&agrave;n tạ như giẻ r&aacute;ch, t&igrave;nh y&ecirc;u với m&ocirc;n văn tan t&aacute;c như l&aacute; n&aacute;t sau mưa d&ocirc;ng. T&ocirc;i trở n&ecirc;n căm gh&eacute;t văn chương, v&agrave; chuyển hướng sang học kinh tế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ba năm cấp ba t&ocirc;i chẳng nhớ g&igrave; nhiều, chỉ nhớ m&atilde;i một điều. Đ&oacute; l&agrave; hồi học đội tuyển quốc gia, được học một người thầy đặc biệt. Nghe đồn thầy l&agrave; một trong s&aacute;u thầy gi&aacute;o dạy văn hay nhất Việt Nam một thời. Học thầy đ&acirc;u c&oacute; v&agrave;i ba buổi m&agrave; t&ocirc;i nhớ suốt đời. Thầy bảo khi ph&acirc;n t&iacute;ch một b&agrave;i văn b&agrave;i thơ n&agrave;o đ&oacute; m&igrave;nh phải chọn một con mắt. Con mắt đ&oacute; l&agrave; điểm ưng nhất trong b&agrave;i văn b&agrave;i thơ, kh&ocirc;ng cần theo &yacute; ai, chỉ cần bản th&acirc;n m&igrave;nh thấy th&iacute;ch l&agrave; được. Phải tập trung l&agrave;m r&otilde;, ph&acirc;n t&iacute;ch n&oacute;, diễn giải tại sao m&igrave;nh thấy chỗ đ&oacute; hay chỗ đ&oacute; đẹp. Rồi từ đ&oacute; lấy n&oacute; l&agrave;m c&aacute;i hồn , l&agrave;m sợi chỉ xuy&ecirc;n suốt, lan tỏa n&oacute; ra, để biến n&oacute; th&agrave;nh điểm nhấn, đem sức sống cho b&agrave;i viết của m&igrave;nh. Thầy ng&agrave;y xưa học c&ugrave;ng thời với L&ecirc; Anh Xu&acirc;n, Ho&agrave;ng Nhuận Cầm v&agrave; nhiều thi sĩ kh&aacute;c. Mỗi lần giảng tới b&agrave;i thơ b&agrave;i văn n&agrave;o l&agrave; thầy kể giai thoại về người đ&oacute;, lũ học tr&ograve; ch&uacute;ng t&ocirc;i ngồi nghe như nuốt từng lời, ghi ch&eacute;p kh&ocirc;ng nhiều m&agrave; cứ nhớ như in.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bởi vậy, t&ocirc;i thấy điều đầu ti&ecirc;n v&agrave; quan trọng nhất để học tốt (m&agrave; c&aacute;c thầy c&ocirc; lại thường bỏ qua) đ&oacute; l&agrave; cảm thụ. Như dạy văn, th&igrave; người dạy phải truyền cho học sinh c&aacute;i t&igrave;nh y&ecirc;u con chữ, c&aacute;i hay c&aacute;i đẹp của văn chương, c&aacute;i ch&acirc;n thiện mỹ m&agrave; mỗi con người đều hướng tới, c&aacute;i sinh động đầy m&agrave;u sắc m&agrave; văn học phản &aacute;nh từ cuộc sống con người. Hay dạy to&aacute;n, th&igrave; c&aacute;i cần l&agrave;m l&agrave; nu&ocirc;i dưỡng niềm đam m&ecirc; logic, ứng dụng của to&aacute;n học v&agrave;o thực tế, vẻ đẹp của to&aacute;n học. Nhưng thử hỏi c&oacute; mấy ai l&agrave;m được vậy. Vất vả với lương gi&aacute;o vi&ecirc;n ba cọc ba đồng, vừa dạy ở trường vừa lo dạy th&ecirc;m, rồi vắt &oacute;c với c&aacute;c th&agrave;nh t&iacute;ch chỉ ti&ecirc;u, phải n&acirc;ng điểm cấy số ảo để c&oacute; bảng điểm đẹp &nbsp;đặng b&aacute;o c&aacute;o với cấp tr&ecirc;n. Đ&oacute; l&agrave; chưa kể thời xưa &quot;chuột chạy c&ugrave;ng s&agrave;o mới v&agrave;o sư phạm&quot;. một số thầy c&ocirc; kh&ocirc;ng đủ điểm v&agrave;o trường kh&aacute;c n&ecirc;n mới học sư phạm chứ đ&acirc;u phải l&agrave; v&igrave; t&igrave;nh y&ecirc;u bục giảng v&agrave; t&acirc;m huyết với sự nghiệp gi&aacute;o dục. C&oacute; người bạn bảo thấy t&ocirc;i viết c&ograve;n hay hơn c&ocirc; gi&aacute;o dạy văn. T&ocirc;i cười nghĩ, v&igrave; mấy c&ocirc; gi&aacute;o m&agrave; bạn thấy kh&ocirc;ng c&oacute; đủ t&igrave;nh y&ecirc;u với văn chương đ&oacute; th&ocirc;i. Hoặc t&igrave;nh y&ecirc;u đ&oacute; vốn kh&ocirc;ng hề tồn tại, hoặc t&igrave;nh y&ecirc;u đ&oacute; bị b&agrave;o m&ograve;n bởi thực tế khắc nghiệt của cuộc sống. Kh&ocirc;ng c&oacute; t&igrave;nh y&ecirc;u th&igrave; l&agrave;m sao viết hay giảng hay, kh&ocirc;ng c&oacute; t&igrave;nh y&ecirc;u th&igrave; l&agrave;m sao truyền thụ được t&igrave;nh y&ecirc;u tới lũ học tr&ograve;. Bởi vậy c&oacute; kh&ocirc;ng biết bao nhi&ecirc;u đứa học tr&ograve; như t&ocirc;i, theo đuổi v&igrave; sở th&iacute;ch, m&agrave; từ bỏ v&igrave; kiệt quệ v&agrave; vỡ mộng. Những ng&agrave;y trong nh&agrave; trường kh&ocirc;ng những kh&ocirc;ng bồi đắp t&igrave;nh y&ecirc;u với văn chương m&agrave; c&ograve;n t&agrave;n ph&aacute; n&oacute; nữa.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nghĩ lại, đ&oacute; ch&iacute;nh l&agrave; điểm yếu của hệ thống gi&aacute;o dục hiện thời. Thay v&igrave; ươm mầm t&igrave;nh y&ecirc;u với tri thức cho học tr&ograve; th&igrave; lại hủy hoại n&oacute;. Thay v&igrave; nu&ocirc;i dưỡng sự s&aacute;ng tạo th&igrave; lại b&oacute;p chẹt cho n&oacute; chết đi. Nh&agrave; gi&aacute;o dục người Mỹ Neil Postman từng n&oacute;i: &quot;Trẻ em đến trường với những dấu chấm hỏi v&agrave; ra trường với những dấu chấm hết.&quot; Trường học truyền thống đ&ocirc;i khi l&agrave;m thui chột đi khả năng t&ograve; m&ograve;, v&agrave; l&ograve;ng ham hiểu biết. Ở Việt Nam t&igrave;nh h&igrave;nh c&agrave;ng bi đ&aacute;t hơn. Từ nhỏ học tr&ograve; đi học đ&atilde; được dặn d&ograve; phải học giỏi đặng sau n&agrave;y mới c&oacute; c&ocirc;ng ăn việc l&agrave;m, để lo cho gia đ&igrave;nh phụng dưỡng cha mẹ. Chẳng thấy ai dặn h&atilde;y học v&igrave; t&igrave;nh y&ecirc;u tri thức. Tốt nghiệp cấp ba thi v&agrave;o đại học cứ chọn đại ng&agrave;nh n&agrave;o dễ kiếm việc l&agrave;m. Rồi học xong bốn năm đại học cũng chẳng biết m&igrave;nh th&iacute;ch g&igrave; muốn g&igrave;, tự hỏi ta sẽ l&agrave;m chi đời ta. Rồi đ&acirc;m đầu đi l&agrave;m kiến ăn qua ng&agrave;y. Rồi lấy vợ lấy chồng, sinh con đẻ c&aacute;i, rồi gi&agrave; đi. Rồi nằm tr&ecirc;n giường bệnh nghĩ lại thời trai trẻ, nhớ &ocirc;i ta cũng đ&atilde; một thời ước được l&agrave;m kỹ sư n&ocirc;ng nghiệp, ta đ&atilde; một thời mơ mộng l&agrave;m nh&agrave; văn, ta đ&atilde; một thời muốn l&agrave;m nhạc sĩ, vũ c&ocirc;ng, l&agrave;m thợ chụp ảnh, l&agrave;m đầu bếp... Rồi lại tự hỏi: ta đ&atilde; l&agrave;m chi đời ta. Thế rồi cũng chết đi. Than &ocirc;i, kiếp người thử hỏi chỉ như vậy th&ocirc;i sao?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Mỗi kỳ thi đại học đến, t&ocirc;i lại thầm ch&uacute;c c&aacute;c em sĩ tử ch&acirc;n cứng đ&aacute; mềm, ch&uacute;c c&aacute;c em ki&ecirc;n cường dũng cảm. Đường đời c&ograve;n rộng v&agrave; d&agrave;i lắm. Đậu đại học chẳng khẳng định được g&igrave;, rớt đại học biết đ&acirc;u l&agrave; điềm ph&uacute;c. Đừng học v&igrave; tr&agrave;o lưu, h&atilde;y học v&igrave; ham th&iacute;ch. Theo đuổi tri thức đ&iacute;ch thực, kh&aacute;m ph&aacute; đam m&ecirc; bản th&acirc;n. Ch&uacute; &yacute; tới những điều m&agrave; x&atilde; hội v&agrave; gia đ&igrave;nh vẫn hay khuy&ecirc;n bảo, chưa chắc ch&uacute;ng l&agrave; tốt nhất cho em. H&atilde;y bảo trọng!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>HỌC HAY L&Agrave; KH&Ocirc;NG HỌC</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Bất k&igrave; ai dừng học tập đều gi&agrave;, d&ugrave; anh ta ở tuổi hai mươi hay t&aacute;m mươi.&quot; (Henry Ford)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Minh nhắn tin cho t&ocirc;i, sự bối rối hiện l&ecirc;n trong c&acirc;u chữ. Em ch&aacute;n ng&aacute;n chương tr&igrave;nh học ở trường, chẳng c&oacute; hứng th&uacute; với bất kỳ m&ocirc;n học n&agrave;o. Bỏ b&ecirc; việc học một thời gian, em thấy vừa tội lỗi vừa mệt mỏi, v&igrave; kh&ocirc;ng c&oacute; bất kỳ động lực n&agrave;o để học tiếp. Em muốn nghỉ học ở trường để l&agrave;m việc kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Minh kh&ocirc;ng phải l&agrave; trường hợp hiếm gặp. Từng l&agrave;m c&ocirc;ng t&aacute;c tư vấn hướng nghiệp cho nhiều bạn trẻ, t&ocirc;i gặp kh&ocirc;ng &iacute;t bạn trẻ như Minh. Họ thấy việc học ở trường kh&ocirc;ng c&oacute; &yacute; nghĩa g&igrave;, n&ecirc;n ch&aacute;n nản bỏ học, hoặc bảo lưu kết quả học tập.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Điểm chung m&agrave; t&ocirc;i nhận thấy ở những người trẻ n&agrave;y l&agrave; g&igrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đ&oacute; l&agrave; sự mất niềm tin v&agrave;o hệ thống gi&aacute;o dục. Chương tr&igrave;nh đ&agrave;o tạo nh&agrave;m ch&aacute;n, kh&ocirc;ng c&oacute; ứng dụng thực tế, học xong kh&ocirc;ng biết để l&agrave;m g&igrave;. Kết quả l&agrave; c&oacute; kh&ocirc;ng &iacute;t người bỏ học giữa chừng. Người học dở nghỉ học cũng c&oacute; m&agrave; người học giỏi nghỉ học cũng kh&ocirc;ng thiếu. Đ&ocirc;i khi t&ocirc;i cũng kh&ocirc;ng khỏi tự hỏi, ng&agrave;y xưa tốt nghiệp đ&uacute;ng hạn, được cấp bằng đại học, h&oacute;a ra l&agrave; do m&igrave;nh h&egrave;n, kh&ocirc;ng d&aacute;m bỏ học, hay v&igrave; m&igrave;nh đ&atilde; đủ ki&ecirc;n nhẫn để theo đến c&ugrave;ng? Hay l&agrave;, v&igrave; m&igrave;nh lu&ocirc;n giữ được niềm tin ng&acirc;y thơ v&agrave;o nền gi&aacute;o dục nước nh&agrave;?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">N&oacute;i về bỏ học giữa chừng, ai cũng nhắc đến Bill Gates v&agrave; Steve Jobs, hai huyền thoại từng bỏ dở việc học ở c&aacute;c trường đại học danh tiếng. Với một số người bạn từng bỏ dở việc học quanh t&ocirc;i, nếu họ cứ tiếp tục đi học l&agrave;ng nh&agrave;ng như bao người, chưa chắc g&igrave; b&acirc;y giờ họ đ&atilde; g&acirc;y dựng được những c&aacute;i m&agrave; học đang c&oacute;. Nhưng, n&oacute;i đi th&igrave; cũng phải n&oacute;i lại, mỗi năm tr&ecirc;n thế giới c&oacute; kh&ocirc;ng biết bao nhi&ecirc;u triệu sinh vi&ecirc;n bỏ học, m&agrave; số lượng l&agrave;m n&ecirc;n sự nghiệp lẫy lừng như Bill Gates hay Steve Jobs th&igrave; chỉ đếm được tr&ecirc;n đầu ng&oacute;n tay. Bạn b&egrave; t&ocirc;i cũng thế, để được như b&acirc;y giờ, họ cũng đ&atilde; trải qua những qu&atilde;ng thời gian nhiều ch&ocirc;ng gai khốn kh&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i kh&ocirc;ng hề phản đối việc bỏ học ở trường. Theo cuốn s&aacute;ch&nbsp;<em>B&agrave;n về tự do</em>&nbsp;của John Stuart Mill, mỗi người đều c&oacute; quyền sống theo &yacute; th&iacute;ch của m&igrave;nh, miễn sao kh&ocirc;ng ảnh hưởng đến quyền sống của người kh&aacute;c. Việc bỏ học hay tiếp tục ho&agrave;n tất chương tr&igrave;nh học tập l&agrave; quyết định c&aacute; nh&acirc;n của mỗi người. Nhưng kh&ocirc;ng phải cứ thấy người n&agrave;y nghỉ học l&agrave;m n&ecirc;n nghiệp lớn th&igrave; m&igrave;nh cũng sẽ l&agrave;m được như vậy. Cũng như kh&ocirc;ng phải trường học n&agrave;y tốt cho người n&agrave;y th&igrave; cũng tốt cho người kia. Kh&ocirc;ng phải c&aacute;i g&igrave; hợp với người ta cũng hợp với m&igrave;nh. Cho n&ecirc;n khi gặp c&aacute;c trường hợp cần tư vấn về học tập, đối với bạn n&agrave;y th&igrave; t&ocirc;i gật g&ugrave;, c&oacute; lẽ điều n&agrave;y l&agrave; tốt nhất cho em. C&ograve;n đối với bạn kia th&igrave; t&ocirc;i nhắn ngược lại: &quot;Điều quan trọng đối với em b&acirc;y giờ l&agrave; tốt nghiệp đại học.&quot; Quyết định bỏ dở việc học l&agrave; một việc đầy can đảm v&agrave; đi k&egrave;m rất nhiều kh&oacute; khăn sau đ&oacute;, n&ecirc;n ta phải c&acirc;n nhắc rất kỹ trước khi thực hiện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhưng l&agrave;m thế n&agrave;o để biết l&uacute;c n&agrave;o l&agrave; n&ecirc;n nghỉ học l&uacute;c n&agrave;o th&igrave; kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bỏ qua tầm quan trọng của tấm bằng trong một x&atilde; hội trọng bằng cấp như Việt Nam, th&igrave; c&oacute; nhiều điều ta cần c&acirc;n nhắc trước khi quyết định bỏ học.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đầu ti&ecirc;n, quyết định nghỉ học thường xuất ph&aacute;t từ việc qu&aacute; ch&aacute;n nản với chương tr&igrave;nh học ở trường. Một số bạn thấy m&igrave;nh kh&ocirc;ng hứng th&uacute; g&igrave; với ng&agrave;nh học của m&igrave;nh v&agrave; quyết định nghỉ. Mặc d&ugrave; họ chưa c&oacute; dự định r&otilde; r&agrave;ng sẽ l&agrave;m g&igrave; khi nghỉ học. Hoặc c&oacute; dự định mơ hồ rằng nghỉ để l&agrave;m điều m&igrave;nh th&iacute;ch, hoặc d&agrave;nh thời gian cho việc kh&aacute;c ưu ti&ecirc;n hơn. Nhưng sau khi nghỉ học ở trường, với khoảng thời gian trống đột ngột như vậy, c&oacute; bao nhi&ecirc;u người tận dụng một c&aacute;ch tốt nhất thời gian của họ, hay lại sa đ&agrave; v&agrave;o ngủ nướng, chơi game, facebook, v&agrave; những việc v&ocirc; bổ tốn thời gian kh&aacute;c. Rồi cuối c&ugrave;ng kế hoạch đặt ra th&igrave; kh&ocirc;ng tới đ&acirc;u m&agrave; đến cả tấm bằng cũng kh&ocirc;ng c&oacute;. Những người như Bill Gates, Steve Jobs, Warren Buffet, Mark Zuckerberg...đều bỏ học đại học, nhưng họ bỏ sau khi đ&atilde; x&aacute;c định được hướng đi cho m&igrave;nh. Do vậy, với những trường hợp muốn nghỉ học chỉ đơn thuần v&igrave; ch&aacute;n, m&agrave; kh&ocirc;ng c&oacute; một dự định cụ thể r&otilde; r&agrave;ng, th&igrave; t&ocirc;i cho rằng kh&ocirc;ng n&ecirc;n bỏ hẳn việc học ch&iacute;nh kh&oacute;a.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một số bạn kh&aacute;c m&agrave; t&ocirc;i gặp nghỉ học để thi lại, chuyển học lại một ng&agrave;nh kh&aacute;c, một lĩnh vực kh&aacute;c m&agrave; họ thấy hứng th&uacute; hơn. Người đang học b&aacute;o ch&iacute; th&iacute;ch nhảy qua học l&agrave;m phim. Người đang học y dược muốn chuyển sang t&igrave;m hiểu về đầu tư ngoại hối. Một em đang học kế to&aacute;n nhưng th&iacute;ch l&agrave;m du lịch. C&ograve;n em kh&aacute;c đang năm nhất sư phạm lại muốn học kinh doanh. Tất cả họ đều c&oacute; chung một c&acirc;u hỏi: C&oacute; n&ecirc;n chuyển sang học ng&agrave;nh y&ecirc;u th&iacute;ch kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Điều đ&oacute; cần x&aacute;c định l&agrave;: Ng&agrave;nh m&agrave; họ muốn chuyển qua c&oacute; thực sự ph&ugrave; hợp với họ kh&ocirc;ng? Hay đ&oacute; chỉ l&agrave; &yacute; th&iacute;ch, l&agrave; cảm x&uacute;c bồng bột nhất thời. Giả sử nghỉ học chỗ n&agrave;y nhảy qua chỗ kh&aacute;c, th&igrave; c&oacute; chắc g&igrave; m&igrave;nh sẽ th&iacute;ch học hơn v&agrave; theo đuổi đến c&ugrave;ng kh&ocirc;ng. Hay lại ch&aacute;n nản rồi muốn nghỉ tiếp, để rồi rơi v&agrave;o một c&aacute;i v&ograve;ng luẩn quẩn kh&ocirc;ng lối tho&aacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một c&aacute;ch đơn giản để phần n&agrave;o hiểu được m&igrave;nh c&oacute; ph&ugrave; hợp với ng&agrave;nh học mới kh&ocirc;ng, l&agrave; hỏi. T&igrave;m kiếm những người hiện đang học ng&agrave;nh đ&oacute;, c&oacute; thể l&agrave; bạn m&igrave;nh, bạn của bạn m&igrave;nh, con c&aacute;i của bạn của cha mẹ m&igrave;nh, hay bất kỳ người n&agrave;o đang học ng&agrave;nh đ&oacute; m&agrave; bạn biết. H&atilde;y hỏi xem gi&aacute;o tr&igrave;nh của họ gồm những m&ocirc;n g&igrave;. Họ th&iacute;ch v&agrave; kh&ocirc;ng th&iacute;ch chương tr&igrave;nh học chỗ n&agrave;o. Cảm nhận của họ ra sao. Họ nghĩ để học tốt ng&agrave;nh đ&oacute; cần những ti&ecirc;u ch&iacute; g&igrave;. V&agrave; x&aacute;c định xem m&igrave;nh c&oacute; những khả năng, sở trường, t&iacute;nh c&aacute;ch g&igrave; ph&ugrave; hợp với ng&agrave;nh học m&agrave; m&igrave;nh muốn theo đuổi. Sau khi t&igrave;m hiểu xong th&igrave; ta sẽ c&oacute; được ch&uacute;t th&ocirc;ng tin để x&aacute;c định rằng m&igrave;nh c&oacute; sẵn s&agrave;ng theo học ng&agrave;nh đ&oacute; hay kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Điều m&acirc;u thuẫn ở đ&acirc;y, l&agrave; nhiều người trong ch&uacute;ng ta tuy ch&aacute;n nản v&agrave; mất niềm tin v&agrave;o trường học, nhưng trong tiềm thức vẫn qu&aacute; phụ thuộc v&agrave;o gi&aacute;o dục truyền thống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ch&uacute;ng ta vẫn giữ tư duy rằng phải đi học ở trường lớp, phải c&oacute; bằng cấp chứng chỉ, phải được đ&agrave;o tạo b&agrave;i bản th&igrave; mới c&oacute; thể l&agrave;m được trong một ng&agrave;nh n&agrave;o đ&oacute;. V&agrave; cũng v&igrave; sai lầm đ&oacute;, n&ecirc;n học sinh sinh vi&ecirc;n th&igrave; chỉ chăm chăm t&igrave;m mọi c&aacute;ch l&agrave;m sao để học thật giỏi ở trường. C&ograve;n cha mẹ th&igrave; t&igrave;m hết thầy n&agrave;y đến lớp nọ để cho con đi học th&ecirc;m, cũng chỉ với mục đ&iacute;ch đạt điểm thật cao ở lớp ch&iacute;nh kh&oacute;a. Thời gian học ở nh&agrave; cũng chỉ để bổ sung, phục vụ cho việc học ở trường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vậy n&ecirc;n hễ nhắc tới việc bỏ học l&agrave; nhiều người nhầm tưởng rằng tất cả sự học kết th&uacute;c ở đ&oacute;. N&ecirc;n cũng nảy sinh t&acirc;m l&yacute; sau khi tốt nghiệp ra đời l&agrave; thở d&agrave;i h&acirc;n hoan, quăng hết s&aacute;ch vở v&agrave;o g&oacute;c nh&agrave;, nhảy ra đi l&agrave;m. S&aacute;ng mở mắt ra đi, tối về lăn ra ngủ, dừng việc bổ sung kiến thức, n&ecirc;n c&agrave;ng ng&agrave;y c&agrave;ng thụt l&ugrave;i. V&agrave; rồi ch&uacute;ng ta hụt hẫng trước thực tế phũ ph&agrave;ng rằng kết quả học tập xuất sắc chẳng mấy li&ecirc;n quan tới th&agrave;nh c&ocirc;ng trong sự nghiệp. Ch&uacute;ng ta ngỡ ng&agrave;ng nhận ra bằng cấp kh&ocirc;ng phải l&agrave; tấm v&eacute; th&ocirc;ng h&agrave;nh tr&ecirc;n chuyến t&agrave;u đi đến cuộc đời mơ ước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ch&uacute;ng ta hay nhầm lẫn rằng gi&aacute;o dục truyền thống th&ocirc;ng qua nh&agrave; trường l&agrave; con đường duy nhất. Tr&ecirc;n thực tế, n&oacute; l&agrave; c&aacute;ch phổ biến nhất, nhưng kh&ocirc;ng phải l&agrave; duy nhất, c&agrave;ng kh&ocirc;ng phải l&agrave; c&aacute;ch ho&agrave;n hảo nhất. Việc học ch&iacute;nh kh&oacute;a ở trường kh&ocirc;ng phải l&agrave; tất cả sự nghiệp học h&agrave;nh. Gi&aacute;o dục truyền thống c&oacute; điểm lợi nhưng cũng c&oacute; điểm bất lợi. Điểm lợi của gi&aacute;o dục truyền thống từ nh&agrave; trường l&agrave; ta sẽ được đ&agrave;o tạo một c&aacute;ch b&agrave;i bản c&oacute; hệ thống, với nhiều l&yacute; thuyết, x&acirc;y dựng nền tảng vững chắc v&agrave; từ từ đi l&ecirc;n như m&ocirc; h&igrave;nh kim tự th&aacute;p. Bất lợi lớn nhất của n&oacute; l&agrave; hạn chế sự ph&aacute;t triển tối đa tiềm năng của mỗi c&aacute; nh&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trường học kh&ocirc;ng phải l&agrave; m&ocirc; h&igrave;nh gi&aacute;o dục ho&agrave;n hảo. Ng&agrave;y c&agrave;ng c&oacute; nhiều c&aacute; nh&acirc;n, gia đ&igrave;nh v&agrave; tổ chức kh&aacute;c nhau tr&ecirc;n thế giới t&igrave;m hiểu những phương ph&aacute;p gi&aacute;o dục thay thế cho phương ph&aacute;p truyền thống th&ocirc;ng qua nh&agrave; trường. C&oacute; thể kể đến c&aacute;c phương ph&aacute;p như tự học, học ở nh&agrave;, tho&aacute;t trường học, gi&aacute;o dục cấp tiến. Phương ph&aacute;p gi&aacute;o dục mở th&ocirc;ng qua c&aacute;c trang web MOOC cũng kh&aacute; phổ biến ở thế giới phương T&acirc;y, nhưng vẫn chưa được người Việt ta quan t&acirc;m ch&uacute; &yacute;. L&agrave; một người tự học, t&ocirc;i nhận thấy điểm mạnh của phương ph&aacute;p n&agrave;y l&agrave; tiết ki&ecirc;m được thời gian, học những g&igrave; m&igrave;nh th&iacute;ch v&agrave; thực h&agrave;nh theo &yacute; m&igrave;nh. Người tự học đ&ocirc;i khi kh&ocirc;ng học rộng m&agrave; học s&acirc;u. Nếu th&iacute;ch, họ c&oacute; thể bỏ qua c&aacute;c bước đ&agrave;o tạo cơ bản, v&agrave; đ&agrave;o s&acirc;u v&agrave;o một mảng m&igrave;nh quan t&acirc;m rồi tự thực h&agrave;nh để n&acirc;ng cao kỹ năng. Nhưng bất lợi của n&oacute; l&agrave; đ&ograve;i hỏi kỷ luật bản th&acirc;n, m&igrave;nh tự dạy m&igrave;nh, cần sự chủ động từ ch&iacute;nh m&igrave;nh rất lớn chứ kh&ocirc;ng phải tiếp thu kiến thức thụ động như gi&aacute;o dục truyền thống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thực tế ng&agrave;y nay kh&ocirc;ng &iacute;t người tự học, tự dạy m&igrave;nh một lĩnh vực mới v&agrave; kiếm sống tốt bằng nghề đ&oacute;, mặc d&ugrave; n&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng li&ecirc;n quan g&igrave; đến chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo của m&igrave;nh. Kiến Long, một người bạn t&ocirc;i tốt nghiệp thạc sĩ viễn th&ocirc;ng ở Ph&aacute;p nhưng muốn l&agrave;m gi&aacute;o dục n&ecirc;n về Việt Nam mở trường chuy&ecirc;n dạy kỹ năng mềm cho trẻ em. Một người bạn kh&aacute;c, học quản trị kinh doanh xong chuyển sang l&agrave;m quay phim chụp ảnh, vừa học vừa l&agrave;m. Trang, một c&ocirc; b&eacute; dễ thương học kinh tế giờ tự thiết kế cắt may quần &aacute;o v&agrave; b&aacute;n đồ m&igrave;nh may được. T&ocirc;i học ngoại thương xong giờ l&agrave;m nghề viết. Những trường hợp như thế t&ocirc;i kể ho&agrave;i cũng kh&ocirc;ng hết.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Để chuyển đổi th&agrave;nh c&ocirc;ng, ch&uacute;ng t&ocirc;i tự dạy m&igrave;nh ng&agrave;nh nghề mới, tự học lấy nghề, ph&aacute;t triển tay nghề bằng c&aacute;ch thử l&agrave;m. Sau một thời gian tay nghề vững, đ&oacute; l&agrave; l&uacute;c thực hiện c&uacute; nhảy rẽ ngang l&agrave;m theo đam m&ecirc; của m&igrave;nh. Ho&agrave;n to&agrave;n kh&ocirc;ng đợi phải học theo b&agrave;i bản, hay học từ nh&agrave; trường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhị Đặng, c&ocirc; bạn t&ocirc;i quen trong một chương tr&igrave;nh cho người trẻ, chia sẻ về qu&aacute; tr&igrave;nh thực hiện &quot;c&uacute; nhảy đam m&ecirc;&quot; của m&igrave;nh. L&agrave; một designer c&oacute; tiếng, Nhị hiện đang l&agrave;m việc tự do chủ yếu dựa tr&ecirc;n kỹ năng chụp ảnh v&agrave; l&agrave;m phim. Nhưng c&ocirc; bạn n&agrave;y vốn tốt nghiệp ng&agrave;nh Marketing, đại học Kinh tế. Từ nhỏ bạn đ&atilde; m&ecirc; c&aacute;c chuyển động hoạt h&igrave;nh, phim ảnh. Đến năm thứ ba đại học th&igrave; bạn quyết định tự học về thiết kế để thỏa niềm y&ecirc;u th&iacute;ch. Sau đ&oacute; bạn tự m&agrave;y m&ograve;, l&ecirc;n c&aacute;c trang mạng về phim ảnh, t&igrave;m hiểu về c&aacute;ch dựng video, hiệu ứng. Bạn vẫn tốt nghiệp Kinh tế để ho&agrave;n th&agrave;nh bằng đại học, nhưng sau đ&oacute; quyết định theo con đường m&igrave;nh đ&atilde; chọn. Bạn vừa l&agrave;m vừa học bằng c&aacute;ch nhảy v&agrave;o lĩnh vực thiết kế với nhiều c&ocirc;ng việc cho c&aacute;c tạp ch&iacute; kh&aacute;c nhau như&nbsp;<em>Elle, F fashion, Soul Academy, Womanhealth</em>trong suốt mấy năm liền. C&ocirc;ng việc kh&ocirc;ng hạn chế thời gian v&agrave; địa điểm cố định n&ecirc;n Nhị vừa l&agrave;m vừa c&oacute; thể đi du lịch nhiều nơi tr&ecirc;n thế giới. Trong thời gian vừa l&agrave;m vừa đi du lịch, bạn quen th&ecirc;m nhiều người nổi tiếng trong giới dựng phim c&ugrave;ng chung sở th&iacute;ch về phim ảnh v&agrave; du lịch. Từ đ&oacute;, tay nghề của bạn ng&agrave;y c&agrave;ng được n&acirc;ng cao, v&agrave; bạn x&acirc;y dựng được một nền tảng vững v&agrave;ng với đam m&ecirc; của m&igrave;nh. Phim ngắn&nbsp;<em>Lost in Indonesia</em>&nbsp;do bạn l&agrave;m được lọt v&agrave;o Staffpick của trang web chia sẻ video nổi tiếng Vimeo v&agrave; nhận được sự quan t&acirc;m của rất nhiều du kh&aacute;ch quốc tế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Mark Twain từng viết rằng: &quot;Một số người c&oacute; được nền gi&aacute;o dục m&agrave; kh&ocirc;ng cần đi học. Những người kh&aacute;c th&igrave; c&oacute; được n&oacute; sau khi đ&atilde; ra khỏi trường.&quot; C&ograve;n Haruki Murakami th&igrave; viết: &quot;Trường học l&agrave; thế đấy. Điều quan trọng nhất ta học được ở trường l&agrave; c&aacute;i sự thật rằng những điều quan trọng nhất ta lại kh&ocirc;ng học hỏi được ở trường.&quot; Bạn đọc th&acirc;n mến, xin bạn đừng nghĩ rằng t&ocirc;i c&oacute; th&ugrave; hằn truyền kiếp với trường học hay l&agrave; một kẻ học h&agrave;nh kh&ocirc;ng ra g&igrave; n&ecirc;n mới ch&aacute;n gh&eacute;t trường lớp đến thế. Thực tế ho&agrave;n to&agrave;n ngược lại, nếu theo như c&acirc;u chuyện về trường học lo&agrave;i vật, th&igrave; từ nhỏ tới lớn t&ocirc;i lu&ocirc;n l&agrave; một con lươn được rất nhiều huy chương. T&ocirc;i chỉ muốn nhấn mạnh một điều rằng: Trường học tr&ecirc;n thực tế &nbsp;kh&ocirc;ng đ&oacute;ng g&oacute;p nhiều v&agrave;o việc gi&aacute;o dục cho con người như ch&uacute;ng ta vẫn tưởng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một điều kh&aacute;c t&ocirc;i muốn nhấn mạnh l&agrave;: Những người xuất sắc đều l&agrave; những người tự học. Họ c&oacute; thể được đ&agrave;o tạo b&agrave;i bản trong một chừng mực n&agrave;o đ&oacute;, nhưng họ to&agrave;n đ&agrave;o s&acirc;u nỗ lực m&agrave;y m&ograve; t&igrave;m kiếm, &nbsp;tự bổ sung kiến thức cho những g&igrave; m&igrave;nh c&ograve;n thiếu, chứ kh&ocirc;ng ỷ lại, phụ thuộc v&agrave;o gi&aacute;o dục nh&agrave; trường. Điển h&igrave;nh của những người tự học l&agrave; Abraham Lincoln, Michael Faraday, Charles Darwin, Thomas Edison v&agrave; Leonardo da Vinci. Họ kh&ocirc;ng quan trọng m&igrave;nh c&oacute; bằng cấp hay kh&ocirc;ng. Họ tự học, tự ph&aacute;t triển bản th&acirc;n suốt cả đời m&igrave;nh. Với kỹ năng tự học xuất sắc v&agrave; khả năng nắm bắt vấn đề một c&aacute;ch nhanh ch&oacute;ng, họ kh&ocirc;ng mất qu&aacute; nhiều thời gian để c&oacute; được kiến thức s&acirc;u rộng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Do vậy, tiếp tục hay bỏ dở việc học ở trường kh&ocirc;ng phải l&agrave; điều quan trọng. Mấu chốt m&agrave; ta cần ghi nhớ l&agrave; lu&ocirc;n chủ động v&agrave; t&iacute;ch cực trong việc tiếp thu, trau dồi tri thức, kể cả c&oacute; học ở trường hay kh&ocirc;ng. Bỏ học kh&ocirc;ng quyết định th&agrave;nh c&ocirc;ng hay thất bại của bạn. Nhưng nếu bạn kh&ocirc;ng biết tự học cả trong v&agrave; sau khi rời khỏi ghế nh&agrave; trường th&igrave; bạn sẽ cầm chắc thất bại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>1. ĐIỀU ĐƠN GIẢN GI&Uacute;P T&Ocirc;I SỐNG TỐT HƠN</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;H&atilde;y l&agrave;m điều g&igrave; đ&oacute; c&oacute; &iacute;ch thay v&igrave; chỉ giết thời gian. Bởi v&igrave; thời gian đang dần giết bạn&quot;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">(Paulo Coelho)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Gia nhập v&agrave;o cộng đồng của những người tự học, ta sẽ thấy họ c&oacute; nhiều mẹo vặt được gọi l&agrave; life-hack, những c&aacute;ch kh&aacute;c nhau để tiếp thu kiến thức, tận dụng thời gian v&agrave; ph&aacute;t triển bản th&acirc;n. Bản th&acirc;n l&agrave; một người tự học, t&ocirc;i cũng c&oacute; một số phương ph&aacute;p của ri&ecirc;ng m&igrave;nh. Sau đ&acirc;y t&ocirc;i xin chia sẻ với bạn đọc một số mẹo vặt đơn giản, gi&uacute;p cuộc sống của t&ocirc;i được cải thiện tốt hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>1. N&oacute;i kh&ocirc;ng với ti vi.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&oacute; một c&acirc;u n&oacute;i h&agrave;i hước của Groucho Marx l&agrave;: &quot;T&ocirc;i thấy ti vi mang t&iacute;nh gi&aacute;o dục rất cao. Mỗi khi c&oacute; ai đ&oacute; bật ti vi l&ecirc;n, t&ocirc;i liền đi qua ph&ograve;ng kh&aacute;c v&agrave; đọc một quyển s&aacute;ch.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nh&agrave; đầu tư người Mỹ James Altucher c&oacute; một c&acirc;u n&oacute;i kh&aacute;c: &quot;Bật ti vi l&ecirc;n l&agrave; tắt cả thế giới.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Tiểu thuyết gia Stephen King th&igrave; lại n&oacute;i: &quot;Để trở th&agrave;nh một nh&agrave; văn tốt, th&igrave; h&atilde;y r&uacute;t ph&iacute;ch c&aacute;i ti vi của bạn, quấn d&acirc;y quanh ti vi, v&agrave; quẳng n&oacute; v&agrave;o nh&agrave; kho.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một số nghi&ecirc;n cứu khoa học đ&atilde; chỉ ra rằng việc xem ti vi qu&aacute; nhiều l&agrave;m chậm đi hoạt động của bộ n&atilde;o con người v&agrave; l&agrave;m giảm mức độ thỏa m&atilde;n trong cuộc sống. Từ l&acirc;u t&ocirc;i đ&atilde; loại bỏ ti vi ra khỏi cuộc sống của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vấn đề của c&aacute;c chương tr&igrave;nh ti vi hiện giờ l&agrave; qu&aacute; nhiều quảng c&aacute;o, thừa mứa giải tr&iacute; v&agrave; qu&aacute; &iacute;t kiến thức. Kh&ocirc;ng phải ti vi kh&ocirc;ng c&oacute; những chương tr&igrave;nh bổ &iacute;ch. Nhưng điểm bất lợi của n&oacute; l&agrave; ta phải sắp xếp thời gian để xem chương tr&igrave;nh m&igrave;nh th&iacute;ch, thay v&igrave; xem v&agrave;o l&uacute;c rảnh rỗi. V&agrave; sau khi chương tr&igrave;nh đ&oacute; kết th&uacute;c, theo qu&aacute;n t&iacute;nh, ta rất dễ ngồi l&igrave; ở đ&oacute; v&agrave; chuyển hết k&ecirc;nh n&agrave;y đến k&ecirc;nh kh&aacute;c để xem tiếp. Thế l&agrave; v&agrave;i giờ đồng hồ mỗi ng&agrave;y đi tong. Ta đi học, đi l&agrave;m, về nh&agrave;, ăn tối, ngồi trước ti vi đến khi mắt d&iacute;u lại, rồi đi ngủ. Bảo sao người ta kh&ocirc;ng c&oacute; thời gian để l&agrave;m những điều m&igrave;nh th&iacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i từng đọc được một c&acirc;u thế n&agrave;y: Khoảng thời gian từ 8 giờ tối đến 10 giờ tối mỗi ng&agrave;y sẽ quyết định ch&uacute;ng ta trở th&agrave;nh người như thế n&agrave;o. Cho n&ecirc;n t&ocirc;i kh&ocirc;ng thể bỏ thời gian đ&oacute; ra chỉ để xem ti vi, n&oacute; qu&aacute; l&atilde;ng ph&iacute;. T&ocirc;i vẫn xem c&aacute;c chương tr&igrave;nh truyền h&igrave;nh n&agrave;o m&igrave;nh thấy bổ &iacute;ch. Nhưng t&ocirc;i kh&ocirc;ng d&agrave;nh thời gian cố định để ch&agrave;o đ&oacute;n c&aacute;c chương tr&igrave;nh đ&oacute; tr&ecirc;n ti vi, m&agrave; l&ecirc;n mặng xem khi n&agrave;o t&ocirc;i rảnh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>2. Đi ngủ trước nửa đ&ecirc;m.&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">D&acirc;n Mỹ thường truyền tai nhau c&acirc;u n&oacute;i:&nbsp;<em>Nothing good happens after midnight</em>&nbsp;- chẳng c&oacute; g&igrave; tốt đẹp xảy ra sau nửa đ&ecirc;m, phỏng lại c&acirc;u thoại:&nbsp;<em>Nothing good happens after 2 A.M</em>. trong loạt phim truyền h&igrave;nh nổi tiếng&nbsp;<em>How I met your mother</em>.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thực tế l&agrave; kh&ocirc;ng c&oacute; điều g&igrave; tốt l&agrave;nh xảy ra sau nửa đ&ecirc;m, cả b&ecirc;n ngo&agrave;i lẫn b&ecirc;n trong cơ thể. Sau 12 giờ đ&ecirc;m, kẻ xấu thường lợi dụng thời cơ thực hiện mưu đồ của m&igrave;nh, cướp của, giết người, bắt c&oacute;c... C&ograve;n b&ecirc;n trong cơ thể, th&igrave; l&uacute;c n&agrave;y đầu &oacute;c con người đ&atilde; mụ mẫm v&agrave; th&acirc;n thể đ&atilde; đờ đẫn, hoạt động kh&ocirc;ng c&ograve;n được s&aacute;ng suốt, m&agrave; lại dễ nảy sinh những suy nghĩ u tối.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&aacute;c nh&agrave; khoa học đ&atilde; chỉ ra rằng từ khi sinh ra con người đ&atilde; c&oacute; một số lượng tế b&agrave;o thần kinh nhất định, v&agrave; ch&uacute;ng chỉ giảm đi theo thời gian chứ kh&ocirc;ng tăng l&ecirc;n. Việc thức khuya l&acirc;u d&agrave;i l&agrave;m chết c&aacute;c tế b&agrave;o thần kinh, g&acirc;y giảm tr&iacute; nhớ v&agrave; l&agrave; nguy&ecirc;n nh&acirc;n cho c&aacute;c chứng bệnh kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Do vậy, ngay từ nhỏ, t&ocirc;i đ&atilde; được m&aacute; m&igrave;nh tập cho th&oacute;i quen đi ngủ sớm. Hồi c&ograve;n ở chung với ba m&aacute;, ăn cơm xong, l&agrave;m b&agrave;i tập về nh&agrave;, khoảng 9 giờ tối l&agrave; t&ocirc;i l&ecirc;n giường đi ngủ. Cho n&ecirc;n b&acirc;y giờ t&ocirc;i thường đi ngủ rất sớm. Cứ 8 giờ tối l&agrave; ng&aacute;p ngắn ng&aacute;p d&agrave;i, 9 giờ tối l&agrave; mắt nhắm mắt mở, 10 giờ tối l&agrave; đ&atilde; nằm y&ecirc;n vị tr&ecirc;n giường. Bạn b&egrave; thường tr&ecirc;u chọc khi n&agrave;o g&agrave; l&ecirc;n chuồng l&agrave; t&ocirc;i cũng l&ecirc;n giường. Kệ, giấc ngủ l&agrave; quan trọng, c&aacute;i g&igrave; c&ograve;n lại th&igrave; để mai t&iacute;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>3. Thức dậy l&uacute;c b&igrave;nh minh.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bởi v&igrave; t&ocirc;i ngủ l&uacute;c 10 giờ tối, n&ecirc;n t&ocirc;i thường dậy v&agrave;o 4 giờ s&aacute;ng. &quot;Trời, l&agrave;m g&igrave; thức sớm vậy?&quot; bạn c&oacute; thể hỏi. &Agrave;, c&oacute; nhiều việc để l&agrave;m lắm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Từ 4 giờ tới 6 giờ s&aacute;ng l&agrave; những giờ linh thi&ecirc;ng t&ocirc;i d&agrave;nh cho ch&iacute;nh m&igrave;nh. T&ocirc;i ngồi thiền, đọc s&aacute;ch, viết l&aacute;ch, nghi&ecirc;n cứu t&agrave;i liệu hoặc xem những th&ocirc;ng tin truyền cảm hứng v&agrave; &yacute; tưởng cho bản th&acirc;n. S&aacute;ng sớm l&agrave; khoảng thời gian y&ecirc;n tĩnh m&agrave; ta kh&ocirc;ng bị l&agrave;m phiền hay ph&acirc;n t&acirc;m bởi những thứ xung quanh. Đầu &oacute;c con người cũng thường hoạt động mạnh mẽ v&agrave; s&aacute;ng suốt nhất. N&ecirc;n t&ocirc;i d&agrave;nh những giờ đầu ti&ecirc;n của ng&agrave;y mới cho c&aacute;c c&ocirc;ng việc ph&aacute;t triển bản th&acirc;n v&agrave; l&agrave;m những g&igrave; m&igrave;nh y&ecirc;u th&iacute;ch. Cũng nhờ tận dụng khoảng thời gian s&aacute;ng sớm m&agrave; t&ocirc;i mới c&oacute; thể vừa đi l&agrave;m to&agrave;n thời gian, vừa viết xong quyển s&aacute;ch đầu tay, vừa ho&agrave;n th&agrave;nh kh&oacute;a học l&agrave;m gi&aacute;o vi&ecirc;n yoga trong một thời gian ngắn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Dĩ nhi&ecirc;n l&agrave; kh&ocirc;ng nhiều người muốn thức dậy l&uacute;c 4 giờ s&aacute;ng. Thực tế th&igrave; t&ugrave;y cơ địa mỗi người m&agrave; ta c&oacute; xu hướng thức khuya hoặc dậy sớm. Tuy nhi&ecirc;n, việc đặt đồng hồ để dậy sớm hơn mỗi ng&agrave;y v&agrave; d&agrave;nh thời gian để chạy bộ hay tập thể dục l&agrave; một việc n&ecirc;n l&agrave;m. Thử tưởng tượng thay v&igrave; thức dậy s&aacute;t giờ, cuống cuồng vệ sinh rồi chạy đi học đi l&agrave;m, bạn thức dậy sớm hơn một t&iacute;, vận động cơ thể cho đổ mồ h&ocirc;i, ngồi đọc một v&agrave;i trang s&aacute;ch, ăn s&aacute;ng rồi l&agrave;m việc hay học h&agrave;nh. Một ng&agrave;y mới sẽ bắt đầu rất sảng kho&aacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cũng giống như c&aacute;c th&oacute;i quen kh&aacute;c, nếu muốn dậy sớm, ta chỉ cần luyện tập để tạo th&oacute;i quen. Kh&ocirc;ng cần bắt đầu qu&aacute; tham vọng. Chỉ cần bắt đầu bằng c&aacute;ch đặt đồng hồ b&aacute;o thức sớm hơn 15 ph&uacute;t so với b&igrave;nh thường, v&agrave; cố gắng dậy đ&uacute;ng thời gian đ&oacute;. Chỉ 15 ph&uacute;t th&ocirc;i, tự nhủ với bản th&acirc;n để cố gắng tu&acirc;n theo thời gian biểu mới. Rồi sau khi đ&atilde; thấy thoải m&aacute;i với qu&atilde;ng thời gian n&agrave;y, ta tăng l&ecirc;n th&agrave;nh 30 ph&uacute;t. Nếu dậy sớm hơn 30 ph&uacute;t mỗi ng&agrave;y, th&igrave; một tuần bạn c&oacute; th&ecirc;m gần bốn giờ trống để đầu tư cho bản th&acirc;n. Ta kh&ocirc;ng bao giờ thiếu thời gian để l&agrave;m điều m&igrave;nh th&iacute;ch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>4. Bớt đọc b&aacute;o v&agrave; tạp ch&iacute;.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trong quyển s&aacute;ch&nbsp;<strong><em><a href=\"https://docsach24.com/ebook/toi-tu-hoc-thu-giang-nguyen-duy-can.2536.html\" target=\"_blank\">T&ocirc;i tự học</a>&nbsp;</em></strong>, học giả Nguyễn Duy Cần c&oacute; ph&acirc;n t&iacute;ch rất cụ thể tại sao kh&ocirc;ng n&ecirc;n đọc b&aacute;o. Trước hết, xin lỗi bạn b&egrave; của t&ocirc;i những người đang l&agrave;m b&aacute;o. C&oacute; kh&ocirc;ng &iacute;t c&aacute;c tờ b&aacute;o cho ra c&aacute;c ấn phẩm chất lượng tốt. C&oacute; rất nhiều nh&agrave; b&aacute;o t&acirc;m huyết với nghề, cố gắng cung cấp kiến thức, th&ocirc;ng tin x&aacute;c thực cho độc giả. Tuy nhi&ecirc;n, v&igrave; một tờ b&aacute;o thường l&agrave; cơ quan ph&aacute;t ng&ocirc;n của một tổ chức n&agrave;o đ&oacute;, n&ecirc;n khi truyền đạt th&ocirc;ng tin, n&oacute; thường mang quan điểm v&agrave; c&aacute;i nh&igrave;n của đơn vị m&agrave; n&oacute; đại diện. Mặt kh&aacute;c, ng&agrave;y nay, thiểu số những tờ b&aacute;o ch&iacute;nh thống, cung cấp th&ocirc;ng tin trung thực v&agrave; kh&aacute;ch quan đ&atilde; bị v&ugrave;i dập bởi h&agrave;ng tấn những trang th&ocirc;ng tin l&aacute; cải, lộ h&agrave;ng, giật g&acirc;n, giải tr&iacute; v&agrave; nhiều thức v&ocirc; bổ kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nghe theo lời khuy&ecirc;n của cụ Nguyễn Duy Cần, t&ocirc;i kh&ocirc;ng đọc b&aacute;o nhiều. Buổi s&aacute;ng đọc lướt qua rất nhanh c&aacute;c tờ b&aacute;o để nắm tin tức trong ng&agrave;y, v&agrave; đ&aacute;nh dấu lại những b&agrave;i b&aacute;o c&oacute; vẻ chất lượng để d&agrave;nh đọc l&uacute;c cuối ng&agrave;y. T&ocirc;i kh&ocirc;ng bao giờ ngồi &ocirc;m tờ b&aacute;o gặm nhấm từng chữ một hoặc lang thang b&aacute;o mạng đọc hết b&agrave;i n&agrave;y đến b&agrave;i kh&aacute;c. Thực tế l&agrave; b&acirc;y giờ kh&ocirc;ng cần đọc b&aacute;o m&agrave; chỉ cần lướt qua mạng x&atilde; hội cũng biết được kh&aacute; nhiều th&ocirc;ng tin. Những b&agrave;i b&aacute;o ph&acirc;n t&iacute;ch tốt nhất hay tin tức n&oacute;ng hổi nhất lu&ocirc;n được nhiều người chia sẻ. Muốn biết th&ocirc;ng tin mới chỉ cần đọc những b&agrave;i được chia sẻ bởi những người c&oacute; độ tin cậy cao.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thay v&igrave; đọc b&aacute;o, t&ocirc;i đọc blog. Kh&aacute;c với b&aacute;o ch&iacute; mang tiếng l&agrave; kh&aacute;ch quan m&agrave; sự thật l&agrave; chưa hẳn thế, c&aacute;c b&agrave;i blog được x&aacute;c định r&otilde; r&agrave;ng ho&agrave;n to&agrave;n l&agrave; của chủ quan người viết. Blog c&oacute; những ưu điểm m&agrave; b&aacute;o ch&iacute; kh&ocirc;ng b&ugrave; lại được, mỗi trang blog thường ch&uacute; trọng một v&agrave;i lĩnh vực cụ thể, đưa cho ta rất nhiều mẹo hay, nhiều trải nghiệm c&aacute; nh&acirc;n, cảm x&uacute;c ri&ecirc;ng tư của người viết đối với sản phẩm, dịch vụ, địa điểm... m&agrave; ta quan t&acirc;m. V&agrave; khi cần t&ocirc;i dễ d&agrave;ng lọc bỏ được những yếu tố mang t&iacute;nh c&aacute; nh&acirc;n để lấy th&ocirc;ng tin thiết yếu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Blog của những người th&agrave;nh c&ocirc;ng, nổi tiếng hay c&aacute;c chuy&ecirc;n gia thường chia sẻ rất nhiều b&iacute; k&iacute;p, kỹ năng hoặc th&ocirc;ng tin c&oacute; gi&aacute; trị, với quan điểm g&oacute;c nh&igrave;n của họ, cho ta nhiều kiến thức trong qu&aacute; tr&igrave;nh tự học, hiểu r&otilde; hơn về con đường đi l&ecirc;n của họ v&agrave; định hướng lại suy nghĩ của bản th&acirc;n. Quan trọng l&agrave; t&igrave;m đ&uacute;ng blog để theo d&otilde;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>5. Tận dụng thời gian di chuyển.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Người ta thường tốn trung b&igrave;nh một giờ đến hai giờ đồng hồ cho việc di chuyển hằng ng&agrave;y. Bạn thường l&agrave;m g&igrave; trong khoảng thời gian ấy? D&ugrave;ng nốt những l&uacute;c di chuyển cho việc ph&aacute;t triển bản th&acirc;n th&igrave; thật l&agrave; nhất cử lưỡng tiện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thời gian l&aacute;i xe tr&ecirc;n đường sẽ l&agrave; thời gian để t&ocirc;i động n&atilde;o, sắp xếp hoặc bổ sung &yacute; tưởng cho một b&agrave;i viết b&agrave;o đ&oacute;. Nhiều b&agrave;i viết của t&ocirc;i được nảy ra v&agrave; ho&agrave;n thiện trong l&uacute;c t&ocirc;i đi tr&ecirc;n đường. Trong quyển s&aacute;ch&nbsp;<em>How to live 24 hours a day</em>&nbsp;(tạm dịch:&nbsp;<a href=\"https://docsach24.com/ebook/song-24-gio-mot-ngay.33.html\" target=\"_blank\">L&agrave;m thế n&agrave;o để sống 24 giờ một ng&agrave;y</a>), t&aacute;c giả Arnold Bennett cũng đề cập: D&agrave;nh thời gian di chuyển đi l&agrave;m buổi s&aacute;ng để suy nghĩ một c&aacute;ch s&acirc;u sắc một chủ đề n&agrave;o đ&oacute; l&agrave; c&aacute;ch tốt nhất để r&egrave;n luyện bộ n&atilde;o của bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một c&aacute;ch kh&aacute;c để tận dụng thời gian di chuyển l&agrave; nghe podcast. T&ocirc;i thường l&ecirc;n mạng tải về c&aacute;c podcast của những chương tr&igrave;nh m&igrave;nh quan t&acirc;m v&agrave; lưu lại trong điện thoại để nghe mỗi s&aacute;ng khi đi l&agrave;m. Đặc biệt l&agrave; những chương tr&igrave;nh đối thoại với c&aacute;c chuy&ecirc;n gia trong c&aacute;c lĩnh vực kh&aacute;c nhau, hoặc tin tức tr&ecirc;n thế giới, v&agrave; b&igrave;nh luận của nhiều b&ecirc;n li&ecirc;n quan. Ba mươi ph&uacute;t l&aacute;i xe đi l&agrave;m mỗi s&aacute;ng l&agrave; vừa đủ thời lượng cho một podcast, vừa nghe để nắm th&ecirc;m th&ocirc;ng tin mới, vừa để luyện tiếng Anh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>6. Tr&aacute;nh xa những thứ c&oacute; hại cho sức khỏe.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">May mắn c&oacute; bạn b&egrave; to&agrave;n những người quan t&acirc;m chăm s&oacute;c sức khỏe, n&ecirc;n t&ocirc;i cũng luyện tập được kh&aacute; nhiều th&oacute;i quen c&oacute; lợi cho sức khỏe. V&iacute; dụ như chỉ uống nước lọc, kh&ocirc;ng uống c&aacute;c loại nước uống đ&oacute;ng chai kh&aacute;c, cũng kh&ocirc;ng d&ugrave;ng đ&aacute; lạnh. Tất cả c&aacute;c loại nước ngọt, nước giải kh&aacute;t tr&agrave;n ngập tr&ecirc;n thị trường đều chứa nhiều h&oacute;a chất. Hạn chế ăn những thực phẩm qu&aacute; ngọt hoặc qu&aacute; mặn. Kh&ocirc;ng ăn s&ocirc; c&ocirc; la v&agrave; sử dụng &iacute;t những sản phẩm c&oacute; đường, v&igrave; đường khiến cho cơ thể mệt mỏi. Hạn chế thức ăn c&oacute; nguồn gốc động vật, giữ chế độ dinh dưỡng chứa nhiều rau xanh, củ quả, tr&aacute;i c&acirc;y, ngũ cốc. V&agrave; nếu được th&igrave; chuyển sang ăn chay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cơ thể l&agrave; ng&ocirc;i đền nu&ocirc;i dưỡng t&acirc;m hồn con người. Ch&uacute;ng ta sinh ra tr&ecirc;n thế giới n&agrave;y, mỗi linh hồn đều c&oacute; một sứ mệnh. Cơ thể ta, th&acirc;n x&aacute;c ta l&agrave; nơi cư ngụ của linh hồn, của t&acirc;m tr&iacute;. Th&acirc;n ta l&agrave; phương tiện để thực hiện sứ mệnh của m&igrave;nh, truyền tải những &yacute; tưởng của m&igrave;nh, chia sẻ những gi&aacute; trị v&agrave; gi&uacute;p &iacute;ch cho cuộc đời. Ch&uacute;ng ta c&oacute; thể thay đổi quần &aacute;o, nh&agrave; cửa, xe cộ, c&ocirc;ng việc, sự nghiệp, người y&ecirc;u, vợ chồng. Nhưng cơ thể l&agrave; thứ sẽ đi theo ta suốt cả đời m&agrave; ta kh&ocirc;ng thể thay đổi được. Nếu kh&ocirc;ng quan t&acirc;m chăm s&oacute;c đến cơ thể, l&agrave;m sao ta c&oacute; thể sống một cuộc đời vui tươi hạnh ph&uacute;c v&agrave; ho&agrave;n th&agrave;nh những g&igrave; m&igrave;nh mơ ước?</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>7. Lu&ocirc;n mang theo một quyển s&aacute;ch b&ecirc;n m&igrave;nh.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Lu&ocirc;n mang theo một quyển s&aacute;ch b&ecirc;n m&igrave;nh l&agrave; th&oacute;i quen của nhiều người th&agrave;nh đạt. Trong quyển&nbsp;<em>On writing</em>&nbsp;(tạm dịch: B&agrave;n về viết) của Stephen King, t&aacute;c giả chia sẻ rằng đ&acirc;y l&agrave; một trong những c&aacute;ch khiến &ocirc;ng ph&aacute;t triển trong sự nghiệp viết văn của m&igrave;nh. Trong cuộc sống c&oacute; l&uacute;c n&agrave;o m&agrave; ta kh&ocirc;ng chờ đợi một c&aacute;i g&igrave; đ&oacute;. Chờ mẹ đi si&ecirc;u thị, chờ bạn đến ăn tối hoặc cafe, chờ phỏng vấn, chờ kh&aacute;m bệnh, xếp h&agrave;ng chờ mua đồ, chờ mọi người đến đủ cho một cuộc hẹn. Tranh thủ những khoảng thời gian đ&oacute; để đọc v&agrave;i trang s&aacute;ch thay v&igrave; thụ động để thời gian tr&ocirc;i qua, cứ mỗi ng&agrave;y một một ch&uacute;t như vậy, một tuần &iacute;t nhất cũng th&ecirc;m được v&agrave;i giờ để đọc s&aacute;ch. V&agrave; kiến thức của ta tăng l&ecirc;n rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>8. Kh&ocirc;ng sa đ&agrave; v&agrave;o tr&ograve; chơi điện tử.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một người bạn của t&ocirc;i bảo từ nhỏ đến lớn cậu kh&ocirc;ng bao giờ chơi tr&ograve; chơi điện tử. V&igrave; bạn ấy c&oacute; một người cha cực kỳ nghi&ecirc;m khắc. Ng&agrave;y xưa, khi phong tr&agrave;o chơi điện tử mới bắt đầu rộ l&ecirc;n, bạn bị cấm tiệt n&ecirc;n chẳng bao giờ được chơi. Mỗi lần được 10 điểm hay đứng nhất lớp, th&igrave; bạn ấy mới được ba thưởng bằng c&aacute;ch cho ra h&agrave;ng điện tử...ng&oacute; người ta chơi trong v&ograve;ng 15 ph&uacute;t. Vậy n&ecirc;n khi lớn l&ecirc;n cậu ấy kh&ocirc;ng bị nghiện chơi điện tử như bọn con trai c&ugrave;ng lứa. Kh&ocirc;ng biết c&oacute; phải nhờ vậy m&agrave; bạn t&ocirc;i lanh lợi v&agrave; giỏi giang hơn hầu hết những người c&ugrave;ng tuổi hay kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&aacute;i g&igrave; cũng vậy, c&oacute; mặt lợi mặt hại. Nhưng nếu nhiều qu&aacute; th&igrave; sẽ lợi bất cập hại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thử tưởng tượng một anh ch&agrave;ng m&ecirc; chơi điện tử, kết h&ocirc;n rồi vẫn c&agrave;y v&agrave;i v&aacute;n<em>&nbsp;Dota&nbsp;</em>hay&nbsp;<em>Li&ecirc;n Minh Huyền Thoại&nbsp;</em>trước khi đi ngủ. Mỗi đ&ecirc;m anh đeo tai nghe cắm mặt v&agrave;o laptop chiến c&ugrave;ng lũ bạn rồi thỉnh thoảng h&eacute;t l&ecirc;n, n&agrave;o l&agrave; giết n&oacute;, sao ngu dữ, rồi n&oacute;i tiếng &quot;Đan Mạch&quot;... Vợ anh ngao ng&aacute;n ngồi &ocirc;m ti vi, rồi một m&igrave;nh đi ngủ trong đợi chờ m&ograve;n mỏi. Thật l&agrave; một cảnh tượng đầy ch&aacute;n nản.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>9. Chia sẻ th&ocirc;ng tin.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhiều bạn b&egrave; t&ocirc;i kh&ocirc;ng c&oacute; t&agrave;i khoản Facebook. Rất nhiều người kh&aacute;c hầu như chẳng chia sẻ g&igrave; tr&ecirc;n c&aacute;c trang mạng x&atilde; hội, kh&ocirc;ng blog, kh&ocirc;ng Facebook, kh&ocirc;ng Twitter, kh&ocirc;ng Instagram. T&ocirc;i c&oacute; thể hiểu v&igrave; sao, mạng x&atilde; hội thường qu&aacute; ồn &agrave;o kh&ocirc;ng ph&ugrave; hợp cho sự r&egrave;n luyện tr&iacute; &oacute;c v&agrave; tinh thần con người. Nhiều người t&agrave;i năng lựa chọn mai danh ẩn t&iacute;ch khỏi thế giới mạng, sống h&agrave;i l&ograve;ng với ch&iacute;nh m&igrave;nh v&agrave; kh&ocirc;ng c&oacute; nhu cầu thể hiện bản th&acirc;n, li&ecirc;n lạc hay giải tr&iacute; qua mạng x&atilde; hội. T&ocirc;i cũng đ&atilde; từng một thời như vậy. Nhưng từ khi t&ocirc;i chia sẻ nhiều hơn qua blog, qua Facebook hay c&aacute;c trang mạng x&atilde; hội kh&aacute;c, t&ocirc;i nhận ra rằng điều n&agrave;y cũng c&oacute; nhiều điểm hay của n&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Thứ nhất, những chia sẻ của t&ocirc;i được đ&oacute;n nhận, được đồng t&igrave;nh, phần n&agrave;o gi&uacute;p t&ocirc;i tự tin v&agrave; thoải m&aacute;i bộc lộ bản th&acirc;n nhiều hơn. Thứ hai, qua những chia sẻ tr&ecirc;n mạng n&agrave;y, t&ocirc;i quen th&ecirc;m nhiều bạn b&egrave;, những người t&agrave;i giỏi v&agrave; dễ thương, gi&uacute;p cuộc sống của m&igrave;nh phong ph&uacute; v&agrave; vui vẻ hơn. Thứ ba, khi chia sẻ những điều bổ &iacute;ch với người kh&aacute;c, t&ocirc;i cũng nhận lại được những th&ocirc;ng tin bổ &iacute;ch tương tự. Nhiều người chia sẻ với nhau, thế l&agrave; mọi người c&ugrave;ng tiến bộ. Chia sẻ th&ocirc;ng tin cực kỳ c&oacute; lợi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>10. Điều cuối c&ugrave;ng n&agrave;y l&agrave; của bạn.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bạn c&oacute; b&iacute; quyết g&igrave; gi&uacute;p cuộc sống tốt hơn? H&atilde;y chia sẻ để ch&uacute;ng ta c&ugrave;ng ho&agrave;n thiện nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>TỰ SỰ CỦA MỘT NGƯỜI HỌC NGOẠI THƯƠNG</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Tuổi thanh xu&acirc;n thường bị ph&iacute; phạm khi người ta c&ograve;n trẻ.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">(George Bernard Shaw)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Một c&ocirc; b&eacute; sinh vi&ecirc;n từng kể với t&ocirc;i về c&acirc;u chuyện thi đại học của m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Em bảo những năm phổ th&ocirc;ng em học rất giỏi, l&uacute;c n&agrave;o cũng đứng đầu khối lớp. Cả thời cấp ba, em lu&ocirc;n mơ ước sau n&agrave;y được v&agrave;o đại học Ngoại thương. Em cũng tự tin v&agrave;o học lực của m&igrave;nh, n&ecirc;n đ&atilde; nộp đơn thi v&agrave;o trường n&agrave;y. Năm em thi, Ngoại thương đột ngột n&acirc;ng điểm s&agrave;n. Em chỉ thiếu nửa điểm để v&agrave;o ng&agrave;nh m&igrave;nh th&iacute;ch. V&igrave; đ&atilde; dồn hết t&acirc;m sức v&agrave;o Ngoại thương v&agrave; kh&ocirc;ng hề chuẩn bị phương &aacute;n dự ph&ograve;ng n&ecirc;n em bị sốc rất nặng. Em miễn cưỡng nộp đơn nguyện vọng hai v&agrave;o một trường đại học kh&aacute;c. Nhưng khi v&agrave;o học em cũng kh&ocirc;ng thể h&ograve;a nhập được với bạn b&egrave;. Trong l&ograve;ng vẫn mơ tưởng tới Ngoại thương, em kh&ocirc;ng thấy hứng th&uacute; g&igrave; với chương tr&igrave;nh học hiện tại. Em nghĩ nếu m&igrave;nh học đ&uacute;ng trường, đ&uacute;ng ng&agrave;nh m&igrave;nh th&iacute;ch th&igrave; chắc mọi sự đ&atilde; kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Danh tiếng của Ngoại thương quả l&agrave; kh&ocirc;ng thể chối c&atilde;i. Trường đại học h&agrave;ng đầu trong khối c&aacute;c trường về kinh tế, được mệnh danh l&agrave; Harvard của Việt Nam, l&agrave; đ&iacute;ch đến của bao nhi&ecirc;u sĩ tử &nbsp;trước mỗi kỳ thi đại học.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cuộc tr&ograve; chuyện với c&ocirc; b&eacute; sinh vi&ecirc;n l&agrave;m t&ocirc;i nhớ lại ng&agrave;y xưa của m&igrave;nh. L&uacute;c nộp đơn thi đại học, chẳng biết hướng nghiệp l&agrave; g&igrave;, cũng chẳng được ai tư vấn cho. Chọn thi Ngoại thương v&igrave; một l&yacute; do rất chi l&agrave;...&quot;dốt&quot;: V&igrave; một người chị t&ocirc;i ngưỡng mộ cũng đang học trường đ&oacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hồi đ&oacute; cứ nghĩ Ngoại thương l&agrave; một c&aacute;i g&igrave; đ&oacute; cao xa sang chảnh v&agrave; tuyệt vời &ocirc;ng mặt trời lắm. Nhưng thực sự kh&ocirc;ng hề biết ch&iacute;nh x&aacute;c trường đ&agrave;o tạo những g&igrave; v&agrave; học xong rồi sẽ l&agrave;m nghề g&igrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Đến khi v&agrave;o trường rồi mới biết, rằng học Ngoại thương l&agrave;:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">L&agrave; hai năm đầu ở trường, sĩ số lớp vắng gần ph&acirc;n nửa so với ng&agrave;y đầu nhập học do bọn bạn c&ugrave;ng lớp đua nhau bỏ đi du học.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">L&agrave; nếu học ở Ngoại thương cơ sở II tại S&agrave;i G&ograve;n, th&igrave; c&oacute; khả năng thất vọng cao với chất lượng cơ sở vật chất. Diện t&iacute;ch trường b&eacute; x&iacute;u. Năm đầu đại học của t&ocirc;i trường c&ograve;n kh&ocirc;ng c&oacute; cả cơ sở ri&ecirc;ng, phải học k&eacute; với Cao đẳng kinh tế đối ngoại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">L&agrave; cảm gi&aacute;c xa vời lạ lẫm khi học c&ugrave;ng trường với to&agrave;n người nổi tiếng. Nhỏ đ&oacute; mới đăng quang hoa hậu. Đứa kia th&igrave; người mẫu. Đứa nọ l&agrave; diễn vi&ecirc;n. Đứa kh&aacute;c MC dẫn chương tr&igrave;nh truyền h&igrave;nh nổi tiếng. Đứa nữa th&igrave; ca sĩ, mỗi lần n&oacute; thử giọng l&agrave; tiếng của n&oacute; vang l&ecirc;n khắp cả trường, đứng ở tầng năm nghe r&otilde; giọng n&oacute; từ tầng một.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Những đứa kh&aacute;c kh&ocirc;ng trong giới showbiz th&igrave; bỏ học đi l&agrave;m, kinh doanh th&agrave;nh c&ocirc;ng, doanh nh&acirc;n c&aacute;c kiểu. Rồi nhiều đứa kh&aacute;c nữa đạt giải hết cuộc thi n&agrave;y tới cuộc thi nọ n&agrave;o &yacute; tưởng s&aacute;ng tạo n&agrave;o đại sứ m&ocirc;i trường n&agrave;o nh&agrave; thiết kế n&agrave;o nh&agrave; quảng c&aacute;o trẻ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&ograve;n v&ocirc; số sinh vi&ecirc;n kh&aacute;c nữa trong lớp th&igrave; l&uacute;c n&agrave;o cũng nhanh nhẹn hoạt b&aacute;t dư thừa năng lượng. Mỗi lần v&agrave;o lớp thuyết tr&igrave;nh ch&uacute;ng n&oacute; đứng n&oacute;i suốt buổi, tranh luận tới c&ugrave;ng, tự tin thể hiện ch&iacute;nh m&igrave;nh, kh&ocirc;ng ngại l&agrave;m tr&ograve; đi&ecirc;n rồ. Nghe m&agrave; ch&oacute;ng hết cả mặt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Ng&oacute; lại bản th&acirc;n m&igrave;nh, s&aacute;ng cắm đầu đến lớp, chiều quay mặt về nh&agrave;. Nằm trong th&agrave;nh phần thường d&acirc;n của Ngoại thương. Đ&ocirc;i khi t&ocirc;i tự hỏi kh&ocirc;ng biết m&igrave;nh c&oacute; v&agrave;o nhầm trường kh&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Học Ngoại thương đ&ocirc;i khi cũng hơi &aacute;p lực. &Aacute;p lực ở chỗ mỗi lần được hỏi học trường g&igrave; m&agrave; n&oacute;i ra t&ecirc;n đ&oacute; l&agrave; người đối diện trầm trồ khen ngợi, ồ d&acirc;n trường đ&oacute; năng động lắm nha, giỏi giang lắm nha. Chỉ biết cười trừ &nbsp;kh&ocirc;ng biết n&oacute;i g&igrave;. V&igrave; thời đ&oacute; t&ocirc;i thấy m&igrave;nh chả c&oacute; t&iacute; g&igrave; giống vậy. V&igrave; biết nhiều đứa bạn của t&ocirc;i cũng trầm hiền im im. To&agrave;n bọn kh&ocirc;ng biết c&aacute;ch thể hiện bản th&acirc;n, cũng chưa biết v&igrave; sao m&igrave;nh rơi xuống tr&aacute;i đất. Vẫn nhiều trăn trở đời sinh vi&ecirc;n. Vẫn s&aacute;ng đạp xe l&ecirc;n giảng đường, ăn m&igrave; g&oacute;i, b&aacute;nh m&igrave;, cơm bụi. Ph&aacute;t tờ rơi, phụ b&agrave;n, dạy th&ecirc;m, mưu sinh lăn lộn với cuộc sống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Học Ngoại thương l&agrave; ngỡ ng&agrave;ng nhận ra một sự thật phũ ph&agrave;ng rằng m&igrave;nh chả học được g&igrave; nhiều. Cả thời sinh vi&ecirc;n, t&ocirc;i thấy m&igrave;nh nếu c&oacute; tiến bộ ch&uacute;t n&agrave;o th&igrave; phần lớn l&agrave; do chơi chung với bọn bạn giỏi giang cần c&ugrave;. Ai đ&atilde; học qua Ngoại thương rồi đềi biết rằng danh tiếng của trường c&oacute; được kh&ocirc;ng phải nhờ v&agrave;o chất lượng đ&agrave;o tạo, m&agrave; chủ yếu l&agrave; từ sinh vi&ecirc;n. Điểm đầu v&agrave;o cao, th&igrave; bản th&acirc;n sinh vi&ecirc;n đ&atilde; giỏi sẵn, chẳng đ&agrave;o tạo nhiều th&igrave; ch&uacute;ng vẫn cứ tự vươn l&ecirc;n th&agrave;nh c&ocirc;ng trong cuộc sống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Học Ngoại thương l&agrave; mỗi khi ai đ&oacute; n&oacute;i sinh vi&ecirc;n Ngoại thương chảnh hay tự tin qu&aacute; mức th&igrave; muốn g&acirc;n cổ l&ecirc;n c&atilde;i. Muốn n&oacute;i rằng m&igrave;nh đ&acirc;y cũng Ngoại thương, c&oacute; d&aacute;m chảnh, c&oacute; tự tin đ&acirc;u. Vẫn sống b&igrave;nh lặng l&agrave;m việc b&igrave;nh thường, n&oacute;i năng lựa trước lựa sau sợ đụng chạm mất l&ograve;ng. Vẫn nhiều lo sợ, vẫn mặc cảm tự ti, vẫn run khi đứng trước nh&agrave; tuyển dụng, vẫn đầy hoang mang lo lắng tr&ecirc;n đường đời. L&agrave;m g&igrave; c&oacute; vụ đ&oacute;ng cốp c&aacute;i m&aacute;c Ngoại thương l&ecirc;n người m&agrave; được quyền chảnh, được quyền ỷ lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Học Ngoại thương cũng l&agrave; khi nghe tin sinh vi&ecirc;n Ngoại thương đ&ograve;i lương th&aacute;ng ngh&igrave;n đ&ocirc; mới chịu đi l&agrave;m th&igrave; tự hỏi liệu trường m&igrave;nh c&oacute; đứa đi&ecirc;n kh&ugrave;ng dữ vậy sao ta. Thấy mấy đứa bạn kh&aacute;c to&agrave;n c&oacute; đầu &oacute;c biết suy nghĩ thực tế m&agrave;. Tự hỏi xong rồi tiếp tục cắm mặt v&agrave;o b&agrave;n giấy với mức lương v&agrave;i triệu đồng một th&aacute;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Lớp đại học của t&ocirc;i mỗi lần họp mặt vẫn cứ bảo nhau: &quot;Th&uacute; thật với m&agrave;y bốn năm ở trường m&igrave;nh tao thấy m&igrave;nh chả được c&aacute;i g&igrave;. Chỉ được c&aacute;i c&oacute; ch&uacute;ng m&agrave;y l&agrave;m bạn.&quot; Thực ra đ&oacute; kh&ocirc;ng phải l&agrave; lỗi của c&aacute;c thầy c&ocirc; hay của ban gi&aacute;m hiệu, t&ocirc;i biết c&aacute;c thầy c&ocirc; cũng đ&atilde; cố gắng hết sức trong khả năng của m&igrave;nh. Họ cũng mệt mỏi kh&ocirc;ng k&eacute;m v&igrave; những hạn chế trong hệ thống đ&agrave;o tạo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhưng nếu em n&agrave;o mười t&aacute;m tuổi đến hỏi t&ocirc;i c&oacute; n&ecirc;n thi v&agrave;o Ngoại thương kh&ocirc;ng th&igrave; c&acirc;u trả lời vẫn l&agrave; c&oacute;. V&igrave; kh&ocirc;ng v&agrave;o Ngoại thương th&igrave; l&agrave;m g&igrave; biết trường n&agrave;o kh&aacute;c, so đi t&iacute;nh lại th&igrave; Ngoại thương l&agrave; tốt nhất trong khối kinh tế rồi. V&igrave; v&agrave;o trường m&agrave; c&oacute; bọn bạn giỏi th&igrave; m&igrave;nh cũng được học hỏi theo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">V&igrave; d&acirc;n Ngoại thương, mỗi đứa một m&agrave;u nhưng m&agrave; lại c&oacute; một điểm chung. Đ&oacute; l&agrave; giờ bắt gặp ai cựu Ngoại thương ở ngo&agrave;i đời, t&ocirc;i vẫn biết chắc rằng d&ugrave; m&igrave;nh c&oacute; thể th&iacute;ch kh&ocirc;ng th&iacute;ch người đ&oacute;, chơi được với họ hoặc kh&ocirc;ng, nhưng sẽ lu&ocirc;n c&ocirc;ng nhận rằng họ s&acirc;u sắc, rằng họ c&oacute; khả năng nh&igrave;n nhận vấn đề, họ l&agrave;m được việc, v&agrave; kh&ocirc;ng &quot;thiếu muối&quot; vớ va vớ vẩn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&oacute; học Ngoại thương mới biết d&ugrave; c&oacute; tốt nghiệp trường g&igrave; đi chăng nữa, th&igrave; c&oacute; rất nhiều người sau khi ra trường vẫn lạc lối chả biết đời m&igrave;nh sẽ đi đ&acirc;u về đ&acirc;u, v&agrave; nhiều người kh&aacute;c kh&ocirc;ng thể n&agrave;o được gọi l&agrave; th&agrave;nh c&ocirc;ng theo chuẩn mực th&ocirc;ng thường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i kể cho c&ocirc; b&eacute; đang mở v&agrave;o Ngoại thương nghe c&acirc;u chuyện của m&igrave;nh, n&oacute;i rằng ngay cả Harvard của Việt Nam c&ograve;n thế, th&igrave; đừng tr&ocirc;ng chờ g&igrave; v&agrave;o sự học ở c&aacute;c đại học kh&aacute;c. T&ocirc;i n&oacute;i, học trường g&igrave; kh&ocirc;ng hề quyết định sự th&agrave;nh bại của em. Điều quyết định rằng em c&oacute; th&agrave;nh c&ocirc;ng khi ra đời hay kh&ocirc;ng, đ&oacute; l&agrave; khả năng tự học.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bốn năm đại học, điều hối tiếc lớn nhất của t&ocirc;i l&agrave; kh&ocirc;ng chủ động tiếp thu kiến thức. Chỉ biết học theo chương tr&igrave;nh của trường, học giỏi c&aacute;c m&ocirc;n ở trường l&agrave; đủ. M&agrave; qu&ecirc;n đọc s&aacute;ch, qu&ecirc;n tự học, qu&ecirc;n đ&agrave;o s&acirc;u nghi&ecirc;n cứu những g&igrave; m&igrave;nh quan t&acirc;m. Ra trường ngơ ng&aacute;c bấn loạn. Kiến thức ở trường hầu như bỏ x&oacute;, phải học việc lại từ đầu, m&agrave; c&aacute;i cần phải học nhiều nhất l&agrave; kỹ năng x&atilde; hội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bốn năm đại học, điều hối tiếc thứ nh&igrave; l&agrave; kh&ocirc;ng l&agrave;m nhiều. Kh&ocirc;ng tham gia c&aacute;c c&acirc;u lạc bộ sinh vi&ecirc;n, kh&ocirc;ng tổ chức chương tr&igrave;nh, kh&ocirc;ng biết chỗ n&agrave;o để hoạt động thiện nguyện. Giờ ra trường rồi mới thấy v&ocirc; số điều c&oacute; thể l&agrave;m l&uacute;c m&igrave;nh c&ograve;n l&agrave; sinh vi&ecirc;n. Sau ra trường ng&acirc;y ng&acirc;y thơ thơ, phải v&aacute;c mặt đi học lại những thứ ở trường kh&ocirc;ng dạy.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cho n&ecirc;n em mười t&aacute;m tuổi ơi, kỳ tuyển sinh lần n&agrave;y, nếu em đậu Ngoại thương, hay đậu đ&uacute;ng trường m&igrave;nh muốn, th&igrave; đừng vội cho đ&oacute; l&agrave; th&agrave;nh c&ocirc;ng. Nếu rớt đại học cũng đừng thất vọng. Kh&ocirc;ng quan trọng l&agrave; m&igrave;nh học trường g&igrave;, quan trọng l&agrave; m&igrave;nh tự học thế n&agrave;o. Th&agrave;nh c&ocirc;ng kh&ocirc;ng hề ưu ti&ecirc;n những người d&aacute;n m&aacute;c B&aacute;ch khoa, Kinh tế, Ngoại thương, hay du học.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Người thầy trong quyển s&aacute;ch&nbsp;<em>Điều nhỏ nhặt tạo n&ecirc;n số phận</em>&nbsp;(t&aacute;c giả Andy Andrews) n&ecirc;u ra c&acirc;u hỏi: H&igrave;nh thức học tập tuyền t&uacute;y nhất l&agrave; g&igrave;? Rồi &ocirc;ng tự trả lời: &quot;H&igrave;nh thức thuần t&uacute;y nhất của việc học tập l&agrave; từng c&aacute; nh&acirc;n tự nhận ra ai l&agrave; người tiếp tục h&agrave;nh tr&igrave;nh t&igrave;m kiếm tri thức b&ecirc;n ngo&agrave;i lớp học, với niềm đam m&ecirc; ngập tr&agrave;n để c&oacute; sự hiểu biết.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Than &ocirc;i, khi nhận ra ch&acirc;n l&yacute; đ&oacute;, th&igrave; t&ocirc;i đ&atilde; hết thời sinh vi&ecirc;n của m&igrave;nh, đ&atilde; rất nhiều thời gian l&atilde;ng ph&iacute; tr&ocirc;i qua.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">***</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>GI&Aacute; TRỊ CỦA NGHỊCH CẢNH</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">&quot;Khi mọi thứ đang chống lại bạn, h&atilde;y nhớ rằng, m&aacute;y bay cất c&aacute;nh bằng c&aacute;ch bay ngược chiều gi&oacute; chứ kh&ocirc;ng phải xu&ocirc;i chiều gi&oacute;.&quot;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">(Henry Ford)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">C&oacute; một thời gian rảnh rỗi, t&ocirc;i tiến h&agrave;nh một dự &aacute;n c&aacute; nh&acirc;n, nhận tư vấn t&igrave;m việc l&agrave;m cho c&aacute;c bạn sinh vi&ecirc;n sắp v&agrave; mới ra trường, với điều kiện rằng đổi lại, họ sẽ kể cho t&ocirc;i nghe một c&acirc;u chuyện. Bất kỳ c&acirc;u chuyện g&igrave; họ muốn. Từ chuyện con m&egrave;o nh&agrave; m&igrave;nh hay con ch&oacute; nh&agrave; h&agrave;ng x&oacute;m, chuyện của bản th&acirc;n, chuyện của gia đ&igrave;nh hay một c&acirc;u chuyện m&agrave; họ từng chứng kiến. Chỉ cần l&agrave;m t&ocirc;i th&iacute;ch c&acirc;u chuyện đ&oacute;, t&ocirc;i sẽ li&ecirc;n lạc v&agrave; tư vấn t&igrave;m việc miễn ph&iacute; cho họ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bạn đo&aacute;n thử c&aacute;c c&acirc;u chuyện sẽ l&agrave; g&igrave;? Phần lớn những c&acirc;u chuyện l&agrave; về tuổi thơ khốn kh&oacute;, hiện tại t&uacute;ng thiếu, tranh đấu v&igrave; miếng cơm manh &aacute;o, hoang mang đi t&igrave;m một con đường ri&ecirc;ng cho m&igrave;nh. C&oacute; c&acirc;u chuyện khiến t&ocirc;i mất ngủ, trằn trọc v&igrave; kh&ocirc;ng biết n&ecirc;n l&agrave;m thế n&agrave;o để gi&uacute;p chủ nh&acirc;n c&acirc;u chuyện. C&oacute; c&acirc;u chuyện l&agrave;m t&ocirc;i x&uacute;c động đồng cảm v&igrave; những kỷ niệm tuổi thơ kh&ocirc;ng mấy &ecirc;m đẹp. Cũng c&oacute; những c&acirc;u chuyện khiến t&ocirc;i bực tức v&igrave; viết sai ch&iacute;nh tả qu&aacute; nhiều, hoặc c&aacute;ch giao tiếp kh&ocirc;ng ph&ugrave; hợp, bởi t&ocirc;i vốn nhạy cảm ng&ocirc;n từ. Đ&atilde; c&oacute; l&uacute;c t&ocirc;i thấy bất lực trước nỗi khổ của con người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Cũng c&oacute; một v&agrave;i lần, t&ocirc;i nhận được những tin nhắn rất khẩn thiết, kiểu như chị ơi em muốn vươn l&ecirc;n, chị ơi em muốn tho&aacute;t khỏi cảnh ngh&egrave;o, muốn c&oacute; xe hơi trong v&agrave;i ba năm nữa, chị ơi h&atilde;y cho em cơ hội g&igrave; đ&oacute; để học hỏi kinh nghiệm, em phải l&agrave;m sao đ&acirc;y, chị ơi h&atilde;y gi&uacute;p em... T&ocirc;i thở d&agrave;i. Muốn ph&aacute;t triển bản th&acirc;n cần sự nỗ lực hết sức từ b&ecirc;n trong, hỗ trợ b&ecirc;n ngo&agrave;i chỉ l&agrave; thứ yếu. Thực sự nếu c&oacute; thể gi&uacute;p c&aacute;c bạn ấy thay đổi cuộc đời nhanh v&agrave; dễ d&agrave;ng như thế, t&ocirc;i sẽ l&agrave;m ngay. M&agrave; nếu t&ocirc;i c&oacute; c&aacute;i quyền năng của Ch&uacute;a trời như thế, t&ocirc;i sẽ l&agrave;m ph&eacute;p để biến bản th&acirc;n m&igrave;nh th&agrave;nh Paulo Coelho hay J.K.Powling chỉ ngay sau một đ&ecirc;m ngủ dậy. Ai chẳng ước mong th&agrave;nh c&ocirc;ng đến trong nh&aacute;y mắt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">T&ocirc;i chỉ muốn nhắn nhủ những bạn trẻ đang bơ vơ tr&ecirc;n con đường cuộc sống rằng, nếu bạn đang phải trải qua những ng&agrave;y khốn kh&oacute;, phải đương đầu với nghịch cảnh cuộc đời, th&igrave; đ&oacute; thực sự l&agrave; một m&oacute;n qu&agrave; v&ocirc; gi&aacute;. C&oacute; thể l&uacute;c đang khổ sở với những vấn đề của m&igrave;nh, ta chỉ m&atilde;i vật v&atilde; đau buồn. Nhưng sau n&agrave;y nh&igrave;n lại, ta sẽ thấy rằng những kh&oacute; khăn đ&atilde; đương đầu gi&uacute;p tạo n&ecirc;n c&acirc;u chuyện của ri&ecirc;ng ta.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">H&atilde;y lấy bộ truyện nổi tiếng thế giới Harry Potter ra l&agrave;m v&iacute; dụ. T&ocirc;i cực kỳ th&iacute;ch Harry Potter. Nhớ những đ&ecirc;m t&ocirc;i thức đến ba bốn giờ s&aacute;ng &ocirc;m quyển truyện trong tay, vừa đọc vừa quẹt nước mắt. Harry v&agrave; những người bạn của cậu chỉ l&agrave; tưởng tượng. Thế giới của ph&ugrave; thủy, đũa thần v&agrave; chổi bay c&oacute; thể kh&ocirc;ng c&oacute; thật. Nhưng nỗi buồn v&agrave; sự tuyệt vọng l&agrave; thật. Cảm gi&aacute;c đau đớn v&agrave; c&ocirc; độc l&agrave; thật. Sợ h&atilde;i v&agrave; mất m&aacute;t l&agrave; thật. C&oacute; ai trong ch&uacute;ng ta chưng từng một lần trong đời thức dậy m&agrave; kh&ocirc;ng biết h&ocirc;m nay m&igrave;nh phải l&agrave;m g&igrave;, nh&igrave;n mọi thứ xung quanh chỉ to&agrave;n m&agrave;u x&aacute;m v&agrave; thấy m&igrave;nh đang lạc lối? C&oacute; ai sống tr&ecirc;n đời m&agrave; chưa từng biết đến khổ đau, chưa từng bị chối bỏ bởi người m&igrave;nh y&ecirc;u thương, bởi gia đ&igrave;nh hay x&atilde; hội? Nhưng nỗi đau trong c&acirc;u chuyện đ&atilde; kết nối người đọc v&agrave; t&aacute;c giả, kết nối người đọc với nhau, v&agrave; g&oacute;p phần l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng của bộ truyện. V&agrave; những mất m&aacute;t đau thương ấy ch&acirc;n thật, bởi v&igrave; được viết bởi một con người đ&atilde; nếm trải bao điều khốn kh&oacute; của cuộc đời. J.K.Rowling phải chứng kiến c&aacute;i chết của người mẹ khi vừa hai mươi mấy tuổi, rồi h&ocirc;n nh&acirc;n tan vỡ, ngh&egrave;o hết mức c&oacute; thể ngh&egrave;o, v&agrave; trở th&agrave;nh một b&agrave; mẹ đơn th&acirc;n. Nếu kh&ocirc;ng trải qua từng ấy đau khổ, chắc g&igrave; Rowling đ&atilde; viết n&ecirc;n c&acirc;u chuyện s&acirc;u sắc dường vậy, chắc g&igrave; Harry Potter đ&atilde; lay động l&ograve;ng người v&agrave; th&agrave;nh c&ocirc;ng đến thế? 1</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vậy n&ecirc;n, nếu bạn đang phải vật lộn với cuộc sống, đang phải đấu tranh với những kh&oacute; khăn trong đời, th&igrave; h&atilde;y cảm ơn Thượng đế v&igrave; m&oacute;n qu&agrave; của Người, h&atilde;y &ocirc;m gh&igrave; lấy cuộc đời bạn với tất cả nhưng đớn đau khổ sở của n&oacute;, v&agrave; sống với tất cả sức lực v&agrave; nhiệt huyết của bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">V&igrave; với những kinh nghiệm của m&igrave;nh, v&agrave; kinh nghiệm của tất cả những người đ&atilde; đi trước ch&uacute;ng ta, t&ocirc;i biết rằng qu&atilde;ng đường gian kh&oacute; kh&ocirc;ng m&atilde;i k&eacute;o d&agrave;i. Những kh&oacute; khăn gi&uacute;p l&agrave;m n&ecirc;n c&acirc;u chuyện của bạn. C&ocirc; g&aacute;i c&oacute; tuổi thơ bị ngược đ&atilde;i trở th&agrave;nh người hoạt động t&igrave;nh nguyện t&iacute;ch cực v&igrave; quyền trẻ em, ch&agrave;ng trai từng tuyệt vọng đến mức muốn tự tử v&igrave; thất bạ rồi trở th&agrave;nh doanh nh&acirc;n th&agrave;nh c&ocirc;ng, một người từng viết c&acirc;u n&agrave;o sai ch&iacute;nh tả c&acirc;u nấy lại l&agrave; một t&aacute;c giả ăn kh&aacute;ch sau những ng&agrave;y th&aacute;ng khổ luyện. C&acirc;u chuyện của bạn như &nbsp;thế n&agrave;o l&agrave; do bạn quyết định. H&atilde;y viết l&ecirc;n c&acirc;u chuyện của cuộc đời bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vậy n&ecirc;n, bạn th&acirc;n mến, đừng cầu nguyện để đời bạn kh&ocirc;ng phải trải qua những nghịch cảnh, kh&oacute; khăn. M&agrave; cầu nguyện để bạn đủ sức lực để đương đầu với những s&oacute;ng gi&oacute; của cuộc đời. H&atilde;y mỉm cười, nh&igrave;n l&ecirc;n bầu trời cao xa. D&ugrave; l&agrave; ng&agrave;y trời xanh nắng đẹp hay ng&agrave;y m&acirc;y m&ugrave; &acirc;m u, th&igrave; bạn cũng sẽ kh&ocirc;ng c&oacute; cơ hội nh&igrave;n thấy bầu trời ch&iacute;nh x&aacute;c như thế n&agrave;y lần nữa. Ng&agrave;y h&ocirc;m nay tr&ocirc;i qua sẽ l&agrave; m&atilde;i m&atilde;i kh&ocirc;ng quay trở lại. Mọi chuyện rồi cũng sẽ qua. Trong khi đ&oacute;, th&igrave; đừng ch&igrave;m trong những thất vọng của đời m&igrave;nh nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>H&atilde;y bơi đi.&nbsp;</strong></span></p>', 0, 'phan-1-toi-da-hoc-nhu-the-nao', NULL, NULL);

