-- ----------------------------
-- Records of truyen
-- ----------------------------
INSERT INTO `truyen` VALUES (19, 'Ỷ thiên đồ long ký', 'Ỷ Thiên Đồ Long ký (Trung văn phồn thể: 倚天屠龍記), còn được dịch ra tiếng Việt là Cô gái Đồ Long ( chính là Chu Chỉ Nhược vì cô đã khám phá ra được bí mật), là một tiểu thuyết võ hiệp của nhà văn Kim Dung. Đây là cuốn cuối cùng trong bộ tiểu thuyết Xạ điêu tam khúc.', 13, 19, 'y-thien-do-long-ky46.jpg', 'y-thien-do-long-ky', 0, 'Kim Dung', '2021-11-01 13:36:01', '2021-12-09 13:36:04');
INSERT INTO `truyen` VALUES (14, 'Một lít nước mắt', 'Những ngày mùa hạ trôi nhanh, không dưng tôi nhớ tới dòng trạng thái Facebook từ lâu lắm của một người bạn: \"Cuộc sống này có quá ngắn ngủi hay chăng?\"', 11, 15, 'mot-lit-nuoc-mat2.jpg', 'mot-lit-nuoc-mat', 0, 'Kito Aya', '2021-12-09 13:36:07', '2021-12-09 13:36:37');
INSERT INTO `truyen` VALUES (15, 'Trên đường băng', '\"Khi còn trẻ, hãy ra ngoài nhiều hơn ở nhà. Hãy nhào vô xin người khác \"bóc hết, lột sạch\" khả năng của mình.', 11, 15, 'tren-duong-bang94.jpg', 'tren-duong-bang', 0, 'Tony buổi sáng', '2021-12-09 13:36:09', '2021-12-09 13:36:40');
INSERT INTO `truyen` VALUES (16, 'Đừng vì một người dưng', 'Có phải thanh xuân vốn dĩ là thế? Yêu một người xa lạ,', 12, 16, 'dung-vi-mot-nguoi-dung28.jpg', 'dung-vi-mot-nguoi-dung', 0, 'Tâm Storm', '2021-11-03 13:36:12', '2021-12-09 13:36:44');
INSERT INTO `truyen` VALUES (17, 'Điều ước', 'Những câu nói ấy có thể đối với bạn là hết sức bình thường, nhưng đối với tôi nó là niềm mơ ước mãi mãi không được thực hiện.', 12, 16, 'dieu-uoc30.png', 'dieu-uoc', 0, 'Chị gái ni béo', '2021-12-09 13:36:14', '2021-12-09 13:36:49');
INSERT INTO `truyen` VALUES (18, 'Xa nhà mùa dịch', 'Bước vào cái tuổi 18 tôi bắt đầu suy nghĩ cho tương lai của chính tôi. Phải chọn trường nào? Ở đâu?', 12, 16, 'xa-nha-mua-dich72.jpg', 'xa-nha-mua-dich', 0, 'Sưu tầm', '2021-11-18 13:36:18', '2021-12-09 13:36:51');
INSERT INTO `truyen` VALUES (11, 'Chàng trai và chiếc rìu', 'Cuộc sống của chúng ta cũng như vậy. Chăm chỉ không có gì là xấu, nhưng..', 10, 13, 'chang-trai-va-chec-riu82.jpg', 'chang-trai-va-chiec-riu', 0, 'Sưu tầm', '2021-11-23 13:36:21', '2021-12-09 13:36:54');
INSERT INTO `truyen` VALUES (12, 'Người Cô Độc', 'Người cô độc - “Là mọi thứ trừ sự trần tục”. Đây là tất cả những gì có thể nói về cuốn sách “Người cô độc” – một bức chân dung đầy khắc khoải dưới ngòi bút của tiểu thuyết gia Christopher Isherwood.', 11, 14, 'nguoi_co_doc41.jpg', 'nguoi-co-doc', 0, 'Christorpher Isherwood', '2021-11-19 13:36:23', '2021-12-09 13:36:57');
INSERT INTO `truyen` VALUES (13, 'Tuổi trẻ đáng giá bao nhiêu', '\"Bạn hối tiếc vì không nắm bắt lấy một cơ hội nào đó, chẳng có ai phải mất ngủ.', 11, 15, 'tuoi-tre-dang-gia-bao-nhieu54.jpg', 'tuoi-tre-dang-gia-bao-nhieu', 0, 'Rosie Nguyễn', '2021-12-09 13:36:25', '2021-12-09 13:37:00');
INSERT INTO `truyen` VALUES (9, 'Tình cờ gặp lại nhau', 'Một chiều mưa đầy, nghe bài tình cờ gặp lại nhau của ca sĩ Thái Châu, lại nhớ về người, dù cuộc gặp gỡ năm trước không phải tình cờ...', 8, 4, 'tinh-co-gap-lai-nhau2.jpg', 'tinh-co-gap-lai-nhau', 0, 'Lê Hạnh', '2021-12-09 13:36:28', '2021-12-09 13:37:03');
INSERT INTO `truyen` VALUES (10, 'Tháng năm tuổi trẻ', 'Tuổi trẻ là quãng thời gian đẹp và đáng nhớ nhất trong cuộc đời mỗi người. Là khoảng thời gian ta nhận thức được giá trị của bản thân, tìm kiếm con đường dành riêng cho mình, sống với bao đam mê, khát vọng và nếm trải mùi vị đắng cay, ngọt ngào của tình yêu. Tuổi trẻ gắn liền với những kỷ niệm khó quên hữu ích cho suốt chặng đường đời chúng ta.', 9, 10, 'thang-nam-tuoi-tre0.jpg', 'thang-nam-tuoi-tre', 0, 'Hoa anh đào', '2021-11-12 13:36:31', '2021-12-09 13:37:06');
INSERT INTO `truyen` VALUES (20, 'Cảm ơn mọi người đã lắng nghe', 'Cảm ơn mọi người', 14, 24, 'sach-hay-ve-loi-cam-on-cover38.png', 'cam-on-moi-nguoi-da-lang-nghe', 1, 'Phạm Thanh Trường', '2021-12-09 13:36:34', '2021-12-09 13:37:09');

-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `email_verified_at` timestamp(0) NULL DEFAULT NULL,
  `password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `remember_token` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `users_email_unique`(`email`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

